// RegionDashboard.js
// Enhanced Region Admin dashboard with farmer profiles, request management, and product management
// Requirements covered:
// - Farmer profiles visibility for all farmers within region scope
// - Filter requests by status: pending, accepted, approved, rejected
// - Accept/reject/approve functionality for region admins
// - Product management with category dropdown
// - Zone admin management within region scope

import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const API = 'http://localhost:5000/api/admins';

export default function RegionDashboard() {
    const navigate = useNavigate();

    // Auth
    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);

    // Tabs
    const [activeTab, setActiveTab] = useState('overview'); // overview | farmers | requests | products | zones

    // Farmers state
    const [farmers, setFarmers] = useState([]);
    const [filteredFarmers, setFilteredFarmers] = useState([]);
    const [farmerSearchQuery, setFarmerSearchQuery] = useState('');
    const [selectedFarmer, setSelectedFarmer] = useState(null);
    const [showFarmerModal, setShowFarmerModal] = useState(false);

    // Requests state
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [statusFilter, setStatusFilter] = useState('all'); // all | pending | accepted | approved | rejected
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [requestAction, setRequestAction] = useState(''); // Approve | Accept | Reject
    const [requestFeedback, setRequestFeedback] = useState('');

    // Products state
    const [productsAll, setProductsAll] = useState([]);
    const [productsMine, setProductsMine] = useState([]);
    const [productsOthers, setProductsOthers] = useState([]);
    const [productView, setProductView] = useState('all'); // all | my | others
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        subCategory: '',
        amount: '',
        price: '',
        unit: '',
        description: '',
        manufacturer: '',
        expiryDate: ''
    });
    const [productError, setProductError] = useState('');

    // Product categories with subcategories (same as Kebele dashboard)
    const productCategories = {
        'Chemicals': {
            subcategories: ['Fertilizers', 'Pesticides', 'Herbicides', 'Fungicides', 'Insecticides', 'Growth Regulators'],
            units: ['kg', 'liters', 'bottles', 'sachets']
        },
        'Machinery': {
            subcategories: ['Tractors', 'Plows', 'Harvesters', 'Seeders', 'Cultivators', 'Irrigation Equipment'],
            units: ['units', 'pieces']
        },
        'Seeds': {
            subcategories: ['Cereal Seeds', 'Vegetable Seeds', 'Fruit Seeds', 'Legume Seeds', 'Cash Crop Seeds'],
            units: ['kg', 'packets', 'bags']
        },
        'Tools': {
            subcategories: ['Hand Tools', 'Cutting Tools', 'Measuring Tools', 'Safety Equipment', 'Storage Equipment'],
            units: ['pieces', 'sets', 'units']
        },
        'Livestock': {
            subcategories: ['Feed', 'Vaccines', 'Supplements', 'Equipment', 'Medicine'],
            units: ['kg', 'liters', 'bottles', 'doses']
        },
        'Organic': {
            subcategories: ['Organic Fertilizers', 'Bio-pesticides', 'Compost', 'Organic Seeds'],
            units: ['kg', 'liters', 'bags']
        }
    };

    // Zone admins state
    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [showZoneForm, setShowZoneForm] = useState(false);
    const [editingZone, setEditingZone] = useState(null);
    const [zoneForm, setZoneForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        zoneName: ''
    });
    const [zoneError, setZoneError] = useState('');

    // Helpers
    const authHeaders = useCallback(() => {
        const token = localStorage.getItem('token');
        return {
            'Authorization': 'Bearer ' + token,
            'Content-Type': 'application/json'
        };
    }, []);

    // Initial auth + fetch
    useEffect(() => {
        const token = localStorage.getItem('token');
        const userDataRaw = localStorage.getItem('user');
        const userType = localStorage.getItem('userType');

        if (!token || !userDataRaw || userType !== 'Region') {
            navigate('/login');
            return;
        }
        const parsed = JSON.parse(userDataRaw);
        setUser(parsed);

        if (document.documentElement.classList.contains('dark')) {
            setDarkMode(true);
        }

        // kick off fetches
        fetchAll();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [navigate]);

    const fetchRequests = useCallback(async() => {
        // We use the status-details endpoint so Region can see level-by-level fields
        const res = await fetch(API + '/requests/status', { headers: authHeaders() });
        if (!res.ok) throw new Error('Failed to load requests');
        const data = await res.json();
        setRequests(Array.isArray(data) ? data : []);
    }, [authHeaders]);

    const fetchProducts = useCallback(async() => {
        const resAll = await fetch(API + '/getproducts', { headers: authHeaders() });
        if (resAll.ok) setProductsAll(await resAll.json());

        const resMine = await fetch(API + '/myproducts', { headers: authHeaders() });
        if (resMine.ok) setProductsMine(await resMine.json());

        const resOthers = await fetch(API + '/otherproducts', { headers: authHeaders() });
        if (resOthers.ok) setProductsOthers(await resOthers.json());
    }, [authHeaders]);

    const fetchFarmers = useCallback(async() => {
        const res = await fetch(API + '/admins', { headers: authHeaders() });
        if (!res.ok) return;
        const data = await res.json();
        // Keep only farmers in this Region scope
        const arr = Array.isArray(data) ? data : [];
        const result = arr.filter(function(item) {
            return item && item.type === 'farmer' && user && item.region_name === user.region_name;
        });
        setFarmers(result);
        setFilteredFarmers(result);
    }, [authHeaders, user]);

    const fetchZoneAdmins = useCallback(async() => {
        const res = await fetch(API + '/admins', { headers: authHeaders() });
        if (!res.ok) return;
        const data = await res.json();
        // Keep only Zone admins in this Region scope
        const arr = Array.isArray(data) ? data : [];
        const result = arr.filter(function(item) {
            return item && item.role === 'Zone' && user && item.region_name === user.region_name;
        });
        setZoneAdmins(result);
    }, [authHeaders, user]);

    const fetchAll = useCallback(async() => {
        try {
            await fetchRequests();
            await fetchProducts();
            await fetchZoneAdmins();
        } catch (e) {
            console.error(e);
        }
    }, [fetchRequests, fetchProducts, fetchZoneAdmins]);

    // Derived stats
    const stats = useMemo(() => {
        const total = requests.length;
        const overallPending = requests.filter(function(r) { return r && r.status === 'Pending'; }).length;
        const overallAccepted = requests.filter(function(r) { return r && r.status === 'Accepted'; }).length;
        const overallRejected = requests.filter(function(r) { return r && r.status === 'Rejected'; }).length;
        const regionApproved = requests.filter(function(r) { return r && r.region_status === 'Approved'; }).length;
        const regionAccepted = requests.filter(function(r) { return r && r.region_status === 'Accepted'; }).length;

        let totalProducts = 0;
        if (productView === 'my') totalProducts = productsMine.length;
        else if (productView === 'others') totalProducts = productsOthers.length;
        else totalProducts = productsAll.length;

        return {
            totalRequests: total,
            pendingRequests: overallPending,
            acceptedRequests: overallAccepted,
            rejectedRequests: overallRejected,
            regionApproved: regionApproved,
            regionAccepted: regionAccepted,
            totalProducts: totalProducts,
            totalZones: zoneAdmins.length
        };
    }, [requests, productView, productsAll, productsMine, productsOthers, zoneAdmins]);

    // Filtering + search for requests
    useEffect(() => {
        let data = requests.slice();

        // search
        if (searchQuery && searchQuery.trim() !== '') {
            const q = searchQuery.toLowerCase();
            data = data.filter(function(r) {
                const farmer = (r && r.farmer_name) ? String(r.farmer_name).toLowerCase() : '';
                const product = (r && r.product_name) ? String(r.product_name).toLowerCase() : '';
                const idStr = r && r.id ? String(r.id) : '';
                const farmerIdStr = r && r.farmer_id ? String(r.farmer_id) : '';
                const reg = (r && r.region_name) ? String(r.region_name).toLowerCase() : '';
                const zone = (r && r.zone_name) ? String(r.zone_name).toLowerCase() : '';
                const wor = (r && r.woreda_name) ? String(r.woreda_name).toLowerCase() : '';
                const keb = (r && r.kebele_name) ? String(r.kebele_name).toLowerCase() : '';
                return idStr.indexOf(q) > -1 || farmerIdStr.indexOf(q) > -1 || farmer.indexOf(q) > -1 || product.indexOf(q) > -1 || reg.indexOf(q) > -1 || zone.indexOf(q) > -1 || wor.indexOf(q) > -1 || keb.indexOf(q) > -1;
            });
        }

        // status buckets for Region view
        if (statusFilter === 'pending') {
            // "pending (below region)": anything still Pending overall in the pipeline (kebele/zone/woreda/region not finished)
            data = data.filter(function(r) { return r && r.status === 'Pending'; });
        } else if (statusFilter === 'regionAccepted') {
            // accepted by Region only
            data = data.filter(function(r) { return r && r.region_status === 'Accepted'; });
        } else if (statusFilter === 'regionApproved') {
            // approved by Region only
            data = data.filter(function(r) { return r && r.region_status === 'Approved'; });
        }

        setFilteredRequests(data);
    }, [requests, statusFilter, searchQuery]);

    // UI helpers
    const toggleDark = function() {
        setDarkMode(function(d) { return !d; });
        document.documentElement.classList.toggle('dark');
    };

    const statusPill = function(val) {
        const map = {
            'Pending': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
            'Approved': 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
            'Accepted': 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
            'Rejected': 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300'
        };
        const cls = map[val] ? map[val] : '';
        return <span className = { 'px-2 py-1 text-xs font-medium rounded-full ' + cls } > { val } < /span>;
    };

    const levelBlock = function(label, status, admin, feedback, date) {
        return ( <
            div className = "p-3 rounded border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900" >
            <
            div className = "flex items-center gap-2 mb-1" >
            <
            span className = "text-sm font-semibold" > { label } < /span> { statusPill(status || 'Pending') } < /
            div > <
            div className = "text-xs text-gray-600 dark:text-gray-300 space-y-0.5" >
            <
            div > Admin: < span className = "font-medium" > { admin ? admin : '-' } < /span></div >
            <
            div > Feedback: < span className = "font-medium" > { feedback ? feedback : '-' } < /span></div >
            <
            div > Date: < span className = "font-medium" > { date ? new Date(date).toLocaleString() : '-' } < /span></div >
            <
            /div> < /
            div >
        );
    };

    // Request actions (PUT /requests/:id/status)
    const openRequestAction = function(req, action) {
        setSelectedRequest(req);
        setRequestAction(action);
        setRequestFeedback('');
        setShowRequestModal(true);
    };

    const performRequestAction = async function() {
        if (!selectedRequest || !requestAction) return;

        let statusVal = 'Pending';
        if (requestAction === 'Approve') statusVal = 'Approved';
        if (requestAction === 'Accept') statusVal = 'Accepted';
        if (requestAction === 'Reject') statusVal = 'Rejected';

        try {
            const res = await fetch(API + '/requests/' + selectedRequest.id + '/status', {
                method: 'PUT',
                headers: authHeaders(),
                body: JSON.stringify({ status: statusVal, feedback: requestFeedback })
            });
            const data = await res.json();
            if (!res.ok) {
                alert((data && data.message) ? data.message : 'Error updating request');
                return;
            }
            setShowRequestModal(false);
            setSelectedRequest(null);
            setRequestAction('');
            setRequestFeedback('');
            await fetchRequests();
            alert('Request ' + requestAction.toLowerCase() + 'ed successfully');
        } catch (e) {
            console.error(e);
            alert('Server error');
        }
    };

    // Products handlers
    const openCreateProduct = function() {
        setEditingProduct(null);
        setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
        setProductError('');
        setShowProductForm(true);
    };

    const openEditProduct = function(p) {
        setEditingProduct(p);
        setProductForm({
            name: p && p.name ? p.name : '',
            category: p && p.category ? p.category : '',
            amount: p && p.amount ? String(p.amount) : '',
            price: p && p.price ? String(p.price) : '',
            description: p && p.description ? p.description : ''
        });
        setProductError('');
        setShowProductForm(true);
    };

    const saveProduct = async function(e) {
        e.preventDefault();
        setProductError('');
        try {
            let url = API + '/addproduct';
            let method = 'POST';
            if (editingProduct && editingProduct.id) {
                url = API + '/updateproduct/' + editingProduct.id;
                method = 'PUT';
            }
            const res = await fetch(url, { method: method, headers: authHeaders(), body: JSON.stringify(productForm) });
            const data = await res.json();
            if (!res.ok) {
                setProductError((data && data.message) ? data.message : 'Error saving product');
                return;
            }
            setShowProductForm(false);
            setEditingProduct(null);
            setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
            await fetchProducts();
            alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
        } catch (e) {
            console.error(e);
            setProductError('Server error');
        }
    };

    const deleteProduct = async function(p) {
        if (!user || !p || !p.created_by_admin_id || p.created_by_admin_id !== user.id) {
            alert('You can only delete your own products');
            return;
        }
        if (!window.confirm('Delete this product?')) return;
        try {
            const res = await fetch(API + '/deleteproduct/' + p.id, { method: 'DELETE', headers: authHeaders() });
            const data = await res.json();
            if (!res.ok) {
                alert((data && data.message) ? data.message : 'Error deleting product');
                return;
            }
            await fetchProducts();
            alert('Product deleted');
        } catch (e) {
            console.error(e);
            alert('Server error');
        }
    };

    // Zone admin handlers
    const openCreateZone = function() {
        setEditingZone(null);
        setZoneForm({ fullName: '', phoneNumber: '', password: '', confirmPassword: '', zoneName: '' });
        setZoneError('');
        setShowZoneForm(true);
    };

    const openEditZone = function(admin) {
        setEditingZone(admin);
        setZoneForm({
            fullName: admin && admin.full_name ? admin.full_name : '',
            phoneNumber: admin && admin.phone_number ? admin.phone_number : '',
            password: '',
            confirmPassword: '',
            zoneName: admin && admin.zone_name ? admin.zone_name : ''
        });
        setZoneError('');
        setShowZoneForm(true);
    };

    const saveZoneAdmin = async function(e) {
        e.preventDefault();
        setZoneError('');

        if (!editingZone && zoneForm.password !== zoneForm.confirmPassword) {
            setZoneError('Passwords do not match');
            return;
        }

        try {
            let url = API + '/register';
            let method = 'POST';
            if (editingZone && editingZone.id) {
                url = API + '/edit/' + editingZone.id;
                method = 'PUT';
            }

            const payload = {
                fullName: zoneForm.fullName,
                phoneNumber: zoneForm.phoneNumber,
                role: 'Zone',
                region_name: user ? user.region_name : '',
                zone_name: zoneForm.zoneName,
                password: zoneForm.password
            };

            if (editingZone) {
                // If editing and password left blank, backend may ignore change; keep key for uniformity
                if (!payload.password) delete payload.password;
            }

            const res = await fetch(url, { method: method, headers: authHeaders(), body: JSON.stringify(payload) });
            const data = await res.json();
            if (!res.ok) {
                setZoneError((data && data.message) ? data.message : 'Error saving admin');
                return;
            }
            setShowZoneForm(false);
            setEditingZone(null);
            await fetchZoneAdmins();
            alert('Saved');
        } catch (e) {
            console.error(e);
            setZoneError('Server error');
        }
    };

    const deleteZoneAdmin = async function(admin) {
        if (!window.confirm('Delete this Zone admin?')) return;
        try {
            const res = await fetch(API + '/delete/' + admin.id, { method: 'DELETE', headers: authHeaders() });
            const data = await res.json();
            if (!res.ok) {
                alert((data && data.message) ? data.message : 'Error deleting admin');
                return;
            }
            await fetchZoneAdmins();
            alert('Deleted');
        } catch (e) {
            console.error(e);
            alert('Server error');
        }
    };

    // Renderers
    const Header = function() {
        return ( <
            header className = "border-b border-gray-200 dark:border-gray-800 bg-white/70 dark:bg-gray-900/70 backdrop-blur" >
            <
            div className = "mx-auto max-w-7xl px-4 py-3 flex items-center justify-between" >
            <
            div className = "flex items-center gap-3" >
            <
            div className = "h-9 w-9 rounded-xl bg-gray-900 dark:bg-white" > < /div> <
            div >
            <
            div className = "text-sm text-gray-500 dark:text-gray-400" > Region < /div> <
            div className = "font-semibold" > { user ? user.full_name : '' } < /div> < /
            div > <
            /div> <
            div className = "flex items-center gap-2" >
            <
            button onClick = { toggleDark }
            className = "px-3 py-1.5 text-sm rounded-xl border border-gray-200 dark:border-gray-800 hover:bg-gray-50 dark:hover:bg-gray-800" > { darkMode ? 'Light' : 'Dark' } <
            /button> <
            button onClick = {
                function() {
                    localStorage.removeItem('token');
                    localStorage.removeItem('user');
                    localStorage.removeItem('userType');
                    navigate('/login');
                }
            }
            className = "px-3 py-1.5 text-sm rounded-xl bg-red-600 text-white" >
            Logout <
            /button> < /
            div > <
            /div> < /
            header >
        );
    };

    const StatCard = function({ label, value, sub }) {
        return ( <
            div className = "rounded-2xl border border-gray-200 dark:border-gray-800 p-4 bg-white dark:bg-gray-900" >
            <
            div className = "text-sm text-gray-500 dark:text-gray-400" > { label } < /div> <
            div className = "text-2xl font-bold mt-1" > { value } < /div> {
            sub ? < div className = "text-xs text-gray-500 dark:text-gray-400 mt-1" > { sub } < /div> : null} < /
            div >
        );
    };

    if (!user) {
        return <div className = "min-h-screen flex items-center justify-center" > Loading < /div>;
    }

    return ( <
            div className = "min-h-screen bg-gray-50 dark:bg-gray-950 text-gray-900 dark:text-white" >
            <
            Header / >

            <
            main className = "mx-auto max-w-7xl px-4 py-6" > { /* Tabs */ } <
            div className = "mb-6 flex flex-wrap gap-2" > {
                ['overview', 'requests', 'products', 'zones'].map(function(tab) {
                    return ( <
                        button key = { tab }
                        onClick = {
                            function() { setActiveTab(tab); }
                        }
                        className = { 'rounded-xl px-4 py-2 text-sm capitalize border ' + (activeTab === tab ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800 hover:bg-gray-100 dark:hover:bg-gray-800') } > { tab } <
                        /button>
                    );
                })
            } <
            /div>

            { /* OVERVIEW */ } {
                activeTab === 'overview' ? ( <
                    section className = "grid gap-4 sm:grid-cols-2 lg:grid-cols-4" >
                    <
                    StatCard label = "Total Requests"
                    value = { stats.totalRequests }
                    /> <
                    StatCard label = "Pending (overall)"
                    value = { stats.pendingRequests }
                    /> <
                    StatCard label = "Accepted (overall)"
                    value = { stats.acceptedRequests }
                    /> <
                    StatCard label = "Rejected (overall)"
                    value = { stats.rejectedRequests }
                    /> <
                    StatCard label = "Region Approved"
                    value = { stats.regionApproved }
                    sub = "region_status === 'Approved'" / >
                    <
                    StatCard label = "Region Accepted"
                    value = { stats.regionAccepted }
                    sub = "region_status === 'Accepted'" / >
                    <
                    StatCard label = "Products"
                    value = { stats.totalProducts }
                    sub = { productView.toUpperCase() }
                    /> <
                    StatCard label = "Zone Admins"
                    value = { stats.totalZones }
                    /> < /
                    section >
                ) : null
            }

            { /* REQUESTS */ } {
                activeTab === 'requests' ? ( <
                    section className = "space-y-4" >
                    <
                    div className = "flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between" >
                    <
                    div className = "flex items-center gap-2" >
                    <
                    button onClick = {
                        function() { setStatusFilter('all'); }
                    }
                    className = { 'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'all' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800') } >
                    All <
                    /button> <
                    button onClick = {
                        function() { setStatusFilter('pending'); }
                    }
                    className = { 'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'pending' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800') } >
                    Pending(below Region) <
                    /button> <
                    button onClick = {
                        function() { setStatusFilter('regionApproved'); }
                    }
                    className = { 'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'regionApproved' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800') } >
                    Approved(Region) <
                    /button> <
                    button onClick = {
                        function() { setStatusFilter('regionAccepted'); }
                    }
                    className = { 'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'regionAccepted' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800') } >
                    Accepted(Region) <
                    /button> < /
                    div >

                    <
                    div className = "flex items-center gap-2" >
                    <
                    input value = { searchQuery }
                    onChange = {
                        function(e) { setSearchQuery(e.target.value); }
                    }
                    placeholder = "Search (id, farmer, product, location)"
                    className = "rounded-xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900 px-3 py-1.5 text-sm w-72" / >
                    <
                    button onClick = { fetchRequests }
                    className = "rounded-xl px-3 py-1.5 text-sm border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900" >
                    Refresh <
                    /button> < /
                    div > <
                    /div>

                    <
                    div className = "overflow-x-auto rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900" >
                    <
                    table className = "min-w-full text-sm" >
                    <
                    thead className = "bg-gray-50 dark:bg-gray-950/40" >
                    <
                    tr >
                    <
                    th className = "text-left px-4 py-2" > # < /th> <
                    th className = "text-left px-4 py-2" > Farmer < /th> <
                    th className = "text-left px-4 py-2" > Product < /th> <
                    th className = "text-left px-4 py-2" > Qty < /th> <
                    th className = "text-left px-4 py-2" > Price < /th> <
                    th className = "text-left px-4 py-2" > Overall < /th> <
                    th className = "text-left px-4 py-2" > Region < /th> <
                    th className = "text-left px-4 py-2" > Actions < /th> < /
                    tr > <
                    /thead> <
                    tbody > {
                        filteredRequests.length === 0 ? ( <
                            tr >
                            <
                            td colSpan = { 8 }
                            className = "text-center px-4 py-6 text-gray-500" > No requests < /td> < /
                            tr >
                        ) : filteredRequests.map(function(r) {
                            return ( <
                                tr key = { r.id }
                                className = "border-t border-gray-100 dark:border-gray-800" >
                                <
                                td className = "px-4 py-2" > { r.id } < /td> <
                                td className = "px-4 py-2" >
                                <
                                div className = "font-medium" > { r.farmer_name } < /div> <
                                div className = "text-xs text-gray-500" > { r.region_name }
                                / {r.zone_name} / { r.woreda_name }
                                / {r.kebele_name}</div >
                                <
                                /td> <
                                td className = "px-4 py-2" > { r.product_name } < /td> <
                                td className = "px-4 py-2" > { r.quantity } < /td> <
