const AddUserModal = ({ onClose, onSubmit, currentUser }) => {
        const { register, handleSubmit, formState: { errors } } = useForm();
        const getNextRole = () => {
            if (currentUser && currentUser.role === ADMIN_ROLES.FEDERAL) return ADMIN_ROLES.REGION;
            if (currentUser && currentUser.role === ADMIN_ROLES.REGION) return ADMIN_ROLES.ZONE;
            if (currentUser && currentUser.role === ADMIN_ROLES.ZONE) return ADMIN_ROLES.WOREDA;
            if (currentUser && currentUser.role === ADMIN_ROLES.WOREDA) return ADMIN_ROLES.KEBELE;
            return 'Farmer';
        };

        const getNextLocations = () => {
            if (currentUser && currentUser.role === ADMIN_ROLES.FEDERAL) return locations.regions;
            if (currentUser && currentUser.role === ADMIN_ROLES.REGION) return locations.zones.filter(z => z.parent === currentUser.location);
            if (currentUser && currentUser.role === ADMIN_ROLES.ZONE) return locations.woredas.filter(w => w.parent === currentUser.location);
            if (currentUser && currentUser.role === ADMIN_ROLES.WOREDA) return locations.kebeles.filter(k => k.parent === currentUser.location);
            return [];
        };
        const nextRole = getNextRole();
        const nextLocations = getNextLocations();

        return ( <
                Modal show = { true }
                onClose = { onClose }
                title = { `Add New ${nextRole} Admin` } >
                <
                form onSubmit = { handleSubmit(onSubmit) }
                className = "p-6" >
                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 font-bold mb-2" > Full Name < /label> <
                input {...register("fullName", { required: "Full name is required" }) }
                className = "w-full p-2 border rounded-lg"
                type = "text" /
                >
                {
                    errors.fullName && < p className = "text-red-500 text-sm mt-1" > { errors.fullName.message } < /p>} < /
                    div > <
                    div className = "mb-4" >
                    <
                    label className = "block text-gray-700 font-bold mb-2" > Phone Number < /label> <
                    input {...register("phone", { required: "Phone number is required" }) }
                    className = "w-full p-2 border rounded-lg"
                    type = "tel" /
                    >
                    {
                        errors.phone && < p className = "text-red-500 text-sm mt-1" > { errors.phone.message } < /p>} < /
                        div > <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 font-bold mb-2" > Password < /label> <
                        input {...register("password", { required: "Password is required" }) }
                        className = "w-full p-2 border rounded-lg"
                        type = "password" /
                        >
                        {
                            errors.password && < p className = "text-red-500 text-sm mt-1" > { errors.password.message } < /p>} < /
                            div > <
                            div className = "mb-4" >
                            <
                            label className = "block text-gray-700 font-bold mb-2" > Location < /label> <
                            select {...register("location", { required: "Location is required" }) }
                            className = "w-full p-2 border rounded-lg" >
                            <
                            option value = "" > Select a location < /option> {
                            nextLocations.map(loc => ( <
                                option key = { loc }
                                value = { loc } > { loc } < /option>
                            ))
                        } <
                        /select> {
                        errors.location && < p className = "text-red-500 text-sm mt-1" > { errors.location.message } < /p>} < /
                        div > <
                        input type = "hidden" {...register("role") }
                        value = { nextRole }
                        /> <
                        button type = "submit"
                        className = "w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-colors" >
                        Add User <
                        /button> < /
                        form > <
                        /Modal>
                    );
                };

                const AddProductModal = ({ onClose, onSubmit }) => {
                        const { register, handleSubmit, formState: { errors } } = useForm();
                        return ( <
                                Modal show = { true }
                                onClose = { onClose }
                                title = "Add New Product" >
                                <
                                form onSubmit = { handleSubmit(onSubmit) }
                                className = "p-6" >
                                <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 font-bold mb-2" > Product Name < /label> <
                                input {...register("name", { required: "Product name is required" }) }
                                className = "w-full p-2 border rounded-lg"
                                type = "text" /
                                >
                                {
                                    errors.name && < p className = "text-red-500 text-sm mt-1" > { errors.name.message } < /p>} < /
                                    div > <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 font-bold mb-2" > Department < /label> <
                                    select {...register("department", { required: "Department is required" }) }
                                    className = "w-full p-2 border rounded-lg" >
                                    <
                                    option value = "" > Select Department < /option> <
                                    option value = "Biological Input Supply" > Biological Input Supply < /option> <
                                    option value = "Chemical Input Supply" > Chemical Input Supply < /option> < /
                                    select > {
                                        errors.department && < p className = "text-red-500 text-sm mt-1" > { errors.department.message } < /p>} < /
                                        div > <
                                        div className = "mb-4" >
                                        <
                                        label className = "block text-gray-700 font-bold mb-2" > Description < /label> <
                                        textarea {...register("description") }
                                        rows = "3"
                                        className = "w-full p-2 border rounded-lg" >
                                        <
                                        /textarea> < /
                                        div > <
                                        button type = "submit"
                                        className = "w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-colors" >
                                        Add Product <
                                        /button> < /
                                        form > <
                                        /Modal>
                                    );
                                };


                                const Reports = () => {
                                    return ( <
                                        div className = "p-8" >
                                        <
                                        h2 className = "text-2xl font-bold mb-4" > Reports < /h2> <
                                        div className = "grid grid-cols-1 md:grid-cols-2 gap-8" >
                                        <
                                        div className = "bg-white p-6 rounded-xl shadow-lg" >
                                        <
                                        h3 className = "font-bold text-xl mb-4" > Requests by Region < /h3> <
                                        p className = "text-gray-500" > [Bar Chart: A placeholder
                                            for a bar chart showing requests by region
                                        ] <
                                        /p> < /
                                        div > <
                                        div className = "bg-white p-6 rounded-xl shadow-lg" >
                                        <
                                        h3 className = "font-bold text-xl mb-4" > Product Type Usage < /h3> <
                                        p className = "text-gray-500" > [Pie Chart: A placeholder
                                            for a pie chart showing product type usage
                                        ] <
                                        /p> < /
                                        div > <
                                        /div> < /
                                        div >
                                    );
                                };

                                const DashboardCard = ({ title, count, icon }) => ( <
                                    div className = "bg-white p-6 rounded-xl shadow-lg flex items-center justify-between" >
                                    <
                                    div >
                                    <
                                    h3 className = "text-xl font-semibold text-gray-500" > { title } < /h3> <
                                    p className = "text-4xl font-bold text-gray-800" > { count } < /p> < /
                                    div > <
                                    div className = "text-5xl text-blue-500" > { icon } < /div> < /
                                    div >
                                );

                                const Modal = ({ show, onClose, title, message, children }) => {
                                        if (!show) return null;
                                        return ( <
                                            div className = "fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4" >
                                            <
                                            div className = "bg-white rounded-xl shadow-2xl w-full max-w-md" >
                                            <
                                            div className = "flex justify-between items-center p-4 border-b" >
                                            <
                                            h2 className = "text-xl font-bold" > { title } < /h2> <
                                            button onClick = { onClose }
                                            className = "text-gray-500 hover:text-gray-800 text-2xl leading-none" > & times; < /button> < /
                                            div > <
                                            div className = "p-4" > {
                                                message && < p className = "mb-4 text-gray-700" > { message } < /p>} { children } < /
                                                div > <
                                                /div> < /
                                                div >
                                            );
                                        };

                                        const LoginScreen = () => {
                                                const { register, handleSubmit, formState: { errors } } = useForm();
                                                const onSubmit = data => {
                                                    handleLogin(data.phone, data.password);
                                                };
                                                return ( <
                                                        div className = "flex items-center justify-center min-h-screen bg-gray-100 p-4" >
                                                        <
                                                        div className = "bg-white p-8 rounded-xl shadow-2xl w-full max-w-sm" >
                                                        <
                                                        h2 className = "text-3xl font-bold text-center mb-6" > Admin Login < /h2> <
                                                        p className = "text-center text-gray-500 mb-6" > Use one of the mock credentials to log in . < /p> <
                                                        form onSubmit = { handleSubmit(onSubmit) } >
                                                        <
                                                        div className = "mb-4" >
                                                        <
                                                        label className = "block text-gray-700 font-bold mb-2" > Phone Number < /label> <
                                                        input {...register("phone", { required: "Phone number is required" }) }
                                                        className = "w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                                        type = "tel"
                                                        placeholder = "e.g. 0912345678" /
                                                        >
                                                        {
                                                            errors.phone && < p className = "text-red-500 text-sm mt-1" > { errors.phone.message } < /p>} < /
                                                            div > <
                                                            div className = "mb-6" >
                                                            <
                                                            label className = "block text-gray-700 font-bold mb-2" > Password < /label> <
                                                            input {...register("password", { required: "Password is required" }) }
                                                            className = "w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                                            type = "password"
                                                            placeholder = "password123" /
                                                            >
                                                            {
                                                                errors.password && < p className = "text-red-500 text-sm mt-1" > { errors.password.message } < /p>} < /
                                                                div > <
                                                                button
                                                                type = "submit"
                                                                className = "w-full bg-blue-500 text-white p-3 rounded-lg font-bold hover:bg-blue-600 transition-colors" >
                                                                Login <
                                                                /button> < /
                                                                form > <
                                                                /div> < /
                                                                div >
                                                            );
                                                        };

                                                        if (loading || !authReady) {
                                                            return <div className = "flex items-center justify-center min-h-screen text-2xl font-semibold" > Loading... < /div>;
                                                        }

                                                        if (!currentUser || currentPage === 'login') {
                                                            return <LoginScreen / > ;
                                                        }

                                                        return ( <
                                                            div className = "flex min-h-screen bg-gray-100 font-inter" >
                                                            <
                                                            Sidebar currentPage = { currentPage }
                                                            setCurrentPage = { setCurrentPage }
                                                            currentUser = { currentUser }
                                                            /> <
                                                            div className = "flex-1 flex flex-col" >
                                                            <
                                                            header className = "bg-white shadow-md p-4 flex justify-between items-center" >
                                                            <
                                                            h1 className = "text-2xl font-bold text-gray-800" > product Request Management System < /h1> <
                                                            div className = "flex items-center space-x-4" >
                                                            <
                                                            span className = "text-gray-700" > { currentUser.fullName }({ currentUser.role }) < /span> <
                                                            button onClick = { handleLogout }
                                                            className = "bg-red-500 text-white p-2 rounded-lg hover:bg-red-600 transition-colors" > Logout < /button> < /
                                                            div > <
                                                            /header> <
                                                            main className = "flex-1 overflow-auto" > { currentPage === 'dashboard' && < MainDashboard / > } { currentPage === 'master-data' && < MasterData / > } { currentPage === 'reports' && < Reports / > } {
                                                                selectedRequest && < RequestApprovalForm request = { selectedRequest }
                                                                onClose = {
                                                                    () => setSelectedRequest(null)
                                                                }
                                                                />} < /
                                                                main > <
                                                                    /div> <
                                                                Modal show = { showModal }
                                                                onClose = {
                                                                    () => setShowModal(false)
                                                                }
                                                                title = "Notification"
                                                                message = { modalContent }
                                                                /> < /
                                                                div >
                                                            );
                                                        }
