import React, { useState } from 'react';
import api from './api';

function App() {
    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleLogin = async(e) => {
        e.preventDefault();
        try {
            const res = await api.post('/login', { phone, password });
            alert('Login successful! Welcome ' + res.data.role);
            setMessage('');
        } catch (err) {
            console.log(err);
            setMessage('Login failed: ' + (err.response ? err.response.data.message : 'Network Error'));
        }
    };

    return ( <
        div className = "min-h-screen flex items-center justify-center bg-gray-100" >
        <
        form onSubmit = { handleLogin }
        className = "bg-white p-8 rounded shadow-md w-80" >
        <
        h1 className = "text-2xl mb-4 font-bold text-center" > Login < /h1> {
            message && < div className = "text-red-500 mb-2" > { message } < /div>} <
                label className = "block mb-2" > Phone Number < /label> <
                input
            type = "text"
            value = { phone }
            onChange = {
                (e) => setPhone(e.target.value) }
            className = "border p-2 mb-4 w-full rounded"
            placeholder = "Enter phone number"
            required
                /
                >
                <
                label className = "block mb-2" > Password < /label> <
                input
            type = "password"
            value = { password }
            onChange = {
                (e) => setPassword(e.target.value) }
            className = "border p-2 mb-4 w-full rounded"
            placeholder = "Enter password"
            required
                /
                >
                <
                button
            type = "submit"
            className = "bg-blue-500 text-white p-2 rounded w-full hover:bg-blue-600" >
                Login <
                /button> <
                /form> <
                /div>
        );
    }

    export default App;
