import React, { useState, useEffect } from 'react';
import './index.css';
import { useForm } from 'react-hook-form';

// ----------------------
// Mock Data (Simulates a database)
// ----------------------
const initialUsers = [{
    phoneNumber: '111-222-3333',
    password: 'password',
    role: 'Federal',
    fullName: 'Federal Admin',
    id: 'fed-1'
}, {
    phoneNumber: '444-555-6666',
    password: 'password',
    role: 'Region',
    fullName: 'Amhara Regional Admin',
    regionName: 'Amhara',
    id: 'reg-1'
}, {
    phoneNumber: '777-888-9999',
    password: 'password',
    role: 'Zone',
    fullName: 'North Shewa Zone Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    id: 'zone-1'
}, {
    phoneNumber: '987-654-3210',
    password: 'password',
    role: 'Woreda',
    fullName: 'Kewet Woreda Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    id: 'wor-1'
}, {
    phoneNumber: '987-654-3211',
    password: 'password',
    role: 'Kebele',
    fullName: 'Alem Ketema Kebele Rep',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'keb-1'
}, {
    phoneNumber: '123-456-7890',
    password: 'password',
    role: 'Farmer',
    fullName: 'Abebe Kebede',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'farm-1'
}];

const initialProducts = [{
    id: 'prod-1',
    name: 'Tractor',
    description: 'Used for plowing and land prep',
    category: 'Machinery',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet'
}, {
    id: 'prod-2',
    name: 'Fertilizer A',
    description: 'High-yield fertilizer for corn',
    category: 'Fertilizer',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet'
}, {
    id: 'prod-3',
    name: 'Harvester',
    description: 'Used for harvesting teff',
    category: 'Machinery',
    regionName: 'Amhara'
}];

const initialRequests = [{
    id: 'REQ12345',
    requesterName: 'Abebe Kebede',
    requesterId: 'farm-1',
    productName: 'Tractor',
    productPurpose: 'Land preparation',
    amount: 1,
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    status: 'Pending',
    previousApprovals: {
        Woreda: 'Approved'
    }
}];

// ----------------------
// Modal Component
// ----------------------
const Modal = ({ show, onClose, title, message }) => {
    if (!show) return null;
    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "relative p-5 border w-96 shadow-lg rounded-xl bg-white" >
        <
        div className = "mt-3 text-center" >
        <
        h3 className = "text-xl leading-6 font-bold text-gray-900" > { title } < /h3> <
        div className = "mt-2 px-7 py-3" >
        <
        p className = "text-sm text-gray-500" > { message } < /p> <
        /div> <
        div className = "items-center px-4 py-3" >
        <
        button onClick = { onClose }
        className = "px-4 py-2 bg-blue-600 text-white text-base font-medium rounded-lg w-full shadow-md hover:bg-blue-700 transition" >
        OK <
        /button> <
        /div> <
        /div> <
        /div> <
        /div>
    );
};

// ----------------------
// Main App Component
// ----------------------
const App = () => {
        const [currentUser, setCurrentUser] = useState(null);
        const [users, setUsers] = useState(initialUsers);
        const [products, setProducts] = useState(initialProducts);
        const [requests, setRequests] = useState(initialRequests);
        const [showModal, setShowModal] = useState(false);
        const [modalContent, setModalContent] = useState({ title: '', message: '' });
        const [currentPage, setCurrentPage] = useState('dashboard');
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [isDarkMode, setIsDarkMode] = useState(false);

        useEffect(() => {
            if (isDarkMode) {
                document.documentElement.classList.add('dark');
            } else {
                document.documentElement.classList.remove('dark');
            }
        }, [isDarkMode]);

        const handleLogin = (user) => {
            setCurrentUser(user);
            setCurrentPage('dashboard');
        };

        const handleLogout = () => {
            setCurrentUser(null);
        };

        const handleRegistration = (newUser) => {
            if (users.some(u => u.phoneNumber === newUser.phoneNumber)) {
                setModalContent({ title: 'Error', message: 'User with this phone number already exists.' });
                setShowModal(true);
                return;
            }

            const newUserWithId = {...newUser, id: `${newUser.role.toLowerCase()}-${users.length + 1}` };
            setUsers([...users, newUserWithId]);
            setModalContent({
                title: 'Success',
                message: `${newUser.fullName} has been registered as a ${newUser.role}. Their login details are Phone: ${newUser.phoneNumber}, Password: ${newUser.password}`
            });
            setShowModal(true);
        };

        const handleDeleteUser = (userId, role) => {
            setUsers(users.filter(u => u.id !== userId));
            setModalContent({ title: 'Success', message: `A ${role} account has been deleted.` });
            setShowModal(true);
        };

        const handleAddProduct = (newProduct) => {
            const newProductWithId = {...newProduct, id: `prod-${products.length + 1}` };
            setProducts([...products, newProductWithId]);
            setModalContent({ title: 'Success', message: 'New product added successfully.' });
            setShowModal(true);
        };

        const handleUpdateProduct = (updatedProduct) => {
            setProducts(products.map(p => p.id === updatedProduct.id ? updatedProduct : p));
            setModalContent({ title: 'Success', message: 'Product updated successfully.' });
            setShowModal(true);
        };

        const handleDeleteProduct = (productId) => {
            setProducts(products.filter(p => p.id !== productId));
            setModalContent({ title: 'Success', message: 'Product deleted successfully.' });
            setShowModal(true);
        };

        const handleApprovalDecision = (requestId, decision, feedback) => {
            setRequests(requests.map(req => {
                if (req.id === requestId) {
                    // Determine the next status based on the decision
                    const newStatus = decision === 'Approve' ? 'Approved' : 'Rejected';
                    // Add this approval to the request's history
                    const newPreviousApprovals = {
                        ...req.previousApprovals,
                        [currentUser.role]: newStatus
                    };

                    return {
                        ...req,
                        status: newStatus,
                        feedback,
                        previousApprovals: newPreviousApprovals
                    };
                }
                return req;
            }));
            setSelectedRequest(null); // Close the form
            setModalContent({ title: 'Decision Submitted', message: `The request has been ${decision.toLowerCase()}.` });
            setShowModal(true);
        };

        const handleProductRequest = (request) => {
            const newRequest = {
                ...request,
                id: `REQ${10000 + requests.length}`,
                status: 'Pending',
                previousApprovals: {},
                requesterId: currentUser.id,
                requesterName: currentUser.fullName
            };
            setRequests([...requests, newRequest]);
            setModalContent({ title: 'Request Sent', message: 'Your product request has been submitted.' });
            setShowModal(true);
        };

        // Helper function to filter requests based on user location
        const getFilteredRequests = (user, allRequests) => {
            switch (user.role) {
                case 'Federal':
                    return allRequests;
                case 'Region':
                    return allRequests.filter(req => req.regionName === user.regionName);
                case 'Zone':
                    return allRequests.filter(req => req.zoneName === user.zoneName);
                case 'Woreda':
                    return allRequests.filter(req => req.woredaName === user.woredaName);
                case 'Kebele':
                    return allRequests.filter(req => req.kebeleName === user.kebeleName);
                case 'Farmer':
                    return allRequests.filter(req => req.requesterId === user.id);
                default:
                    return [];
            }
        };

        const renderMainContent = () => {
                if (!currentUser) {
                    return <LoginPage onLogin = { handleLogin }
                    users = { users }
                    />;
                }

                // For farmers, render a simpler view
                if (currentUser.role === 'Farmer') {
                    const filteredProducts = products.filter(p =>
                        p.kebeleName === currentUser.kebeleName ||
                        (p.kebeleName === undefined && p.woredaName === currentUser.woredaName) ||
                        (p.woredaName === undefined && p.zoneName === currentUser.zoneName) ||
                        (p.zoneName === undefined && p.regionName === currentUser.regionName) ||
                        (p.regionName === undefined)
                    );
                    return ( <
                        div className = "bg-gray-100 dark:bg-gray-900 min-h-screen font-sans antialiased text-gray-900 dark:text-gray-100 transition-colors duration-300" >
                        <
                        TopBar currentUser = { currentUser }
                        onLogout = { handleLogout }
                        isDarkMode = { isDarkMode }
                        setIsDarkMode = { setIsDarkMode }
                        /> <
                        main className = "p-8" >
                        <
                        FarmerDashboard currentUser = { currentUser }
                        products = { filteredProducts }
                        requests = { getFilteredRequests(currentUser, requests) }
                        onRequest = { handleProductRequest }
                        /> <
                        /main> <
                        Modal show = { showModal }
                        onClose = {
                            () => setShowModal(false) }
                        title = { modalContent.title }
                        message = { modalContent.message }
                        /> <
                        /div>
                    );
                }

                // For all other users (Admins)
                return ( <
                        div className = "flex bg-gray-100 dark:bg-gray-900 min-h-screen text-gray-900 dark:text-gray-100 transition-colors duration-300" >
                        <
                        Sidebar currentUser = { currentUser }
                        currentPage = { currentPage }
                        setCurrentPage = { setCurrentPage }
                        /> <
                        div className = "flex-1 overflow-auto" >
                        <
                        TopBar currentUser = { currentUser }
                        onLogout = { handleLogout }
                        isDarkMode = { isDarkMode }
                        setIsDarkMode = { setIsDarkMode }
                        /> <
                        main className = "p-8" > {
                            currentPage === 'dashboard' && < DashboardOverview requests = { getFilteredRequests(currentUser, requests) }
                            currentUser = { currentUser }
                            />} {
                                currentPage === 'product-requests' && < RequestsTable requests = { getFilteredRequests(currentUser, requests) }
                                currentUser = { currentUser }
                                setSelectedRequest = { setSelectedRequest }
                                />} {
                                    currentPage === 'approvals' && selectedRequest && < RequestApprovalForm request = { selectedRequest }
                                    onDecision = { handleApprovalDecision }
                                    onClose = {
                                        () => setSelectedRequest(null) }
                                    />} {
                                        currentPage === 'master-data' && < MasterData
                                        currentUser = { currentUser }
                                        users = { users }
                                        products = { products }
                                        onRegister = { handleRegistration }
                                        onDeleteUser = { handleDeleteUser }
                                        onAddProduct = { handleAddProduct }
                                        onUpdateProduct = { handleUpdateProduct }
                                        onDeleteProduct = { handleDeleteProduct }
                                        />} {
                                            currentPage === 'reports' && ['Federal', 'Region'].includes(currentUser.role) && < Reports currentUser = { currentUser }
                                            requests = { getFilteredRequests(currentUser, requests) }
                                            />} <
                                            /main> <
                                            Modal show = { showModal }
                                            onClose = {
                                                () => setShowModal(false) }
                                            title = { modalContent.title }
                                            message = { modalContent.message }
                                            /> <
                                            /div> <
                                            /div>
                                        );
                                    };

                                    return renderMainContent();
                                };

                                // ----------------------
                                // Sidebar Component
                                // ----------------------
                                const Sidebar = ({ currentUser, currentPage, setCurrentPage }) => {
                                    const menuItems = {
                                        'dashboard': '🏠 Dashboard',
                                        'product-requests': '📦 Product Requests',
                                        'approvals': '✅ Approvals',
                                        'master-data': '🛠 Master Data',
                                        'reports': '📊 Reports'
                                    };

                                    // Filter menu based on user role
                                    const filteredMenuItems = Object.keys(menuItems).filter(key => {
                                        // Reports are only for Federal and Region admins
                                        if (key === 'reports' && !['Federal', 'Region'].includes(currentUser.role)) {
                                            return false;
                                        }
                                        return true;
                                    });

                                    return ( <
                                        aside className = "w-64 min-h-screen bg-gray-800 text-gray-100 flex flex-col" >
                                        <
                                        div className = "p-4 text-center text-2xl font-bold border-b border-gray-700" >
                                        FMS <
                                        /div> <
                                        nav className = "flex-1" >
                                        <
                                        ul className = "space-y-2 p-4" > {
                                            filteredMenuItems.map(key => ( <
                                                li key = { key } >
                                                <
                                                button onClick = {
                                                    () => setCurrentPage(key) }
                                                className = { `w-full text-left p-3 rounded-lg transition-colors duration-200 ${currentPage === key ? 'bg-gray-700 font-bold' : 'hover:bg-gray-700'}` } >
                                                { menuItems[key] } <
                                                /button> <
                                                /li>
                                            ))
                                        } <
                                        /ul> <
                                        /nav> <
                                        /aside>
                                    );
                                };

                                // ----------------------
                                // TopBar Component
                                // ----------------------
                                const TopBar = ({ currentUser, onLogout, isDarkMode, setIsDarkMode }) => ( <
                                    header className = "flex justify-between items-center p-4 bg-white dark:bg-gray-800 border-b dark:border-gray-700 shadow-sm sticky top-0 z-10" >
                                    <
                                    h1 className = "text-2xl font-bold text-gray-800 dark:text-gray-100" > Product Request Management System < /h1> <
                                    div className = "flex items-center space-x-4" >
                                    <
                                    button onClick = {
                                        () => setIsDarkMode(!isDarkMode) }
                                    className = "p-2 rounded-full hover:bg-gray-100 dark:hover:bg-gray-700 transition" >
                                    { isDarkMode ? '☀️' : '🌙' } <
                                    /button> <
                                    div className = "relative group" >
                                    <
                                    button className = "flex items-center space-x-2 p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition" >
                                    <
                                    span className = "text-lg font-medium" > 👤{ currentUser.fullName } < /span> <
                                    svg className = "w-4 h-4 text-gray-500"
                                    fill = "none"
                                    stroke = "currentColor"
                                    viewBox = "0 0 24 24"
                                    xmlns = "http://www.w3.org/2000/svg" >
                                    <
                                    path strokeLinecap = "round"
                                    strokeLinejoin = "round"
                                    strokeWidth = "2"
                                    d = "M19 9l-7 7-7-7" > < /path> <
                                    /svg> <
                                    /button> <
                                    div className = "absolute right-0 mt-2 w-48 bg-white dark:bg-gray-700 rounded-lg shadow-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 z-20" >
                                    <
                                    ul className = "py-1" >
                                    <
                                    li >
                                    <
                                    button onClick = { onLogout }
                                    className = "w-full text-left px-4 py-2 text-gray-800 dark:text-gray-100 hover:bg-gray-100 dark:hover:bg-gray-600 transition" > Logout < /button> <
                                    /li> <
                                    /ul> <
                                    /div> <
                                    /div> <
                                    /div> <
                                    /header>
                                );

                                // ----------------------
                                // Login Page Component
                                // ----------------------
                                const LoginPage = ({ onLogin, users }) => {
                                    const [phoneNumber, setPhoneNumber] = useState('');
                                    const [password, setPassword] = useState('');
                                    const [showModal, setShowModal] = useState(false);
                                    const [modalMessage, setModalMessage] = useState('');

                                    const handleSubmit = (e) => {
                                        e.preventDefault();
                                        const user = users.find(
                                            (u) => u.phoneNumber === phoneNumber && u.password === password
                                        );
                                        if (user) {
                                            onLogin(user);
                                        } else {
                                            setModalMessage('Invalid phone number or password.');
                                            setShowModal(true);
                                        }
                                    };
                                    return ( <
                                        div className = "flex items-center justify-center min-h-screen bg-gray-200 dark:bg-gray-900" >
                                        <
                                        div className = "bg-white dark:bg-gray-800 p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-300" >
                                        <
                                        h2 className = "text-4xl font-extrabold text-center text-gray-800 dark:text-gray-100 mb-6" > FMS Login < /h2> <
                                        form onSubmit = { handleSubmit }
                                        className = "space-y-6" >
                                        <
                                        div >
                                        <
                                        label htmlFor = "phoneNumber"
                                        className = "text-sm font-medium text-gray-700 dark:text-gray-300 block mb-1" > Phone Number < /label> <
                                        input type = "text"
                                        id = "phoneNumber"
                                        value = { phoneNumber }
                                        onChange = {
                                            (e) => setPhoneNumber(e.target.value) }
                                        className = "w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                                        placeholder = "e.g., 987-654-3210" /
                                        >
                                        <
                                        /div> <
                                        div >
                                        <
                                        label htmlFor = "password"
                                        className = "text-sm font-medium text-gray-700 dark:text-gray-300 block mb-1" > Password < /label> <
                                        input type = "password"
                                        id = "password"
                                        value = { password }
                                        onChange = {
                                            (e) => setPassword(e.target.value) }
                                        className = "w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                                        placeholder = "Enter your password" /
                                        >
                                        <
                                        /div> <
                                        button type = "submit"
                                        className = "w-full p-3 text-white bg-blue-600 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 font-semibold transition-all duration-200 shadow-lg" >
                                        Log In <
                                        /button> <
                                        /form> <
                                        /div> <
                                        Modal show = { showModal }
                                        onClose = {
                                            () => setShowModal(false) }
                                        title = "Login Failed"
                                        message = { modalMessage }
                                        /> <
                                        /div>
                                    );
                                };

                                // ----------------------
                                // Dashboard Overview Component
                                // ----------------------
                                const DashboardOverview = ({ requests, currentUser }) => {
                                    const stats = {
                                        total: requests.length,
                                        approved: requests.filter(r => r.status === 'Approved').length,
                                        pending: requests.filter(r => r.status === 'Pending').length,
                                        rejected: requests.filter(r => r.status === 'Rejected').length
                                    };

                                    const StatCard = ({ title, value, color }) => ( <
                                        div className = { `p-6 rounded-xl shadow-md bg-white dark:bg-gray-800 border-l-4 ${color}` } >
                                        <
                                        h3 className = "text-md font-medium text-gray-500 dark:text-gray-300" > { title } < /h3> <
                                        p className = "mt-1 text-3xl font-semibold text-gray-900 dark:text-white" > { value } < /p> <
                                        /div>
                                    );

                                    return ( <
                                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6" >
                                        <
                                        StatCard title = "Total Requests"
                                        value = { stats.total }
                                        color = "border-blue-500" / >
                                        <
                                        StatCard title = "Approved Requests"
                                        value = { stats.approved }
                                        color = "border-green-500" / >
                                        <
                                        StatCard title = "Pending Requests"
                                        value = { stats.pending }
                                        color = "border-yellow-500" / >
                                        <
                                        StatCard title = "Rejected Requests"
                                        value = { stats.rejected }
                                        color = "border-red-500" / >
                                        <
                                        /div>
                                    );
                                };

                                // ----------------------
                                // Requests Table Component
                                // ----------------------
                                const RequestsTable = ({ requests, currentUser, setSelectedRequest }) => {
                                    return ( <
                                        div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                        <
                                        h2 className = "text-2xl font-bold mb-4" > All Product Requests < /h2> <
                                        div className = "overflow-x-auto" >
                                        <
                                        table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
                                        <
                                        thead className = "bg-gray-50 dark:bg-gray-700" >
                                        <
                                        tr >
                                        <
                                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > ID < /th> <
                                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Requester < /th> <
                                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product < /th> <
                                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
                                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Status < /th> <
                                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Actions < /th> <
                                        /tr> <
                                        /thead> <
                                        tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                                            requests.map((req) => ( <
                                                tr key = { req.id } >
                                                <
                                                td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.id } < /td> <
                                                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.requesterName } < /td> <
                                                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.productName } < /td> <
                                                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                                                td className = "px-6 py-4 whitespace-nowrap" >
                                                <
                                                span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                        req.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                        req.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                        'bg-red-100 text-red-800'
                                    }` } > { req.status } <
                                                /span> <
                                                /td> <
                                                td className = "px-6 py-4 whitespace-nowrap text-right text-sm font-medium" >
                                                <
                                                button onClick = {
                                                    () => setSelectedRequest(req) }
                                                className = "text-blue-600 hover:text-blue-900" >
                                                View <
                                                /button> <
                                                /td> <
                                                /tr>
                                            ))
                                        } <
                                        /tbody> <
                                        /table> <
                                        /div> <
                                        /div>
                                    );
                                };

                                // ----------------------
                                // Request Approval Form
                                // ----------------------
                                const RequestApprovalForm = ({ request, onDecision, onClose }) => {
                                    const [feedback, setFeedback] = useState('');
                                    if (!request) return null;
                                    return ( <
                                        div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                        <
                                        h2 className = "text-2xl font-bold mb-4" > Request Details < /h2> <
                                        p > < span className = "font-semibold" > Request ID: < /span> {request.id}</p >
                                        <
                                        p > < span className = "font-semibold" > Requester: < /span> {request.requesterName}</p >
                                        <
                                        p > < span className = "font-semibold" > Product: < /span> {request.productName}</p >
                                        <
                                        p > < span className = "font-semibold" > Amount: < /span> {request.amount}</p >
                                        <
                                        p > < span className = "font-semibold" > Purpose: < /span> {request.productPurpose}</p >
                                        <
                                        p > < span className = "font-semibold" > Status: < /span> {request.status}</p >
                                        <
                                        div className = "mt-4" >
                                        <
                                        label htmlFor = "feedback"
                                        className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Feedback < /label> <
                                        textarea id = "feedback"
                                        rows = "3"
                                        value = { feedback }
                                        onChange = {
                                            (e) => setFeedback(e.target.value) }
                                        className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" >
                                        < /textarea> <
                                        /div> <
                                        div className = "mt-4 flex justify-end space-x-2" >
                                        <
                                        button onClick = {
                                            () => onDecision(request.id, 'Approve', feedback) }
                                        className = "px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700" >
                                        Approve <
                                        /button> <
                                        button onClick = {
                                            () => onDecision(request.id, 'Reject', feedback) }
                                        className = "px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700" >
                                        Reject <
                                        /button> <
                                        button onClick = { onClose }
                                        className = "px-4 py-2 bg-gray-300 text-gray-800 rounded-lg hover:bg-gray-400" > Close < /button> <
                                        /div> <
                                        /div>
                                    );
                                };

                                // ----------------------
                                // Master Data Component
                                // ----------------------
                                const MasterData = ({ currentUser, users, products, onRegister, onDeleteUser, onAddProduct, onUpdateProduct, onDeleteProduct }) => {
                                    // Determine which roles this user can manage
                                    const getRolesToManage = (role) => {
                                        switch (role) {
                                            case 'Federal':
                                                return ['Region'];
                                            case 'Region':
                                                return ['Zone'];
                                            case 'Zone':
                                                return ['Woreda'];
                                            case 'Woreda':
                                                return ['Kebele'];
                                            case 'Kebele':
                                                return ['Farmer'];
                                            default:
                                                return [];
                                        }
                                    };
                                    const rolesToManage = getRolesToManage(currentUser.role);
                                    const canManageProducts = ['Federal', 'Region', 'Zone', 'Woreda'].includes(currentUser.role);

                                    const getUsersByRole = (role) => users.filter(user => user.role === role);

                                    // Filter products based on user's location
                                    const getFilteredProducts = (user, allProducts) => {
                                        switch (user.role) {
                                            case 'Federal':
                                                return allProducts;
                                            case 'Region':
                                                return allProducts.filter(p => p.regionName === user.regionName || !p.regionName);
                                            case 'Zone':
                                                return allProducts.filter(p => p.zoneName === user.zoneName || !p.zoneName);
                                            case 'Woreda':
                                                return allProducts.filter(p => p.woredaName === user.woredaName || !p.woredaName);
                                            default:
                                                return [];
                                        }
                                    };

                                    return ( <
                                        div className = "space-y-8" >
                                        <
                                        h2 className = "text-3xl font-bold" > Master Data Management < /h2> <
                                        div className = "grid grid-cols-1 md:grid-cols-2 gap-8" > { /* User Management Section */ } <
                                        div className = "space-y-4" >
                                        <
                                        h3 className = "text-2xl font-bold" > User Management < /h3> {
                                            rolesToManage.map(role => ( <
                                                div key = { role }
                                                className = "bg-white dark:bg-gray-800 p-4 rounded-xl shadow-md" >
                                                <
                                                h4 className = "text-xl font-semibold mb-2" > Register { role }
                                                Admin < /h4> <
                                                RegistrationForm role = { role }
                                                onRegister = { onRegister }
                                                /> <
                                                ul className = "mt-4 space-y-2" > {
                                                    getUsersByRole(role).map(user => ( <
                                                        li key = { user.id }
                                                        className = "flex justify-between items-center bg-gray-100 dark:bg-gray-700 p-2 rounded-lg" >
                                                        <
                                                        span > { user.fullName }(ID: { user.id }) < /span> <
                                                        button onClick = {
                                                            () => onDeleteUser(user.id, user.role) }
                                                        className = "text-red-500 hover:text-red-700" > Delete < /button> <
                                                        /li>
                                                    ))
                                                } <
                                                /ul> <
                                                /div>
                                            ))
                                        } <
                                        /div>

                                        { /* Product Management Section */ } {
                                            canManageProducts && ( <
                                                div className = "space-y-4" >
                                                <
                                                h3 className = "text-2xl font-bold" > Product Management < /h3> <
                                                ProductForm onSubmit = { onAddProduct }
                                                /> <
                                                ul className = "mt-4 space-y-2" > {
                                                    getFilteredProducts(currentUser, products).map(product => ( <
                                                        li key = { product.id }
                                                        className = "flex justify-between items-center bg-white dark:bg-gray-800 p-4 rounded-xl shadow-md" >
                                                        <
                                                        div >
                                                        <
                                                        div className = "font-bold" > { product.name } < /div> <
                                                        div className = "text-sm text-gray-600 dark:text-gray-400" > { product.description } < /div> <
                                                        /div> <
                                                        div className = "flex space-x-2" >
                                                        <
                                                        button onClick = {
                                                            () => onUpdateProduct({...product, name: product.name + ' (Edited)' }) }
                                                        className = "text-blue-500 hover:text-blue-700" > Edit < /button> <
                                                        button onClick = {
                                                            () => onDeleteProduct(product.id) }
                                                        className = "text-red-500 hover:text-red-700" > Delete < /button> <
                                                        /div> <
                                                        /li>
                                                    ))
                                                } <
                                                /ul> <
                                                /div>
                                            )
                                        } <
                                        /div> <
                                        /div>
                                    );
                                };

                                // ----------------------
                                // Farmer Dashboard Component
                                // ----------------------
                                const FarmerDashboard = ({ currentUser, products, requests, onRequest }) => {
                                        const { register, handleSubmit, formState: { errors } } = useForm();
                                        const [showForm, setShowForm] = useState(false);

                                        const onSubmit = (data) => {
                                            onRequest({
                                                productName: data.productName,
                                                productPurpose: data.productPurpose,
                                                amount: data.amount,
                                                regionName: currentUser.regionName,
                                                zoneName: currentUser.zoneName,
                                                woredaName: currentUser.woredaName,
                                                kebeleName: currentUser.kebeleName,
                                            });
                                            setShowForm(false);
                                        };

                                        return ( <
                                                div className = "space-y-8" >
                                                <
                                                h1 className = "text-3xl font-bold" > Welcome, { currentUser.fullName } < /h1> <
                                                div className = "grid grid-cols-1 md:grid-cols-2 gap-8" > { /* Product Request Form */ } <
                                                div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                <
                                                div className = "flex justify-between items-center mb-4" >
                                                <
                                                h2 className = "text-2xl font-bold" > New Product Request < /h2> <
                                                button onClick = {
                                                    () => setShowForm(!showForm) }
                                                className = "px-4 py-2 bg-green-500 text-white font-medium rounded-lg hover:bg-green-600 transition" > { showForm ? 'Hide Form' : 'Start Request' } <
                                                /button> <
                                                /div> {
                                                    showForm && ( <
                                                        form onSubmit = { handleSubmit(onSubmit) }
                                                        className = "space-y-4" >
                                                        <
                                                        div >
                                                        <
                                                        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Select Product < /label> <
                                                        select className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" {...register("productName", { required: "Product is required" }) } >
                                                        <
                                                        option value = "" > --Select a product-- < /option> {
                                                            products.map(p => ( <
                                                                option key = { p.id }
                                                                value = { p.name } > { p.name } < /option>
                                                            ))
                                                        } <
                                                        /select> {
                                                            errors.productName && < p className = "text-red-500 text-xs mt-1" > { errors.productName.message } < /p>} <
                                                                /div> <
                                                                div >
                                                                <
                                                                label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Purpose < /label> <
                                                                input
                                                            type = "text"
                                                            className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" {...register("productPurpose", { required: "Purpose is required" }) }
                                                            /> {
                                                                errors.productPurpose && < p className = "text-red-500 text-xs mt-1" > { errors.productPurpose.message } < /p>} <
                                                                    /div> <
                                                                    div >
                                                                    <
                                                                    label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Amount < /label> <
                                                                    input
                                                                type = "number"
                                                                className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" {...register("amount", { required: "Amount is required", min: 1 }) }
                                                                /> {
                                                                    errors.amount && < p className = "text-red-500 text-xs mt-1" > { errors.amount.message } < /p>} <
                                                                        /div> <
                                                                        button
                                                                    type = "submit"
                                                                    className = "w-full p-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md" >
                                                                        Submit Request <
                                                                        /button> <
                                                                        /form>
                                                                )
                                                            } <
                                                            /div>

                                                            { /* My Request Status */ } <
                                                            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                <
                                                                h2 className = "text-2xl font-bold mb-4" > My Request Status < /h2> {
                                                                    requests.length > 0 ? ( <
                                                                        ul className = "divide-y divide-gray-200 dark:divide-gray-700" > {
                                                                            requests.map(request => ( <
                                                                                li key = { request.id }
                                                                                className = "py-4 flex justify-between items-center" >
                                                                                <
                                                                                div >
                                                                                <
                                                                                div className = "text-lg font-medium text-gray-900 dark:text-white" > { request.productName } < /div> <
                                                                                div className = "text-sm text-gray-500 dark:text-gray-400" > Status: { request.status } < /div> <
                                                                                /div> <
                                                                                span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                        request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                        request.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                        'bg-red-100 text-red-800'
                                    }` } > { request.status } <
                                                                                /span> <
                                                                                /li>
                                                                            ))
                                                                        } <
                                                                        /ul>
                                                                    ) : ( <
                                                                        p className = "text-gray-500" > You have not submitted any requests yet. < /p>
                                                                    )
                                                                } <
                                                                /div> <
                                                                /div> <
                                                                /div>
                                                        );
                                                    };

                                                    // ----------------------
                                                    // Reports Component
                                                    // ----------------------
                                                    const Reports = ({ requests, currentUser }) => {
                                                        const [reportType, setReportType] = useState('summary');

                                                        const requestCounts = requests.reduce((acc, req) => {
                                                            acc[req.productName] = (acc[req.productName] || 0) + req.amount;
                                                            return acc;
                                                        }, {});

                                                        const statusCounts = requests.reduce((acc, req) => {
                                                            acc[req.status] = (acc[req.status] || 0) + req.amount;
                                                            return acc;
                                                        }, {});

                                                        return ( <
                                                            div className = "space-y-6" >
                                                            <
                                                            h2 className = "text-3xl font-bold" > Reports < /h2> <
                                                            div className = "flex space-x-4 mb-4" >
                                                            <
                                                            button onClick = {
                                                                () => setReportType('summary') }
                                                            className = { `px-4 py-2 rounded-lg ${reportType === 'summary' ? 'bg-blue-600 text-white' : 'bg-gray-200 dark:bg-gray-700'}` } >
                                                            Summary <
                                                            /button> <
                                                            button onClick = {
                                                                () => setReportType('detailed') }
                                                            className = { `px-4 py-2 rounded-lg ${reportType === 'detailed' ? 'bg-blue-600 text-white' : 'bg-gray-200 dark:bg-gray-700'}` } >
                                                            Detailed <
                                                            /button> <
                                                            /div>

                                                            {
                                                                reportType === 'summary' && ( <
                                                                    div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                    <
                                                                    h3 className = "text-2xl font-bold mb-4" > Request Summary < /h3> <
                                                                    div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                                                                    <
                                                                    div >
                                                                    <
                                                                    h4 className = "text-xl font-semibold" > Requests by Product < /h4> <
                                                                    ul className = "mt-2" > {
                                                                        Object.entries(requestCounts).map(([product, count]) => ( <
                                                                            li key = { product }
                                                                            className = "flex justify-between items-center" >
                                                                            <
                                                                            span > { product } < /span> <
                                                                            span className = "font-medium" > { count } < /span> <
                                                                            /li>
                                                                        ))
                                                                    } <
                                                                    /ul> <
                                                                    /div> <
                                                                    div >
                                                                    <
                                                                    h4 className = "text-xl font-semibold" > Requests by Status < /h4> <
                                                                    ul className = "mt-2" > {
                                                                        Object.entries(statusCounts).map(([status, count]) => ( <
                                                                            li key = { status }
                                                                            className = "flex justify-between items-center" >
                                                                            <
                                                                            span > { status } < /span> <
                                                                            span className = "font-medium" > { count } < /span> <
                                                                            /li>
                                                                        ))
                                                                    } <
                                                                    /ul> <
                                                                    /div> <
                                                                    /div> <
                                                                    /div>
                                                                )
                                                            }

                                                            {
                                                                reportType === 'detailed' && ( <
                                                                    div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                    <
                                                                    h3 className = "text-2xl font-bold mb-4" > Detailed Requests < /h3> <
                                                                    div className = "overflow-x-auto" >
                                                                    <
                                                                    table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
                                                                    <
                                                                    thead className = "bg-gray-50 dark:bg-gray-700" >
                                                                    <
                                                                    tr >
                                                                    <
                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > ID < /th> <
                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Requester < /th> <
                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product < /th> <
                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Status < /th> <
                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Location < /th> <
                                                                    /tr> <
                                                                    /thead> <
                                                                    tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                                                                        requests.map(req => ( <
                                                                            tr key = { req.id } >
                                                                            <
                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.id } < /td> <
                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.requesterName } < /td> <
                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.productName } < /td> <
                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.status } < /td> <
                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.regionName }, { req.zoneName }, { req.woredaName }, { req.kebeleName } < /td> <
                                                                            /tr>
                                                                        ))
                                                                    } <
                                                                    /tbody> <
                                                                    /table> <
                                                                    /div> <
                                                                    /div>
                                                                )
                                                            } <
                                                            /div>
                                                        );
                                                    };

                                                    // ----------------------
                                                    // Registration Form Component
                                                    // ----------------------
                                                    const RegistrationForm = ({ role, onRegister }) => {
                                                            const { register, handleSubmit, formState: { errors } } = useForm();

                                                            const onSubmit = (data) => {
                                                                onRegister({...data, role });
                                                            };

                                                            return ( <
                                                                    form onSubmit = { handleSubmit(onSubmit) }
                                                                    className = "space-y-4 p-4 bg-gray-50 dark:bg-gray-700 rounded-lg shadow-inner" >
                                                                    <
                                                                    input type = "text"
                                                                    placeholder = "Full Name" {...register("fullName", { required: "Full name is required" }) }
                                                                    className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                    > {
                                                                        errors.fullName && < p className = "text-red-500 text-xs mt-1" > { errors.fullName.message } < /p>} <
                                                                        input
                                                                        type = "text"
                                                                        placeholder = "Phone Number" {...register("phoneNumber", { required: "Phone number is required" }) }
                                                                        className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                        > {
                                                                            errors.phoneNumber && < p className = "text-red-500 text-xs mt-1" > { errors.phoneNumber.message } < /p>} <
                                                                            input
                                                                            type = "password"
                                                                            placeholder = "Password" {...register("password", { required: "Password is required" }) }
                                                                            className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                            > {
                                                                                errors.password && < p className = "text-red-500 text-xs mt-1" > { errors.password.message } < /p>} <
                                                                                button
                                                                                type = "submit"
                                                                                className = "w-full p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700" >
                                                                                Register { role } <
                                                                                /button> <
                                                                                /form>
                                                                            );
                                                                        };

                                                                        // ----------------------
                                                                        // Product Form Component
                                                                        // ----------------------
                                                                        const ProductForm = ({ onSubmit, defaultValues = {} }) => {
                                                                                const { register, handleSubmit, formState: { errors } } = useForm({ defaultValues });
                                                                                return ( <
                                                                                        form onSubmit = { handleSubmit(onSubmit) }
                                                                                        className = "space-y-4 p-4 bg-gray-50 dark:bg-gray-700 rounded-lg shadow-inner" >
                                                                                        <
                                                                                        h3 className = "text-xl font-bold" > Add / Edit Product < /h3> <
                                                                                        input type = "text"
                                                                                        placeholder = "Product Name" {...register("name", { required: "Product name is required" }) }
                                                                                        className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                                        > {
                                                                                            errors.name && < p className = "text-red-500 text-xs mt-1" > { errors.name.message } < /p>} <
                                                                                            textarea
                                                                                            placeholder = "Description" {...register("description") }
                                                                                            className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" >
                                                                                            < /textarea> <
                                                                                            input
                                                                                            type = "text"
                                                                                            placeholder = "Category" {...register("category", { required: "Category is required" }) }
                                                                                            className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                                                                            > {
                                                                                                errors.category && < p className = "text-red-500 text-xs mt-1" > { errors.category.message } < /p>} <
                                                                                                button
                                                                                                type = "submit"
                                                                                                className = "w-full p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700" >
                                                                                                Save Product <
                                                                                                /button> <
                                                                                                /form>
                                                                                            );
                                                                                        };

                                                                                        export default App;
