// App.js (top)
import FederalDashboard from './dashboards/FederalDashboard';
import RegionDashboard from './dashboards/RegionDashboard';
import ZoneDashboard from './dashboards/ZoneDashboard';
import WoredaDashboard from './dashboards/WoredaDashboard';
import KebeleDashboard from './dashboards/KebeleDashboard';
import FarmerDashboard from './dashboards/FarmerDashboard';

// ...

const renderMainContent = () => {
    if (currentPage === 'dashboard') {
        switch (currentUser.role) {
            case 'Federal':
                return ( <
                    FederalDashboard requests = { getFilteredRequests(currentUser, requests) }
                    currentUser = { currentUser }
                    />
                );
            case 'Region':
                return ( <
                    RegionDashboard requests = { getFilteredRequests(currentUser, requests) }
                    currentUser = { currentUser }
                    />
                );
            case 'Zone':
                return ( <
                    ZoneDashboard requests = { getFilteredRequests(currentUser, requests) }
                    currentUser = { currentUser }
                    />
                );
            case 'Woreda':
                return ( <
                    WoredaDashboard requests = { getFilteredRequests(currentUser, requests) }
                    currentUser = { currentUser }
                    />
                );
            case 'Kebele':
                return ( <
                    KebeleDashboard requests = { getFilteredRequests(currentUser, requests) }
                    currentUser = { currentUser }
                    />
                );
            case 'Farmer':
                return ( <
                    FarmerDashboard currentUser = { currentUser }
                    products = { filteredProducts }
                    requests = { getFilteredRequests(currentUser, requests) }
                    onRequest = { handleProductRequest }
                    />
                );
            default:
                return null;
        }
    }

    if (currentPage === 'products') {
        return <ProductList products = { filteredProducts }
        />;
    }

    if (currentPage === 'requests') {
        return ( <
            RequestsPage requests = { getFilteredRequests(currentUser, requests) }
            currentUser = { currentUser }
            onApprove = { handleRequestApproval }
            />
        );
    }

    return null;
};
