// components/ProductManagement.js
import React, { useState, useEffect } from 'react';

const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [editingProduct, setEditingProduct] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        category: '',
        description: '',
        amount: ''
    });

    useEffect(() => {
        // Load products from localStorage
        const savedProducts = JSON.parse(localStorage.getItem('products') || '[]');
        setProducts(savedProducts);
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();

        if (editingProduct) {
            // Update existing product
            const updatedProducts = products.map(product =>
                product.id === editingProduct.id ?
                {...formData, id: editingProduct.id } :
                product
            );
            setProducts(updatedProducts);
            localStorage.setItem('products', JSON.stringify(updatedProducts));
            setEditingProduct(null);
        } else {
            // Add new product
            const newProduct = {
                ...formData,
                id: Date.now().toString()
            };
            const updatedProducts = [...products, newProduct];
            setProducts(updatedProducts);
            localStorage.setItem('products', JSON.stringify(updatedProducts));
        }

        setFormData({ name: '', category: '', description: '', amount: '' });
        setShowForm(false);
    };

    const handleEdit = (product) => {
        setFormData(product);
        setEditingProduct(product);
        setShowForm(true);
    };

    const handleDelete = (productId) => {
        const updatedProducts = products.filter(product => product.id !== productId);
        setProducts(updatedProducts);
        localStorage.setItem('products', JSON.stringify(updatedProducts));
    };

    return ( <
        div >
        <
        div className = "flex justify-between items-center mb-6" >
        <
        h2 className = "text-2xl font-bold text-gray-800" > Product Management < /h2> <
        button onClick = {
            () => setShowForm(true) }
        className = "bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700" >
        Add Product <
        /button> <
        /div>

        {
            showForm && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4" >
                <
                div className = "bg-white p-6 rounded-lg w-96" >
                <
                h3 className = "text-lg font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add Product' } <
                /h3> <
                form onSubmit = { handleSubmit }
                className = "space-y-4" >
                <
                input type = "text"
                placeholder = "Product Name"
                value = { formData.name }
                onChange = {
                    (e) => setFormData({...formData, name: e.target.value }) }
                className = "w-full p-2 border border-gray-300 rounded"
                required /
                >
                <
                input type = "text"
                placeholder = "Category"
                value = { formData.category }
                onChange = {
                    (e) => setFormData({...formData, category: e.target.value }) }
                className = "w-full p-2 border border-gray-300 rounded"
                required /
                >
                <
                textarea placeholder = "Description"
                value = { formData.description }
                onChange = {
                    (e) => setFormData({...formData, description: e.target.value }) }
                className = "w-full p-2 border border-gray-300 rounded"
                rows = "3"
                required /
                >
                <
                input type = "number"
                placeholder = "Amount"
                value = { formData.amount }
                onChange = {
                    (e) => setFormData({...formData, amount: e.target.value }) }
                className = "w-full p-2 border border-gray-300 rounded"
                required /
                >
                <
                div className = "flex space-x-2" >
                <
                button type = "submit"
                className = "bg-blue-600 text-white px-4 py-2 rounded" >
                { editingProduct ? 'Update' : 'Add' } <
                /button> <
                button type = "button"
                onClick = {
                    () => {
                        setShowForm(false);
                        setEditingProduct(null);
                        setFormData({ name: '', category: '', description: '', amount: '' });
                    }
                }
                className = "bg-gray-500 text-white px-4 py-2 rounded" >
                Cancel <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        <
        div className = "grid gap-4 md:grid-cols-2 lg:grid-cols-3" > {
            products.map((product) => ( <
                div key = { product.id }
                className = "border p-4 rounded-lg" >
                <
                h3 className = "font-bold text-lg" > { product.name } < /h3> <
                p className = "text-gray-600" > { product.category } < /p> <
                p className = "text-sm text-gray-500" > { product.description } < /p> <
                p className = "text-green-600 font-bold" > $ { product.amount } < /p> <
                div className = "flex space-x-2 mt-3" >
                <
                button onClick = {
                    () => handleEdit(product) }
                className = "bg-blue-500 text-white px-3 py-1 rounded text-sm" >
                Edit <
                /button> <
                button onClick = {
                    () => handleDelete(product.id) }
                className = "bg-red-500 text-white px-3 py-1 rounded text-sm" >
                Delete <
                /button> <
                /div> <
                /div>
            ))
        } <
        /div> <
        /div>
    );
};

export default ProductManagement;
