            div > <
            div >
            <
            label className = "block mb-1 text-gray-700 dark:text-gray-300" > Secondary Crops < /label> <
            input type = "text"
            value = { farmerForm.secondaryCrops }
            onChange = {
                (e) => setFarmerForm({...farmerForm, secondaryCrops: e.target.value })
