// File: RegionDashboard.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const RegionDashboard = () => {
    const [activeTab, setActiveTab] = useState("overview");
    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const navigate = useNavigate();

    // Requests
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [statusFilter, setStatusFilter] = useState("all");

    // Products
    const [products, setProducts] = useState([]);
    const [productFilter, setProductFilter] = useState("all");
    const [filteredProducts, setFilteredProducts] = useState([]);

    // Zone Admins
    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [showZoneForm, setShowZoneForm] = useState(false);
    const [editingZone, setEditingZone] = useState(null);
    const [zoneForm, setZoneForm] = useState({
        fullName: "",
        phoneNumber: "",
        password: "",
        confirmPassword: "",
        zoneName: "",
    });

    // Stats
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        acceptedRequests: 0,
        rejectedRequests: 0,
        totalProducts: 0,
        totalZones: 0,
    });

    // --- Effects ---
    useEffect(() => {
        const token = localStorage.getItem("token");
        const userData = JSON.parse(localStorage.getItem("user") || "null");
        const userType = localStorage.getItem("userType");

        if (!token || userType !== "Region" || !userData) {
            navigate("/login");
            return;
        }
        setUser(userData);
        fetchDashboardData(userData);
    }, [navigate]);

    useEffect(() => {
        if (darkMode) {
            document.documentElement.classList.add("dark");
        } else {
            document.documentElement.classList.remove("dark");
        }
    }, [darkMode]);

    useEffect(() => {
        filterRequests();
    }, [statusFilter, requests]);

    useEffect(() => {
        filterProducts();
    }, [productFilter, products]);

    // --- Fetch Data ---
    const fetchDashboardData = async(userData) => {
        try {
            const token = localStorage.getItem("token");

            // Requests
            const requestsRes = await fetch(
                "http://localhost:5000/api/admins/requests/status", { headers: { Authorization: `Bearer ${token}` } }
            );
            if (requestsRes.ok) {
                const data = await requestsRes.json();
                const regionRequests = data.filter(
                    (r) =>
                    r.region_name ===
                    (userData && userData.region_name ? userData.region_name : "")
                );
                setRequests(regionRequests);
                setFilteredRequests(regionRequests);

                setStats((prev) => ({
                    ...prev,
                    totalRequests: regionRequests.length,
                    pendingRequests: regionRequests.filter(
                        (r) => r.region_status === "Pending"
                    ).length,
                    approvedRequests: regionRequests.filter(
                        (r) => r.region_status === "Approved"
                    ).length,
                    acceptedRequests: regionRequests.filter(
                        (r) => r.region_status === "Accepted"
                    ).length,
                    rejectedRequests: regionRequests.filter(
                        (r) => r.region_status === "Rejected"
                    ).length,
                }));
            }

            // Products
            const productsRes = await fetch(
                "http://localhost:5000/api/admins/getproducts", { headers: { Authorization: `Bearer ${token}` } }
            );
            if (productsRes.ok) {
                const data = await productsRes.json();
                setProducts(data);
                setStats((prev) => ({...prev, totalProducts: data.length }));
            }

            // Zones
            const zonesRes = await fetch("http://localhost:5000/api/admins/admins", {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (zonesRes.ok) {
                const data = await zonesRes.json();
                const regionZones = data.filter(
                    (a) =>
                    a.role === "Zone" &&
                    a.region_name ===
                    (userData && userData.region_name ? userData.region_name : "")
                );
                setZoneAdmins(regionZones);
                setStats((prev) => ({...prev, totalZones: regionZones.length }));
            }
        } catch (err) {
            console.error("Error loading dashboard:", err);
        }
    };

    // --- Filters ---
    const filterRequests = () => {
        let filtered = [...requests];
        if (statusFilter !== "all") {
            filtered = filtered.filter(
                (r) => r.region_status.toLowerCase() === statusFilter
            );
        }
        setFilteredRequests(filtered);
    };

    const filterProducts = () => {
        let filtered = [...products];
        if (productFilter === "own" && user) {
            filtered = filtered.filter(
                (p) =>
                p.created_by_admin_id ===
                (user && user.id ? user.id : null)
            );
        } else if (productFilter === "others" && user) {
            filtered = filtered.filter(
                (p) =>
                p.created_by_admin_id !==
                (user && user.id ? user.id : null)
            );
        }
        setFilteredProducts(filtered);
    };

    // --- Zone Admin CRUD ---
    const handleZoneSubmit = async(e) => {
        e.preventDefault();
        const token = localStorage.getItem("token");
        if (zoneForm.password !== zoneForm.confirmPassword) {
            alert("Passwords do not match");
            return;
        }
        const url = editingZone ?
            `http://localhost:5000/api/admins/edit/${editingZone.id}` :
            "http://localhost:5000/api/admins/register";
        const method = editingZone ? "PUT" : "POST";

        const body = {
            fullName: zoneForm.fullName,
            phoneNumber: zoneForm.phoneNumber,
            role: "Zone",
            region_name: user && user.region_name ? user.region_name : "",
            zone_name: zoneForm.zoneName,
            password: zoneForm.password,
        };

        const res = await fetch(url, {
            method,
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify(body),
        });

        if (res.ok) {
            setShowZoneForm(false);
            setEditingZone(null);
            setZoneForm({
                fullName: "",
                phoneNumber: "",
                password: "",
                confirmPassword: "",
                zoneName: "",
            });
            fetchDashboardData(user);
            alert(editingZone ? "Zone updated" : "Zone added");
        } else {
            const err = await res.json();
            alert(err.message || "Error saving zone admin");
        }
    };

    const handleDeleteZone = async(id) => {
        if (!window.confirm("Delete this zone admin?")) return;
        const token = localStorage.getItem("token");
        const res = await fetch(`http://localhost:5000/api/admins/delete/${id}`, {
            method: "DELETE",
            headers: { Authorization: `Bearer ${token}` },
        });
        if (res.ok) {
            fetchDashboardData(user);
            alert("Zone admin deleted");
        }
    };

    // --- Helpers ---
    const getStatusBadge = (status) => {
        const map = {
            Pending: "bg-yellow-100 text-yellow-800",
            Approved: "bg-blue-100 text-blue-800",
            Accepted: "bg-green-100 text-green-800",
            Rejected: "bg-red-100 text-red-800",
        };
        return ( <
            span className = { `px-2 py-1 text-xs rounded-full font-medium ${
          map[status] || "bg-gray-100 text-gray-700"
        }` } >
            { status } <
            /span>
        );
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        localStorage.removeItem("userType");
        navigate("/login");
    };

    // --- UI ---
    return ( <
        div className = "p-6 bg-gray-50 min-h-screen dark:bg-gray-900" >
        <
        div className = "flex justify-between items-center mb-6" >
        <
        h1 className = "text-2xl font-bold text-gray-800 dark:text-white" >
        Region Dashboard({ user && user.region_name ? user.region_name : "" }) <
        /h1> <
        div className = "flex space-x-3" >
        <
        button onClick = {
            () => setDarkMode(!darkMode) }
        className = "px-3 py-1 bg-gray-200 dark:bg-gray-700 rounded" >
        { darkMode ? "Light Mode" : "Dark Mode" } <
        /button> <
        button onClick = { handleLogout }
        className = "px-3 py-1 bg-red-600 text-white rounded" >
        Logout <
        /button> <
        /div> <
        /div>

        { /* Stats */ } <
        div className = "grid grid-cols-2 md:grid-cols-4 gap-4 mb-6" >
        <
        div className = "p-4 bg-white dark:bg-gray-800 rounded shadow" >
        <
        p className = "text-gray-500 dark:text-gray-300" > Total Requests < /p> <
        p className = "text-xl font-bold" > { stats.totalRequests } < /p> <
        /div> <
        div className = "p-4 bg-white dark:bg-gray-800 rounded shadow" >
        <
        p className = "text-gray-500 dark:text-gray-300" > Pending < /p> <
        p className = "text-xl font-bold" > { stats.pendingRequests } < /p> <
        /div> <
        div className = "p-4 bg-white dark:bg-gray-800 rounded shadow" >
        <
        p className = "text-gray-500 dark:text-gray-300" > Approved < /p> <
        p className = "text-xl font-bold" > { stats.approvedRequests } < /p> <
        /div> <
        div className = "p-4 bg-white dark:bg-gray-800 rounded shadow" >
        <
        p className = "text-gray-500 dark:text-gray-300" > Accepted < /p> <
        p className = "text-xl font-bold" > { stats.acceptedRequests } < /p> <
        /div> <
        /div>

        { /* Tabs */ } <
        div className = "flex space-x-4 mb-6" > {
            ["overview", "requests", "products", "zones"].map((tab) => ( <
                button key = { tab }
                onClick = {
                    () => setActiveTab(tab) }
                className = { `px-4 py-2 rounded-lg ${
              activeTab === tab
                ? "bg-blue-600 text-white"
                : "bg-gray-200 dark:bg-gray-700 dark:text-gray-300"
            }` } >
                { tab.charAt(0).toUpperCase() + tab.slice(1) } <
                /button>
            ))
        } <
        /div>

        { /* Requests */ } {
            activeTab === "requests" && ( <
                div >
                <
                div className = "flex space-x-3 mb-4" > {
                    ["all", "pending", "approved", "accepted"].map((f) => ( <
                        button key = { f }
                        onClick = {
                            () => setStatusFilter(f) }
                        className = { `px-3 py-1 rounded ${
                  statusFilter === f
                    ? "bg-blue-600 text-white"
                    : "bg-gray-200 dark:bg-gray-700 dark:text-gray-300"
                }` } >
                        { f } <
                        /button>
                    ))
                } <
                /div> <
                table className = "w-full text-left border" >
                <
                thead className = "bg-gray-100 dark:bg-gray-800" >
                <
                tr >
                <
                th className = "p-2" > Farmer < /th> <
                th className = "p-2" > Product < /th> <
                th className = "p-2" > Qty < /th> <
                th className = "p-2" > Status < /th> <
                /tr> <
                /thead> <
                tbody > {
                    filteredRequests.map((r) => ( <
                        tr key = { r.id }
                        className = "border-b dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-800" >
                        <
                        td className = "p-2" > { r.farmer_name } < /td> <
                        td className = "p-2" > { r.product_name } < /td> <
                        td className = "p-2" > { r.quantity } < /td> <
                        td className = "p-2" > { getStatusBadge(r.region_status) } < /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table> <
                /div>
            )
        }

        { /* Products */ } {
            activeTab === "products" && ( <
                div >
                <
                div className = "flex space-x-3 mb-4" > {
                    ["all", "own", "others"].map((f) => ( <
                        button key = { f }
                        onClick = {
                            () => setProductFilter(f) }
                        className = { `px-3 py-1 rounded ${
                  productFilter === f
                    ? "bg-blue-600 text-white"
                    : "bg-gray-200 dark:bg-gray-700 dark:text-gray-300"
                }` } >
                        { f } <
                        /button>
                    ))
                } <
                /div> <
                ul className = "space-y-2" > {
                    filteredProducts.map((p) => ( <
                        li key = { p.id }
                        className = "p-3 bg-white dark:bg-gray-800 rounded shadow" >
                        <
                        p className = "font-semibold" > { p.name } < /p> <
                        p className = "text-sm text-gray-500" > { p.category } < /p> <
                        /li>
                    ))
                } <
                /ul> <
                /div>
            )
        }

        { /* Zones */ } {
            activeTab === "zones" && ( <
                div >
                <
                button className = "mb-3 px-4 py-2 bg-blue-600 text-white rounded"
                onClick = {
                    () => setShowZoneForm(true) } >
                Add Zone Admin <
                /button> <
                ul className = "space-y-2" > {
                    zoneAdmins.map((z) => ( <
                        li key = { z.id }
                        className = "p-3 bg-white dark:bg-gray-800 rounded shadow flex justify-between" >
                        <
                        div >
                        <
                        p className = "font-semibold" > { z.full_name } < /p> <
                        p className = "text-sm text-gray-500" > { z.zone_name } < /p> <
                        /div> <
                        div className = "space-x-2" >
                        <
                        button className = "px-3 py-1 bg-yellow-500 text-white rounded"
                        onClick = {
                            () => {
                                setEditingZone(z);
                                setZoneForm({
                                    fullName: z.full_name,
                                    phoneNumber: z.phone_number,
                                    password: "",
                                    confirmPassword: "",
                                    zoneName: z.zone_name,
                                });
                                setShowZoneForm(true);
                            }
                        } >
                        Edit <
                        /button> <
                        button className = "px-3 py-1 bg-red-600 text-white rounded"
                        onClick = {
                            () => handleDeleteZone(z.id) } >
                        Delete <
                        /button> <
                        /div> <
                        /li>
                    ))
                } <
                /ul>

                {
                    showZoneForm && ( <
                        div className = "mt-4 p-4 bg-gray-100 dark:bg-gray-800 rounded" >
                        <
                        h3 className = "font-semibold mb-2" > { editingZone ? "Edit Zone" : "Add Zone" } <
                        /h3> <
                        form onSubmit = { handleZoneSubmit }
                        className = "space-y-2" >
                        <
                        input type = "text"
                        placeholder = "Full Name"
                        value = { zoneForm.fullName }
                        onChange = {
                            (e) =>
                            setZoneForm({...zoneForm, fullName: e.target.value })
                        }
                        className = "w-full p-2 border rounded" /
                        >
                        <
                        input type = "text"
                        placeholder = "Phone Number"
                        value = { zoneForm.phoneNumber }
                        onChange = {
                            (e) =>
                            setZoneForm({...zoneForm, phoneNumber: e.target.value })
                        }
                        className = "w-full p-2 border rounded" /
                        >
                        <
                        input type = "text"
                        placeholder = "Zone Name"
                        value = { zoneForm.zoneName }
                        onChange = {
                            (e) =>
                            setZoneForm({...zoneForm, zoneName: e.target.value })
                        }
                        className = "w-full p-2 border rounded" /
                        >
                        <
                        input type = "password"
                        placeholder = "Password"
                        value = { zoneForm.password }
                        onChange = {
                            (e) =>
                            setZoneForm({...zoneForm, password: e.target.value })
                        }
                        className = "w-full p-2 border rounded" /
                        >
                        <
                        input type = "password"
                        placeholder = "Confirm Password"
                        value = { zoneForm.confirmPassword }
                        onChange = {
                            (e) =>
                            setZoneForm({
                                ...zoneForm,
                                confirmPassword: e.target.value,
                            })
                        }
                        className = "w-full p-2 border rounded" /
                        >
                        <
                        div className = "flex space-x-2" >
                        <
                        button type = "submit"
                        className = "px-4 py-2 bg-green-600 text-white rounded" >
                        Save <
                        /button> <
                        button type = "button"
                        className = "px-4 py-2 bg-gray-400 text-white rounded"
                        onClick = {
                            () => {
                                setShowZoneForm(false);
                                setEditingZone(null);
                            }
                        } >
                        Cancel <
                        /button> <
                        /div> <
                        /form> <
                        /div>
                    )
                } <
                /div>
            )
        } <
        /div>
    );
};

export default RegionDashboard;
