                /button> <
                button type = "submit"
                className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                { editingFarmer ? 'Update' : 'Register' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        { /* Product Modal */ } {
            showProductModal && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                <
                div className = { `w-full max-w-md p-6 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                <
                h2 className = "text-xl font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add New Product' } < /h2> <
                form onSubmit = { handleProductSubmit } >
                <
                div className = "mb-4" >
                <
                label className = "block mb-1" > Product Name < /label> <
                input type = "text"
                value = { productForm.name }
                onChange = {
                    (e) => setProductForm({...productForm, name: e.target.value }) }
                className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-1" > Category < /label> <
                input type = "text"
                value = { productForm.category }
                onChange = {
                    (e) => setProductForm({...productForm, category: e.target.value }) }
                className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-1" > Amount < /label> <
                input type = "number"
                min = "1"
                step = "1"
                value = { productForm.amount }
                onChange = {
                    (e) => setProductForm({...productForm, amount: e.target.value }) }
                className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                required /
                >
                <
                /div> <
                div className = "mb-4" >
                <
                label className = "block mb-1" > Price(Birr) < /label> <
                input type = "number"
                min = "1"
                step = "0.01"
                value = { productForm.price }
                onChange = {
                    (e) => setProductForm({...productForm, price: e.target.value }) }
                className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                required /
                >
                <
                /div> <
                div className = "mb-6" >
                <
                label className = "block mb-1" > Description < /label> <
                textarea value = { productForm.description }
                onChange = {
                    (e) => setProductForm({...productForm, description: e.target.value }) }
                className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                rows = "3" >
                < /textarea> <
                /div> <
                div className = "flex justify-end space-x-2" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowProductModal(false);
                        setEditingProduct(null);
                        setProductForm({ name: '', category: '', amount: '', description: '', price: '' });
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                Cancel <
                /button> <
                button type = "submit"
                className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                { editingProduct ? 'Update' : 'Add' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        { /* Request Modal */ } {
            showRequestModal && selectedRequest && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                <
                div className = { `w-full max-w-2xl p-6 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                <
                h2 className = "text-xl font-bold mb-4" > Request Details < /h2>

                <
                div className = "mb-6" >
                <
                h3 className = "text-lg font-semibold" > { selectedRequest.product_name } < /h3> <
                div className = "grid grid-cols-2 gap-4 mt-2" >
                <
                div >
                <
                p > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                <
                p > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
                <
                p > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                <
                p > < strong > Price: < /strong> Birr {selectedRequest.price}</p >
                <
                /div> <
                div >
                <
                p > < strong > Category: < /strong> {selectedRequest.category}</p >
                <
                p > < strong > Amount: < /strong> {selectedRequest.product_amount}</p >
                <
                p > < strong > Date: < /strong> {new Date(selectedRequest.created_at).toLocaleDateString()}</p >
                <
                p > < strong > Status: < /strong> {getStatusBadge(selectedRequest.status)}</p >
                <
                /div> <
                /div> {
                    selectedRequest.note && ( <
                        p className = "mt-3" > < strong > Note: < /strong> {selectedRequest.note}</p >
                    )
                } <
                /div>

                <
                div className = "mb-6" >
                <
                h3 className = "text-lg font-semibold mb-3" > Approval Status < /h3> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } <
                /div>

                {
                    requestStatus && ( <
                        div className = "mb-6" >
                        <
                        h3 className = "text-lg font-semibold mb-3" > Update Status to: { requestStatus } < /h3> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-1" > Reason / Feedback(Optional) < /label> <
                        textarea value = { decisionReason }
                        onChange = {
                            (e) => setDecisionReason(e.target.value) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        rows = "3" >
                        < /textarea> <
                        /div> <
                        /div>
                    )
                }

                <
                div className = "flex justify-end space-x-2" >
                <
                button onClick = {
                    () => {
                        setShowRequestModal(false);
                        setSelectedRequest(null);
                        setRequestStatus('');
                        setDecisionReason('');
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                Close <
                /button> {
                    requestStatus && ( <
                        button onClick = { confirmStatusUpdate }
                        className = { `py-2 px-4 rounded text-white ${
                                        requestStatus === 'Rejected' ? 'bg-red-500 hover:bg-red-600' : 
                                        requestStatus === 'Approved' ? 'bg-blue-500 hover:bg-blue-600' : 
                                        'bg-green-500 hover:bg-green-600'
                                    }` } >
                        Confirm { requestStatus } <
                        /button>
                    )
                } <
                /div> <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default KebeleDashboard;
