import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MasterData() {
    const [admins, setAdmins] = useState([]);
    const [products, setProducts] = useState([]);

    const [newAdmin, setNewAdmin] = useState({ phone: '', password: '', role: 'Region' });
    const [newProduct, setNewProduct] = useState({ name: '', category: '', description: '', amount: '' });

    const token = localStorage.getItem('token');

    // Fetch admins & products
    useEffect(() => {
        fetchAdmins();
        fetchProducts();
    }, []);

    const fetchAdmins = async() => {
        try {
            const res = await axios.get('http://localhost:5000/api/admins/list', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setAdmins(res.data);
        } catch (err) {
            console.error('Error fetching admins', err);
        }
    };

    const fetchProducts = async() => {
        try {
            const res = await axios.get('http://localhost:5000/api/admins/products', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setProducts(res.data);
        } catch (err) {
            console.error('Error fetching products', err);
        }
    };

    const addAdmin = async() => {
        try {
            await axios.post('http://localhost:5000/api/admins/register', newAdmin, {
                headers: { Authorization: `Bearer ${token}` }
            });
            fetchAdmins();
            setNewAdmin({ phone: '', password: '', role: 'Region' });
        } catch (err) {
            console.error('Error adding admin', err);
        }
    };

    const deleteAdmin = async(id) => {
        if (!window.confirm('Are you sure you want to delete this admin?')) return;
        try {
            await axios.delete(`http://localhost:5000/api/admins/delete${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            fetchAdmins();
        } catch (err) {
            console.error('Error deleting admin', err);
        }
    };

    const addProduct = async() => {
        try {
            await axios.post('http://localhost:5000/api/admins/products', newProduct, {
                headers: { Authorization: `Bearer ${token}` }
            });
            fetchProducts();
            setNewProduct({ name: '', category: '', description: '', amount: '' });
        } catch (err) {
            console.error('Error adding product', err);
        }
    };

    const deleteProduct = async(id) => {
        if (!window.confirm('Are you sure you want to delete this product?')) return;
        try {
            await axios.delete(`http://localhost:5000/api/admins/products/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            fetchProducts();
        } catch (err) {
            console.error('Error deleting product', err);
        }
    };

    return ( <
        div className = "space-y-8" > { /* Admin Management */ } <
        div className = "bg-white p-6 rounded-lg shadow" >
        <
        h2 className = "text-xl font-bold mb-4" > Regional Admins < /h2> <
        div className = "flex gap-2 mb-4" >
        <
        input className = "border p-2 rounded"
        placeholder = "Phone"
        value = { newAdmin.phone }
        onChange = {
            (e) => setNewAdmin({...newAdmin, phone: e.target.value }) }
        /> <
        input className = "border p-2 rounded"
        placeholder = "Password"
        type = "password"
        value = { newAdmin.password }
        onChange = {
            (e) => setNewAdmin({...newAdmin, password: e.target.value }) }
        /> <
        select className = "border p-2 rounded"
        value = { newAdmin.role }
        onChange = {
            (e) => setNewAdmin({...newAdmin, role: e.target.value }) } >
        <
        option value = "Region" > Region < /option> <
        /select> <
        button className = "bg-blue-500 text-white px-4 py-2 rounded"
        onClick = { addAdmin } > Add < /button> <
        /div> <
        table className = "w-full border" >
        <
        thead >
        <
        tr className = "bg-gray-100" >
        <
        th className = "border p-2" > Phone < /th> <
        th className = "border p-2" > Role < /th> <
        th className = "border p-2" > Actions < /th> <
        /tr> <
        /thead> <
        tbody > {
            admins.map(admin => ( <
                tr key = { admin.id } >
                <
                td className = "border p-2" > { admin.phone } < /td> <
                td className = "border p-2" > { admin.role } < /td> <
                td className = "border p-2" >
                <
                button className = "bg-red-500 text-white px-2 py-1 rounded"
                onClick = {
                    () => deleteAdmin(admin.id) } > Delete < /button> <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div>

        { /* Product Management */ } <
        div className = "bg-white p-6 rounded-lg shadow" >
        <
        h2 className = "text-xl font-bold mb-4" > Products < /h2> <
        div className = "flex gap-2 mb-4" >
        <
        input className = "border p-2 rounded"
        placeholder = "Name"
        value = { newProduct.name }
        onChange = {
            (e) => setNewProduct({...newProduct, name: e.target.value }) }
        /> <
        input className = "border p-2 rounded"
        placeholder = "Category"
        value = { newProduct.category }
        onChange = {
            (e) => setNewProduct({...newProduct, category: e.target.value }) }
        /> <
        input className = "border p-2 rounded"
        placeholder = "Description"
        value = { newProduct.description }
        onChange = {
            (e) => setNewProduct({...newProduct, description: e.target.value }) }
        /> <
        input className = "border p-2 rounded"
        placeholder = "Amount"
        type = "number"
        value = { newProduct.amount }
        onChange = {
            (e) => setNewProduct({...newProduct, amount: e.target.value }) }
        /> <
        button className = "bg-green-500 text-white px-4 py-2 rounded"
        onClick = { addProduct } > Add < /button> <
        /div> <
        table className = "w-full border" >
        <
        thead >
        <
        tr className = "bg-gray-100" >
        <
        th className = "border p-2" > Name < /th> <
        th className = "border p-2" > Category < /th> <
        th className = "border p-2" > Description < /th> <
        th className = "border p-2" > Amount < /th> <
        th className = "border p-2" > Actions < /th> <
        /tr> <
        /thead> <
        tbody > {
            products.map(product => ( <
                tr key = { product.id } >
                <
                td className = "border p-2" > { product.name } < /td> <
                td className = "border p-2" > { product.category } < /td> <
                td className = "border p-2" > { product.description } < /td> <
                td className = "border p-2" > { product.amount } < /td> <
                td className = "border p-2" >
                <
                button className = "bg-red-500 text-white px-2 py-1 rounded"
                onClick = {
                    () => deleteProduct(product.id) } > Delete < /button> <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div> <
        /div>
    );
}
