/**
 * src/utils/constants.js
 * This file centralizes all the static and reusable data used across the application,
 * making it easier to manage and update.
 */

// Define the possible statuses for a request
export const REQUEST_STATUS = {
    PENDING: 'pending',
    APPROVED: 'approved',
    REJECTED: 'rejected',
};

// Define the two main departments based on previous conversations.
// This prevents "magic strings" and ensures consistency.
export const DEPARTMENTS = {
    BIOLOGICAL_INPUT_SUPPLY: 'Biological Input Supply',
    CHEMICAL_INPUT_SUPPLY: 'Chemical Input Supply',
};
