import React, { useState, useEffect } from 'react';
import {
    Users,
    Package,
    FileText,
    CheckCircle,
    XCircle,
    Clock,
    Plus,
    Edit,
    Trash2,
    Eye,
    Filter,
    Download,
    BarChart3,
    TrendingUp,
    AlertTriangle,
    Settings,
    Moon,
    Sun,
    Bell,
    Search,
    RefreshCw,
    MapPin,
    Calendar,
    DollarSign,
    Truck,
    UserCheck,
    Building,
    Wheat,
    Home,
    Phone,
    Mail,
    Shield,
    Activity,
    PieChart,
    Target,
    Award,
    Zap
} from 'lucide-react';

const RegionDashboard = () => {
    // State management
    const [darkMode, setDarkMode] = useState(false);
    const [activeTab, setActiveTab] = useState('overview');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // Data states
    const [admins, setAdmins] = useState([]);
    const [farmers, setFarmers] = useState([]);
    const [products, setProducts] = useState([]);
    const [requests, setRequests] = useState([]);
    const [reports, setReports] = useState([]);
    const [statistics, setStatistics] = useState({});
    const [myReports, setMyReports] = useState([]);
    const [myProducts, setMyProducts] = useState([]);
    const [otherProducts, setOtherProducts] = useState([]);

    // Filter states
    const [requestFilter, setRequestFilter] = useState('all');
    const [reportFilter, setReportFilter] = useState('all');
    const [searchTerm, setSearchTerm] = useState('');
    const [farmerFilter, setFarmerFilter] = useState('all');
    const [productFilter, setProductFilter] = useState('all');

    // Modal states
    const [showAdminModal, setShowAdminModal] = useState(false);
    const [showProductModal, setShowProductModal] = useState(false);
    const [showReportModal, setShowReportModal] = useState(false);
    const [showFarmerModal, setShowFarmerModal] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const [modalMode, setModalMode] = useState('create'); // create, edit, view

    // Form states
    const [adminForm, setAdminForm] = useState({
        full_name: '',
        phone_number: '',
        password: '',
        role: 'Zone',
        region_name: '',
        zone_name: '',
        woreda_name: '',
        kebele_name: ''
    });

    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        sub_category: '',
        price: '',
        amount: '',
        unit: '',
        description: '',
        manufacturer: '',
        expiry_date: ''
    });

    const [reportForm, setReportForm] = useState({
        reported_admin_id: '',
        report_type: 'Misconduct',
        title: '',
        description: '',
        evidence: '',
        priority: 'Medium'
    });

    const [farmerForm, setFarmerForm] = useState({
        full_name: '',
        phone_number: '',
        password: '',
        region_name: '',
        zone_name: '',
        woreda_name: '',
        kebele_name: '',
        land_size_hectares: '',
        crop_types: '',
        crops_season: '',
        land_type: '',
        farming_experience: '',
        irrigation_type: '',
        farming_method: '',
        primary_crops: '',
        secondary_crops: '',
        soil_type: '',
        has_livestock: false,
        livestock_types: '',
        annual_income: '',
        education_level: ''
    });

    const [requestStatusForm, setRequestStatusForm] = useState({
        status: 'Approved',
        feedback: ''
    });

    // API base URL
    const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

    // Utility function for API calls
    const apiCall = async(endpoint, options = {}) => {
        const token = localStorage.getItem('token');
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
                ...options.headers
            },
            ...options
        };

        const response = await fetch(`${API_BASE}${endpoint}`, config);
        const data = await response.json();

        if (!response.ok) {
            throw new Error(data.message || 'API call failed');
        }

        return data;
    };

    // Data fetching functions
    const fetchAdmins = async() => {
        try {
            const data = await apiCall('/admins/admins');
            setAdmins(data);
        } catch (err) {
            setError('Failed to fetch admins: ' + err.message);
        }
    };

    const fetchFarmers = async() => {
        try {
            const data = await apiCall('/admins/farmers');
            setFarmers(data.farmers || data);
        } catch (err) {
            setError('Failed to fetch farmers: ' + err.message);
        }
    };

    const fetchProducts = async() => {
        try {
            const data = await apiCall('/admins/getproducts');
                setProducts(data);
        } catch (err) {
            setError('Failed to fetch products: ' + err.message);
        }
    };

    const fetchMyProducts = async() => {
        try {
            const data = await apiCall('/admins/myproducts');
                setMyProducts(data);
        } catch (err) {
            setError('Failed to fetch my products: ' + err.message);
        }
    };

    const fetchOtherProducts = async() => {
        try {
            const data = await apiCall('/admins/otherproducts');
                setOtherProducts(data);
        } catch (err) {
            setError('Failed to fetch other products: ' + err.message);
        }
    };

    const fetchRequests = async() => {
        try {
            const data = await apiCall('/admins/getrequests');
            setRequests(data.requests || data);
        } catch (err) {
            setError('Failed to fetch requests: ' + err.message);
        }
    };

    const fetchReports = async() => {
        try {
            const data = await apiCall('/admins/reports');
            setReports(data.reports || data);
        } catch (err) {
            setError('Failed to fetch reports: ' + err.message);
        }
    };

    const fetchMyReports = async() => {
        try {
            const data = await apiCall('/admins/reports/my');
            setMyReports(data.reports || data);
        } catch (err) {
            setError('Failed to fetch my reports: ' + err.message);
        }
    };

    const fetchStatistics = async() => {
        try {
            const data = await apiCall('/admins/reports/statistics');
            setStatistics(data.statistics || data);
        } catch (err) {
            setError('Failed to fetch statistics: ' + err.message);
        }
    };

    // CRUD operations
    const createAdmin = async() => {
        try {
            setLoading(true);
            await apiCall('/admins/register', {
                method: 'POST',
                body: JSON.stringify(adminForm)
            });
            setSuccess('Zone admin created successfully');
            setShowAdminModal(false);
            resetAdminForm();
            fetchAdmins();
        } catch (err) {
            setError('Failed to create admin: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateAdmin = async() => {
        try {
            setLoading(true);
            await apiCall(`/admins/edit/${selectedItem.id}` , {
                method: 'PUT',
                body: JSON.stringify(adminForm)
            });
            setSuccess('Admin updated successfully');
            setShowAdminModal(false);
            resetAdminForm();
            fetchAdmins();
        } catch (err) {
            setError('Failed to update admin: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const createProduct = async() => {
        try {
            setLoading(true);
            await apiCall('/admins/addproduct', {
                method: 'POST',
                body: JSON.stringify(productForm)
            });
            setSuccess('Product created successfully');
            setShowProductModal(false);
            resetProductForm();
            fetchProducts();
            fetchMyProducts();
        } catch (err) {
            setError('Failed to create product: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateProduct = async() => {
        try {
            setLoading(true);
            await apiCall(`/admins/editproduct/${selectedItem.id}`, {
                method: 'PUT',
                body: JSON.stringify(productForm)
            });
            setSuccess('Product updated successfully');
            setShowProductModal(false);
            resetProductForm();
            fetchProducts();
            fetchMyProducts();
        } catch (err) {
            setError('Failed to update product: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) return;

        try {
            setLoading(true);
            await apiCall(`/admins/deleteproduct/${productId}`, {
                method: 'DELETE'
            });
            setSuccess('Product deleted successfully');
            fetchProducts();
            fetchMyProducts();
        } catch (err) {
            setError('Failed to delete product: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateRequestStatus = async(requestId, status, feedback = '') => {
        try {
            setLoading(true);
            await apiCall(`/admins/editrequest/${requestId}/status`, {
                method: 'PUT',
                body: JSON.stringify({ status, feedback })
            });
            setSuccess(`Request ${status.toLowerCase()} successfully`);
            fetchRequests();
        } catch (err) {
            setError('Failed to update request status: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const deleteRejectedRequest = async(requestId) => {
        if (!window.confirm('Are you sure you want to delete this rejected request?')) return;

        try {
            setLoading(true);
            await apiCall(`/admins/deleterequest/${requestId}`, {
                method: 'DELETE'
            });
            setSuccess('Rejected request deleted successfully');
            fetchRequests();
        } catch (err) {
            setError('Failed to delete request: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const createReport = async() => {
        try {
            setLoading(true);
            await apiCall('/admins/reports', {
                method: 'POST',
                body: JSON.stringify(reportForm)
            });
            setSuccess('Report submitted successfully');
            setShowReportModal(false);
            resetReportForm();
            fetchReports();
            fetchMyReports();
        } catch (err) {
            setError('Failed to create report: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateReportStatus = async(reportId, status, resolutionNotes = '') => {
        try {
            setLoading(true);
            await apiCall(`/admins/reports/${reportId}/status`, {
                method: 'PUT',
                body: JSON.stringify({ status, resolution_notes: resolutionNotes })
            });
            setSuccess('Report status updated successfully');
            fetchReports();
            fetchMyReports();
        } catch (err) {
            setError('Failed to update report status: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    // Quick action helpers (prompt-based) so buttons are functional without modals
    const handleAddZoneAdmin = async() => {
        try {
            const full_name = window.prompt('Zone Admin full name:');
            if (!full_name) return;
            const phone_number = window.prompt('Phone number:');
            if (!phone_number) return;
            const password = window.prompt('Temporary password:');
            if (!password) return;
            const zone_name = window.prompt('Zone name (within your region):');
            if (!zone_name) return;
            const payload = { fullName: full_name, phoneNumber: phone_number, password, role: 'Zone', zone_name };
            // region_name is inferred on backend from req.user, but include if known in token
            await apiCall('/admins/register', { method: 'POST', body: JSON.stringify(payload) });
            setSuccess('Zone admin created');
            fetchAdmins();
        } catch (e) {
            setError(e.message);
        }
    };

    const handleEditAdmin = async(admin) => {
        try {
            const phoneNumber = window.prompt('New phone (blank to keep):', admin.phone_number || '');
            const zone_name = window.prompt('New zone (blank to keep):', admin.zone_name || '');
            const body = {};
            if (phoneNumber !== null && phoneNumber !== admin.phone_number) body.phoneNumber = phoneNumber;
            if (zone_name !== null && zone_name !== admin.zone_name) body.zone_name = zone_name;
            if (Object.keys(body).length === 0) return;
            await apiCall(`/admins/edit/${admin.id}`, { method: 'PUT', body: JSON.stringify(body) });
            setSuccess('Admin updated');
            fetchAdmins();
        } catch (e) { setError(e.message); }
    };

    const handleAddProduct = async() => {
        try {
            const name = window.prompt('Product name:'); if (!name) return;
            const category = window.prompt('Category:'); if (!category) return;
            const sub_category = window.prompt('Sub category:'); if (!sub_category) return;
            const amount = parseInt(window.prompt('Amount:' )||'0',10); if (!amount) return;
            const price = parseFloat(window.prompt('Price:')||'0'); if (!price) return;
            const unit = window.prompt('Unit (e.g., kg, qt):') || '';
            const description = window.prompt('Description (optional):') || '';
            const manufacturer = window.prompt('Manufacturer (optional):') || '';
            const expiry_date = window.prompt('Expiry date (YYYY-MM-DD):') || '';
            const body = { name, category, sub_category, amount, price, unit, description, manufacturer, expiry_date };
            await apiCall('/admins/addproduct', { method: 'POST', body: JSON.stringify(body) });
            setSuccess('Product added');
            fetchProducts();
        } catch (e) { setError(e.message); }
    };

    const handleEditProduct = async(product) => {
        try {
            const name = window.prompt('Name (blank keep):', product.name || '') || undefined;
            const category = window.prompt('Category (blank keep):', product.category || '') || undefined;
            const sub_category = window.prompt('Sub category (blank keep):', product.sub_category || '') || undefined;
            const amountStr = window.prompt('Amount (blank keep):', String(product.amount ?? ''));
            const amount = amountStr === null || amountStr === '' ? undefined : parseInt(amountStr,10);
            const priceStr = window.prompt('Price (blank keep):', String(product.price ?? ''));
            const price = priceStr === null || priceStr === '' ? undefined : parseFloat(priceStr);
            const expiry_date = window.prompt('Expiry YYYY-MM-DD (blank keep):', product.expiry_date || '') || undefined;
            const body = { name, category, sub_category, amount, price, expiry_date };
            await apiCall(`/admins/editproduct/${product.id}`, { method: 'PUT', body: JSON.stringify(body) });
            setSuccess('Product updated');
            fetchProducts();
        } catch (e) { setError(e.message); }
    };

    const handleCreateReport = async() => {
        try {
            const reported_admin_id = parseInt(window.prompt('Reported admin id:' )||'0',10); if (!reported_admin_id) return;
            const report_type = window.prompt('Type (Misconduct/Incompetence/Abuse of Power/Corruption/Other):','Misconduct') || 'Misconduct';
            const title = window.prompt('Title:'); if (!title) return;
            const description = window.prompt('Description:'); if (!description) return;
            const priority = window.prompt('Priority (Low/Medium/High/Critical):','Medium') || 'Medium';
            const body = { reported_admin_id, report_type, title, description, priority };
            await apiCall('/admins/reports', { method: 'POST', body: JSON.stringify(body) });
            setSuccess('Report created');
            fetchReports();
        } catch (e) { setError(e.message); }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        window.location.href = '/login';
    };

    // Form reset functions
    const resetAdminForm = () => {
        setAdminForm({
            full_name: '',
            phone_number: '',
            password: '',
            role: 'Zone',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: ''
        });
    };

    const resetProductForm = () => {
        setProductForm({
            name: '',
            category: '',
            sub_category: '',
            price: '',
            amount: '',
            unit: '',
            description: '',
            manufacturer: '',
            expiry_date: ''
        });
    };

    const resetReportForm = () => {
        setReportForm({
            reported_admin_id: '',
            report_type: 'Misconduct',
            title: '',
            description: '',
            evidence: '',
            priority: 'Medium'
        });
    };

    const resetFarmerForm = () => {
        setFarmerForm({
            full_name: '',
            phone_number: '',
            password: '',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: '',
            land_size_hectares: '',
            crop_types: '',
            crops_season: '',
            land_type: '',
            farming_experience: '',
            irrigation_type: '',
            farming_method: '',
            primary_crops: '',
            secondary_crops: '',
            soil_type: '',
            has_livestock: false,
            livestock_types: '',
            annual_income: '',
            education_level: ''
        });
    };

    // Modal functions
    const openAdminModal = (mode, admin = null) => {
        setModalMode(mode);
        setSelectedItem(admin);
        if (admin && mode === 'edit') {
            setAdminForm({
                full_name: admin.full_name || '',
                phone_number: admin.phone_number || '',
                password: '',
                role: admin.role || 'Zone',
                region_name: admin.region_name || '',
                zone_name: admin.zone_name || '',
                woreda_name: admin.woreda_name || '',
                kebele_name: admin.kebele_name || ''
            });
        } else {
            resetAdminForm();
        }
        setShowAdminModal(true);
    };

    const openProductModal = (mode, product = null) => {
        setModalMode(mode);
        setSelectedItem(product);
        if (product && mode === 'edit') {
            setProductForm({
                name: product.name || '',
                category: product.category || '',
                sub_category: product.sub_category || '',
                price: product.price || '',
                amount: product.amount || '',
                unit: product.unit || '',
                description: product.description || '',
                manufacturer: product.manufacturer || '',
                expiry_date: product.expiry_date || ''
            });
        } else {
            resetProductForm();
        }
        setShowProductModal(true);
    };

    const openReportModal = (mode, report = null) => {
        setModalMode(mode);
        setSelectedItem(report);
        if (report && mode === 'edit') {
            setReportForm({
                reported_admin_id: report.reported_admin_id || '',
                report_type: report.report_type || 'Misconduct',
                title: report.title || '',
                description: report.description || '',
                evidence: report.evidence || '',
                priority: report.priority || 'Medium'
            });
            } else {
            resetReportForm();
        }
        setShowReportModal(true);
    };

    const openFarmerModal = (mode, farmer = null) => {
        setModalMode(mode);
        setSelectedItem(farmer);
        if (farmer && mode === 'edit') {
            setFarmerForm({
                full_name: farmer.full_name || '',
                phone_number: farmer.phone_number || '',
                password: '',
                region_name: farmer.region_name || '',
                zone_name: farmer.zone_name || '',
                woreda_name: farmer.woreda_name || '',
                kebele_name: farmer.kebele_name || '',
                land_size_hectares: farmer.land_size_hectares || '',
                crop_types: farmer.crop_types || '',
                crops_season: farmer.crops_season || '',
                land_type: farmer.land_type || '',
                farming_experience: farmer.farming_experience || '',
                irrigation_type: farmer.irrigation_type || '',
                farming_method: farmer.farming_method || '',
                primary_crops: farmer.primary_crops || '',
                secondary_crops: farmer.secondary_crops || '',
                soil_type: farmer.soil_type || '',
                has_livestock: farmer.has_livestock || false,
                livestock_types: farmer.livestock_types || '',
                annual_income: farmer.annual_income || '',
                education_level: farmer.education_level || ''
