                    className = { `w-full pl-10 pr-4 py-2 rounded-lg border ${
                                            darkMode 
                                                ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                                : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                        }` }
                    /> < /
                    div > <
                    /div>

                    <
                    div className = "overflow-x-auto" >
                    <
                    table className = "w-full" >
                    <
                    thead >
                    <
                    tr className = { `border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}` } >
                    <
                    th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Farmer < /th> <
                    th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Product < /th> <
                    th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Quantity < /th> <
                    th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Status < /th> <
                    th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Location < /th> <
                    th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Actions < /th> < /
                    tr > <
                    /thead> <
                    tbody > {
                        filteredRequests.map((request) => ( <
                                tr key = { request.id }
                                className = { `border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}` } >
                                <
                                td className = "py-3 px-4" >
                                <
                                p className = { `font-medium ${darkMode ? 'text-white' : 'text-gray-900'}` } > { request.farmer_name } <
                                /p> {
                                request.farmer_phone && ( <
                                    p className = { `text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}` } > { request.farmer_phone } <
                                    /p>
                                )
                            } <
                            /td> <
                            td className = "py-3 px-4" >
                            <
                            p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { request.product_name } <
                            /p> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { request.quantity } <
                            /p> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            span className = { `px-2 py-1 rounded-full text-xs font-medium ${
                                                        request.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                                        request.status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                                        request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                                        'bg-gray-100 text-gray-800'
                                                    }` } > { request.status } <
                            /span> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { request.farmer_kebele }, { request.farmer_woreda } <
                            /p> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            div className = "flex gap-2" > {
                                request.status === 'Pending' && ( <
                                    >
                                    <
                                    button onClick = {
                                        () => updateRequestStatus(request.id, 'Approved')
                                    }
                                    className = "p-2 text-green-600 hover:bg-green-100 rounded-lg"
                                    title = "Approve Request" >
                                    <
                                    CheckCircle className = "h-4 w-4" / >
                                    <
                            /button> <
                            button onClick = {
                                        () => updateRequestStatus(request.id, 'Rejected')
                                    }
                                    className = "p-2 text-red-600 hover:bg-red-100 rounded-lg"
                                    title = "Reject Request" >
                                    <
                                    XCircle className = "h-4 w-4" / >
                                    <
                            /button> < /
                                    >
                                )
                            } {
                                request.status === 'Rejected' && ( <
                                    button onClick = {
                                        () => deleteRejectedRequest(request.id)
                                    }
                                    className = "p-2 text-red-600 hover:bg-red-100 rounded-lg"
                                    title = "Delete Request" >
                                    <
                                    Trash2 className = "h-4 w-4" / >
                                    <
                                    /button>
                                )
                            } <
                            /div> < /
                            td > <
                            /tr>
                        ))
                } <
                /tbody> < /
                table > <
            /div> < /
            div >
        )
    }

    { /* Reports Tab */ } {
        activeTab === 'reports' && ( <
                div className = { `${darkMode ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md` } >
                <
                div className = "flex justify-between items-center mb-6" >
                <
                h3 className = { `text-lg font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}` } >
                Report Management <
                /h3> <
            button onClick = {
                    () => openReportModal('create')
                }
                className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg flex items-center gap-2" >
                <
                Plus className = "h-4 w-4" / >
                Create Report <
            /button> < /
            div >

            <
                div className = "mb-4" >
            <
                div className = "relative" >
            <
                Search className = "absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" / >
            <
            input type = "text"
                placeholder = "Search reports..."
                value = { searchTerm }
            onChange = {
                    (e) => setSearchTerm(e.target.value)
                }
                className = { `w-full pl-10 pr-4 py-2 rounded-lg border ${
                                            darkMode 
                                                ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                                                : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500'
                                        }` }
            /> < /
            div > <
                /div>

                <
                div className = "overflow-x-auto" >
                <
                table className = "w-full" >
                <
                thead >
                <
                tr className = { `border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}` } >
                <
                th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Title < /th> <
                th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Type < /th> <
                th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Priority < /th> <
                th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Status < /th> <
                th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Created < /th> <
                th className = { `text-left py-3 px-4 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } > Actions < /th> < /
                tr > <
                /thead> <
                tbody > {
                    filteredReports.map((report) => ( <
                            tr key = { report.id }
                            className = { `border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}` } >
                            <
                            td className = "py-3 px-4" >
                            <
                            p className = { `font-medium ${darkMode ? 'text-white' : 'text-gray-900'}` } > { report.title } <
                            /p> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { report.report_type } <
                            /p> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            span className = { `px-2 py-1 rounded-full text-xs font-medium ${
                                                        report.priority === 'Critical' ? 'bg-red-100 text-red-800' :
                                                        report.priority === 'High' ? 'bg-orange-100 text-orange-800' :
                                                        report.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                                                        'bg-green-100 text-green-800'
                                                    }` } > { report.priority } <
                            /span> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            span className = { `px-2 py-1 rounded-full text-xs font-medium ${
                                                        report.status === 'Resolved' ? 'bg-green-100 text-green-800' :
                                                        report.status === 'Under Review' ? 'bg-blue-100 text-blue-800' :
                                                        report.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                                        'bg-gray-100 text-gray-800'
                                                    }` } > { report.status } <
                            /span> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { new Date(report.created_at).toLocaleDateString() } <
                            /p> < /
                            td > <
                            td className = "py-3 px-4" >
                            <
                            div className = "flex gap-2" >
                            <
                            button onClick = {
                                () => openReportModal('view', report)
                            }
                            className = "p-2 text-gray-500 hover:text-gray-700"
                            title = "View Details" >
                            <
                            Eye className = "h-4 w-4" / >
                            <
                            /button> {
                            report.status === 'Pending' && ( <
                                button onClick = {
                                    () => updateReportStatus(report.id, 'Under Review')
                                }
                                className = "p-2 text-blue-600 hover:bg-blue-100 rounded-lg"
                                title = "Start Review" >
                                <
                                Activity className = "h-4 w-4" / >
                                <
                                /button>
                            )
                        } {
                            report.status === 'Under Review' && ( <
                                button onClick = {
                                    () => updateReportStatus(report.id, 'Resolved')
                                }
                                className = "p-2 text-green-600 hover:bg-green-100 rounded-lg"
                                title = "Mark Resolved" >
                                <
                                CheckCircle className = "h-4 w-4" / >
                                <
                                /button>
                            )
                        } <
                        /div> < /
                        td > <
                        /tr>
                    ))
            } <
            /tbody> < /
            table > <
            /div> < /
            div >
    )
}

{ /* Statistics Tab */ } {
    activeTab === 'statistics' && ( <
        div className = { `${darkMode ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md` } >
        <
        h3 className = { `text-lg font-semibold mb-6 ${darkMode ? 'text-white' : 'text-gray-900'}` } >
        Report Statistics <
        /h3>

        <
        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8" >
        <
        div className = { `p-4 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-gray-50'}` } >
        <
        div className = "flex items-center justify-between" >
        <
    div >
    <
        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } > Total Reports < /p> <
        p className = { `text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { statistics.total_reports || 0 } <
        /p> < /
div > <
        FileText className = "h-8 w-8 text-blue-600" / >
        <
        /div> < /
        div >

        <
        div className = { `p-4 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-gray-50'}` } >
        <
        div className = "flex items-center justify-between" >
        <
    div >
    <
        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } > Pending < /p> <
        p className = { `text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { statistics.pending_reports || 0 } <
        /p> < /
div > <
        Clock className = "h-8 w-8 text-yellow-600" / >
        <
        /div> < /
        div >

        <
        div className = { `p-4 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-gray-50'}` } >
        <
        div className = "flex items-center justify-between" >
        <
        div >
        <
        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } > Resolved < /p> <
        p className = { `text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { statistics.resolved_reports || 0 } <
        /p> < /
        div > <
        CheckCircle className = "h-8 w-8 text-green-600" / >
        <
        /div> < /
        div >

        <
        div className = { `p-4 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-gray-50'}` } >
        <
        div className = "flex items-center justify-between" >
        <
        div >
        <
        p className = { `text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}` } > Critical < /p> <
        p className = { `text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}` } > { statistics.critical_priority || 0 } <
        /p> < /
        div > <
        AlertTriangle className = "h-8 w-8 text-red-600" / >
        <
        /div> < /
        div > <
        /div>

        { /* Simple Bar Chart */ } <
        div className = { `p-6 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-gray-50'}` } >
        <
        h4 className = { `text-lg font-semibold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}` } >
        Report Status Distribution <
        /h4> <
        div className = "flex items-end gap-4 h-64" >
        <
        div className = "flex flex-col items-center flex-1" >
        <
        div className = "w-full bg-blue-500 rounded-t"
        style = {
            {
                height: `${((statistics.pending_reports || 0) / Math.max(statistics.total_reports || 1, 1)) * 200}px`
            }
        } >
        <
        /div> <
        p className = { `text-sm mt-2 ${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > Pending < /p> <
        p className = { `text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}` } > { statistics.pending_reports || 0 } <
        /p> < /
        div > <
        div className = "flex flex-col items-center flex-1" >
        <
        div className = "w-full bg-green-500 rounded-t"
        style = {
            {
                height: `${((statistics.resolved_reports || 0) / Math.max(statistics.total_reports || 1, 1)) * 200}px`
            }
        } >
        <
        /div> <
        p className = { `text-sm mt-2 ${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > Resolved < /p> <
        p className = { `text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}` } > { statistics.resolved_reports || 0 } <
        /p> < /
        div > <
        div className = "flex flex-col items-center flex-1" >
        <
        div className = "w-full bg-yellow-500 rounded-t"
        style = {
            {
                height: `${((statistics.under_review_reports || 0) / Math.max(statistics.total_reports || 1, 1)) * 200}px`
            }
        } >
        <
        /div> <
        p className = { `text-sm mt-2 ${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > Under Review < /p> <
        p className = { `text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}` } > { statistics.under_review_reports || 0 } <
        /p> < /
        div > <
        div className = "flex flex-col items-center flex-1" >
        <
        div className = "w-full bg-red-500 rounded-t"
        style = {
            {
                height: `${((statistics.dismissed_reports || 0) / Math.max(statistics.total_reports || 1, 1)) * 200}px`
            }
        } >
        <
        /div> <
        p className = { `text-sm mt-2 ${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > Dismissed < /p> <
        p className = { `text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}` } > { statistics.dismissed_reports || 0 } <
        /p> < /
        div > <
        /div> < /
        div > <
        /div>
    )
} <
/div> < /
div > <
/div>
);
};

export default RegionDashboard;
