// pages/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [loginType, setLoginType] = useState('farmer'); // 'farmer' or 'admin'
    const [formData, setFormData] = useState({
        phoneNumber: '',
        password: '',
        adminType: 'Federal' // For admin dropdown
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [showAbout, setShowAbout] = useState(false);
    const navigate = useNavigate();

    const adminTypes = [
        { value: 'Federal', label: 'Federal Admin', icon: '🏛️', desc: 'Top-level oversight & final approvals' },
        { value: 'Region', label: 'Region Admin', icon: '🌍', desc: 'Regional level management' },
        { value: 'Zone', label: 'Zone Admin', icon: '📍', desc: 'Zone level coordination' },
        { value: 'Woreda', label: 'Woreda Admin', icon: '🏘️', desc: 'Woreda level administration' },
        { value: 'Kebele', label: 'Kebele Representative', icon: '🏠', desc: 'Local community representative' }
    ];

    const handleSubmit = async(e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            let endpoint = '';
            let requestBody = {};

            // Determine the correct API endpoint based on login type
            if (loginType === 'farmer') {
                endpoint = 'http://localhost:5000/api/farmers/login';
                requestBody = {
                    phoneNumber: formData.phoneNumber,
                    password: formData.password
                };
            } else {
                endpoint = 'http://localhost:5000/api/admins/login';
                requestBody = {
                    phoneNumber: formData.phoneNumber,
                    password: formData.password
                };
            }

            const response = await fetch(endpoint, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody)
            });

            const data = await response.json();
            console.log('API Response:', data);

            if (!response.ok) {
                throw new Error(data.message || 'Login failed');
            }

            // Handle response based on login type
            if (loginType === 'farmer') {
                const farmerData = data.farmer || data;
                const token = data.token;

                if (!token) {
                    throw new Error('Authentication error: No token received');
                }

                localStorage.setItem('token', token);
                localStorage.setItem('user', JSON.stringify(farmerData));
                localStorage.setItem('userType', 'Farmer');
                navigate('/farmer-dashboard');

            } else {
                const adminData = data.user || data;
                const token = data.token;
                const actualUserType = adminData.role;

                if (!token || !actualUserType) {
                    throw new Error('Authentication error: Invalid response format');
                }

                // Auto-set the correct admin type based on user's actual role
                setFormData(prev => ({ ...prev, adminType: actualUserType }));

                localStorage.setItem('token', token);
                localStorage.setItem('user', JSON.stringify(adminData));
                localStorage.setItem('userType', actualUserType);

                // Redirect to appropriate admin dashboard
                switch (actualUserType) {
                    case 'Federal':
                        navigate('/federal-dashboard');
                        break;
                    case 'Region':
                        navigate('/region-dashboard');
                        break;
                    case 'Zone':
                        navigate('/zone-dashboard');
                        break;
                    case 'Woreda':
                        navigate('/woreda-dashboard');
                        break;
                    case 'Kebele':
                        navigate('/kebele-dashboard');
                        break;
                    default:
                        navigate('/');
                }
            }

        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-green-50 via-blue-50 to-yellow-50">
            {/* Header */}
            <div className="bg-white shadow-sm border-b">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center py-4">
                        <div className="flex items-center space-x-3">
                            <div className="text-2xl">🇪🇹</div>
                            <div>
                                <h1 className="text-xl font-bold text-gray-900">Ethiopian Agricultural Product Request System</h1>
                                <p className="text-sm text-gray-600">የኢትዮጵያ የግብርና ምርት ጥያቄ ስርዓት</p>
                            </div>
                        </div>
                        <button
                            onClick={() => setShowAbout(!showAbout)}
                            className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                        >
                            About System
                        </button>
                    </div>
                </div>
            </div>

            <div className="flex min-h-screen">
                {/* Left Side - System Information */}
                <div className="hidden lg:flex lg:w-1/2 bg-gradient-to-br from-green-600 to-blue-600 text-white p-12 flex-col justify-center">
                    <div className="max-w-md">
                        <h2 className="text-4xl font-bold mb-6">🌾 Streamlined Agricultural Management</h2>
                        <div className="space-y-6">
                            <div className="flex items-start space-x-3">
                                <div className="text-2xl">📋</div>
                                <div>
                                    <h3 className="text-xl font-semibold mb-2">Product Request Management</h3>
                                    <p className="text-green-100">Submit and track agricultural machinery and product requests through a structured approval workflow.</p>
                                </div>
                            </div>
                            <div className="flex items-start space-x-3">
                                <div className="text-2xl">🔄</div>
                                <div>
                                    <h3 className="text-xl font-semibold mb-2">Multi-Level Approval</h3>
                                    <p className="text-green-100">Kebele → Woreda → Zone → Region → Federal approval hierarchy ensures proper oversight.</p>
                                </div>
                            </div>
                            <div className="flex items-start space-x-3">
                                <div className="text-2xl">📊</div>
                                <div>
                                    <h3 className="text-xl font-semibold mb-2">Real-Time Tracking</h3>
                                    <p className="text-green-100">Monitor request status at each administrative level with detailed feedback and timestamps.</p>
                                </div>
                            </div>
                            <div className="flex items-start space-x-3">
                                <div className="text-2xl">🎯</div>
                                <div>
                                    <h3 className="text-xl font-semibold mb-2">Role-Based Access</h3>
                                    <p className="text-green-100">Secure access control ensuring users see only relevant information for their administrative level.</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Right Side - Login Form */}
                <div className="w-full lg:w-1/2 flex items-center justify-center p-8">
                    <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-md">
                        {/* Login Type Selection */}
                        <div className="text-center mb-8">
                            <h2 className="text-2xl font-bold text-gray-800 mb-2">Welcome Back</h2>
                            <p className="text-gray-600">Choose your login type to continue</p>
                        </div>

                        <div className="flex mb-6 bg-gray-100 rounded-lg p-1">
                            <button
                                type="button"
                                onClick={() => setLoginType('farmer')}
                                className={`flex-1 py-3 px-4 rounded-md text-sm font-medium transition-all ${
                                    loginType === 'farmer'
                                        ? 'bg-green-500 text-white shadow-md'
                                        : 'text-gray-600 hover:text-gray-800'
                                }`}
                            >
                                🚜 Farmer
                            </button>
                            <button
                                type="button"
                                onClick={() => setLoginType('admin')}
                                className={`flex-1 py-3 px-4 rounded-md text-sm font-medium transition-all ${
                                    loginType === 'admin'
                                        ? 'bg-blue-500 text-white shadow-md'
                                        : 'text-gray-600 hover:text-gray-800'
                                }`}
                            >
                                👨‍💼 Administrator
                            </button>
                        </div>

                        {/* Error Message */}
                        {error && (
                            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-4">
                                {error}
                            </div>
                        )}

                        {/* Login Form */}
                        <form onSubmit={handleSubmit} className="space-y-4">
                            {/* Admin Type Dropdown - Only show for admin login */}
                            {loginType === 'admin' && (
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Administrative Level
                                    </label>
                                    <select
                                        value={formData.adminType}
                                        onChange={(e) => setFormData({...formData, adminType: e.target.value})}
                                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        required
                                    >
                                        {adminTypes.map((type) => (
                                            <option key={type.value} value={type.value}>
                                                {type.icon} {type.label}
                                            </option>
                                        ))}
                                    </select>
                                    <p className="text-xs text-gray-500 mt-1">
                                        {adminTypes.find(t => t.value === formData.adminType)?.desc}
                                    </p>
                                </div>
                            )}

                            {/* Phone Number */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Phone Number
                                </label>
                                <input
                                    type="tel"
                                    value={formData.phoneNumber}
                                    onChange={(e) => setFormData({...formData, phoneNumber: e.target.value})}
                                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    placeholder="+251911234567"
                                    required
                                />
                            </div>

                            {/* Password */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Password
                                </label>
                                <input
                                    type="password"
                                    value={formData.password}
                                    onChange={(e) => setFormData({...formData, password: e.target.value})}
                                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    placeholder="Enter your password"
                                    required
                                />
                            </div>

                            {/* Submit Button */}
                            <button
                                type="submit"
                                disabled={loading}
                                className={`w-full py-3 px-4 rounded-lg font-medium transition-all ${
                                    loginType === 'farmer'
                                        ? 'bg-green-600 hover:bg-green-700 text-white'
                                        : 'bg-blue-600 hover:bg-blue-700 text-white'
                                } disabled:opacity-50 disabled:cursor-not-allowed`}
                            >
                                {loading ? (
                                    <span className="flex items-center justify-center">
                                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        Signing In...
                                    </span>
                                ) : (
                                    `Sign In as ${loginType === 'farmer' ? 'Farmer' : 'Administrator'}`
                                )}
                            </button>
                        </form>

                        {/* Help Section */}
                        <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                            <h3 className="font-semibold text-gray-700 mb-2">🔐 Access Information</h3>
                            <div className="text-sm text-gray-600 space-y-1">
                                {loginType === 'farmer' ? (
                                    <div>
                                        <p><strong>🚜 Farmers:</strong> Use your registered phone number and password</p>
                                        <p className="text-xs text-gray-500 mt-1">Submit product requests, track status, and manage your applications</p>
                                    </div>
                                ) : (
                                    <div>
                                        <p><strong>👨‍💼 Administrators:</strong> Select your level and use your credentials</p>
                                        <p className="text-xs text-gray-500 mt-1">Approve/reject requests, manage users, and oversee your administrative area</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* About System Modal */}
            {showAbout && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white rounded-lg max-w-2xl w-full max-h-[80vh] overflow-y-auto">
                        <div className="p-6">
                            <div className="flex justify-between items-center mb-4">
                                <h2 className="text-2xl font-bold text-gray-800">About the System</h2>
                                <button
                                    onClick={() => setShowAbout(false)}
                                    className="text-gray-500 hover:text-gray-700"
                                >
                                    ✕
                                </button>
                            </div>
                            <div className="space-y-4 text-gray-600">
                                <div>
                                    <h3 className="font-semibold text-gray-800 mb-2">🎯 System Purpose</h3>
                                    <p>The Ethiopian Agricultural Product Request System streamlines and automates the process of requesting, approving, and managing agricultural products for farmers across Ethiopia's administrative hierarchy.</p>
                                </div>
                                <div>
                                    <h3 className="font-semibold text-gray-800 mb-2">🔄 Approval Workflow</h3>
                                    <div className="bg-gray-50 p-3 rounded">
                                        <p className="font-mono text-sm">Farmer → Kebele → Woreda → Zone → Region → Federal</p>
                                    </div>
                                    <p className="mt-2">Each level can <strong>Approve</strong> (move to next level), <strong>Reject</strong> (stop process), or <strong>Accept</strong> (product available at this level).</p>
                                </div>
                                <div>
                                    <h3 className="font-semibold text-gray-800 mb-2">👥 User Roles</h3>
                                    <ul className="list-disc list-inside space-y-1">
                                        <li><strong>Farmers:</strong> Submit and track product requests</li>
                                        <li><strong>Kebele Representatives:</strong> Local community management</li>
                                        <li><strong>Woreda Administrators:</strong> District level oversight</li>
                                        <li><strong>Zone Administrators:</strong> Zone level coordination</li>
                                        <li><strong>Region Administrators:</strong> Regional management</li>
                                        <li><strong>Federal Administrators:</strong> Top-level oversight and final approvals</li>
                                    </ul>
                                </div>
                                <div>
                                    <h3 className="font-semibold text-gray-800 mb-2">✨ Key Features</h3>
                                    <ul className="list-disc list-inside space-y-1">
                                        <li>Secure role-based access control</li>
                                        <li>Real-time request tracking</li>
                                        <li>Multi-level approval workflow</li>
                                        <li>Master data management</li>
                                        <li>Comprehensive reporting and dashboards</li>
                                        <li>Mobile-responsive design</li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
