import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously, signOut } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc, collection, query, onSnapshot } from 'firebase/firestore';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Modal from './components/Modal';
import LoginForm from './pages/LoginForm';
import DashboardPage from './pages/DashboardPage';
import ReportsPage from './pages/ReportsPage';
import NewRequestForm from './pages/NewRequestForm';
import RequestApprovalForm from './pages/RequestApprovalForm';

// The following global variables are provided by the Canvas environment.
// We must always use them for Firebase setup.
// eslint-disable-next-line no-undef
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
// eslint-disable-next-line no-undef
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {
    // --------------------------------------------------------------------------------------------------
    // IMPORTANT: REPLACE THE FOLLOWING PLACEHOLDER VALUES WITH YOUR ACTUAL FIREBASE PROJECT CONFIGURATION.
    // YOU CAN FIND THIS IN YOUR FIREBASE PROJECT SETTINGS.
    // --------------------------------------------------------------------------------------------------
    apiKey: "YOUR_API_KEY",
    authDomain: "YOUR_AUTH_DOMAIN",
    projectId: "YOUR_PROJECT_ID",
    storageBucket: "YOUR_STORAGE_BUCKET",
    messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
    appId: "YOUR_APP_ID"
};
// eslint-disable-next-line no-undef
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';

export default function App() {
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);
    const [currentUser, setCurrentUser] = useState(null);
    const [authReady, setAuthReady] = useState(false);
    const [loading, setLoading] = useState(true);
    const [requests, setRequests] = useState([]);
    const [locations, setLocations] = useState({ regions: [], zones: [], woredas: [] });
    const [currentPage, setCurrentPage] = useState('dashboard');
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);
    const [isDarkMode, setIsDarkMode] = useState(false);

    // Initialize Firebase and set up auth state listener
    useEffect(() => {
        try {
            if (!firebaseConfig.projectId || firebaseConfig.projectId === "YOUR_PROJECT_ID") {
                console.error("Firebase Initialization Error: 'projectId' is not provided or is a placeholder. Please check your firebaseConfig.");
                setLoading(false);
                return;
            }
            const app = initializeApp(firebaseConfig);
            const firestore = getFirestore(app);
            const authentication = getAuth(app);
            setDb(firestore);
            setAuth(authentication);

            const unsubscribeAuth = onAuthStateChanged(authentication, async(user) => {
                if (user) {
                    setCurrentUser(user);
                    const userRef = doc(firestore, 'artifacts', appId, 'users', user.uid);
                    const userSnap = await getDoc(userRef);
                    if (userSnap.exists()) {
                        setCurrentUser(prev => ({...prev, ...userSnap.data() }));
                    }
                    setAuthReady(true);
                } else {
                    try {
                        if (initialAuthToken) {
                            await signInWithCustomToken(authentication, initialAuthToken);
                        } else {
                            await signInAnonymously(authentication);
                        }
                    } catch (error) {
                        console.error('Authentication Error:', error);
                    }
                }
            });

            return () => unsubscribeAuth();
        } catch (error) {
            console.error('Firebase initialization error:', error);
            setLoading(false);
        }
    }, [firebaseConfig.projectId]);

    // Fetch locations from Firestore
    useEffect(() => {
        if (!authReady || !db) return;

        const locationsRef = collection(db, 'artifacts', appId, 'public', 'data', 'locations');
        const unsubscribeLocations = onSnapshot(locationsRef, (snapshot) => {
            const fetchedLocations = { regions: [], zones: [], woredas: [] };
            snapshot.docs.forEach(doc => {
                const data = doc.data();
                if (data.type === 'region') fetchedLocations.regions.push(data);
                if (data.type === 'zone') fetchedLocations.zones.push(data);
                if (data.type === 'woreda') fetchedLocations.woredas.push(data);
            });
            setLocations(fetchedLocations);
            setLoading(false);
        });

        return () => unsubscribeLocations();
    }, [authReady, db]);

    // Fetch requests from Firestore
    useEffect(() => {
        if (!authReady || !currentUser || !db) return;

        const requestsRef = collection(db, 'artifacts', appId, 'public', 'data', 'requests');
        const q = query(requestsRef);

        const unsubscribeRequests = onSnapshot(q, (snapshot) => {
            const fetchedRequests = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setRequests(fetchedRequests);
        });

        return () => unsubscribeRequests();
    }, [authReady, currentUser, db]);

    const showModalMessage = (message) => {
        setModalContent(message);
        setShowModal(true);
    };

    const handleLogin = async(userCredentials) => {
        let userId;
        if (auth.currentUser) {
            userId = auth.currentUser.uid;
        } else {
            userId = crypto.randomUUID();
        }

        const userRef = doc(db, 'artifacts', appId, 'users', userId);

        const userData = {
            role: userCredentials.role,
            locationLevel: userCredentials.locationLevel,
            locationId: userCredentials.locationId,
            name: userCredentials.name,
        };

        try {
            await setDoc(userRef, userData, { merge: true });
            setCurrentUser(prev => ({...prev, ...userData }));
            showModalMessage('Login successful!');
        } catch (error) {
            console.error('Error setting user data:', error);
            showModalMessage('Failed to set user data. Please try again.');
        }
    };

    const handleLogout = async() => {
        try {
            await signOut(auth);
            setCurrentUser(null);
            showModalMessage('Logged out successfully!');
        } catch (error) {
            console.error('Error signing out:', error);
            showModalMessage('Failed to log out. Please try again.');
        }
    };

    const handleViewRequest = (request) => {
        setSelectedRequest(request);
        setCurrentPage('view-request');
    };

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    const toggleDarkMode = () => {
        setIsDarkMode(!isDarkMode);
    };

    const getPendingRequests = () => requests.filter(req => req.status === 'Pending');
    const getApprovedRequests = () => requests.filter(req => req.status === 'Approved');
    const getRejectedRequests = () => requests.filter(req => req.status === 'Rejected');

    if (loading || !authReady) {
        return <div className = "flex items-center justify-center min-h-screen" > Loading... < /div>;
    }

    if (!currentUser || !currentUser.role) {
        return <LoginForm onLogin = { handleLogin }
        locations = { locations }
        showModal = { showModalMessage }
        />;
    }

    return ( <
            div className = { `flex min-h-screen bg-gray-100 font-sans antialiased ${isDarkMode ? 'dark' : ''}` } >
            <
            Sidebar currentPage = { currentPage }
            setCurrentPage = { setCurrentPage }
            isSidebarOpen = { isSidebarOpen }
            toggleSidebar = { toggleSidebar }
            userRole = { currentUser.role }
            /> <
            div className = "flex-1 flex flex-col" >
            <
            Header user = { currentUser }
            onLogout = { handleLogout }
            isSidebarOpen = { isSidebarOpen }
            toggleSidebar = { toggleSidebar }
            isDarkMode = { isDarkMode }
            toggleDarkMode = { toggleDarkMode }
            /> <
            main className = "flex-1 p-8" > {
                currentPage === 'dashboard' && < DashboardPage user = { currentUser }
                requests = { requests }
                onViewRequest = { handleViewRequest }
                />} {
                    currentPage === 'pending-requests' && < DashboardPage user = { currentUser }
                    requests = { getPendingRequests() }
                    onViewRequest = { handleViewRequest }
                    />} {
                        currentPage === 'approved-requests' && < DashboardPage user = { currentUser }
                        requests = { getApprovedRequests() }
                        onViewRequest = { handleViewRequest }
                        />} {
                            currentPage === 'rejected-requests' && < DashboardPage user = { currentUser }
                            requests = { getRejectedRequests() }
                            onViewRequest = { handleViewRequest }
                            />} {
                                currentPage === 'reports' && < ReportsPage user = { currentUser }
                                />} {
                                    currentPage === 'new-request' && < NewRequestForm user = { currentUser }
                                    showModal = { showModalMessage }
                                    db = { db }
                                    auth = { auth }
                                    />} {
                                        currentPage === 'view-request' && < RequestApprovalForm user = { currentUser }
                                        request = { selectedRequest }
                                        showModal = { showModalMessage }
                                        db = { db }
                                        auth = { auth }
                                        />} <
                                        /main> <
                                        /div> <
                                        Modal show = { showModal }
                                        onClose = {
                                            () => setShowModal(false) }
                                        title = "Notification"
                                        message = { modalContent }
                                        /> <
                                        /div>
                                    );
                                }
