import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './KebeleDashboard.css';

const KebeleDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const navigate = useNavigate();

        // Farmer Management State
        const [farmers, setFarmers] = useState([]);
        const [showFarmerModal, setShowFarmerModal] = useState(false);
        const [editingFarmer, setEditingFarmer] = useState(null);
        const [farmerForm, setFarmerForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: ''
        });

        // Product Management State
        const [products, setProducts] = useState([]);
        const [showProductModal, setShowProductModal] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            description: '',
            category: '',
            amount: ''
        });

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [selectedRequests, setSelectedRequests] = useState([]);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [requestStatus, setRequestStatus] = useState('');
        const [decisionReason, setDecisionReason] = useState('');
        const [bulkAction, setBulkAction] = useState('');
        const [overallStatus, setOverallStatus] = useState('');
        const [feedback, setFeedback] = useState('');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            totalFarmers: 0,
            totalProducts: 0
        });

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Kebele' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        const fetchDashboardData = async() => {
            const token = localStorage.getItem('token');
            setLoading(true);
            setError('');
            try {
                // Fetch requests
                const requestsRes = await fetch('http://localhost:3001/api/admins/getrequests', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (requestsRes.status === 401) return navigate('/login');
                const requestsData = await requestsRes.json();
                setRequests(requestsData.requests);

                // Fetch farmers in scope
                const farmersRes = await fetch('http://localhost:3001/api/admins/admins', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (farmersRes.status === 401) return navigate('/login');
                const farmersData = await farmersRes.json();
                setFarmers(farmersData.admins.filter(admin => admin.role === 'Farmer'));

                // Fetch products
                const productsRes = await fetch('http://localhost:3001/api/admins/getproducts', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (productsRes.status === 401) return navigate('/login');
                const productsData = await productsRes.json();
                setProducts(productsData.products);

                // Calculate stats
                const pending = requestsData.requests.filter(req => req.status === 'Pending').length;
                const approved = requestsData.requests.filter(req => req.status === 'Approved').length;
                const rejected = requestsData.requests.filter(req => req.status === 'Rejected').length;
                setStats({
                    totalRequests: requestsData.requests.length,
                    pendingRequests: pending,
                    approvedRequests: approved,
                    rejectedRequests: rejected,
                    totalFarmers: farmersData.admins.filter(admin => admin.role === 'Farmer').length,
                    totalProducts: productsData.products.length,
                });

            } catch (err) {
                console.error('Error fetching dashboard data:', err);
                setError('Failed to fetch data.');
            } finally {
                setLoading(false);
            }
        };

        // Helper function to get status badge style
        const getStatusBadge = (status) => {
            switch (status) {
                case 'Pending':
                    return <span className = "badge pending" > Pending < /span>;
                case 'Approved':
                    return <span className = "badge approved" > Approved < /span>;
                case 'Rejected':
                    return <span className = "badge rejected" > Rejected < /span>;
                case 'Accepted':
                    return <span className = "badge accepted" > Accepted < /span>;
                case 'Kebele_Approved':
                    return <span className = "badge kebele-approved" > Kebele Approved < /span>;
                case 'Woreda_Approved':
                    return <span className = "badge woreda-approved" > Woreda Approved < /span>;
                case 'Zone_Approved':
                    return <span className = "badge zone-approved" > Zone Approved < /span>;
                case 'Region_Approved':
                    return <span className = "badge region-approved" > Region Approved < /span>;
                case 'Federal_Approved':
                    return <span className = "badge federal-approved" > Federal Approved < /span>;
                default:
                    return <span className = "badge pending" > Pending < /span>;
            }
        };

        const handleApproveRequest = async(requestId) => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`http://localhost:3001/api/approval-workflow/requests/${requestId}/status/level`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({ status: 'Approved', feedback })
                });
                if (!res.ok) {
                    throw new Error('Failed to approve request');
                }
                fetchDashboardData();
                setShowRequestModal(false);
                setFeedback('');
            } catch (err) {
                console.error('Error approving request:', err);
                setError('Failed to approve request.');
            }
        };

        const handleRejectRequest = async(requestId) => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`http://localhost:3001/api/approval-workflow/requests/${requestId}/status/level`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({ status: 'Rejected', feedback })
                });
                if (!res.ok) {
                    throw new Error('Failed to reject request');
                }
                fetchDashboardData();
                setShowRequestModal(false);
                setFeedback('');
            } catch (err) {
                console.error('Error rejecting request:', err);
                setError('Failed to reject request.');
            }
        };

        const handleAcceptRequest = async(requestId) => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`http://localhost:3001/api/approval-workflow/requests/${requestId}/status/level`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({ status: 'Accepted', feedback })
                });
                if (!res.ok) {
                    throw new Error('Failed to accept request');
                }
                fetchDashboardData();
                setShowRequestModal(false);
                setFeedback('');
            } catch (err) {
                console.error('Error accepting request:', err);
                setError('Failed to accept request.');
            }
        };

        const handleShowRequestDetails = async(requestId) => {
            const token = localStorage.getItem('token');
            try {
                const res = await fetch(`http://localhost:3001/api/approval-workflow/requests/${requestId}/status`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!res.ok) {
                    throw new Error('Failed to fetch request status');
                }
                const data = await res.json();
                setSelectedRequest(data);
                setOverallStatus(data.overallStatus);
                setShowRequestModal(true);
            } catch (err) {
                console.error('Error fetching request details:', err);
                setError('Failed to load request details.');
            }
        };


        const renderRequests = () => ( <
            div className = "requests-table-container" >
            <
            table >
            <
            thead >
            <
            tr >
            <
            th > Request ID < /th> <
            th > Farmer Name < /th> <
            th > Product < /th> <
            th > Quantity < /th> <
            th > Status < /th> <
            th > Actions < /th> <
            /tr> <
            /thead> <
            tbody > {
                requests.length > 0 ? (
                    requests.map(req => ( <
                        tr key = { req.id } >
                        <
                        td > { req.id } < /td> <
                        td > { req.farmer_name } < /td> <
                        td > { req.product_name } < /td> <
                        td > { req.quantity } < /td> <
                        td > { getStatusBadge(req.kebele_status) } < /td> <
                        td >
                        <
                        button className = "btn"
                        onClick = {
                            () => handleShowRequestDetails(req.id) } >
                        View Details <
                        /button> <
                        /td> <
                        /tr>
                    ))
                ) : ( <
                    tr >
                    <
                    td colSpan = "6" > No requests found. < /td> <
                    /tr>
                )
            } <
            /tbody> <
            /table> <
            /div>
        );

        const renderFarmers = () => ( <
            div className = "farmers-table-container" >
            <
            table >
            <
            thead >
            <
            tr >
            <
            th > Full Name < /th> <
            th > Phone Number < /th> <
            th > Location < /th> <
            th > Actions < /th> <
            /tr> <
            /thead> <
            tbody > {
                farmers.length > 0 ? (
                    farmers.map(farmer => ( <
                        tr key = { farmer.id } >
                        <
                        td > { farmer.full_name } < /td> <
                        td > { farmer.phone_number } < /td> <
                        td > { farmer.kebele_name }, { farmer.woreda_name } < /td> <
                        td > { /* You can add actions here if needed */ } <
                        button className = "btn" > View < /button> <
                        /td> <
                        /tr>
                    ))
                ) : ( <
                    tr >
                    <
                    td colSpan = "4" > No farmers found in your kebele. < /td> <
                    /tr>
                )
            } <
            /tbody> <
            /table> <
            /div>
        );

        const renderProducts = () => ( <
            div className = "products-table-container" >
            <
            table >
            <
            thead >
            <
            tr >
            <
            th > Product Name < /th> <
            th > Category < /th> <
            th > Amount < /th> <
            /tr> <
            /thead> <
            tbody > {
                products.length > 0 ? (
                    products.map(product => ( <
                        tr key = { product.id } >
                        <
                        td > { product.name } < /td> <
                        td > { product.category } < /td> <
                        td > { product.amount } < /td> <
                        /tr>
                    ))
                ) : ( <
                    tr >
                    <
                    td colSpan = "3" > No products found. < /td> <
                    /tr>
                )
            } <
            /tbody> <
            /table> <
            /div>
        );

        const renderStats = () => ( <
            div className = "stats-container" >
            <
            div className = "stat-card" >
            <
            h3 > Total Requests < /h3> <
            p > { stats.totalRequests } < /p> <
            /div> <
            div className = "stat-card" >
            <
            h3 > Pending Requests < /h3> <
            p > { stats.pendingRequests } < /p> <
            /div> <
            div className = "stat-card" >
            <
            h3 > Approved Requests < /h3> <
            p > { stats.approvedRequests } < /p> <
            /div> <
            div className = "stat-card" >
            <
            h3 > Rejected Requests < /h3> <
            p > { stats.rejectedRequests } < /p> <
            /div> <
            div className = "stat-card" >
            <
            h3 > Total Farmers < /h3> <
            p > { stats.totalFarmers } < /p> <
            /div> <
            div className = "stat-card" >
            <
            h3 > Total Products < /h3> <
            p > { stats.totalProducts } < /p> <
            /div> <
            /div>
        );

        return ( <
            div className = "dashboard-container" >
            <
            header className = "dashboard-header" >
            <
            h2 > Kebele Admin Dashboard < /h2> {
                user && < span className = "user-info" > Logged in as { user.full_name }({ user.role }) < /span>} <
                    button className = "logout-btn"
                onClick = {
                        () => {
                            localStorage.clear();
                            navigate('/login');
                        }
                    } > Logout < /button> <
                    /header>

                <
                div className = "dashboard-content" >
                    <
                    nav className = "sidebar" >
                    <
                    ul >
                    <
                    li className = { activeTab === 'overview' ? 'active' : '' }
                onClick = {
                        () => setActiveTab('overview') } > Overview < /li> <
                    li className = { activeTab === 'requests' ? 'active' : '' }
                onClick = {
                        () => setActiveTab('requests') } > Manage Requests < /li> <
                    li className = { activeTab === 'farmers' ? 'active' : '' }
                onClick = {
                        () => setActiveTab('farmers') } > Manage Farmers < /li> <
                    li className = { activeTab === 'products' ? 'active' : '' }
                onClick = {
                        () => setActiveTab('products') } > Products < /li> <
                    /ul> <
                    /nav>

                <
                main className = "main-content" > { activeTab === 'overview' && renderStats() } { activeTab === 'requests' && renderRequests() } { activeTab === 'farmers' && renderFarmers() } { activeTab === 'products' && renderProducts() } <
                    /main> <
                    /div>

                { /* Request Details Modal */ } {
                    selectedRequest && showRequestModal && ( <
                        div className = "modal-overlay" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > Request Details < /h3> <
                        div className = "modal-body" >
                        <
                        h4 > Overall Status: { getStatusBadge(overallStatus) } < /h4> <
                        p > < strong > Request ID: < /strong> {selectedRequest.id}</p >
                        <
                        p > < strong > Farmer Name: < /strong> {selectedRequest.farmer_name}</p >
                        <
                        p > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                        <
                        p > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                        <
                        p > < strong > Note: < /strong> {selectedRequest.note}</p >

                        <
                        div className = "approval-section" >
                        <
                        h4 > My Decision: < /h4> <
                        div className = "form-group" >
                        <
                        label htmlFor = "feedback" > Feedback: < /label> <
                        textarea id = "feedback"
                        value = { feedback }
                        onChange = {
                            (e) => setFeedback(e.target.value) }
                        rows = "3"
                        placeholder = "Enter your feedback here..." >
                        < /textarea> <
                        /div> <
                        div className = "modal-actions" >
                        <
                        button className = "btn approve-btn"
                        onClick = {
                            () => handleApproveRequest(selectedRequest.id) }
                        disabled = { selectedRequest.kebele_status !== 'Pending' } >
                        Approve <
                        /button> <
                        button className = "btn reject-btn"
                        onClick = {
                            () => handleRejectRequest(selectedRequest.id) }
                        disabled = { selectedRequest.kebele_status !== 'Pending' } >
                        Reject <
                        /button> <
                        button className = "btn accept-btn"
                        onClick = {
                            () => handleAcceptRequest(selectedRequest.id) }
                        disabled = { selectedRequest.kebele_status !== 'Pending' } >
                        Accept <
                        /button> <
                        /div> <
                        /div>

                        <
                        /div> <
                        div className = "modal-footer" >
                        <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowRequestModal(false);
                                setSelectedRequest(null);
                                setFeedback('');
                            }
                        } >
                        Close <
                        /button> <
                        /div> <
                        /div> <
                        /div>
                    )
                } <
                /div>
            );
        };

        export default KebeleDashboard;
