    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value })
}
className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
required / >
    <
    /div>
)
} <
div className = "flex justify-end space-x-4" >
    <
    button type = "button"
onClick = {
    () => {
        setShowAdminModal(false);
        setEditingAdmin(null);
        setAdminForm({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            role: 'Kebele',
            kebele_name: ''
        });
    }
}
className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" > Cancel < /button> <
button type = "submit"
className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" > { editingAdmin ? 'Update' : 'Register' } <
    /button> < /
div > <
    /form> < /
div > <
    /div>
)
}

{ /* Product Modal */ } {
    showProductModal && ( <
            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
            <
            div className = "bg-white dark:bg-gray-800 rounded-lg p-6 w-full max-w-md" >
            <
            h2 className = "text-xl font-semibold mb-4 dark:text-white" > { editingProduct ? 'Edit Product' : 'Add New Product' } <
            /h2> <
            form onSubmit = { handleProductSubmit } >
            <
            div className = "mb-4" >
            <
            label className = "block text-gray-700 dark:text-gray-300 mb-2" > Product Name < /label> <
            input type = "text"
            value = { productForm.name }
            onChange = {
                (e) => setProductForm({...productForm, name: e.target.value })
            }
            className = { `w-full border ${productErrors.name ? 'border-red-500' : 'border-gray-300'} rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600` }
            required / > {
                productErrors.name && < p className = "text-red-500 text-sm mt-1" > { productErrors.name } < /p>} < /
                div > <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 dark:text-gray-300 mb-2" > Description < /label> <
                textarea value = { productForm.description }
                onChange = {
                    (e) => setProductForm({...productForm, description: e.target.value })
                }
                className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                rows = "3" > < /textarea> < /
                div > <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 dark:text-gray-300 mb-2" > Category < /label> <
                input type = "text"
                value = { productForm.category }
                onChange = {
                    (e) => setProductForm({...productForm, category: e.target.value })
                }
                className = { `w-full border ${productErrors.category ? 'border-red-500' : 'border-gray-300'} rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600` }
                required / > {
                    productErrors.category && < p className = "text-red-500 text-sm mt-1" > { productErrors.category } < /p>} < /
                    div > <
                    div className = "mb-4" >
                    <
                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Amount < /label> <
                    input type = "number"
                    value = { productForm.amount }
                    onChange = {
                        (e) => setProductForm({...productForm, amount: e.target.value })
                    }
                    className = { `w-full border ${productErrors.amount ? 'border-red-500' : 'border-gray-300'} rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600` }
                    required / > {
                        productErrors.amount && < p className = "text-red-500 text-sm mt-1" > { productErrors.amount } < /p>} < /
                        div > <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 dark:text-gray-300 mb-2" > Price(ETB) < /label> <
                        input type = "number"
                        value = { productForm.price }
                        onChange = {
                            (e) => setProductForm({...productForm, price: e.target.value })
                        }
                        className = { `w-full border ${productErrors.price ? 'border-red-500' : 'border-gray-300'} rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600` }
                        required / > {
                            productErrors.price && < p className = "text-red-500 text-sm mt-1" > { productErrors.price } < /p>} < /
                            div > <
                            div className = "flex justify-end space-x-4" >
                            <
                            button type = "button"
                            onClick = {
                                () => {
                                    setShowProductModal(false);
                                    setEditingProduct(null);
                                    setProductForm({ name: '', description: '', category: '', amount: '', price: '' });
                                    setProductErrors({});
                                }
                            }
                            className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" > Cancel < /button> <
                            button type = "submit"
                            className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" > { editingProduct ? 'Update' : 'Add' } <
                            /button> < /
                            div > <
                            /form> < /
                            div > <
                            /div>
                        )
                    }

                    { /* Request Modal */ } {
                        showRequestModal && ( <
                            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                            <
                            div className = "bg-white dark:bg-gray-800 rounded-lg p-6 w-full max-w-2xl" >
                            <
                            h2 className = "text-xl font-semibold mb-4 dark:text-white" > Request Details < /h2>

                            {
                                selectedRequest && ( <
                                    div className = "request-details" >
                                    <
                                    div className = "grid grid-cols-1 md:grid-cols-2 gap-4 mb-6" >
                                    <
                                    div className = "p-3 bg-gray-50 dark:bg-gray-700 rounded" >
                                    <
                                    h3 className = "font-semibold text-gray-800 dark:text-white" > Farmer Information < /h3> <
                                    p className = "text-sm text-gray-600 dark:text-gray-300" > < strong > Name: < /strong> {selectedRequest.farmer_name}</p >
                                    <
                                    p className = "text-sm text-gray-600 dark:text-gray-300" > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
                                    <
                                    p className = "text-sm text-gray-600 dark:text-gray-300" > < strong > Kebele: < /strong> {selectedRequest.kebele_name}</p >
                                    <
                                    /div> <
                                    div className = "p-3 bg-gray-50 dark:bg-gray-700 rounded" >
                                    <
                                    h3 className = "font-semibold text-gray-800 dark:text-white" > Product Information < /h3> <
                                    p className = "text-sm text-gray-600 dark:text-gray-300" > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                                    <
                                    p className = "text-sm text-gray-600 dark:text-gray-300" > < strong > Category: < /strong> {selectedRequest.product_category}</p >
                                    <
                                    p className = "text-sm text-gray-600 dark:text-gray-300" > < strong > Amount: < /strong> {selectedRequest.amount}</p >
                                    <
                                    p className = "text-sm text-gray-600 dark:text-gray-300" > < strong > Price: < /strong> {selectedRequest.price}</p >
                                    <
                                    /div> < /
                                    div >

                                    <
                                    h3 className = "font-semibold mb-2 dark:text-white" > Approval Status < /h3> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) }

                                    {
                                        canWoredaAdminAct(selectedRequest) && requestStatus && ( <
                                            div className = "mt-6" >
                                            <
                                            h3 className = "font-semibold mb-2 dark:text-white" > Update Status to: { requestStatus } < /h3> <
                                            div className = "mb-4" >
                                            <
                                            label className = "block text-gray-700 dark:text-gray-300 mb-2" > Reason / Feedback(Optional) < /label> <
                                            textarea value = { decisionReason }
                                            onChange = {
                                                (e) => setDecisionReason(e.target.value)
                                            }
                                            className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                            rows = "3"
                                            placeholder = "Enter reason for your decision" > < /textarea> < /
                                            div > <
                                            div className = "flex justify-end space-x-4" >
                                            <
                                            button onClick = {
                                                () => {
                                                    setShowRequestModal(false);
                                                    setRequestStatus('');
                                                    setDecisionReason('');
                                                }
                                            }
                                            className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" > Cancel < /button> <
                                            button onClick = { confirmStatusUpdate }
                                            className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" > Confirm < /button> < /
                                            div > <
                                            /div>
                                        )
                                    }

                                    {
                                        !requestStatus && ( <
                                            div className = "flex justify-end mt-6" >
                                            <
                                            button onClick = {
                                                () => setShowRequestModal(false)
                                            }
                                            className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" > Close < /button> < /
                                            div >
                                        )
                                    } <
                                    /div>
                                )
                            } <
                            /div> < /
                            div >
                        )
                    } <
                    /div>
                );
            };

            export default WoredaDashboard;
