import React, { useState, useEffect } from 'react';
import {
    Users,
    Package,
    FileText,
    CheckCircle,
    XCircle,
    Clock,
    Plus,
    Edit,
    Trash2,
    Eye,
    Filter,
    Download,
    BarChart3,
    TrendingUp,
    AlertTriangle,
    Settings,
    Moon,
    Sun,
    Bell,
    Search,
    RefreshCw,
    MapPin,
    Calendar,
    DollarSign,
    Truck,
    UserCheck,
    Building,
    Wheat,
    Home,
    Phone,
    Mail,
    Shield,
    Activity,
    PieChart,
    Target,
    Award,
    Zap
} from 'lucide-react';

const RegionDashboard = () => {
    // State management
    const [darkMode, setDarkMode] = useState(false);
    const [activeTab, setActiveTab] = useState('overview');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // Data states
    const [admins, setAdmins] = useState([]);
    const [farmers, setFarmers] = useState([]);
    const [products, setProducts] = useState([]);
    const [requests, setRequests] = useState([]);
    const [reports, setReports] = useState([]);
    const [statistics, setStatistics] = useState({});
    const [myReports, setMyReports] = useState([]);
    const [myProducts, setMyProducts] = useState([]);
    const [otherProducts, setOtherProducts] = useState([]);

    // Filter states
    const [requestFilter, setRequestFilter] = useState('all');
    const [reportFilter, setReportFilter] = useState('all');
    const [searchTerm, setSearchTerm] = useState('');
    const [farmerFilter, setFarmerFilter] = useState('all');
    const [productFilter, setProductFilter] = useState('all');

    // Modal states
    const [showAdminModal, setShowAdminModal] = useState(false);
    const [showProductModal, setShowProductModal] = useState(false);
    const [showReportModal, setShowReportModal] = useState(false);
    const [showFarmerModal, setShowFarmerModal] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const [modalMode, setModalMode] = useState('create'); // create, edit, view

    // Form states
    const [adminForm, setAdminForm] = useState({
        full_name: '',
        phone_number: '',
        password: '',
        role: 'Zone',
        region_name: '',
        zone_name: '',
        woreda_name: '',
        kebele_name: ''
    });

    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        sub_category: '',
        price: '',
        amount: '',
        unit: '',
        description: '',
        manufacturer: '',
        expiry_date: ''
    });

    const [reportForm, setReportForm] = useState({
        reported_admin_id: '',
        report_type: 'Misconduct',
        title: '',
        description: '',
        evidence: '',
        priority: 'Medium'
    });

    const [farmerForm, setFarmerForm] = useState({
        full_name: '',
        phone_number: '',
        password: '',
        region_name: '',
        zone_name: '',
        woreda_name: '',
        kebele_name: '',
        land_size_hectares: '',
        crop_types: '',
        crops_season: '',
        land_type: '',
        farming_experience: '',
        irrigation_type: '',
        farming_method: '',
        primary_crops: '',
        secondary_crops: '',
        soil_type: '',
        has_livestock: false,
        livestock_types: '',
        annual_income: '',
        education_level: ''
    });

    const [requestStatusForm, setRequestStatusForm] = useState({
        status: 'Approved',
        feedback: ''
    });

    // API base URL
    const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

    // Utility function for API calls
    const apiCall = async (endpoint, options = {}) => {
        const token = localStorage.getItem('token');
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
                ...options.headers
            },
            ...options
        };

        const response = await fetch(`${API_BASE}${endpoint}`, config);
        const data = await response.json();

        if (!response.ok) {
            throw new Error(data.message || 'API call failed');
        }

        return data;
    };

    // Data fetching functions
    const fetchAdmins = async () => {
        try {
            const data = await apiCall('/admin/admins');
            setAdmins(data);
        } catch (err) {
            setError('Failed to fetch admins: ' + err.message);
        }
    };

    const fetchFarmers = async () => {
        try {
            const data = await apiCall('/admin/farmers');
            setFarmers(data.farmers || data);
        } catch (err) {
            setError('Failed to fetch farmers: ' + err.message);
        }
    };

    const fetchProducts = async () => {
        try {
            const data = await apiCall('/admin/getproducts');
            setProducts(data);
        } catch (err) {
            setError('Failed to fetch products: ' + err.message);
        }
    };

    const fetchMyProducts = async () => {
        try {
            const data = await apiCall('/admin/myproducts');
            setMyProducts(data);
        } catch (err) {
            setError('Failed to fetch my products: ' + err.message);
        }
    };

    const fetchOtherProducts = async () => {
        try {
            const data = await apiCall('/admin/otherproducts');
            setOtherProducts(data);
        } catch (err) {
            setError('Failed to fetch other products: ' + err.message);
        }
    };

    const fetchRequests = async () => {
        try {
            const data = await apiCall('/admin/getrequests');
            setRequests(data.requests || data);
        } catch (err) {
            setError('Failed to fetch requests: ' + err.message);
        }
    };

    const fetchReports = async () => {
        try {
            const data = await apiCall('/admin/reports');
            setReports(data.reports || data);
        } catch (err) {
            setError('Failed to fetch reports: ' + err.message);
        }
    };

    const fetchMyReports = async () => {
        try {
            const data = await apiCall('/admin/reports/my');
            setMyReports(data.reports || data);
        } catch (err) {
            setError('Failed to fetch my reports: ' + err.message);
        }
    };

    const fetchStatistics = async () => {
        try {
            const data = await apiCall('/admin/reports/statistics');
            setStatistics(data.statistics || data);
        } catch (err) {
            setError('Failed to fetch statistics: ' + err.message);
        }
    };

    // CRUD operations
    const createAdmin = async () => {
        try {
            setLoading(true);
            await apiCall('/admin/register', {
                method: 'POST',
                body: JSON.stringify(adminForm)
            });
            setSuccess('Zone admin created successfully');
            setShowAdminModal(false);
            resetAdminForm();
            fetchAdmins();
        } catch (err) {
            setError('Failed to create admin: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateAdmin = async () => {
        try {
            setLoading(true);
            await apiCall(`/admin/edit/${selectedItem.id}`, {
                method: 'PUT',
                body: JSON.stringify(adminForm)
            });
            setSuccess('Admin updated successfully');
            setShowAdminModal(false);
            resetAdminForm();
            fetchAdmins();
        } catch (err) {
            setError('Failed to update admin: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const createProduct = async () => {
        try {
            setLoading(true);
            await apiCall('/admin/addproduct', {
                method: 'POST',
                body: JSON.stringify(productForm)
            });
            setSuccess('Product created successfully');
            setShowProductModal(false);
            resetProductForm();
            fetchProducts();
            fetchMyProducts();
        } catch (err) {
            setError('Failed to create product: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateProduct = async () => {
        try {
            setLoading(true);
            await apiCall(`/admin/editproduct/${selectedItem.id}`, {
                method: 'PUT',
                body: JSON.stringify(productForm)
            });
            setSuccess('Product updated successfully');
            setShowProductModal(false);
            resetProductForm();
            fetchProducts();
            fetchMyProducts();
        } catch (err) {
            setError('Failed to update product: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const deleteProduct = async (productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) return;

        try {
            setLoading(true);
            await apiCall(`/admin/deleteproduct/${productId}`, {
                method: 'DELETE'
            });
            setSuccess('Product deleted successfully');
            fetchProducts();
            fetchMyProducts();
        } catch (err) {
            setError('Failed to delete product: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateRequestStatus = async (requestId, status, feedback = '') => {
        try {
            setLoading(true);
            await apiCall(`/admin/editrequest/${requestId}/status`, {
                method: 'PUT',
                body: JSON.stringify({ status, feedback })
            });
            setSuccess(`Request ${status.toLowerCase()} successfully`);
            fetchRequests();
        } catch (err) {
            setError('Failed to update request status: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const deleteRejectedRequest = async (requestId) => {
        if (!window.confirm('Are you sure you want to delete this rejected request?')) return;

        try {
            setLoading(true);
            await apiCall(`/admin/deleterequest/${requestId}`, {
                method: 'DELETE'
            });
            setSuccess('Rejected request deleted successfully');
            fetchRequests();
        } catch (err) {
            setError('Failed to delete request: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const createReport = async () => {
        try {
            setLoading(true);
            await apiCall('/admin/reports', {
                method: 'POST',
                body: JSON.stringify(reportForm)
            });
            setSuccess('Report submitted successfully');
            setShowReportModal(false);
            resetReportForm();
            fetchReports();
            fetchMyReports();
        } catch (err) {
            setError('Failed to create report: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const updateReportStatus = async (reportId, status, resolutionNotes = '') => {
        try {
            setLoading(true);
            await apiCall(`/admin/reports/${reportId}/status`, {
                method: 'PUT',
                body: JSON.stringify({ status, resolution_notes: resolutionNotes })
            });
            setSuccess('Report status updated successfully');
            fetchReports();
            fetchMyReports();
        } catch (err) {
            setError('Failed to update report status: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    // Form reset functions
    const resetAdminForm = () => {
        setAdminForm({
            full_name: '',
            phone_number: '',
            password: '',
            role: 'Zone',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: ''
        });
    };

    const resetProductForm = () => {
        setProductForm({
            name: '',
            category: '',
            sub_category: '',
            price: '',
            amount: '',
            unit: '',
            description: '',
            manufacturer: '',
            expiry_date: ''
        });
    };

    const resetReportForm = () => {
        setReportForm({
            reported_admin_id: '',
            report_type: 'Misconduct',
            title: '',
            description: '',
            evidence: '',
            priority: 'Medium'
        });
    };

    const resetFarmerForm = () => {
        setFarmerForm({
            full_name: '',
            phone_number: '',
            password: '',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: '',
            land_size_hectares: '',
            crop_types: '',
            crops_season: '',
            land_type: '',
            farming_experience: '',
            irrigation_type: '',
            farming_method: '',
            primary_crops: '',
            secondary_crops: '',
            soil_type: '',
            has_livestock: false,
            livestock_types: '',
            annual_income: '',
            education_level: ''
        });
    };

    // Modal functions
    const openAdminModal = (mode, admin = null) => {
        setModalMode(mode);
        setSelectedItem(admin);
        if (admin && mode === 'edit') {
            setAdminForm({
                full_name: admin.full_name || '',
                phone_number: admin.phone_number || '',
                password: '',
                role: admin.role || 'Zone',
                region_name: admin.region_name || '',
                zone_name: admin.zone_name || '',
                woreda_name: admin.woreda_name || '',
                kebele_name: admin.kebele_name || ''
            });
        } else {
            resetAdminForm();
        }
        setShowAdminModal(true);
    };

    const openProductModal = (mode, product = null) => {
        setModalMode(mode);
        setSelectedItem(product);
        if (product && mode === 'edit') {
            setProductForm({
                name: product.name || '',
                category: product.category || '',
                sub_category: product.sub_category || '',
                price: product.price || '',
                amount: product.amount || '',
                unit: product.unit || '',
                description: product.description || '',
                manufacturer: product.manufacturer || '',
                expiry_date: product.expiry_date || ''
            });
        } else {
            resetProductForm();
        }
        setShowProductModal(true);
    };

    const openReportModal = (mode, report = null) => {
        setModalMode(mode);
        setSelectedItem(report);
        if (report && mode === 'edit') {
            setReportForm({
                reported_admin_id: report.reported_admin_id || '',
                report_type: report.report_type || 'Misconduct',
                title: report.title || '',
                description: report.description || '',
                evidence: report.evidence || '',
                priority: report.priority || 'Medium'
            });
        } else {
            resetReportForm();
        }
        setShowReportModal(true);
    };

    const openFarmerModal = (mode, farmer = null) => {
        setModalMode(mode);
        setSelectedItem(farmer);
        if (farmer && mode === 'edit') {
            setFarmerForm({
                full_name: farmer.full_name || '',
                phone_number: farmer.phone_number || '',
                password: '',
                region_name: farmer.region_name || '',
                zone_name: farmer.zone_name || '',
                woreda_name: farmer.woreda_name || '',
                kebele_name: farmer.kebele_name || '',
                land_size_hectares: farmer.land_size_hectares || '',
                crop_types: farmer.crop_types || '',
                crops_season: farmer.crops_season || '',
                land_type: farmer.land_type || '',
                farming_experience: farmer.farming_experience || '',
                irrigation_type: farmer.irrigation_type || '',
                farming_method: farmer.farming_method || '',
                primary_crops: farmer.primary_crops || '',
                secondary_crops: farmer.secondary_crops || '',
                soil_type: farmer.soil_type || '',
                has_livestock: farmer.has_livestock || false,
                livestock_types: farmer.livestock_types || '',
                annual_income: farmer.annual_income || '',
                education_level: farmer.education_level || ''
            });
        } else {
            resetFarmerForm();
        }
        setShowFarmerModal(true);
    };

    // Filter functions
    const filteredRequests = requests.filter(request => {
        const matchesSearch = (request.farmer_name && request.farmer_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
            (request.product_name && request.product_name.toLowerCase().includes(searchTerm.toLowerCase()));
        const matchesFilter = requestFilter === 'all' ||
            (request.status && request.status.toLowerCase().includes(requestFilter.toLowerCase()));
        return matchesSearch && matchesFilter;
    });

    const filteredFarmers = farmers.filter(farmer => {
        const matchesSearch = (farmer.full_name && farmer.full_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
            (farmer.phone_number && farmer.phone_number.includes(searchTerm));
        const matchesFilter = farmerFilter === 'all' ||
            (farmerFilter === 'has_livestock' && farmer.has_livestock) ||
            (farmerFilter === 'no_livestock' && !farmer.has_livestock);
        return matchesSearch && matchesFilter;
    });

    const filteredProducts = products.filter(product => {
        const matchesSearch = (product.name && product.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
            (product.category && product.category.toLowerCase().includes(searchTerm.toLowerCase()));
        const matchesFilter = productFilter === 'all' ||
            (product.category && product.category.toLowerCase().includes(productFilter.toLowerCase()));
        return matchesSearch && matchesFilter;
    });

    const filteredReports = reports.filter(report => {
        const matchesSearch = (report.title && report.title.toLowerCase().includes(searchTerm.toLowerCase())) ||
            (report.description && report.description.toLowerCase().includes(searchTerm.toLowerCase()));
        const matchesFilter = reportFilter === 'all' ||
            (report.status && report.status.toLowerCase().includes(reportFilter.toLowerCase()));
        return matchesSearch && matchesFilter;
    });

    // Fetch all data on component mount
    useEffect(() => {
        const fetchEverything = async () => {
            try {
                await Promise.all([
                    fetchAdmins(),
                    fetchFarmers(),
                    fetchProducts(),
                    fetchMyProducts(),
                    fetchOtherProducts(),
                    fetchRequests(),
                    fetchReports(),
                    fetchMyReports(),
                    fetchStatistics()
                ]);
            } catch (err) {
                setError('Failed to fetch data: ' + err.message);
            }
        };

        fetchEverything();
    }, []);

    // Clear messages after 5 seconds
    useEffect(() => {
        if (error || success) {
            const timer = setTimeout(() => {
                setError('');
                setSuccess('');
            }, 5000);
            return () => clearTimeout(timer);
        }
    }, [error, success]);

    return (
        <div className={`min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
            <div className="flex">
                {/* Sidebar */}
                <div className={`w-64 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg`}>
                    <div className="p-6">
                        <div className="flex items-center gap-3 mb-8">
                            <Building className="h-8 w-8 text-blue-600" />
                            <h1 className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                                Region Dashboard
                            </h1>
                        </div>
                        
                        <nav className="space-y-2">
                            {[
                                { id: 'overview', label: 'Overview', icon: Home },
                                { id: 'admins', label: 'Zone Admins', icon: Users },
                                { id: 'farmers', label: 'Farmers', icon: Wheat },
                                { id: 'products', label: 'Products', icon: Package },
                                { id: 'requests', label: 'Requests', icon: FileText },
                                { id: 'reports', label: 'Reports', icon: AlertTriangle },
                                { id: 'statistics', label: 'Statistics', icon: BarChart3 }
                            ].map(tab => (
                                <button
                                    key={tab.id}
                                    onClick={() => setActiveTab(tab.id)}
                                    className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg text-left transition-colors ${
                                        activeTab === tab.id
                                            ? 'bg-blue-100 text-blue-700'
                                            : darkMode
                                                ? 'text-gray-300 hover:bg-gray-700'
                                                : 'text-gray-600 hover:bg-gray-100'
                                    }`}
                                >
                                    <tab.icon className="h-5 w-5" />
                                    {tab.label}
                                </button>
                            ))}
                        </nav>
                    </div>
                </div>

                {/* Main Content */}
                <div className="flex-1 p-6">
                    {/* Header */}
                    <div className="flex justify-between items-center mb-6">
                        <h2 className={`text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                            {activeTab.charAt(0).toUpperCase() + activeTab.slice(1)}
                        </h2>
                        
                        <div className="flex items-center gap-4">
                            <button
                                onClick={() => setDarkMode(!darkMode)}
                                className={`p-2 rounded-lg ${darkMode ? 'bg-gray-700 text-white' : 'bg-gray-200 text-gray-600'}`}
                            >
                                {darkMode ? <Sun className="h-5 w-5" /> : <Moon className="h-5 w-5" />}
                            </button>
                            
                            <button className="p-2 rounded-lg bg-gray-200 text-gray-600">
                                <Bell className="h-5 w-5" />
                            </button>
                            
                            <button className="p-2 rounded-lg bg-gray-200 text-gray-600">
                                <Settings className="h-5 w-5" />
                            </button>
                        </div>
                    </div>

                    {/* Error/Success Messages */}
                    {error && (
                        <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg">
                            {error}
                        </div>
                    )}
                    
                    {success && (
                        <div className="mb-4 p-4 bg-green-100 border border-green-400 text-green-700 rounded-lg">
                            {success}
                        </div>
                    )}

                    {/* Tab Content */}
                    {activeTab === 'overview' && (
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
                            <div className={`p-6 rounded-lg shadow-md ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
                                <div className="flex items-center justify-between">
                                    <div>
                                        <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Total Admins</p>
                                        <p className={`text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                                            {admins.filter(a => a.type === 'admin').length}
                                        </p>
                                    </div>
                                    <Users className="h-8 w-8 text-blue-600" />
                                </div>
                            </div>

                            <div className={`p-6 rounded-lg shadow-md ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
                                <div className="flex items-center justify-between">
                                    <div>
                                        <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Total Farmers</p>
                                        <p className={`text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                                            {farmers.length}
                                        </p>
                                    </div>
                                    <Wheat className="h-8 w-8 text-green-600" />
                                </div>
                            </div>

                            <div className={`p-6 rounded-lg shadow-md ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
                                <div className="flex items-center justify-between">
                                    <div>
                                        <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Total Products</p>
                                        <p className={`text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                                            {products.length}
                                        </p>
                                    </div>
                                    <Package className="h-8 w-8 text-purple-600" />
                                </div>
                            </div>

                            <div className={`p-6 rounded-lg shadow-md ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
                                <div className="flex items-center justify-between">
                                    <div>
                                        <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Pending Requests</p>
                                        <p className={`text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                                            {requests.filter(r => r.status && r.status.toLowerCase().includes('pending')).length}
                                        </p>
                                    </div>
                                    <Clock className="h-8 w-8 text-orange-600" />
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Placeholder for other tab content */}
                    {activeTab !== 'overview' && (
                        <div className={`p-6 rounded-lg shadow-md ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
                            <p className={`text-center ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                                {activeTab.charAt(0).toUpperCase() + activeTab.slice(1)} content will be implemented here.
                            </p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default RegionDashboard;
