import React, { useState } from 'react';

const RequestForm = () => {
    const [formData, setFormData] = useState({
        itemId: '',
        quantity: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Request submitted:', formData);
        alert('Request submitted successfully!');
        setFormData({ itemId: '', quantity: '' });
    };

    return ( <
        form onSubmit = { handleSubmit }
        className = "bg-white rounded-xl shadow-lg p-6 space-y-4" >
        <
        div >
        <
        label htmlFor = "itemId"
        className = "block text-sm font-medium text-gray-700 mb-1" > Item ID < /label> <
        input type = "text"
        id = "itemId"
        name = "itemId"
        value = { formData.itemId }
        onChange = { handleChange }
        required className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200" /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "quantity"
        className = "block text-sm font-medium text-gray-700 mb-1" > Quantity < /label> <
        input type = "number"
        id = "quantity"
        name = "quantity"
        value = { formData.quantity }
        onChange = { handleChange }
        required className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200" /
        >
        <
        /div> <
        button type = "submit"
        className = "bg-indigo-600 text-white font-bold py-3 px-6 rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-300 w-full" >
        Submit Request <
        /button> <
        /form>
    );
};

export default RequestForm;
