import React, { useState, useEffect } from 'react';
import { Layout, Menu, Table, Tag, Space, Button, message, Modal, Form, Input, Select } from 'antd';
import {
    DashboardOutlined,
    UserOutlined,
    FileOutlined,
    LogoutOutlined,
    CheckCircleOutlined,
    CloseCircleOutlined,
    PlusOutlined,
    EditOutlined,
    DeleteOutlined
} from '@ant-design/icons';
import api from '../api';
import './FederalDashboard.css';

const { Header, Sider, Content } = Layout;
const { Option } = Select;

const FederalDashboard = () => {
    const [collapsed, setCollapsed] = useState(false);
    const [requests, setRequests] = useState([]);
    const [regionalAdmins, setRegionalAdmins] = useState([]);
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isAdminModalVisible, setIsAdminModalVisible] = useState(false);
    const [isProductModalVisible, setIsProductModalVisible] = useState(false);
    const [editingAdmin, setEditingAdmin] = useState(null);
    const [editingProduct, setEditingProduct] = useState(null);
    const [activeTab, setActiveTab] = useState('requests');

    const [adminForm] = Form.useForm();
    const [productForm] = Form.useForm();

    useEffect(() => {
        fetchData();
    }, [activeTab]);

    const fetchData = async() => {
        try {
            setLoading(true);
            if (activeTab === 'requests') {
                await fetchRequests();
            } else if (activeTab === 'admins') {
                await fetchRegionalAdmins();
            } else if (activeTab === 'products') {
                await fetchProducts();
            }
        } catch (error) {
            message.error('Failed to fetch data');
        } finally {
            setLoading(false);
        }
    };

    const fetchRequests = async() => {
        const response = await api.get('/admin/requests');
        setRequests(response.data);
    };

    const fetchRegionalAdmins = async() => {
        const response = await api.get('/admin/regional-admins');
        setRegionalAdmins(response.data);
    };

    const fetchProducts = async() => {
        const response = await api.get('/admin/products');
        setProducts(response.data);
    };

    const handleApprove = async(requestId) => {
        try {
            await api.put(`/admin/requests/${requestId}/status`, {
                status: 'accepted'
            });
            message.success('Request approved successfully');
            fetchRequests();
        } catch (error) {
            message.error('Failed to approve request');
        }
    };

    const handleReject = async(requestId) => {
        try {
            await api.put(`/admin/requests/${requestId}/status`, {
                status: 'rejected'
            });
            message.success('Request rejected successfully');
            fetchRequests();
        } catch (error) {
            message.error('Failed to reject request');
        }
    };

    const handleCreateAdmin = async(values) => {
        try {
            if (editingAdmin) {
                await api.put(`/admin/regional-admins/${editingAdmin.id}`, values);
                message.success('Admin updated successfully');
            } else {
                await api.post('/admin/regional-admins', values);
                message.success('Admin created successfully');
            }
            setIsAdminModalVisible(false);
            setEditingAdmin(null);
            adminForm.resetFields();
            fetchRegionalAdmins();
        } catch (error) {
            message.error('Failed to save admin');
        }
    };

    const handleCreateProduct = async(values) => {
        try {
            if (editingProduct) {
                await api.put(`/admin/products/${editingProduct.id}`, values);
                message.success('Product updated successfully');
            } else {
                await api.post('/admin/products', values);
                message.success('Product created successfully');
            }
            setIsProductModalVisible(false);
            setEditingProduct(null);
            productForm.resetFields();
            fetchProducts();
        } catch (error) {
            message.error('Failed to save product');
        }
    };

    const handleDeleteAdmin = async(adminId) => {
        try {
            await api.delete(`/admin/regional-admins/${adminId}`);
            message.success('Admin deleted successfully');
            fetchRegionalAdmins();
        } catch (error) {
            message.error('Failed to delete admin');
        }
    };

    const handleDeleteProduct = async(productId) => {
        try {
            await api.delete(`/admin/products/${productId}`);
            message.success('Product deleted successfully');
            fetchProducts();
        } catch (error) {
            message.error('Failed to delete product');
        }
    };

    const handleEditAdmin = (admin) => {
        setEditingAdmin(admin);
        adminForm.setFieldsValue(admin);
        setIsAdminModalVisible(true);
    };

    const handleEditProduct = (product) => {
        setEditingProduct(product);
        productForm.setFieldsValue(product);
        setIsProductModalVisible(true);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        window.location.href = '/login';
    };

    const requestColumns = [
        { title: 'Request ID', dataIndex: 'id', key: 'id' },
        { title: 'Farmer', dataIndex: 'farmerName', key: 'farmerName' },
        { title: 'Product', dataIndex: 'productName', key: 'productName' },
        { title: 'Quantity', dataIndex: 'quantity', key: 'quantity' },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
            render: status => ( <
                Tag color = { status === 'approved' ? 'green' : status === 'pending' ? 'orange' : 'red' } > { status.toUpperCase() } <
                /Tag>
            ),
        },
        {
            title: 'Action',
            key: 'action',
            render: (_, record) => ( <
                Space size = "middle" > {
                    record.status === 'pending' && ( <
                        >
                        <
                        Button type = "primary"
                        icon = { < CheckCircleOutlined / > }
                        onClick = {
                            () => handleApprove(record.id) } >
                        Approve <
                        /Button> <
                        Button danger icon = { < CloseCircleOutlined / > }
                        onClick = {
                            () => handleReject(record.id) } >
                        Reject <
                        /Button> <
                        />
                    )
                } <
                /Space>
            ),
        },
    ];

    const adminColumns = [
        { title: 'ID', dataIndex: 'id', key: 'id' },
        { title: 'Name', dataIndex: 'name', key: 'name' },
        { title: 'Email', dataIndex: 'email', key: 'email' },
        { title: 'Region', dataIndex: 'region', key: 'region' },
        {
            title: 'Action',
            key: 'action',
            render: (_, record) => ( <
                Space size = "middle" >
                <
                Button icon = { < EditOutlined / > }
                onClick = {
                    () => handleEditAdmin(record) } >
                Edit <
                /Button> <
                Button danger icon = { < DeleteOutlined / > }
                onClick = {
                    () => handleDeleteAdmin(record.id) } >
                Delete <
                /Button> <
                /Space>
            ),
        },
    ];

    const productColumns = [
        { title: 'ID', dataIndex: 'id', key: 'id' },
        { title: 'Name', dataIndex: 'name', key: 'name' },
        { title: 'Description', dataIndex: 'description', key: 'description' },
        { title: 'Price', dataIndex: 'price', key: 'price', render: price => `$${price}` },
        {
            title: 'Action',
            key: 'action',
            render: (_, record) => ( <
                Space size = "middle" >
                <
                Button icon = { < EditOutlined / > }
                onClick = {
                    () => handleEditProduct(record) } >
                Edit <
                /Button> <
                Button danger icon = { < DeleteOutlined / > }
                onClick = {
                    () => handleDeleteProduct(record.id) } >
                Delete <
                /Button> <
                /Space>
            ),
        },
    ];

    const renderContent = () => {
        switch (activeTab) {
            case 'requests':
                return ( <
                    >
                    <
                    h2 > Request Approvals < /h2> <
                    Table columns = { requestColumns }
                    dataSource = { requests }
                    rowKey = "id"
                    loading = { loading }
                    /> <
                    />
                );
            case 'admins':
                return ( <
                    >
                    <
                    div style = {
                        { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 16 } } >
                    <
                    h2 > Regional Admins < /h2> <
                    Button type = "primary"
                    icon = { < PlusOutlined / > }
                    onClick = {
                        () => setIsAdminModalVisible(true) } >
                    Add Admin <
                    /Button> <
                    /div> <
                    Table columns = { adminColumns }
                    dataSource = { regionalAdmins }
                    rowKey = "id"
                    loading = { loading }
                    /> <
                    />
                );
            case 'products':
                return ( <
                    >
                    <
                    div style = {
                        { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 16 } } >
                    <
                    h2 > Products < /h2> <
                    Button type = "primary"
                    icon = { < PlusOutlined / > }
                    onClick = {
                        () => setIsProductModalVisible(true) } >
                    Add Product <
                    /Button> <
                    /div> <
                    Table columns = { productColumns }
                    dataSource = { products }
                    rowKey = "id"
                    loading = { loading }
                    /> <
                    />
                );
            default:
                return null;
        }
    };

    return ( <
        Layout style = {
            { minHeight: '100vh' } } >
        <
        Sider collapsible collapsed = { collapsed }
        onCollapse = { setCollapsed } >
        <
        div className = "logo" > Federal Admin < /div> <
        Menu theme = "dark"
        selectedKeys = {
            [activeTab] }
        mode = "inline"
        onClick = {
            ({ key }) => setActiveTab(key) } >
        <
        Menu.Item key = "requests"
        icon = { < DashboardOutlined / > } >
        Requests <
        /Menu.Item> <
        Menu.Item key = "admins"
        icon = { < UserOutlined / > } >
        Regional Admins <
        /Menu.Item> <
        Menu.Item key = "products"
        icon = { < FileOutlined / > } >
        Products <
        /Menu.Item> <
        Menu.Item key = "logout"
        icon = { < LogoutOutlined / > }
        onClick = { handleLogout } >
        Logout <
        /Menu.Item> <
        /Menu> <
        /Sider> <
        Layout className = "site-layout" >
        <
        Header className = "site-layout-background"
        style = {
            { padding: 0 } }
        /> <
        Content style = {
            { margin: '0 16px' } } >
        <
        div className = "site-layout-background"
        style = {
            { padding: 24, minHeight: 360 } } > { renderContent() } <
        /div> <
        /Content> <
        /Layout>

        { /* Admin Modal */ } <
        Modal title = { editingAdmin ? 'Edit Admin' : 'Create Admin' }
        visible = { isAdminModalVisible }
        onCancel = {
            () => {
                setIsAdminModalVisible(false);
                setEditingAdmin(null);
                adminForm.resetFields();
            }
        }
        footer = { null } >
        <
        Form form = { adminForm }
        layout = "vertical"
        onFinish = { handleCreateAdmin } >
        <
        Form.Item name = "name"
        label = "Name"
        rules = {
            [{ required: true }] } >
        <
        Input / >
        <
        /Form.Item> <
        Form.Item name = "email"
        label = "Email"
        rules = {
            [{ required: true, type: 'email' }] } >
        <
        Input / >
        <
        /Form.Item> <
        Form.Item name = "password"
        label = "Password"
        rules = {
            [{ required: !editingAdmin }] } >
        <
        Input.Password / >
        <
        /Form.Item> <
        Form.Item name = "region"
        label = "Region"
        rules = {
            [{ required: true }] } >
        <
        Input / >
        <
        /Form.Item> <
        Form.Item >
        <
        Button type = "primary"
        htmlType = "submit" > { editingAdmin ? 'Update' : 'Create' } <
        /Button> <
        /Form.Item> <
        /Form> <
        /Modal>

        { /* Product Modal */ } <
        Modal title = { editingProduct ? 'Edit Product' : 'Create Product' }
        visible = { isProductModalVisible }
        onCancel = {
            () => {
                setIsProductModalVisible(false);
                setEditingProduct(null);
                productForm.resetFields();
            }
        }
        footer = { null } >
        <
        Form form = { productForm }
        layout = "vertical"
        onFinish = { handleCreateProduct } >
        <
        Form.Item name = "name"
        label = "Name"
        rules = {
            [{ required: true }] } >
        <
        Input / >
        <
        /Form.Item> <
        Form.Item name = "description"
        label = "Description"
        rules = {
            [{ required: true }] } >
        <
        Input.TextArea / >
        <
        /Form.Item> <
        Form.Item name = "price"
        label = "Price"
        rules = {
            [{ required: true }] } >
        <
        Input type = "number" / >
        <
        /Form.Item> <
        Form.Item >
        <
        Button type = "primary"
        htmlType = "submit" > { editingProduct ? 'Update' : 'Create' } <
        /Button> <
        /Form.Item> <
        /Form> <
        /Modal> <
        /Layout>
    );
};

export default FederalDashboard;
