import React from 'react';

const RequestsTable = ({ requests, onViewRequest }) => {
    const getStatusColor = (status) => {
        if (status.includes('Approved')) return 'bg-green-100 text-green-800';
        if (status.includes('Pending')) return 'bg-yellow-100 text-yellow-800';
        if (status.includes('Rejected')) return 'bg-red-100 text-red-800';
        return 'bg-gray-100 text-gray-800';
    };

    return ( <
        div className = "overflow-x-auto" >
        <
        table className = "min-w-full divide-y divide-gray-200" >
        <
        thead className = "bg-gray-50" >
        <
        tr >
        <
        th scope = "col"
        className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Request ID < /th> <
        th scope = "col"
        className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Requester < /th> <
        th scope = "col"
        className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Product < /th> <
        th scope = "col"
        className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Status < /th> <
        th scope = "col"
        className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Actions < /th> <
        /tr> <
        /thead> <
        tbody className = "bg-white divide-y divide-gray-200" > {
            requests.map((request) => ( <
                tr key = { request.id } >
                <
                td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900" > { request.id } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.requester } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.product } < /td> <
                td className = "px-6 py-4 whitespace-nowrap" >
                <
                span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(request.status)}` } > { request.status } <
                /span> <
                /td> <
                td className = "px-6 py-4 whitespace-nowrap text-right text-sm font-medium" >
                <
                button onClick = {
                    () => onViewRequest(request) }
                className = "text-indigo-600 hover:text-indigo-900 font-medium" >
                View <
                /button> <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div>
    );
};

export default RequestsTable;
