import React from 'react';

const Header = ({ user, onLogout }) => {
        return ( <
                header className = "bg-white shadow-sm py-4 px-8 flex justify-between items-center sticky top-0 z-50" >
                <
                div className = "flex items-center space-x-4" >
                <
                h1 className = "text-2xl font-bold text-gray-800" > Product Request Management System < /h1> {
                user && ( <
                    span className = "bg-indigo-100 text-indigo-800 text-xs font-semibold px-2.5 py-0.5 rounded-full" > { user.role } <
                    /span>
                )
            } <
            /div> {
        user && ( <
            div className = "flex items-center space-x-4" >
            <
            span className = "text-gray-600" > Hello, { user.name } < /span> <
            button onClick = { onLogout }
            className = "bg-red-500 text-white px-4 py-2 rounded-lg text-sm font-semibold hover:bg-red-600 transition-colors duration-200" >
            Logout <
            /button> < /
            div >
        )
    } <
    /header>
);
};

export default Header;
