import React from 'react';

const Header = ({ user, onLogout }) => {
    return ( <
        header className = "bg-white shadow-md p-4 mb-8" >
        <
        div className = "container mx-auto flex justify-between items-center" >
        <
        div className = "flex items-center space-x-4" >
        <
        h1 className = "text-2xl font-bold text-indigo-600" > Product Request Management System < /h1> <
        span className = "bg-indigo-100 text-indigo-800 text-xs font-medium px-2.5 py-0.5 rounded-full" > { user.role } <
        /span> <
        /div> <
        div className = "flex items-center space-x-4" >
        <
        span className = "text-gray-700" > Hello, { user.name } < /span> <
        button onClick = { onLogout }
        className = "bg-red-500 text-white font-semibold py-2 px-4 rounded-full shadow-md hover:bg-red-600 transition-colors duration-300" >
        Logout <
        /button> <
        /div> <
        /div> <
        /header>
    );
};

export default Header;
