import React from 'react';

const ADMIN_ROLES = {
    FEDERAL: 'Federal',
    REGION: 'Region',
    ZONE: 'Zone',
    WOREDA: 'Woreda',
    KEBELE: 'Kebele',
};

const Sidebar = ({ currentPage, setCurrentPage, currentUser }) => {
    const navItems = [
        { name: 'Dashboard', page: 'dashboard', icon: 'üè†', roles: ['Federal', 'Region', 'Zone', 'Woreda', 'Kebele'] },
        { name: 'Master Data', page: 'master-data', icon: 'üìù', roles: ['Federal', 'Region', 'Zone', 'Woreda', 'Kebele'] },
        { name: 'Reports', page: 'reports', icon: 'üìä', roles: ['Federal', 'Region', 'Zone'] },
    ];

    return ( <
        aside className = "w-64 bg-white shadow-xl flex flex-col p-4" >
        <
        div className = "text-2xl font-bold text-center mb-6 text-blue-600" >
        Farmers App <
        /div> <
        nav className = "flex-1" >
        <
        ul > {
            navItems.filter(item => item.roles.includes(currentUser.role)).map(item => ( <
                li key = { item.page }
                className = "mb-2" >
                <
                button onClick = {
                    () => setCurrentPage(item.page) }
                className = { `w-full text-left flex items-center p-3 rounded-lg transition-colors ${
                                    currentPage === item.page
                                        ? 'bg-blue-500 text-white shadow-md'
                                        : 'text-gray-700 hover:bg-gray-100'
                                }` } >
                <
                span className = "mr-3 text-xl" > { item.icon } < /span> <
                span className = "font-medium" > { item.name } < /span> <
                /button> <
                /li>
            ))
        } <
        /ul> <
        /nav> <
        div className = "mt-auto pt-4 border-t border-gray-200" >
        <
        div className = "text-sm text-gray-500" >
        <
        p > Logged in as: { currentUser.fullName } < /p> <
        p > Role: { currentUser.role } < /p> <
        /div> <
        /div> <
        /aside>
    );
};

export default Sidebar;
