import React, { useState } from 'react';

const Form = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form submitted:', formData);
        alert('Form submitted successfully! Check the console for data.');
        setFormData({ name: '', email: '', message: '' });
    };

    return ( <
        form onSubmit = { handleSubmit }
        className = "bg-white rounded-xl shadow-lg p-6 space-y-4" >
        <
        div >
        <
        label htmlFor = "name"
        className = "block text-sm font-medium text-gray-700 mb-1" > Name < /label> <
        input type = "text"
        id = "name"
        name = "name"
        value = { formData.name }
        onChange = { handleChange }
        required className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200" /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "email"
        className = "block text-sm font-medium text-gray-700 mb-1" > Email < /label> <
        input type = "email"
        id = "email"
        name = "email"
        value = { formData.email }
        onChange = { handleChange }
        required className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200" /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "message"
        className = "block text-sm font-medium text-gray-700 mb-1" > Message < /label> <
        textarea id = "message"
        name = "message"
        value = { formData.message }
        onChange = { handleChange }
        required rows = "4"
        className = "w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200" >
        < /textarea> <
        /div> <
        button type = "submit"
        className = "bg-indigo-600 text-white font-bold py-3 px-6 rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-300 w-full" >
        Send Message <
        /button> <
        /form>
    );
};

export default Form;
