import React, { useEffect, useState } from 'react';
import { api } from '../api';

export default function FederalDashboard() {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const reload = async() => {
        setLoading(true);
        try {
            const data = await api('/api/requests');
            setRequests(data);
        } catch (e) { console.error(e); }
        setLoading(false);
    };
    useEffect(() => { reload(); }, []);

    const setStatus = async(id, action) => {
        await api(`/api/requests/${id}/status`, { method: 'PUT', body: { action } });
        reload();
    };
    const del = async(id) => {
        await api(`/api/requests/${id}`, { method: 'DELETE' });
        reload();
    };

    if (loading) return <div > Loading... < /div>;
    return ( <
        div >
        <
        h2 > Federal Dashboard < /h2> <
        table >
        <
        thead >
        <
        tr >
        <
        th > # < /th><th>Farmer</th > < th > Phone < /th><th>Product</th > < th > Qty < /th> <
        th > Region / Zone / Woreda / Kebele < /th><th>Status</th > < th > Action < /th> <
        /tr> <
        /thead> <
        tbody > {
            requests.map(r => ( <
                tr key = { r.id } >
                <
                td > { r.id } < /td> <
                td > { r.farmer_name } < /td> <
                td > { r.farmer_phone } < /td> <
                td > { r.product_name } < /td> <
                td > { r.quantity } < /td> <
                td > {
                    [r.region_name, r.zone_name, r.woreda_name, r.kebele_name].filter(Boolean).join(' / ') } < /td> <
                td > { r.status } < /td> <
                td >
                <
                label > < input type = "radio"
                name = { `act-${r.id}` }
                onChange = {
                    () => setStatus(r.id, 'Accept') }
                /> Accept</label >
                <
                label > < input type = "radio"
                name = { `act-${r.id}` }
                onChange = {
                    () => setStatus(r.id, 'Approve') }
                /> Approve</label >
                <
                label > < input type = "radio"
                name = { `act-${r.id}` }
                onChange = {
                    () => setStatus(r.id, 'Reject') }
                /> Reject</label > {
                    r.status === 'Rejected' && ( <
                        button onClick = {
                            () => del(r.id) } > Delete < /button>
                    )
                } <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div>
    );
}
