import React, { useState, useEffect } from 'react';


import FarmerDashboard from './dashboards/FarmerDashboard';

function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [requests, setRequests] = useState([]);
    const [products, setProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState('dashboard');

    useEffect(() => {
        if (currentUser) {
            fetchRequests();
            fetchProducts();
        }
    }, [currentUser]);

    const handleLogin = async(credentials) => {
        try {
            const response = await api.login(credentials);
            setCurrentUser(response.data.user);
            setCurrentPage('dashboard');
        } catch (err) {
            alert('Login failed: ' + (err.response && err.response.data ? err.response.data.message : err.message));
        }
    };

    const fetchRequests = async() => {
        const response = await api.getRequests();
        setRequests(response.data);
    };

    const fetchProducts = async() => {
        const response = await api.getProducts();
        setProducts(response.data);
    };

    const handleProductRequest = async(productId) => {
        try {
            await api.createRequest({ productId, userId: currentUser.id });
            fetchRequests();
        } catch (err) {
            alert('Request failed: ' + err.message);
        }
    };

    const filteredRequests = requests.filter(req => req.userId === (currentUser ? currentUser.id : null));

    return ( <
        div > {!currentUser ? ( <
                div >
                <
                h2 > Login < /h2> { / * Simple login form example * / } <
                button onClick = {
                    () => handleLogin({ username: 'farmer', password: '1234' })
                } > Login as Farmer < /button> < /
                div >
            ) : currentUser.role === 'Farmer' ? ( <
                FarmerDashboard currentUser = { currentUser }
                products = { products }
                requests = { filteredRequests }
                onRequest = { handleProductRequest }
                />
            ) : ( <
                div > Other dashboards not implemented yet. < /div>
            )
        } <
        /div>
    );
}

export default App;
