{ /* Reports Tab */}
{activeTab === 'reports' && (
    <div>
        <h2 className="text-2xl font-bold mb-2 text-gray-900 dark:text-white">Reports from Higher Admins</h2>
        <p className="text-gray-600 dark:text-gray-300 mb-6">View reports filed against you by higher-level administrators</p>

        {reports.length === 0 ? (
            <div className={`p-6 rounded shadow text-center ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
                <p className="text-gray-600 dark:text-gray-300">No reports found</p>
            </div>
        ) : (
            <div className="space-y-4">
                {reports.map(report => (
                    <div key={report.id} className={`p-6 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
                        <div className="flex justify-between items-start mb-4">
                            <div>
                                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">{report.title}</h3>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                    Reported by: {report.reporter_name} ({report.reporter_role})
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                    Date: {new Date(report.created_at).toLocaleDateString()}
                                </p>
                            </div>
                            <span className={`px-3 py-1 rounded text-sm font-medium ${
                                report.status === 'Pending' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200' :
                                report.status === 'Resolved' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' :
                                'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
                            }`}>
                                {report.status}
                            </span>
                        </div>

                        <div className="mb-4">
                            <p className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Type:</p>
                            <p className="text-gray-900 dark:text-white">{report.report_type}</p>
                        </div>

                        <div className="mb-4">
                            <p className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Priority:</p>
                            <span className={`px-2 py-1 rounded text-xs font-medium ${
                                report.priority === 'High' ? 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200' :
                                report.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200' :
                                'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200'
                            }`}>
                                {report.priority} Priority
                            </span>
                        </div>

                        <div className="mb-4">
                            <p className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Description:</p>
                            <p className="text-gray-900 dark:text-white whitespace-pre-wrap">{report.description}</p>
                        </div>

                        {report.evidence && (
                            <div className="mb-4">
                                <p className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Evidence:</p>
                                <p className="text-gray-900 dark:text-white whitespace-pre-wrap">{report.evidence}</p>
                            </div>
                        )}

                        {report.resolution_notes && (
                            <div className="mt-4 p-4 bg-gray-100 dark:bg-gray-700 rounded">
                                <p className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Resolution Notes:</p>
                                <p className="text-gray-900 dark:text-white">{report.resolution_notes}</p>
                                {report.resolved_at && (
                                    <p className="text-sm text-gray-600 dark:text-gray-400 mt-2">
                                        Resolved on: {new Date(report.resolved_at).toLocaleDateString()}
                                    </p>
                                )}
                            </div>
                        )}
                    </div>
                ))}
            </div>
        )}
    </div>
)}

