        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.land_type } < /p> < /
        div >

        <
        div className = "md:col-span-2" >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Irrigation Type <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.irrigation_type } < /p> < /
        div >

        <
        div className = "md:col-span-2" >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Farming Method <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.farming_method } < /p> < /
        div > <
        /div>

        <
        div className = "flex justify-end gap-3 mt-6" >
        <
        button onClick = {
            () => setShowFarmerModal(false)
        }
        className = { `px-4 py-2 rounded-lg ${
                                    darkMode 
                                        ? 'bg-gray-600 text-white hover:bg-gray-700' 
                                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                                }` } >
        Close <
        /button> < /
        div > <
        /div> < /
        div >
    )
} <
/div>
);
};

export default RegionDashboard;
