import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const ZoneDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // Woreda Admin Management State
    const [woredaAdmins, setWoredaAdmins] = useState([]);
    const [showWoredaForm, setShowWoredaForm] = useState(false);
    const [editingWoreda, setEditingWoreda] = useState(null);
    const [woredaForm, setWoredaForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        woreda_name: ''
    });
    const [woredaError, setWoredaError] = useState('');

    // Farmer Management State
    const [farmers, setFarmers] = useState([]);
    const [showFarmerForm, setShowFarmerForm] = useState(false);
    const [editingFarmer, setEditingFarmer] = useState(null);
    const [farmerForm, setFarmerForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        woreda_name: '',
        kebele_name: ''
    });
    const [farmerError, setFarmerError] = useState('');

    // Product Management State
    const [products, setProducts] = useState([]);
    const [myProducts, setMyProducts] = useState([]);
    const [otherProducts, setOtherProducts] = useState([]);
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        description: '',
        price: ''
    });

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [requestStatus, setRequestStatus] = useState('');
    const [decisionReason, setDecisionReason] = useState('');
    const [statusFilter, setStatusFilter] = useState('all');
    const [woredaFilter, setWoredaFilter] = useState('all');

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        acceptedRequests: 0,
        totalWoredaAdmins: 0,
        totalFarmers: 0,
        totalProducts: 0
    });

    // Woredas in this Zone
    const [woredas, setWoredas] = useState([]);

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Zone' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    useEffect(() => {
        if (requests.length > 0) {
            filterRequests();
        }
    }, [statusFilter, woredaFilter, requests]);

    const filterRequests = () => {
        let filtered = [...requests];

        // Filter by status
        if (statusFilter !== 'all') {
            filtered = filtered.filter(req => req.status === statusFilter);
        }

        // Filter by woreda
        if (woredaFilter !== 'all') {
            filtered = filtered.filter(req => req.woreda_name === woredaFilter);
        }

        setFilteredRequests(filtered);
    };

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch woreda admins
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const data = await adminsResponse.json();
                const woredaAdminsData = data.filter(item => item.type === 'admin' && item.role === 'Woreda');
                setWoredaAdmins(woredaAdminsData);
                setStats(prev => ({...prev, totalWoredaAdmins: woredaAdminsData.length }));

                // Extract unique woredas for filtering
                const uniqueWoredas = [...new Set(woredaAdminsData.map(a => a.woreda_name))];
                setWoredas(uniqueWoredas);
            }

            // Fetch farmers
            const farmersResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (farmersResponse.ok) {
                const data = await farmersResponse.json();
                const farmersData = data.filter(item => item.type === 'farmer');
                setFarmers(farmersData);
                setStats(prev => ({...prev, totalFarmers: farmersData.length }));
            }

            // Fetch all products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch my products
            const myProductsResponse = await fetch('http://localhost:5000/api/admins/myproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (myProductsResponse.ok) {
                const myProductsData = await myProductsResponse.json();
                setMyProducts(myProductsData);
            }

            // Fetch other admins' products
            const otherProductsResponse = await fetch('http://localhost:5000/api/admins/otherproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (otherProductsResponse.ok) {
                const otherProductsData = await otherProductsResponse.json();
                setOtherProducts(otherProductsData);
            }

            // Fetch requests with detailed status
            const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
                filterRequests();

                // Calculate stats from requests data
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'Approved').length;
                const acceptedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    acceptedRequests,
                    rejectedRequests
                }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Woreda Admin Management Functions
    const handleWoredaSubmit = async(e) => {
        e.preventDefault();
        setWoredaError('');

        if (woredaForm.password !== woredaForm.confirmPassword && !editingWoreda) {
            setWoredaError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingWoreda) {
                url = `http://localhost:5000/api/admins/edit/${editingWoreda.id}`;
                method = 'PUT';
            } else {
                url = 'http://localhost:5000/api/admins/register';
                method = 'POST';
            }

            const submitData = {
                fullName: woredaForm.fullName,
                phoneNumber: woredaForm.phoneNumber,
                password: woredaForm.password,
                role: 'Woreda',
                region_name: user.region_name,
                zone_name: user.zone_name,
                woreda_name: woredaForm.woreda_name
            };

            if (editingWoreda && !submitData.password) {
                delete submitData.password;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowWoredaForm(false);
                setEditingWoreda(null);
                setWoredaForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    woreda_name: ''
                });
                fetchDashboardData();
                alert(editingWoreda ? 'Woreda admin updated successfully' : 'Woreda admin registered successfully');
            } else {
                setWoredaError(data.message || 'Error processing woreda admin');
            }
        } catch (error) {
            console.error('Error processing woreda admin:', error);
            setWoredaError('Server error occurred');
        }
    };

    const deleteWoredaAdmin = async(adminId) => {
        if (!window.confirm('Are you sure you want to delete this woreda admin?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
                alert('Woreda admin deleted successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error deleting woreda admin');
            }
        } catch (error) {
            console.error('Error deleting woreda admin:', error);
            alert('Error deleting woreda admin');
        }
    };

    const editWoredaAdmin = (admin) => {
        setEditingWoreda(admin);
        setWoredaForm({
            fullName: admin.full_name || '',
            phoneNumber: admin.phone_number || '',
            password: '',
            confirmPassword: '',
            woreda_name: admin.woreda_name || ''
        });
        setShowWoredaForm(true);
    };

    // Farmer Management Functions
    const handleFarmerSubmit = async(e) => {
        e.preventDefault();
        setFarmerError('');

        if (farmerForm.password !== farmerForm.confirmPassword && !editingFarmer) {
            setFarmerError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const url = 'http://localhost:5000/api/admins/createfarmer';
            const method = 'POST';

            const submitData = {
                fullName: farmerForm.fullName,
                phoneNumber: farmerForm.phoneNumber,
                password: farmerForm.password,
                role: 'Farmer',
                region_name: user.region_name,
                zone_name: user.zone_name,
                woreda_name: farmerForm.woreda_name,
                kebele_name: farmerForm.kebele_name
            };

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowFarmerForm(false);
                setEditingFarmer(null);
                setFarmerForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    woreda_name: '',
                    kebele_name: ''
                });
                fetchDashboardData();
                alert('Farmer registered successfully');
            } else {
                setFarmerError(data.message || 'Error processing farmer');
            }
        } catch (error) {
            console.error('Error processing farmer:', error);
            setFarmerError('Server error occurred');
        }
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            const url = editingProduct ?
                `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                'http://localhost:5000/api/admins/addproduct';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', amount: '', description: '', price: '' });
                fetchDashboardData();
                alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error processing product');
            }
        } catch (error) {
            console.error('Error processing product:', error);
            alert('Error processing product');
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
                alert('Product deleted successfully');
            } else {
                const errorData = await response.json();
                alert(errorData.message || 'Error deleting product');
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        }
    };

    const editProduct = (product) => {
