                    h3 className = "text-lg font-semibold" > { request.product_name } < /h3> <
                    p className = "text-gray-600" > 👨‍🌾Farmer: { request.farmer_name } < /p> <
                    p className = "text-gray-600" > 📍Location: { request.region_name }
                    / {request.zone_name} / { request.woreda_name }
                    / {request.kebele_name}</p >
                    <
                    p className = "text-gray-600" > 📅Requested: { new Date(request.created_at).toLocaleDateString() } < /p> <
                    p className = "text-gray-600" > 💰Amount: { request.amount } < /p> <
                    p className = "text-gray-600" > 📝Reason: { request.reason } < /p> < /
                    div > <
                    div className = "flex flex-col items-end" >
                    <
                    div className = "mb-2" > { getStatusBadge(request.status) } <
                    /div> <
                    div className = "flex space-x-2" >
                    <
                    button onClick = {
                        () => viewRequestDetails(request.id)
                    }
                    className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm" > 👁️View Details <
                    /button> {
                    canZoneAdminAct(request) && ( <
                        >
                        <
                        button onClick = {
                            () => handleStatusUpdate(request, 'Approved')
                        }
                        className = "bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded text-sm" > ✅Approve <
                        /button> <
                        button onClick = {
                            () => handleStatusUpdate(request, 'Rejected')
                        }
                        className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm" > ❌Reject <
                        /button> < /
                        >
                    )
                } {
                    canDeleteRequest(request) && ( <
                        button onClick = {
                            () => handleDeleteRequest(request.id)
                        }
                        className = "bg-gray-500 hover:bg-gray-600 text-white px-3 py-1 rounded text-sm" > 🗑️Delete <
                        /button>
                    )
                } <
                /div> < /
                div > <
                /div> < /
                div >
            ))
} {
    filteredRequests.length === 0 && ( <
        p className = "text-gray-600" > No requests found with the selected filters. < /p>
    )
} <
/div> < /
div >
)
} <
/main> < /
div >

    { /* Woreda Admin Form Modal */ } {
        showWoredaForm && ( <
            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
            <
            div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
            <
            h2 className = "text-xl font-bold mb-4" > { editingWoreda ? 'Edit Woreda Admin' : 'Register Woreda Admin' } <
            /h2> <
            form onSubmit = { handleWoredaSubmit } >
            <
            div className = "mb-4" >
            <
            label className = "block text-gray-700" > Full Name < /label> <
            input type = "text"
            value = { woredaForm.fullName }
            onChange = {
                (e) => setWoredaForm({...woredaForm, fullName: e.target.value })
            }
            className = "w-full border rounded p-2"
            required /
            >
            <
            /div> <
            div className = "mb-4" >
            <
            label className = "block text-gray-700" > Phone Number < /label> <
            input type = "tel"
            value = { woredaForm.phoneNumber }
            onChange = {
                (e) => setWoredaForm({...woredaForm, phoneNumber: e.target.value })
            }
            className = "w-full border rounded p-2"
            required /
            >
            <
            /div> <
            div className = "mb-4" >
            <
            label className = "block text-gray-700" > Woreda Name < /label> <
            input type = "text"
            value = { woredaForm.woreda_name }
            onChange = {
                (e) => setWoredaForm({...woredaForm, woreda_name: e.target.value })
            }
            className = "w-full border rounded p-2"
            required /
            >
            <
            /div> {!editingWoreda && ( <
                >
                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Password < /label> <
                input type = "password"
                value = { woredaForm.password }
                onChange = {
                    (e) => setWoredaForm({...woredaForm, password: e.target.value })
                }
                className = "w-full border rounded p-2"
                required = {!editingWoreda }
                /> < /
                div > <
                div className = "mb-4" >
                <
                label className = "block text-gray-700" > Confirm Password < /label> <
                input type = "password"
                value = { woredaForm.confirmPassword }
                onChange = {
                    (e) => setWoredaForm({...woredaForm, confirmPassword: e.target.value })
                }
                className = "w-full border rounded p-2"
                required = {!editingWoreda }
                /> < /
                div > <
                />
            )
        } {
            woredaError && < p className = "text-red-500 mb-4" > { woredaError } < /p>} <
            div className = "flex justify-end space-x-2" >
                <
                button
            type = "button"
            onClick = {
                () => {
                    setShowWoredaForm(false);
                    setEditingWoreda(null);
                }
            }
            className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                Cancel <
                /button> <
            button
            type = "submit"
            className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" > { editingWoreda ? 'Update' : 'Register' } <
                /button> < /
                div > <
                /form> < /
                div > <
                /div>
        )
    }

{ /* Product Form Modal */ } {
    showProductForm && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
        <
        h2 className = "text-xl font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add Product' } <
        /h2> <
        form onSubmit = { handleProductSubmit } >
        <
        div className = "mb-4" >
        <
        label className = "block text-gray-700" > Product Name < /label> <
        input type = "text"
        value = { productForm.name }
        onChange = {
            (e) => setProductForm({...productForm, name: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700" > Category < /label> <
        input type = "text"
        value = { productForm.category }
        onChange = {
            (e) => setProductForm({...productForm, category: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700" > Amount < /label> <
        input type = "number"
        value = { productForm.amount }
        onChange = {
            (e) => setProductForm({...productForm, amount: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700" > Price < /label> <
        input type = "number"
        step = "0.01"
        value = { productForm.price }
        onChange = {
            (e) => setProductForm({...productForm, price: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700" > Description < /label> <
        textarea value = { productForm.description }
        onChange = {
            (e) => setProductForm({...productForm, description: e.target.value })
        }
        className = "w-full border rounded p-2"
        rows = "3" >
        <
        /textarea> < /
        div > <
        div className = "flex justify-end space-x-2" >
        <
        button type = "button"
        onClick = {
            () => {
                setShowProductForm(false);
                setEditingProduct(null);
            }
        }
        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
        Cancel <
        /button> <
        button type = "submit"
        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" > { editingProduct ? 'Update' : 'Add' } <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    )
}

{ /* Request Status Modal */ } {
    showRequestModal && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white p-6 rounded shadow-lg w-full max-w-2xl" >
        <
        h2 className = "text-xl font-bold mb-4" > Request Details < /h2>

        {
            selectedRequest && ( <
                div className = "mb-6" >
                <
                div className = "grid grid-cols-2 gap-4 mb-4" >
                <
                div >
                <
                p > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                <
                p > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                <
                p > < strong > Amount: < /strong> {selectedRequest.amount}</p >
                <
                /div> <
                div >
                <
                p > < strong > Location: < /strong> {selectedRequest.region_name} / { selectedRequest.zone_name }
                / {selectedRequest.woreda_name} / { selectedRequest.kebele_name } < /p> <
                p > < strong > Request Date: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p >
                <
                p > < strong > Status: < /strong> {getStatusBadge(selectedRequest.status)}</p >
                <
                /div> < /
                div >

                <
                div className = "mb-4" >
                <
                p > < strong > Reason
                for Request: < /strong></p >
                <
                p className = "bg-gray-50 p-3 rounded" > { selectedRequest.reason } < /p> < /
                div >

                { /* Show status at each level */ } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) }

                { /* Decision form for Zone admin if they can act on this request */ } {
                    canZoneAdminAct(selectedRequest) && requestStatus && ( <
                        div className = "mt-4 p-4 bg-blue-50 rounded" >
                        <
                        h3 className = "font-semibold mb-2" > Update Request Status < /h3> <
                        p className = "mb-2" > You are about to < strong > { requestStatus } < /strong> this request.</p >
                        <
                        div className = "mb-3" >
                        <
                        label className = "block text-gray-700 mb-1" > Reason
                        for decision(optional): < /label> <
                        textarea value = { decisionReason }
                        onChange = {
                            (e) => setDecisionReason(e.target.value)
                        }
                        className = "w-full border rounded p-2"
                        rows = "3"
                        placeholder = "Enter reason for your decision" >
                        <
                        /textarea> < /
                        div > <
                        div className = "flex justify-end space-x-2" >
                        <
                        button onClick = {
                            () => {
                                setShowRequestModal(false);
                                setRequestStatus('');
                            }
                        }
                        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                        Cancel <
                        /button> <
                        button onClick = { confirmStatusUpdate }
                        className = { `${requestStatus === 'Approved' ? 'bg-green-500 hover:bg-green-600' : 'bg-red-500 hover:bg-red-600'} text-white px-4 py-2 rounded` } >
                        Confirm { requestStatus } <
                        /button> < /
                        div > <
                        /div>
                    )
                } <
                /div>
            )
        }

        {
            !requestStatus && ( <
                div className = "flex justify-end" >
                <
                button onClick = {
                    () => {
                        setShowRequestModal(false);
                        setSelectedRequest(null);
                    }
                }
                className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                Close <
                /button> < /
                div >
            )
        } <
        /div> < /
        div >
    )
} <
/div>
);
};

export default ZoneDashboard;
