// components/Sidebar.js
import React from 'react';

const Sidebar = ({ activeTab, setActiveTab }) => {
    const user = JSON.parse(localStorage.getItem('user') || 'null');
    const isFederal = user && user.role === 'Federal';

    return ( <
        div className = "w-64 bg-gray-800 text-white min-h-screen p-6" >
        <
        h2 className = "text-xl font-bold mb-8 text-center" > Dashboard < /h2> <
        nav className = "space-y-2" >
        <
        button className = { `w-full text-left px-4 py-3 rounded-lg transition-colors ${
                        activeTab === 'requests' 
                            ? 'bg-blue-600 text-white' 
                            : 'text-gray-300 hover:bg-gray-700'
                    }` }
        onClick = {
            () => setActiveTab('requests') } >
        üìãRequests <
        /button> <
        button className = { `w-full text-left px-4 py-3 rounded-lg transition-colors ${
                        activeTab === 'products' 
                            ? 'bg-blue-600 text-white' 
                            : 'text-gray-300 hover:bg-gray-700'
                    }` }
        onClick = {
            () => setActiveTab('products') } >
        üõçÔ∏èProducts <
        /button> {
            isFederal && ( <
                button className = { `w-full text-left px-4 py-3 rounded-lg transition-colors ${
                            activeTab === 'admins' 
                                ? 'bg-blue-600 text-white' 
                                : 'text-gray-300 hover:bg-gray-700'
                        }` }
                onClick = {
                    () => setActiveTab('admins') } >
                üë•Admin Management <
                /button>
            )
        } <
        /nav> <
        /div>
    );
};

export default Sidebar;
