    } > Requests <
    /button> <
    button className = { `w-full text-left px-4 py-2 ${activeTab === 'farmers' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
    onClick = {
        () => setActiveTab('farmers')
    } > Farmers <
    /button> <
    button className = { `w-full text-left px-4 py-2 ${activeTab === 'reports' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
    onClick = {
        () => setActiveTab('reports')
    } > Reports <
    /button> < /
    nav > <
    /div>

    { /* Main Content */ } <
    div className = "flex-1 overflow-auto" >
    <
    header className = "bg-white dark:bg-gray-800 shadow p-4 flex justify-between items-center" >
    <
    div >
    <
    h1 className = "text-xl font-bold dark:text-white" > Welcome, { user.fullName }(Zone Admin) < /h1> <
    p className = "text-gray-600 dark:text-gray-300" > Region: { user.region_name } | Zone: { user.zone_name } < /p> < /
    div > <
    button onClick = { handleLogout }
    className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded dark:bg-red-600 dark:hover:bg-red-700" >
    Logout <
    /button> < /
    header >

    <
    main className = "p-4" > { activeTab === 'overview' && renderOverview() } { activeTab === 'woredas' && renderWoredaAdmins() } { activeTab === 'products' && renderProducts() } { activeTab === 'requests' && renderRequests() } { activeTab === 'farmers' && renderFarmers() } { activeTab === 'reports' && renderReports() } <
    /main> < /
    div > <
    /div>
);
};

export default ZoneDashboard;
