import React, { useState, useEffect } from 'react';
import './ZoneDashboard.css';

// Mock API functions - replace these with your actual API calls
const mockGetZones = () => {
    return Promise.resolve({
        data: [
            { id: 'zone1', name: 'North Zone' },
            { id: 'zone2', name: 'South Zone' },
            { id: 'zone3', name: 'East Zone' },
            { id: 'zone4', name: 'West Zone' }
        ]
    });
};

const mockGetZoneStats = (zoneId) => {
    const stats = {
        zone1: { totalSales: 125000, activeCustomers: 2450, revenue: 98750, totalOrders: 3120 },
        zone2: { totalSales: 89000, activeCustomers: 1870, revenue: 71200, totalOrders: 2240 },
        zone3: { totalSales: 156000, activeCustomers: 2980, revenue: 124800, totalOrders: 3890 },
        zone4: { totalSales: 72000, activeCustomers: 1540, revenue: 57600, totalOrders: 1800 }
    };
    return Promise.resolve({ data: stats[zoneId] || stats.zone1 });
};

const mockGetZonePerformanceTrend = (zoneId) => {
    const trends = {
        zone1: [
            { period: 'Jan', sales: 10000, revenue: 8000, customers: 200 },
            { period: 'Feb', sales: 12000, revenue: 9600, customers: 220 },
            { period: 'Mar', sales: 15000, revenue: 12000, customers: 250 }
        ],
        zone2: [
            { period: 'Jan', sales: 8000, revenue: 6400, customers: 150 },
            { period: 'Feb', sales: 9000, revenue: 7200, customers: 170 },
            { period: 'Mar', sales: 10000, revenue: 8000, customers: 190 }
        ],
        zone3: [
            { period: 'Jan', sales: 12000, revenue: 9600, customers: 250 },
            { period: 'Feb', sales: 14000, revenue: 11200, customers: 280 },
            { period: 'Mar', sales: 16000, revenue: 12800, customers: 300 }
        ],
        zone4: [
            { period: 'Jan', sales: 6000, revenue: 4800, customers: 120 },
            { period: 'Feb', sales: 7000, revenue: 5600, customers: 130 },
            { period: 'Mar', sales: 8000, revenue: 6400, customers: 140 }
        ]
    };
    return Promise.resolve({ data: trends[zoneId] || trends.zone1 });
};

const ZoneDashboard = () => {
    const [zones, setZones] = useState([]);
    const [selectedZone, setSelectedZone] = useState('');
    const [zoneStats, setZoneStats] = useState(null);
    const [performanceData, setPerformanceData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchZones();
    }, []);

    useEffect(() => {
        if (selectedZone) {
            fetchZoneData(selectedZone);
        }
    }, [selectedZone]);

    const fetchZones = async() => {
        try {
            setLoading(true);
            const response = await mockGetZones();
            setZones(response.data);
            if (response.data.length > 0) {
                setSelectedZone(response.data[0].id);
            }
        } catch (err) {
            setError('Failed to fetch zones');
            console.error('Error fetching zones:', err);
        } finally {
            setLoading(false);
        }
    };

    const fetchZoneData = async(zoneId) => {
        try {
            setLoading(true);
            setError('');

            const [statsResponse, trendResponse] = await Promise.all([
                mockGetZoneStats(zoneId),
                mockGetZonePerformanceTrend(zoneId)
            ]);

            setZoneStats(statsResponse.data);
            setPerformanceData(trendResponse.data);
        } catch (err) {
            setError('Failed to fetch zone data');
            console.error('Error fetching zone data:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleZoneChange = (event) => {
        setSelectedZone(event.target.value);
    };

    const renderBarChart = () => {
        if (!performanceData.length) return null;

        const maxValue = Math.max(
            ...performanceData.map(item => Math.max(item.sales, item.revenue, item.customers))
        );

        return ( <
            div className = "chart-container" >
            <
            h3 > Performance Trend < /h3> <
            div style = {
                { height: '400px', overflowX: 'auto' } } >
            <
            div style = {
                { display: 'flex', alignItems: 'end', height: '350px', gap: '20px', padding: '20px' } } > {
                performanceData.map((item, index) => ( <
                    div key = { index }
                    style = {
                        { display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '10px' } } >
                    <
                    div style = {
                        { display: 'flex', alignItems: 'end', gap: '5px', height: '300px' } } >
                    <
                    div style = {
                        {
                            width: '30px',
                            height: `${(item.sales / maxValue) * 250}px`,
                            background: '#0088FE',
                            borderRadius: '3px 3px 0 0'
                        }
                    }
                    title = { `Sales: $${item.sales}` }
                    /> <
                    div style = {
                        {
                            width: '30px',
                            height: `${(item.revenue / maxValue) * 250}px`,
                            background: '#00C49F',
                            borderRadius: '3px 3px 0 0'
                        }
                    }
                    title = { `Revenue: $${item.revenue}` }
                    /> <
                    div style = {
                        {
                            width: '30px',
                            height: `${(item.customers / maxValue) * 250}px`,
                            background: '#FFBB28',
                            borderRadius: '3px 3px 0 0'
                        }
                    }
                    title = { `Customers: ${item.customers}` }
                    /> <
                    /div> <
                    span style = {
                        { fontSize: '12px' } } > { item.period } < /span> <
                    /div>
                ))
            } <
            /div> <
            /div> <
            div style = {
                { display: 'flex', gap: '20px', justifyContent: 'center', marginTop: '10px' } } >
            <
            div style = {
                { display: 'flex', alignItems: 'center', gap: '5px' } } >
            <
            div style = {
                { width: '15px', height: '15px', background: '#0088FE' } } > < /div> <
            span style = {
                { fontSize: '12px' } } > Sales < /span> <
            /div> <
            div style = {
                { display: 'flex', alignItems: 'center', gap: '5px' } } >
            <
            div style = {
                { width: '15px', height: '15px', background: '#00C49F' } } > < /div> <
            span style = {
                { fontSize: '12px' } } > Revenue < /span> <
            /div> <
            div style = {
                { display: 'flex', alignItems: 'center', gap: '5px' } } >
            <
            div style = {
                { width: '15px', height: '15px', background: '#FFBB28' } } > < /div> <
            span style = {
                { fontSize: '12px' } } > Customers < /span> <
            /div> <
            /div> <
            /div>
        );
    };

    const renderPieChart = () => {
        if (!zoneStats) return null;

        const data = [
            { name: 'Total Sales', value: zoneStats.totalSales || 0 },
            { name: 'Active Customers', value: zoneStats.activeCustomers || 0 },
            { name: 'Revenue', value: zoneStats.revenue || 0 },
            { name: 'Orders', value: zoneStats.totalOrders || 0 }
        ];

        const total = data.reduce((sum, item) => sum + item.value, 0);
        const colors = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

        return ( <
            div className = "chart-container" >
            <
            h3 > Performance Distribution < /h3> <
            div style = {
                { display: 'flex', justifyContent: 'center', alignItems: 'center', height: '300px' } } >
            <
            div style = {
                { position: 'relative', width: '200px', height: '200px' } } > {
                data.map((item, index) => {
                    const percentage = total > 0 ? (item.value / total) * 100 : 0;
                    const angle = (index / data.length) * 360;
                    return ( <
                        div key = { index }
                        style = {
                            {
                                position: 'absolute',
                                width: '200px',
                                height: '200px',
                                clipPath: `conic-gradient(from ${angle}deg to ${angle + 90}deg)`,
                                background: colors[index % colors.length],
                                borderRadius: '50%',
                                opacity: 0.7
                            }
                        }
                        />
                    );
                })
            } <
            /div> <
            /div> <
            div style = {
                { display: 'flex', flexWrap: 'wrap', gap: '10px', justifyContent: 'center', marginTop: '20px' } } > {
                data.map((item, index) => ( <
                    div key = { index }
                    style = {
                        { display: 'flex', alignItems: 'center', gap: '5px' } } >
                    <
                    div style = {
                        { width: '15px', height: '15px', background: colors[index % colors.length] } } > < /div> <
                    span style = {
                        { fontSize: '12px' } } > { item.name }: {
                        ((item.value / total) * 100).toFixed(1) } %
                    <
                    /span> <
                    /div>
                ))
            } <
            /div> <
            /div>
        );
    };

    if (loading && !zoneStats) {
        return ( <
            div className = "loading-container" >
            <
            div > Loading... < /div> <
            /div>
        );
    }

    return ( <
        div className = "zone-dashboard" >
        <
        h1 > Zone Performance Dashboard < /h1>

        {
            error && ( <
                div className = "error-alert" > { error } <
                /div>
            )
        }

        <
        div className = "zone-select" >
        <
        label htmlFor = "zone-select" > Select Zone: < /label> <
        select id = "zone-select"
        value = { selectedZone }
        onChange = { handleZoneChange }
        disabled = { loading } >
        {
            zones.map((zone) => ( <
                option key = { zone.id }
                value = { zone.id } > { zone.name } <
                /option>
            ))
        } <
        /select> <
        /div>

        {
            zoneStats && ( <
                div style = {
                    { display: 'grid', gap: '20px' } } >
                <
                div className = "chart-container" >
                <
                h3 > Performance Overview < /h3> <
                div style = {
                    { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px' } } >
                <
                div className = "metric-card" >
                <
                div className = "metric-title" > Total Sales < /div> <
                div className = "metric-value" > $ {
                    (zoneStats.totalSales || 0).toLocaleString() } < /div> <
                /div> <
                div className = "metric-card" >
                <
                div className = "metric-title" > Active Customers < /div> <
                div className = "metric-value" > {
                    (zoneStats.activeCustomers || 0).toLocaleString() } < /div> <
                /div> <
                div className = "metric-card" >
                <
                div className = "metric-title" > Revenue < /div> <
                div className = "metric-value" > $ {
                    (zoneStats.revenue || 0).toLocaleString() } < /div> <
                /div> <
                div className = "metric-card" >
                <
                div className = "metric-title" > Total Orders < /div> <
                div className = "metric-value" > {
                    (zoneStats.totalOrders || 0).toLocaleString() } < /div> <
                /div> <
                /div> <
                /div>

                <
                div style = {
                    { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' } } > { renderPieChart() } { renderBarChart() } <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default ZoneDashboard;
