import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegionDashboard.css';

const RegionDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const [regionInfo, setRegionInfo] = useState(null);
    const navigate = useNavigate();

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [statusFilter, setStatusFilter] = useState('All');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [showRequestModal, setShowRequestModal] = useState(false);

    // Product Management State
    const [products, setProducts] = useState([]);
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        description: ''
    });

    // Farmer Management State
    const [farmers, setFarmers] = useState([]);
    const [showFarmerForm, setShowFarmerForm] = useState(false);
    const [editingFarmer, setEditingFarmer] = useState(null);
    const [farmerForm, setFarmerForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: ''
    });
    const [farmerError, setFarmerError] = useState('');

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        totalProducts: 0,
        totalFarmers: 0
    });

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Region' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        setRegionInfo({
            name: userData.region_name,
            code: userData.region_name.substring(0, 3).toUpperCase()
        });
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch requests for this region
            const requestsResponse = await fetch('http://localhost:5000/api/admins/getrequests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
                setFilteredRequests(requestsData);

                // Calculate stats
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    rejectedRequests
                }));
            }

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch farmers in this region
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const adminsData = await adminsResponse.json();
                // Filter to only show farmers in this region
                const regionFarmers = adminsData.filter(admin =>
                    admin.type === 'farmer' && admin.region_name === user.region_name
                );
                setFarmers(regionFarmers);
                setStats(prev => ({...prev, totalFarmers: regionFarmers.length }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Filter requests by status
    const filterRequests = (status) => {
        setStatusFilter(status);
        if (status === 'All') {
            setFilteredRequests(requests);
        } else {
            setFilteredRequests(requests.filter(req => req.status === status));
        }
    };

    // Handle request status update
    const updateRequestStatus = async(requestId, status) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/editrequest/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status })
            });

            if (response.ok) {
                fetchDashboardData(); // Refresh data
                setShowRequestModal(false);
                setSelectedRequest(null);
            } else {
                const errorData = await response.json();
                console.error('Error updating request:', errorData.message);
                alert(errorData.message || 'Error updating request');
            }
        } catch (error) {
            console.error('Error updating request:', error);
            alert('Error updating request');
        }
    };

    // View request details
    const viewRequestDetails = (request) => {
        setSelectedRequest(request);
        setShowRequestModal(true);
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                'http://localhost:5000/api/admins/addproduct';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', amount: '', description: '' });
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error saving product:', errorData.message);
                alert(errorData.message || 'Error saving product');
            }
        } catch (error) {
            console.error('Error saving product:', error);
            alert('Error saving product');
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error deleting product:', errorData.message);
                alert(errorData.message || 'Error deleting product');
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        }
    };

    // Farmer Management Functions
    const handleFarmerSubmit = async(e) => {
        e.preventDefault();
        setFarmerError('');

        if (farmerForm.password !== farmerForm.confirmPassword && !editingFarmer) {
            setFarmerError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingFarmer) {
                // Update existing farmer
                url = `http://localhost:5000/api/admins/edit/${editingFarmer.id}`;
                method = 'PUT';
            } else {
                // Create new farmer
                url = 'http://localhost:5000/api/admins/createfarmer';
                method = 'POST';
            }

            // Prepare data for submission
            const submitData = {
                ...farmerForm,
                role: 'Farmer',
                region_name: user.region_name,
                zone_name: user.zone_name || '',
                woreda_name: user.woreda_name || '',
                kebele_name: user.kebele_name || ''
            };

            // Don't send password if editing and password is empty
            if (editingFarmer && !submitData.password) {
                delete submitData.password;
                delete submitData.confirmPassword;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowFarmerForm(false);
                setEditingFarmer(null);
                setFarmerForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: ''
                });
                fetchDashboardData();
            } else {
                setFarmerError(data.message || 'Error processing farmer');
            }
        } catch (error) {
            console.error('Error processing farmer:', error);
            setFarmerError('Server error occurred');
        }
    };

    const deleteFarmer = async(farmerId) => {
        if (!window.confirm('Are you sure you want to delete this farmer?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/delete/${farmerId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error deleting farmer:', errorData.message);
                alert(errorData.message || 'Error deleting farmer');
            }
        } catch (error) {
            console.error('Error deleting farmer:', error);
            alert('Error deleting farmer');
        }
    };

    const editFarmer = (farmer) => {
        setEditingFarmer(farmer);
        setFarmerForm({
            fullName: farmer.full_name || '',
            phoneNumber: farmer.phone_number || '',
            password: '',
            confirmPassword: ''
        });
        setShowFarmerForm(true);
    };

    const editProduct = (product) => {
        setEditingProduct(product);
        setProductForm({
            name: product.name || '',
            category: product.category || '',
            amount: product.amount || '',
            description: product.description || ''
        });
        setShowProductForm(true);
    };

    if (!user || !regionInfo) {
        return <div className = "loading" > Loading... < /div>;
    }

    return ( <
        div className = "region-dashboard" > { /* Sidebar */ } <
        div className = "sidebar" >
        <
        h2 > Region Dashboard < /h2> <
        div className = "region-info" >
        <
        h3 > { regionInfo.name } < /h3> <
        p > Region Code: { regionInfo.code } < /p> < /
        div > <
        nav >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview')
        } > 📊Overview <
        /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests')
        } > 📋Requests <
        /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products')
        } > 📦Products <
        /button> <
        button className = { activeTab === 'farmers' ? 'active' : '' }
        onClick = {
            () => setActiveTab('farmers')
        } > 👨‍🌾Farmers <
        /button> < /
        nav > <
        /div>

        { /* Main Content */ } <
        div className = "main-content" >
        <
        header >
        <
        h1 > Welcome, { user.fullName }({ regionInfo.name }
            Admin) < /h1> <
        button onClick = { handleLogout }
        className = "logout-btn" >
        Logout <
        /button> < /
        header >

        <
        main > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 > Region Overview < /h2> <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > { stats.totalRequests } < /h3> <
                p > Total Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.pendingRequests } < /h3> <
                p > Pending Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.approvedRequests } < /h3> <
                p > Approved Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.rejectedRequests } < /h3> <
                p > Rejected Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalProducts } < /h3> <
                p > Available Products < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalFarmers } < /h3> <
                p > Registered Farmers < /p> < /
                div > <
                /div>

                <
                div className = "recent-activity" >
                <
                h2 > Recent Activity < /h2> <
                div className = "activity-list" > {
                    requests.slice(0, 5).map(request => ( <
                        div key = { request.id }
                        className = "activity-item" >
                        <
                        span className = "activity-type" > Request# { request.id } < /span> <
                        span className = "activity-detail" > { request.product_name } - { request.quantity }
                        units < /span> <
                        span className = { `activity-status ${request.status.toLowerCase()}` } > { request.status } <
                        /span> < /
                        div >
                    ))
                } <
                /div> < /
                div > <
                /div>
            )
        }

        { /* Requests Tab */ } {
            activeTab === 'requests' && ( <
                div className = "requests-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Request Management < /h2> <
                div className = "filter-controls" >
                <
                button className = { statusFilter === 'All' ? 'active' : '' }
                onClick = {
                    () => filterRequests('All')
                } >
                All({ requests.length }) <
                /button> <
                button className = { statusFilter === 'pending' ? 'active' : '' }
                onClick = {
                    () => filterRequests('pending')
                } >
                Pending({ stats.pendingRequests }) <
                /button> <
                button className = { statusFilter === 'accepted' ? 'active' : '' }
                onClick = {
                    () => filterRequests('accepted')
                } >
                Approved({ stats.approvedRequests }) <
                /button> <
                button className = { statusFilter === 'rejected' ? 'active' : '' }
                onClick = {
                    () => filterRequests('rejected')
                } >
                Rejected({ stats.rejectedRequests }) <
                /button> < /
                div > <
                /div>

                <
                div className = "requests-list" > {
                    filteredRequests.length === 0 ? ( <
                        p className = "no-requests" > No requests found < /p>
                    ) : (
                        filteredRequests.map(request => ( <
                            div key = { request.id }
                            className = "request-card" >
                            <
                            div className = "request-info" >
                            <
                            h3 > Request# { request.id } < /h3> <
                            p > < strong > Product: < /strong> {request.product_name}</p >
                            <
                            p > < strong > Quantity: < /strong> {request.quantity}</p >
                            <
                            p > < strong > Requested by: < /strong> {request.farmer_name}</p >
                            <
                            p > < strong > Date: < /strong> {new Date(request.created_at).toLocaleDateString()}</p >
                            <
                            /div> <
                            div className = "request-status" >
                            <
                            span className = { `status-badge ${request.status.toLowerCase()}` } > { request.status } <
                            /span> <
                            button onClick = {
                                () => viewRequestDetails(request)
                            }
                            className = "view-details-btn" >
                            View Details <
                            /button> < /
                            div > <
                            /div>
                        ))
                    )
                } <
                /div>

                { /* Request Detail Modal */ } {
                    showRequestModal && selectedRequest && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > Request Details# { selectedRequest.id } < /h3> <
                        div className = "request-details" >
                        <
                        p > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                        <
                        p > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                        <
                        p > < strong > Requested by: < /strong> {selectedRequest.farmer_name}</p >
                        <
                        p > < strong > Date: < /strong> {new Date(selectedRequest.created_at).toLocaleDateString()}</p >
                        <
                        p > < strong > Status: < /strong>  <
                        span className = { `status-badge ${selectedRequest.status.toLowerCase()}` } > { selectedRequest.status } <
                        /span> < /
                        p > <
                        /div>

                        {
                            selectedRequest.status === 'pending' && ( <
                                div className = "request-actions" >
                                <
                                button onClick = {
                                    () => updateRequestStatus(selectedRequest.id, 'accepted')
                                }
                                className = "approve-btn" >
                                Approve <
                                /button> <
                                button onClick = {
                                    () => updateRequestStatus(selectedRequest.id, 'rejected')
                                }
                                className = "reject-btn" >
                                Reject <
                                /button> < /
                                div >
                            )
                        }

                        <
                        div className = "modal-actions" >
                        <
                        button onClick = {
                            () => {
                                setShowRequestModal(false);
                                setSelectedRequest(null);
                            }
                        }
                        className = "close-btn" >
                        Close <
                        /button> < /
                        div > <
                        /div> < /
                        div >
                    )
                } <
                /div>
            )
        }

        { /* Products Tab */ } {
            activeTab === 'products' && ( <
                div className = "products-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Product Management < /h2> <
                button onClick = {
                    () => setShowProductForm(true)
                }
                className = "add-btn" >
                Add Product <
                /button> < /
                div >

                { /* Product Form Modal */ } {
                    showProductForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> <
                        form onSubmit = { handleProductSubmit } >
                        <
                        input type = "text"
                        placeholder = "Product Name"
                        value = { productForm.name }
                        onChange = {
                            (e) => setProductForm({...productForm, name: e.target.value })
                        }
                        required /
                        >
                        <
                        input type = "text"
                        placeholder = "Category"
                        value = { productForm.category }
                        onChange = {
                            (e) => setProductForm({...productForm, category: e.target.value })
                        }
                        required /
                        >
                        <
                        input type = "number"
                        placeholder = "Amount"
                        value = { productForm.amount }
                        onChange = {
                            (e) => setProductForm({...productForm, amount: e.target.value })
                        }
                        required /
                        >
                        <
                        textarea placeholder = "Description"
                        value = { productForm.description }
                        onChange = {
                            (e) => setProductForm({...productForm, description: e.target.value })
                        }
                        rows = "3"
                        required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > Save < /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowProductForm(false);
                                setEditingProduct(null);
                                setProductForm({ name: '', category: '', amount: '', description: '' });
                            }
                        } >
                        Cancel <
                        /button> < /
                        div > <
                        /form> < /
                        div > <
                        /div>
                    )
                }

                { /* Products List */ } <
                div className = "products-grid" > {
                    products.map((product) => ( <
                        div key = { product.id }
                        className = "product-card" >
                        <
                        h3 > { product.name } < /h3> <
                        p className = "category" > Category: { product.category } < /p> <
                        p className = "amount" > Amount: { product.amount } < /p> <
                        p className = "description" > { product.description } < /p> <
                        div className = "product-actions" >
                        <
                        button onClick = {
                            () => editProduct(product)
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteProduct(product.id)
                        }
                        className = "delete-btn" >
                        Delete <
                        /button> < /
                        div > <
                        /div>
                    ))
                } <
                /div> < /
                div >
            )
        }

        { /* Farmers Tab */ } {
            activeTab === 'farmers' && ( <
                div className = "farmers-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Farmer Management < /h2> <
                button onClick = {
                    () => {
                        setEditingFarmer(null);
                        setFarmerForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: ''
                        });
                        setShowFarmerForm(true);
                    }
                }
                className = "add-btn" >
                Register Farmer <
                /button> < /
                div >

                { /* Farmer Form Modal */ } {
                    showFarmerForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingFarmer ? 'Edit Farmer' : 'Register Farmer' } < /h3> {
                        farmerError && < div className = "error-message" > { farmerError } < /div>} <
                        form onSubmit = { handleFarmerSubmit } >
                        <
                        input type = "text"
                        placeholder = "Full Name"
                        value = { farmerForm.fullName }
                        onChange = {
                            (e) => setFarmerForm({...farmerForm, fullName: e.target.value })
                        }
                        required /
                        >
                        <
                        input type = "tel"
                        placeholder = "Phone Number"
                        value = { farmerForm.phoneNumber }
                        onChange = {
                            (e) => setFarmerForm({...farmerForm, phoneNumber: e.target.value })
                        }
                        required /
                        >
                        {!editingFarmer && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "Password"
                                value = { farmerForm.password }
                                onChange = {
                                    (e) => setFarmerForm({...farmerForm, password: e.target.value })
                                }
                                required /
                                >
                                <
                                input type = "password"
                                placeholder = "Confirm Password"
                                value = { farmerForm.confirmPassword }
                                onChange = {
                                    (e) => setFarmerForm({...farmerForm, confirmPassword: e.target.value })
                                }
                                required /
                                >
                                <
                                />
                            )
                        } {
                            editingFarmer && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "New Password (leave empty to keep current)"
                                value = { farmerForm.password }
                                onChange = {
                                    (e) => setFarmerForm({...farmerForm, password: e.target.value })
                                }
                                /> <
                                input type = "password"
                                placeholder = "Confirm New Password"
                                value = { farmerForm.confirmPassword }
                                onChange = {
                                    (e) => setFarmerForm({...farmerForm, confirmPassword: e.target.value })
                                }
                                /> < /
                                >
                            )
                        } <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > { editingFarmer ? 'Update' : 'Register' } < /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowFarmerForm(false);
                                setEditingFarmer(null);
                                setFarmerError('');
                                setFarmerForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: ''
                                });
                            }
                        } >
                        Cancel <
                        /button> < /
                        div > <
                        /form> < /
                        div > <
                        /div>
                    )
                }

                { /* Farmers List */ } <
                div className = "farmers-list" > {
                    farmers.map((farmer) => ( <
                        div key = { farmer.id }
                        className = "farmer-card" >
                        <
                        h3 > { farmer.full_name } < /h3> <
                        p > Phone: { farmer.phone_number } < /p> <
                        p > Registered: { new Date(farmer.created_at).toLocaleDateString() } < /p> <
                        div className = "farmer-actions" >
                        <
                        button onClick = {
                            () => editFarmer(farmer)
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteFarmer(farmer.id)
                        }
                        className = "delete-btn" >
                        Delete <
                        /button> < /
                        div > <
                        /div>
                    ))
                } <
                /div> < /
                div >
            )
        } <
        /main> < /
        div > <
        /div>
    );
};

export default RegionDashboard;
