// src/App.js

import React, { useState, useEffect } from 'react';
import { MOCK_CHAT_MESSAGES, MOCK_USER } from './data/mockData';
import reportWebVitals from './reportWebVitals';

/**
 * The main application component.
 * This component sets up the chat interface using mock data.
 * It's structured to be easily extendable with state management and real-time functionality.
 */
function App() {
    // State to hold the chat messages. Initialized with mock data.
    const [messages, setMessages] = useState(MOCK_CHAT_MESSAGES);

    // State to hold the current user.
    const [currentUser] = useState(MOCK_USER);

    // useEffect to report web vitals, a standard practice in React applications.
    useEffect(() => {
        reportWebVitals(console.log);
    }, []);

    return (
        // Main container for the app, using Tailwind CSS for full-screen layout,
        // centering content, and a dark theme.
        <
        div className = "flex flex-col h-screen bg-gray-900 text-gray-100 font-sans p-4 antialiased" >
        <
        div className = "flex flex-col flex-grow w-full max-w-4xl mx-auto rounded-xl shadow-2xl bg-gray-800 overflow-hidden" >

        { /* Header for the chat window */ } <
        div className = "p-4 bg-gray-700 text-white font-bold text-xl rounded-t-xl border-b-2 border-gray-600" >
        Gemini Chat <
        /div>

        { /* Message area - this is where all messages will be displayed */ } <
        div className = "flex-grow overflow-y-auto p-4 space-y-4" > {
            messages.map(msg => (
                // Individual message component. We use different styles based on the sender.
                <
                div key = { msg.id }
                className = { `flex ${msg.sender === currentUser.name ? 'justify-end' : 'justify-start'}` } >
                <
                div className = { `flex flex-col max-w-sm px-4 py-2 rounded-xl shadow-lg ${
                  msg.sender === currentUser.name
                    ? 'bg-blue-600 text-white rounded-br-none'
                    : 'bg-gray-700 text-gray-100 rounded-bl-none'
                }` } >
                { /* Sender's name */ } <
                div className = "font-semibold text-sm mb-1 opacity-80" > { msg.sender } <
                /div> { /* Message content */ } <
                div className = "text-base leading-relaxed" > { msg.content } <
                /div> { /* Timestamp */ } <
                div className = "text-xs mt-1 text-right opacity-60" > { msg.timestamp } <
                /div> <
                /div> <
                /div>
            ))
        } <
        /div>

        { /* Input area for sending new messages */ } <
        div className = "p-4 bg-gray-700 rounded-b-xl border-t-2 border-gray-600" >
        <
        form className = "flex space-x-2" >
        <
        input type = "text"
        placeholder = "Type your message..."
        className = "flex-grow p-3 rounded-full bg-gray-600 text-white placeholder-gray-400 border border-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500" /
        >
        <
        button type = "submit"
        className = "bg-blue-600 text-white p-3 rounded-full shadow-lg transition-all duration-300 ease-in-out hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500" >
        <
        svg xmlns = "http://www.w3.org/2000/svg"
        className = "h-6 w-6 transform rotate-90"
        fill = "none"
        viewBox = "0 0 24 24"
        stroke = "currentColor" >
        <
        path strokeLinecap = "round"
        strokeLinejoin = "round"
        strokeWidth = { 2 }
        d = "M12 19l9 2-9-18-9 18 9-2zm0 0v-8" / >
        <
        /svg> <
        /button> <
        /form> <
        /div>

        <
        /div> <
        /div>
    );
}

export default App;
