import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import RequestsTable from './components/RequestsTable';
import ApprovalForm from './components/ApprovalForm';
import LoginPage from './components/LoginPage';
import MasterData from './components/MasterData';
import ReportsPage from './components/ReportsPage';
import { adminRoles, pageNames } from './constants';

// Firebase imports
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, onSnapshot, doc, setDoc } from 'firebase/firestore';

function App() {
    // State for user authentication and role
    const [user, setUser] = useState(null);
    const [userId, setUserId] = useState(null);
    const [isAuthReady, setIsAuthReady] = useState(false);

    // State for current page navigation
    const [currentPage, setCurrentPage] = useState(pageNames.DASHBOARD);

    // State for a selected request for viewing/approving
    const [selectedRequest, setSelectedRequest] = useState(null);

    // State for requests fetched from Firestore
    const [requests, setRequests] = useState([]);

    // New state for UI features
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);
    const [isDarkMode, setIsDarkMode] = useState(false);

    // Firebase instances
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);

    // Function to toggle sidebar
    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    // Function to toggle dark mode
    const toggleDarkMode = () => {
        setIsDarkMode(!isDarkMode);
    };

    // Initializing Firebase and authentication
    useEffect(() => {
        console.log("Starting Firebase initialization...");
        try {
            // eslint-disable-next-line no-undef
            const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
            // eslint-disable-next-line no-undef
            const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

            console.log("Firebase config:", firebaseConfig);
            console.log("Initial auth token:", initialAuthToken);

            const app = initializeApp(firebaseConfig);
            const newDb = getFirestore(app);
            const newAuth = getAuth(app);
            setDb(newDb);
            setAuth(newAuth);

            const authListener = onAuthStateChanged(newAuth, async(authUser) => {
                if (authUser) {
                    console.log("User signed in:", authUser.uid);
                    setUserId(authUser.uid);
                    // NOTE: In a real app, you would fetch the user's role from a database here
                    setUser({ role: adminRoles.FEDERAL, name: 'Admin User' });
                } else {
                    console.log("No user signed in. Attempting sign-in...");
                    try {
                        if (initialAuthToken) {
                            await signInWithCustomToken(newAuth, initialAuthToken);
                            console.log("Signed in with custom token.");
                        } else {
                            await signInAnonymously(newAuth);
                            console.log("Signed in anonymously.");
                        }
                    } catch (error) {
                        console.error("Error during sign-in:", error);
                        setIsAuthReady(true);
                        return;
                    }
                }
                setIsAuthReady(true);
            });

            return () => {
                console.log("Cleaning up auth listener.");
                authListener();
            };
        } catch (error) {
            console.error("Error initializing Firebase:", error);
            setIsAuthReady(true);
        }
    }, []);

    // Real-time data fetching from Firestore
    useEffect(() => {
        if (!db || !userId || !isAuthReady) return;

        console.log("Setting up Firestore listener for userId:", userId);

        // eslint-disable-next-line no-undef
        const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
        const privateRequestsPath = `artifacts/${appId}/users/${userId}/requests`;

        // Set up a real-time listener for the requests collection
        const unsub = onSnapshot(collection(db, privateRequestsPath), (snapshot) => {
            const fetchedRequests = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setRequests(fetchedRequests);
            console.log("Fetched requests:", fetchedRequests);
        }, (error) => {
            console.error("Error fetching requests:", error);
        });

        // Cleanup the listener on component unmount
        return () => {
            console.log("Cleaning up Firestore listener.");
            unsub();
        }
    }, [db, userId, isAuthReady]);


    const handleLogin = (username, password, role) => {
        console.log(`Login attempted with username: ${username}, role: ${role}`);

        // This is the new, specific logic from your SRS document
        if (username === 'shegaw' && password === 'shegaw123' && role === adminRoles.FEDERAL) {
            console.log("Login successful!");
            setUser({ role: role, name: username });
        } else {
            console.log("Login failed: Invalid credentials or role mismatch.");
            const errorMessage = 'Invalid username, password, or role.';
            console.error(errorMessage);
        }
    };

    const handleLogout = async() => {
        if (auth) {
            try {
                await auth.signOut();
                setUser(null);
                setUserId(null);
                setCurrentPage(pageNames.DASHBOARD);
            } catch (error) {
                console.error("Error signing out:", error);
            }
        }
    };

    const handleViewRequest = (request) => {
        setSelectedRequest(request);
        setCurrentPage(pageNames.APPROVALS);
    };

    const handleApproveReject = async(action, feedback) => {
        if (!db || !selectedRequest || !userId) return;

        try {
            // eslint-disable-next-line no-undef
            const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
            const requestDocRef = doc(db, `artifacts/${appId}/users/${userId}/requests`, selectedRequest.id);

            await setDoc(requestDocRef, {
                ...selectedRequest,
                status: action === 'Approve' ? 'Approved' : 'Rejected',
                feedback: feedback
            }, { merge: true });

            console.log('Request processed successfully!');
            setSelectedRequest(null);
            setCurrentPage(pageNames.DASHBOARD);
        } catch (error) {
            console.error("Error updating document:", error);
        }
    };

    const renderContent = () => {
        if (!isAuthReady) {
            return <div className = "text-center p-8 text-xl text-gray-500 dark:text-gray-400" > Authenticating...Please check the console
            for status. < /div>;
        }

        if (!user) {
            return <LoginPage onLogin = { handleLogin }
            isDarkMode = { isDarkMode }
            />;
        }

        if (selectedRequest && currentPage === pageNames.APPROVALS) {
            return ( <
                ApprovalForm request = { selectedRequest }
                onBack = {
                    () => setCurrentPage(pageNames.DASHBOARD) }
                onSubmit = { handleApproveReject }
                />
            );
        }

        switch (currentPage) {
            case pageNames.DASHBOARD:
                return <Dashboard user = { user }
                requests = { requests }
                userId = { userId }
                isDarkMode = { isDarkMode }
                />;
            case pageNames.REQUESTS:
                return <RequestsTable requests = { requests }
                onViewRequest = { handleViewRequest }
                isDarkMode = { isDarkMode }
                />;
            case pageNames.MASTER_DATA:
                return <MasterData user = { user }
                isDarkMode = { isDarkMode }
                />;
            case pageNames.REPORTS:
                return <ReportsPage user = { user }
                isDarkMode = { isDarkMode }
                />;
            default:
                return <Dashboard user = { user }
                requests = { requests }
                userId = { userId }
                isDarkMode = { isDarkMode }
                />;
        }
    };

    return ( <
            div className = { `${isDarkMode ? 'dark' : ''}` } >
            <
            div className = "bg-gray-100 dark:bg-gray-900 min-h-screen font-sans antialiased text-gray-800 dark:text-gray-200 flex" > {
                user && < Sidebar
                userRole = { user.role }
                currentPage = { currentPage }
                onNavigate = { setCurrentPage }
                isSidebarOpen = { isSidebarOpen }
                toggleSidebar = { toggleSidebar }
                isDarkMode = { isDarkMode }
                />} <
                div className = { `flex-1 transition-all duration-300 ${isSidebarOpen ? 'ml-64' : 'ml-20'}` } > {
                    user && < Header
                    user = { user }
                    onLogout = { handleLogout }
                    isSidebarOpen = { isSidebarOpen }
                    toggleSidebar = { toggleSidebar }
                    isDarkMode = { isDarkMode }
                    toggleDarkMode = { toggleDarkMode }
                    />} <
                    main className = "container mx-auto p-4 md:p-8" > { renderContent() } <
                    /main> <
                    /div> <
                    /div> <
                    /div>
                );
            }

            export default App;
