import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
        const [credentials, setCredentials] = useState({
            phoneNumber: '', // Changed from 'phone' to match backend
            password: '',
            role: 'Farmer'
        });
        const [error, setError] = useState('');
        const [isLoading, setIsLoading] = useState(false);
        const navigate = useNavigate();

        const handleChange = (e) => {
            const { name, value } = e.target;
            setCredentials({
                ...credentials,
                [name]: value
            });
        };

        const handleSubmit = async(e) => {
            e.preventDefault();
            setIsLoading(true);
            setError('');

            // Validate phone number (only digits)
            const rawPhoneNumber = credentials.phoneNumber.replace(/\D/g, '');
            if (rawPhoneNumber.length < 10) {
                setError('Please enter a valid phone number (at least 10 digits)');
                setIsLoading(false);
                return;
            }

            try {
                let endpoint;

                // Determine which endpoint to use based on role
                if (credentials.role === 'Farmer') {
                    endpoint = '/api/farmers/login';
                } else {
                    endpoint = '/api/admins/login';
                }

                const response = await fetch(endpoint, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        phoneNumber: rawPhoneNumber, // Changed to match backend expectation
                        password: credentials.password
                    })
                });

                // Check if response is OK before parsing JSON
                if (!response.ok) {
                    const errorText = await response.text();
                    try {
                        const errorData = JSON.parse(errorText);
                        setError(errorData.message || 'Login failed. Please check your credentials.');
                    } catch {
                        setError('Login failed. Please check your credentials.');
                    }
                    setIsLoading(false);
                    return;
                }

                const data = await response.json();

                // Store token and user info
                localStorage.setItem('token', data.token);
                localStorage.setItem('userRole', credentials.role);

                // Store user info based on role
                if (credentials.role === 'Farmer') {
                    localStorage.setItem('userInfo', JSON.stringify(data.farmer));
                } else {
                    localStorage.setItem('userInfo', JSON.stringify(data.user));
                }

                // Redirect based on role
                switch (credentials.role) {
                    case 'Farmer':
                        navigate('/farmer-dashboard');
                        break;
                    case 'Kebele':
                        navigate('/kebele-dashboard');
                        break;
                    case 'Woreda':
                        navigate('/woreda-dashboard');
                        break;
                    case 'Zone':
                        navigate('/zone-dashboard');
                        break;
                    case 'Region':
                        navigate('/region-dashboard');
                        break;
                    case 'Federal':
                        navigate('/federal-dashboard');
                        break;
                    default:
                        navigate('/');
                }
            } catch (err) {
                console.error('Login error:', err);
                setError('Network error. Please check your connection and try again.');
            } finally {
                setIsLoading(false);
            }
        };

        return ( <
            div className = "login-container" >
            <
            div className = "login-card" >
            <
            div className = "login-header" >
            <
            h1 > Agricultural Platform < /h1> <
            p > Sign in with your phone number < /p> <
            /div>

            <
            form onSubmit = { handleSubmit }
            className = "login-form" > {
                error && < div className = "error-message" > { error } < /div>}

                <
                div className = "form-group" >
                <
                label htmlFor = "role" > User Type < /label> <
                select
                id = "role"
                name = "role"
                value = { credentials.role }
                onChange = { handleChange }
                className = "form-control" >
                <
                option value = "Farmer" > Farmer < /option> <
                option value = "Kebele" > Kebele Admin < /option> <
                option value = "Woreda" > Woreda Admin < /option> <
                option value = "Zone" > Zone Admin < /option> <
                option value = "Region" > Region Admin < /option> <
                option value = "Federal" > Federal Admin < /option> <
                /select> <
                /div>

                <
                div className = "form-group" >
                <
                label htmlFor = "phoneNumber" > Phone Number < /label> <
                input
                type = "tel"
                id = "phoneNumber"
                name = "phoneNumber" // Changed to match backend expectation
                value = { credentials.phoneNumber }
                onChange = { handleChange }
                className = "form-control"
                placeholder = "Enter your phone number (e.g., 251911123456)"
                required /
                >
                <
                small className = "input-hint" > Enter digits only(no dashes or spaces) < /small> <
                /div>

                <
                div className = "form-group" >
                <
                label htmlFor = "password" > Password < /label> <
                input
                type = "password"
                id = "password"
                name = "password"
                value = { credentials.password }
                onChange = { handleChange }
                className = "form-control"
                placeholder = "Enter your password"
                required /
                >
                <
                /div>

                <
                button
                type = "submit"
                className = "login-btn"
                disabled = { isLoading } >
                { isLoading ? 'Signing in...' : 'Sign In' } <
                /button> <
                /form>

                <
                div className = "login-footer" >
                <
                p > Don 't have an account? Contact your administrator.</p> <
                /div> <
                /div> <
                /div>
            );
        };

        export default Login;
