/**
 * src/utils/helpers.js
 * This file contains a collection of utility functions that can be used
 * across the application.
 */

/**
 * Formats a Firestore Timestamp object into a readable date string.
 * @param {object} timestamp - The Firestore Timestamp object.
 * @returns {string} The formatted date string (e.g., "Aug 11, 2025").
 */
export const formatTimestampToDate = (timestamp) => {
    if (!timestamp || !timestamp.toDate) {
        console.error("Invalid timestamp object provided.");
        return "";
    }

    const date = timestamp.toDate();
    const options = { year: 'numeric', month: 'short', day: 'numeric' };
    return new Intl.DateTimeFormat('en-US', options).format(date);
};

// You can add more helper functions here as needed, such as
// validation functions, data transformation utilities, etc.
