import React, { useState } from 'react';
// This assumes Tailwind CSS is configured and available.
// The icons are from lucide-react. You can use any icon library or SVG.
import { Lock, User } from 'lucide-react';

// The main component for the admin login page.
function App() {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    // Function to handle the form submission.
    const handleSubmit = async(e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // A simple validation check.
        if (!phoneNumber || !password) {
            setError('Please enter both phone number and password.');
            setLoading(false);
            return;
        }

        try {
            // Create the payload for the API call.
            const payload = {
                phoneNumber,
                password,
            };

            // Send the request to your backend's admin login endpoint.
            const response = await fetch('/api/admin/login', { // Replace with your actual backend URL
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(payload),
            });

            // Check if the response was successful.
            if (response.ok) {
                // Handle successful login (e.g., redirect or save a token).
                console.log('Login successful!');
                // For demonstration, we'll just show a success message.
                alert('Login successful! You are logged in as an Admin.');
                // Redirect to a dashboard or home page.
            } else {
                // Parse the error from the backend.
                const errorData = await response.json();
                setError(errorData.message || 'Invalid phone number or password. Please try again.');
            }
        } catch (err) {
            console.error('Login error:', err);
            setError('An error occurred. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    return ( <
        div className = "min-h-screen bg-gray-100 flex items-center justify-center p-4" >
        <
        div className = "bg-white p-8 rounded-xl shadow-2xl w-full max-w-md border border-gray-200" >
        <
        div className = "text-center" >
        <
        h1 className = "text-4xl font-extrabold text-gray-900 mb-2" > Log In < /h1> <
        p className = "text-gray-500 mb-6" > Enter your credentials to access the admin system. < /p> < /
        div >

        <
        form onSubmit = { handleSubmit }
        className = "space-y-6" >
        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700 mb-1" > Phone Number < /label> <
        div className = "relative rounded-md shadow-sm" >
        <
        div className = "absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none" >
        <
        User className = "h-5 w-5 text-gray-400" / >
        <
        /div> <
        input type = "text"
        value = { phoneNumber }
        onChange = {
            (e) => setPhoneNumber(e.target.value)
        }
        className = "block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
        placeholder = "944233773"
        disabled = { loading }
        /> < /
        div > <
        /div>

        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700 mb-1" > Password < /label> <
        div className = "relative rounded-md shadow-sm" >
        <
        div className = "absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none" >
        <
        Lock className = "h-5 w-5 text-gray-400" / >
        <
        /div> <
        input type = "password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value)
        }
        className = "block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
        placeholder = "••••••••"
        disabled = { loading }
        /> < /
        div > <
        /div>

        {
            error && ( <
                div className = "text-red-500 text-sm text-center font-medium" > { error } <
                /div>
            )
        }

        <
        button type = "submit"
        className = "w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-semibold text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 ease-in-out"
        disabled = { loading } > { loading ? 'Logging In...' : 'Sign In' } <
        /button> < /
        form > <
        /div> < /
        div >
    );
}

export default App;
