import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';

const WoredaDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const [darkMode, setDarkMode] = useState(false);
        const navigate = useNavigate();

        // Admin Management State
        const [admins, setAdmins] = useState([]);
        const [showAdminModal, setShowAdminModal] = useState(false);
        const [editingAdmin, setEditingAdmin] = useState(null);
        const [adminForm, setAdminForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            role: 'Woreda'
        });
        const [adminError, setAdminError] = useState('');

        // Product Management State
        const [products, setProducts] = useState([]);
        const [filteredProducts, setFilteredProducts] = useState([]);
        const [productView, setProductView] = useState('my'); // 'my', 'others', 'all'
        const [showProductModal, setShowProductModal] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            amount: '',
            price: '',
            description: ''
        });
        const [productError, setProductError] = useState('');

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [filteredRequests, setFilteredRequests] = useState([]);
        const [statusFilter, setStatusFilter] = useState('all');
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [requestStatus, setRequestStatus] = useState('');
        const [decisionReason, setDecisionReason] = useState('');

        // Farmer Management State
        const [farmers, setFarmers] = useState([]);
        const [kebeleFilter, setKebeleFilter] = useState('all');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            acceptedRequests: 0,
            totalAdmins: 0,
            totalFarmers: 0,
            totalProducts: 0
        });

        // Kebeles in this Woreda
        const [kebeles, setKebeles] = useState([]);

        // Apply dark mode to the entire document
        useEffect(() => {
            if (darkMode) {
                document.documentElement.classList.add('dark');
            } else {
                document.documentElement.classList.remove('dark');
            }
        }, [darkMode]);

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Woreda' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        useEffect(() => {
            if (requests.length > 0) {
                filterRequests();
            }
        }, [statusFilter, kebeleFilter, requests]);

        useEffect(() => {
            if (products.length > 0) {
                filterProducts();
            }
        }, [productView, products]);

        const filterRequests = useCallback(() => {
            let filtered = [...requests];

            // Filter by status
            if (statusFilter !== 'all') {
                filtered = filtered.filter(req => req.status === statusFilter);
            }

            // Filter by kebele
            if (kebeleFilter !== 'all') {
                filtered = filtered.filter(req => req.kebele_name === kebeleFilter);
            }

            setFilteredRequests(filtered);
        }, [statusFilter, kebeleFilter, requests]);

        const filterProducts = () => {
            let filtered = [...products];

            // Filter by product ownership
            if (productView === 'my') {
                filtered = filtered.filter(product => product.created_by_admin_id === user.id);
            } else if (productView === 'others') {
                filtered = filtered.filter(product => product.created_by_admin_id !== user.id);
            }

            setFilteredProducts(filtered);
        };

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Fetch admins in this woreda
                const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (adminsResponse.ok) {
                    const adminsData = await adminsResponse.json();
                    // Filter to only show woreda admins in this woreda
                    const woredaAdmins = adminsData.filter(admin =>
                        admin.role === 'Woreda' &&
                        admin.woreda_name === user.woreda_name &&
                        admin.zone_name === user.zone_name &&
                        admin.region_name === user.region_name
                    );
                    setAdmins(woredaAdmins);
                    setStats(prev => ({...prev, totalAdmins: woredaAdmins.length }));
                }

                // Fetch farmers in this woreda
                const farmersResponse = await fetch('http://localhost:5000/api/admins/farmers', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (farmersResponse.ok) {
                    const farmersData = await farmersResponse.json();
                    // Filter farmers by woreda
                    const woredaFarmers = farmersData.filter(farmer =>
                        farmer.woreda_name === user.woreda_name &&
                        farmer.zone_name === user.zone_name &&
                        farmer.region_name === user.region_name
                    );
                    setFarmers(woredaFarmers);
                    setStats(prev => ({...prev, totalFarmers: woredaFarmers.length }));

                    // Extract unique kebeles for filtering
                    const uniqueKebeles = [...new Set(woredaFarmers.map(f => f.kebele_name))];
                    setKebeles(uniqueKebeles);
                }

                // Fetch all products
                const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    filterProducts();
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Fetch requests with detailed status
                const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    // Filter requests by woreda
                    const woredaRequests = requestsData.filter(request =>
                        request.woreda_name === user.woreda_name &&
                        request.zone_name === user.zone_name &&
                        request.region_name === user.region_name
                    );
                    setRequests(woredaRequests);
                    filterRequests();

                    // Calculate stats from requests data
                    const totalRequests = woredaRequests.length;
                    const pendingRequests = woredaRequests.filter(req => req.status === 'Pending').length;
                    const approvedRequests = woredaRequests.filter(req => req.status === 'Approved').length;
                    const acceptedRequests = woredaRequests.filter(req => req.status === 'Accepted').length;
                    const rejectedRequests = woredaRequests.filter(req => req.status === 'Rejected').length;

                    setStats(prev => ({
                        ...prev,
                        totalRequests,
                        pendingRequests,
                        approvedRequests,
                        acceptedRequests,
                        rejectedRequests
                    }));
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Check if Woreda admin can act on this request
        const canWoredaAdminAct = (request) => {
            // Woreda admin can only act on requests that have been approved by Kebele admin
            return request.kebele_status === 'Approved' && request.woreda_status === 'Pending';
        };

        // Update request status at woreda level
        const updateRequestStatus = async(requestId, status, reason = '') => {
            try {
                const token = localStorage.getItem('token');

                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        status: status,
                        feedback: reason
                    })
                });

                const responseData = await response.json();

                if (response.ok) {
                    setShowRequestModal(false);
                    setSelectedRequest(null);
                    setRequestStatus('');
                    setDecisionReason('');
                    fetchDashboardData();
                    alert(`Request ${status} successfully`);
                } else {
                    alert(responseData.message || 'Error updating request');
                }
            } catch (error) {
                console.error('Error updating request:', error);
                alert('Error updating request');
            }
        };

        const handleStatusUpdate = (request, status) => {
            setSelectedRequest(request);
            setRequestStatus(status);
            setShowRequestModal(true);
        };

        const confirmStatusUpdate = () => {
            if (selectedRequest && requestStatus) {
                updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
            }
        };

        const viewRequestDetails = async(requestId) => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const requestDetails = await response.json();
                    setSelectedRequest(requestDetails);
                    setShowRequestModal(true);
                } else {
                    alert('Error fetching request details');
                }
            } catch (error) {
                console.error('Error fetching request details:', error);
                alert('Error fetching request details');
            }
        };

        const getStatusBadge = (status) => {
            const statusClasses = {
                'Pending': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
                'Approved': 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
                'Accepted': 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
                'Rejected': 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300'
            };

            return ( <
                span className = { `px-2 py-1 rounded-full text-xs font-medium ${statusClasses[status] || ''}` } > { status } <
                /span>
            );
        };

        const getLevelStatus = (level, request) => {
            const status = request[`${level}_status`];
            const admin = request[`${level}_admin_name`];
            const feedback = request[`${level}_feedback`];
            const date = request[`${level}_approved_at`];

            const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

            return ( <
                div className = "mb-4 p-3 bg-gray-50 dark:bg-gray-700 rounded" >
                <
                h4 className = "font-semibold dark:text-white" > { formatLevel }
                Level < /h4> <
                p className = "dark:text-gray-300" > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p className = "dark:text-gray-300" > < strong > Admin: < /strong> {admin}</p > } { feedback && < p className = "dark:text-gray-300" > < strong > Feedback: < /strong> {feedback}</p > } { date && < p className = "dark:text-gray-300" > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
                /div>
            );
        };

        // Admin Management Functions
        const handleAdminSubmit = async(e) => {
            e.preventDefault();
            setAdminError('');

            if (adminForm.password !== adminForm.confirmPassword && !editingAdmin) {
                setAdminError('Passwords do not match');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                let url, method;

                if (editingAdmin) {
                    url = `http://localhost:5000/api/admins/edit/${editingAdmin.id}`;
                    method = 'PUT';
                } else {
                    url = 'http://localhost:5000/api/admins/register';
                    method = 'POST';
                }

                // Prepare data for submission
                const submitData = {
                    fullName: adminForm.fullName,
                    phoneNumber: adminForm.phoneNumber,
                    password: adminForm.password,
                    role: 'Woreda',
                    region_name: user.region_name,
                    zone_name: user.zone_name,
                    woreda_name: user.woreda_name
                };

                // Don't send password if editing and password is empty
                if (editingAdmin && !submitData.password) {
                    delete submitData.password;
                }

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(submitData)
                });

                const data = await response.json();

                if (response.ok) {
                    setShowAdminModal(false);
                    setEditingAdmin(null);
                    setAdminForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        role: 'Woreda'
                    });
                    fetchDashboardData();
                    alert(editingAdmin ? 'Woreda admin updated successfully' : 'Woreda admin registered successfully');
                } else {
                    setAdminError(data.message || 'Error processing admin');
                }
            } catch (error) {
                console.error('Error processing admin:', error);
                setAdminError('Server error occurred');
            }
        };

        const deleteAdmin = async(adminId) => {
            if (!window.confirm('Are you sure you want to delete this woreda admin?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Woreda admin deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting woreda admin');
                }
            } catch (error) {
                console.error('Error deleting admin:', error);
                alert('Error deleting woreda admin');
            }
        };

        const editAdmin = (admin) => {
            setEditingAdmin(admin);
            setAdminForm({
                fullName: admin.full_name || '',
                phoneNumber: admin.phone_number || '',
                password: '',
                confirmPassword: '',
                role: admin.role || 'Woreda'
            });
            setShowAdminModal(true);
        };

        // Product Management Functions
        const handleProductSubmit = async(e) => {
            e.preventDefault();
            setProductError('');

            // Validate product amount and price
            if (parseInt(productForm.amount) < 1) {
                setProductError('Product amount must be at least 1');
                return;
            }

            if (parseFloat(productForm.price) <= 0) {
                setProductError('Product price must be greater than 0');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const url = editingProduct ?
                    `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                    'http://localhost:5000/api/admins/addproduct';

                const method = editingProduct ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(productForm)
                });

                if (response.ok) {
                    setShowProductModal(false);
                    setEditingProduct(null);
                    setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
                    fetchDashboardData();
                    alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
                } else {
                    const errorData = await response.json();
                    setProductError(errorData.message || 'Error saving product');
                }
            } catch (error) {
                console.error('Error saving product:', error);
                setProductError('Error saving product');
            }
        };

        const deleteProduct = async(productId) => {
            if (!window.confirm('Are you sure you want to delete this product?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Product deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting product');
                }
            } catch (error) {
                console.error('Error deleting product:', error);
                alert('Error deleting product');
            }
        };

        const editProduct = (product) => {
            // Check if product belongs to current admin
            if (product.created_by_admin_id !== user.id) {
                alert('You can only edit your own products');
                return;
            }

            setEditingProduct(product);
            setProductForm({
                name: product.name || '',
                category: product.category || '',
                amount: product.amount || '',
                price: product.price || '',
                description: product.description || ''
            });
            setShowProductModal(true);
        };

        if (!user) {
            return <div className = "flex justify-center items-center h-screen dark:bg-gray-900" > Loading... < /div>;
        }

        return ( <
                div className = { `flex h-screen ${darkMode ? 'dark bg-gray-900' : 'bg-gray-100'}` } > { /* Sidebar */ } <
                div className = "w-64 bg-blue-800 dark:bg-gray-800 text-white" >
                <
                div className = "p-4" >
                <
                h2 className = "text-xl font-bold" > Woreda Dashboard < /h2> <
                div className = "woreda-info mt-4" >
                <
                h3 className = "text-lg font-semibold" > { user.woreda_name } < /h3> <
                p className = "text-sm" > Region: { user.region_name } < /p> <
                p className = "text-sm" > Zone: { user.zone_name } < /p> <
                /div> <
                div className = "mt-4 flex items-center" >
                <
                span className = "mr-2 dark:text-white" > Dark Mode < /span> <
                button onClick = {
                    () => setDarkMode(!darkMode) }
                className = { `relative inline-flex h-6 w-11 items-center rounded-full ${darkMode ? 'bg-blue-600' : 'bg-gray-200'}` } >
                <
                span className = { `inline-block h-4 w-4 transform rounded-full bg-white transition ${darkMode ? 'translate-x-6' : 'translate-x-1'}` }
                /> <
                /button> <
                /div> <
                /div> <
                nav className = "mt-6" >
                <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'overview' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
                onClick = {
                    () => setActiveTab('overview') } >
                📊Overview <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'admins' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
                onClick = {
                    () => setActiveTab('admins') } >
                👥Woreda Admins <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'farmers' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
                onClick = {
                    () => setActiveTab('farmers') } >
                👨‍🌾Farmers <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'products' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
                onClick = {
                    () => setActiveTab('products') } >
                📦Products <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'requests' ? 'bg-blue-700 dark:bg-gray-700' : 'hover:bg-blue-700 dark:hover:bg-gray-700'}` }
                onClick = {
                    () => setActiveTab('requests') } >
                📋Requests <
                /button> <
                /nav> <
                /div>

                { /* Main Content */ } <
                div className = "flex-1 overflow-auto" >
                <
                header className = "bg-white dark:bg-gray-800 shadow p-4 flex justify-between items-center" >
                <
                div >
                <
                h1 className = "text-xl font-bold dark:text-white" > Welcome, { user.fullName }({ user.woreda_name }
                    Admin) < /h1> <
                p className = "text-gray-600 dark:text-gray-300" > Woreda: { user.woreda_name }, Zone: { user.zone_name }, Region: { user.region_name } < /p> <
                /div> <
                button onClick = { handleLogout }
                className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded dark:bg-red-600 dark:hover:bg-red-700" >
                Logout <
                /button> <
                /header>

                <
                main className = "p-4" > { /* Overview Tab */ } {
                    activeTab === 'overview' && ( <
                        div className = "overview-tab" >
                        <
                        h2 className = "text-2xl font-bold mb-4 dark:text-white" > Woreda Overview < /h2> <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6" >
                        <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.totalRequests } < /h3> <
                        p className = "dark:text-gray-300" > Total Requests < /p> <
                        /div> <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.pendingRequests } < /h3> <
                        p className = "dark:text-gray-300" > Pending Requests < /p> <
                        /div> <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.approvedRequests } < /h3> <
                        p className = "dark:text-gray-300" > Approved Requests < /p> <
                        /div> <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.acceptedRequests } < /h3> <
                        p className = "dark:text-gray-300" > Accepted Requests < /p> <
                        /div> <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.rejectedRequests } < /h3> <
                        p className = "dark:text-gray-300" > Rejected Requests < /p> <
                        /div> <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.totalProducts } < /h3> <
                        p className = "dark:text-gray-300" > Available Products < /p> <
                        /div> <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.totalAdmins } < /h3> <
                        p className = "dark:text-gray-300" > Woreda Admins < /p> <
                        /div> <
                        div className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold dark:text-white" > { stats.totalFarmers } < /h3> <
                        p className = "dark:text-gray-300" > Registered Farmers < /p> <
                        /div> <
                        /div>

                        <
                        div className = "quick-actions bg-white dark:bg-gray-800 p-4 rounded shadow" >
                        <
                        h2 className = "text-xl font-bold mb-4 dark:text-white" > Quick Actions < /h2> <
                        div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                        <
                        button onClick = {
                            () => setActiveTab('admins') }
                        className = "bg-blue-500 hover:bg-blue-600 text-white p-4 rounded text-center dark:bg-blue-600 dark:hover:bg-blue-700" >
                        👥Create Woreda Admin <
                        /button> <
                        button onClick = {
                            () => setActiveTab('products') }
                        className = "bg-green-500 hover:bg-green-600 text-white p-4 rounded text-center dark:bg-green-600 dark:hover:bg-green-700" >
                        📦Add Product <
                        /button> <
                        button onClick = {
                            () => setActiveTab('requests') }
                        className = "bg-purple-500 hover:bg-purple-600 text-white p-4 rounded text-center dark:bg-purple-600 dark:hover:bg-purple-700" >
                        📋Manage Requests <
                        /button> <
                        button className = "bg-gray-500 hover:bg-gray-600 text-white p-4 rounded text-center dark:bg-gray-600 dark:hover:bg-gray-700" > 📊Generate Reports <
                        /button> <
                        /div> <
                        /div> <
                        /div>
                    )
                }

                { /* Woreda Admins Tab */ } {
                    activeTab === 'admins' && ( <
                        div className = "admins-tab" >
                        <
                        div className = "flex justify-between items-center mb-4" >
                        <
                        h2 className = "text-2xl font-bold dark:text-white" > Woreda Admin Management < /h2> <
                        button onClick = {
                            () => setShowAdminModal(true) }
                        className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded dark:bg-green-600 dark:hover:bg-green-700" >
                        Add Woreda Admin <
                        /button> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            admins.map((admin) => ( <
                                div key = { admin.id }
                                className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > { admin.full_name } < /h3> <
                                p className = "text-gray-600 dark:text-gray-300" > 📞Phone: { admin.phone_number } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 📍Woreda: { admin.woreda_name } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 📅Created: { new Date(admin.created_at).toLocaleDateString() } < /p> <
                                div className = "mt-4 flex space-x-2" >
                                <
                                button onClick = {
                                    () => editAdmin(admin) }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                                ✏️Edit <
                                /button> <
                                button onClick = {
                                    () => deleteAdmin(admin.id) }
                                className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded dark:bg-red-600 dark:hover:bg-red-700" >
                                🗑️Delete <
                                /button> <
                                /div> <
                                /div>
                            ))
                        } {
                            admins.length === 0 && ( <
                                p className = "text-gray-600 dark:text-gray-300" > No woreda admins found. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Farmers Tab */ } {
                    activeTab === 'farmers' && ( <
                        div className = "farmers-tab" >
                        <
                        div className = "mb-4" >
                        <
                        h2 className = "text-2xl font-bold mb-2 dark:text-white" > Farmer Management < /h2> <
                        p className = "text-gray-600 dark:text-gray-300" > Viewing farmers in your woreda: { user.woreda_name } < /p>

                        <
                        div className = "flex flex-wrap items-center space-x-4 mt-4" >
                        <
                        div className = "flex items-center space-x-2 mb-2" >
                        <
                        label className = "font-medium dark:text-white" > Filter by Kebele: < /label> <
                        select value = { kebeleFilter }
                        onChange = {
                            (e) => setKebeleFilter(e.target.value) }
                        className = "border rounded p-1 dark:bg-gray-700 dark:text-white dark:border-gray-600" >
                        <
                        option value = "all" > All Kebeles < /option> {
                            kebeles.map(kebele => ( <
                                option key = { kebele }
                                value = { kebele } > { kebele } < /option>
                            ))
                        } <
                        /select> <
                        /div> <
                        /div> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            farmers
                            .filter(farmer => kebeleFilter === 'all' || farmer.kebele_name === kebeleFilter)
                            .map((farmer) => ( <
                                div key = { farmer.id }
                                className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > { farmer.full_name } < /h3> <
                                p className = "text-gray-600 dark:text-gray-300" > 📞Phone: { farmer.phone_number } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 📍Kebele: { farmer.kebele_name } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 📅Registered: { new Date(farmer.created_at).toLocaleDateString() } < /p> <
                                div className = "farmer-requests mt-4" >
                                <
                                h4 className = "font-medium dark:text-white" > Recent Requests: < /h4> {
                                    requests
                                        .filter(req => req.farmer_id === farmer.id)
                                        .slice(0, 3)
                                        .map(req => ( <
                                            div key = { req.id }
                                            className = "flex justify-between items-center mt-2" >
                                            <
                                            span className = "text-sm text-gray-600 dark:text-gray-400" > { req.product_name } < /span> { getStatusBadge(req.status) } <
                                            /div>
                                        ))
                                } {
                                    requests.filter(req => req.farmer_id === farmer.id).length === 0 && ( <
                                        p className = "text-sm text-gray-600 dark:text-gray-400 mt-2" > No requests from this farmer < /p>
                                    )
                                } <
                                /div> <
                                /div>
                            ))
                        } {
                            farmers.length === 0 && ( <
                                p className = "text-gray-600 dark:text-gray-300" > No farmers found in your woreda. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Products Tab */ } {
                    activeTab === 'products' && ( <
                        div className = "products-tab" >
                        <
                        div className = "flex justify-between items-center mb-4" >
                        <
                        h2 className = "text-2xl font-bold dark:text-white" > Product Management < /h2> <
                        button onClick = {
                            () => setShowProductModal(true) }
                        className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded dark:bg-green-600 dark:hover:bg-green-700" >
                        Add Product <
                        /button> <
                        /div>

                        { /* Product Filter */ } <
                        div className = "mb-4 flex items-center space-x-4" >
                        <
                        label className = "font-medium dark:text-white" > Filter Products: < /label> <
                        select value = { productView }
                        onChange = {
                            (e) => setProductView(e.target.value) }
                        className = "border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600" >
                        <
                        option value = "my" > My Products < /option> <
                        option value = "others" > Other Admins ' Products</option> <
                        option value = "all" > All Products < /option> <
                        /select> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            filteredProducts.map((product) => ( <
                                div key = { product.id }
                                className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > { product.name } < /h3> <
                                p className = "text-gray-600 dark:text-gray-300" > { product.description } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 📦Category: { product.category } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 💰Amount: { product.amount } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 💵Price: { product.price }
                                Birr < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 📅Created: { new Date(product.created_at).toLocaleDateString() } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 👤Added by: { product.created_by_name || 'Unknown' } < /p> { /* Show location for products added by other admins */ } {
                                    productView === 'others' && product.admin_role && ( <
                                        p className = "text-gray-600 dark:text-gray-300" > 📍Location: { product.region_name || 'N/A' } { product.zone_name ? ` / ${product.zone_name}` : '' } { product.woreda_name ? ` / ${product.woreda_name}` : '' } <
                                        /p>
                                    )
                                } <
                                div className = "mt-4 flex space-x-2" > {
                                    product.created_by_admin_id === user.id && ( <
                                        >
                                        <
                                        button onClick = {
                                            () => editProduct(product) }
                                        className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                                        ✏️Edit <
                                        /button> <
                                        button onClick = {
                                            () => deleteProduct(product.id) }
                                        className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded dark:bg-red-600 dark:hover:bg-red-700" >
                                        🗑️Delete <
                                        /button> <
                                        />
                                    )
                                } <
                                /div> <
                                /div>
                            ))
                        } {
                            filteredProducts.length === 0 && ( <
                                p className = "text-gray-600 dark:text-gray-300" > No products found with the selected filters. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Requests Tab */ } {
                    activeTab === 'requests' && ( <
                        div className = "requests-tab" >
                        <
                        div className = "mb-4" >
                        <
                        h2 className = "text-2xl font-bold mb-2 dark:text-white" > Request Management < /h2> <
                        p className = "text-gray-600 dark:text-gray-300" > Manage farmer requests in your woreda: { user.woreda_name } < /p>

                        <
                        div className = "flex flex-wrap items-center space-x-4 mt-4" >
                        <
                        div className = "flex items-center space-x-2 mb-2" >
                        <
                        label className = "font-medium dark:text-white" > Filter by Status: < /label> <
                        select value = { statusFilter }
                        onChange = {
                            (e) => setStatusFilter(e.target.value) }
                        className = "border rounded p-1 dark:bg-gray-700 dark:text-white dark:border-gray-600" >
                        <
                        option value = "all" > All Requests < /option> <
                        option value = "Pending" > Pending < /option> <
                        option value = "Approved" > Approved < /option> <
                        option value = "Accepted" > Accepted < /option> <
                        option value = "Rejected" > Rejected < /option> <
                        /select> <
                        /div>

                        <
                        div className = "flex items-center space-x-2 mb-2" >
                        <
                        label className = "font-medium dark:text-white" > Filter by Kebele: < /label> <
                        select value = { kebeleFilter }
                        onChange = {
                            (e) => setKebeleFilter(e.target.value) }
                        className = "border rounded p-1 dark:bg-gray-700 dark:text-white dark:border-gray-600" >
                        <
                        option value = "all" > All Kebeles < /option> {
                            kebeles.map(kebele => ( <
                                option key = { kebele }
                                value = { kebele } > { kebele } < /option>
                            ))
                        } <
                        /select> <
                        /div> <
                        /div> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 gap-4" > {
                            filteredRequests.map((request) => ( <
                                div key = { request.id }
                                className = "bg-white dark:bg-gray-800 p-4 rounded shadow" >
                                <
                                div className = "flex justify-between items-start" >
                                <
                                div >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > { request.farmer_name } < /h3> <
                                p className = "text-gray-600 dark:text-gray-300" > 📞{ request.farmer_phone } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > 📍{ request.kebele_name } < /p> <
                                /div> <
                                div className = "text-right" >
                                <
                                p className = "text-gray-600 dark:text-gray-300" > { new Date(request.created_at).toLocaleDateString() } < /p> { getStatusBadge(request.status) } <
                                /div> <
                                /div>

                                <
                                div className = "mt-4" >
                                <
                                h4 className = "font-medium dark:text-white" > Request Details: < /h4> <
                                p className = "text-gray-600 dark:text-gray-300" > Product: { request.product_name } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > Amount: { request.requested_amount } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > Reason: { request.request_reason } < /p> <
                                /div>

                                <
                                div className = "mt-4" >
                                <
                                h4 className = "font-medium dark:text-white" > Status History: < /h4> <
                                div className = "text-sm" >
                                <
                                p className = "text-gray-600 dark:text-gray-300" > Kebele: { request.kebele_status || 'Pending' } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > Woreda: { request.woreda_status || 'Pending' } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > Zone: { request.zone_status || 'Pending' } < /p> <
                                p className = "text-gray-600 dark:text-gray-300" > Region: { request.region_status || 'Pending' } < /p> <
                                /div> <
                                /div>

                                <
                                div className = "mt-4 flex flex-wrap gap-2" >
                                <
                                button onClick = {
                                    () => viewRequestDetails(request.id) }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm dark:bg-blue-600 dark:hover:bg-blue-700" >
                                📋View Details <
                                /button>

                                {
                                    canWoredaAdminAct(request) && ( <
                                        >
                                        <
                                        button onClick = {
                                            () => handleStatusUpdate(request, 'Approved') }
                                        className = "bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded text-sm dark:bg-green-600 dark:hover:bg-green-700" >
                                        ✅Approve <
                                        /button> <
                                        button onClick = {
                                            () => handleStatusUpdate(request, 'Rejected') }
                                        className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm dark:bg-red-600 dark:hover:bg-red-700" >
                                        ❌Reject <
                                        /button> <
                                        />
                                    )
                                } <
                                /div> <
                                /div>
                            ))
                        } {
                            filteredRequests.length === 0 && ( <
                                p className = "text-gray-600 dark:text-gray-300" > No requests found with the selected filters. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                } <
                /main> <
                /div>

                { /* Admin Modal */ } {
                    showAdminModal && ( <
                        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" >
                        <
                        div className = "bg-white dark:bg-gray-800 p-6 rounded-lg w-full max-w-md" >
                        <
                        h2 className = "text-xl font-bold mb-4 dark:text-white" > { editingAdmin ? 'Edit Woreda Admin' : 'Add Woreda Admin' } <
                        /h2> {
                            adminError && < p className = "text-red-500 mb-4" > { adminError } < /p>} <
                                form onSubmit = { handleAdminSubmit } >
                                <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 dark:text-gray-300 mb-2" > Full Name < /label> <
                                input
                            type = "text"
                            value = { adminForm.fullName }
                            onChange = {
                                (e) => setAdminForm({...adminForm, fullName: e.target.value }) }
                            className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                            required
                                /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 dark:text-gray-300 mb-2" > Phone Number < /label> <
                                input
                            type = "tel"
                            value = { adminForm.phoneNumber }
                            onChange = {
                                (e) => setAdminForm({...adminForm, phoneNumber: e.target.value }) }
                            className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                            required
                                /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 dark:text-gray-300 mb-2" > { editingAdmin ? 'New Password (leave blank to keep current)' : 'Password' } <
                                /label> <
                                input
                            type = "password"
                            value = { adminForm.password }
                            onChange = {
                                (e) => setAdminForm({...adminForm, password: e.target.value }) }
                            className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                            minLength = { 6 }
                            required = {!editingAdmin }
                            /> <
                            /div> {
                                !editingAdmin && ( <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Confirm Password < /label> <
                                    input type = "password"
                                    value = { adminForm.confirmPassword }
                                    onChange = {
                                        (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                    className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                    minLength = { 6 }
                                    required /
                                    >
                                    <
                                    /div>
                                )
                            } <
                            div className = "flex justify-end space-x-2" >
                                <
                                button
                            type = "button"
                            onClick = {
                                () => {
                                    setShowAdminModal(false);
                                    setEditingAdmin(null);
                                    setAdminForm({
                                        fullName: '',
                                        phoneNumber: '',
                                        password: '',
                                        confirmPassword: '',
                                        role: 'Woreda'
                                    });
                                }
                            }
                            className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
                                Cancel <
                                /button> <
                                button
                            type = "submit"
                            className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                                { editingAdmin ? 'Update' : 'Create' } <
                                /button> <
                                /div> <
                                /form> <
                                /div> <
                                /div>
                        )
                    }

                    { /* Product Modal */ } {
                        showProductModal && ( <
                            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" >
                            <
                            div className = "bg-white dark:bg-gray-800 p-6 rounded-lg w-full max-w-md" >
                            <
                            h2 className = "text-xl font-bold mb-4 dark:text-white" > { editingProduct ? 'Edit Product' : 'Add Product' } <
                            /h2> {
                                productError && < p className = "text-red-500 mb-4" > { productError } < /p>} <
                                    form onSubmit = { handleProductSubmit } >
                                    <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Product Name < /label> <
                                    input
                                type = "text"
                                value = { productForm.name }
                                onChange = {
                                    (e) => setProductForm({...productForm, name: e.target.value }) }
                                className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                required
                                    /
                                    >
                                    <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Category < /label> <
                                    select
                                value = { productForm.category }
                                onChange = {
                                    (e) => setProductForm({...productForm, category: e.target.value }) }
                                className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                required
                                    >
                                    <
                                    option value = "" > Select Category < /option> <
                                    option value = "Crop" > Crop < /option> <
                                    option value = "Livestock" > Livestock < /option> <
                                    option value = "Equipment" > Equipment < /option> <
                                    option value = "Fertilizer" > Fertilizer < /option> <
                                    option value = "Seed" > Seed < /option> <
                                    option value = "Other" > Other < /option> <
                                    /select> <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Amount < /label> <
                                    input
                                type = "number"
                                min = "1"
                                value = { productForm.amount }
                                onChange = {
                                    (e) => setProductForm({...productForm, amount: e.target.value }) }
                                className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                required
                                    /
                                    >
                                    <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Price(Birr) < /label> <
                                    input
                                type = "number"
                                min = "0.01"
                                step = "0.01"
                                value = { productForm.price }
                                onChange = {
                                    (e) => setProductForm({...productForm, price: e.target.value }) }
                                className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                required
                                    /
                                    >
                                    <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Description < /label> <
                                    textarea
                                value = { productForm.description }
                                onChange = {
                                    (e) => setProductForm({...productForm, description: e.target.value }) }
                                className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                rows = "3" >
                                    < /textarea> <
                                    /div> <
                                    div className = "flex justify-end space-x-2" >
                                    <
                                    button
                                type = "button"
                                onClick = {
                                    () => {
                                        setShowProductModal(false);
                                        setEditingProduct(null);
                                        setProductForm({
                                            name: '',
                                            category: '',
                                            amount: '',
                                            price: '',
                                            description: ''
                                        });
                                    }
                                }
                                className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
                                    Cancel <
                                    /button> <
                                    button
                                type = "submit"
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded dark:bg-blue-600 dark:hover:bg-blue-700" >
                                    { editingProduct ? 'Update' : 'Create' } <
                                    /button> <
                                    /div> <
                                    /form> <
                                    /div> <
                                    /div>
                            )
                        }

                        { /* Request Detail Modal */ } {
                            showRequestModal && selectedRequest && ( <
                                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 overflow-auto" >
                                <
                                div className = "bg-white dark:bg-gray-800 p-6 rounded-lg w-full max-w-2xl max-h-screen overflow-y-auto" >
                                <
                                h2 className = "text-xl font-bold mb-4 dark:text-white" > Request Details < /h2>

                                <
                                div className = "mb-4" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > Farmer Information < /h3> <
                                p className = "dark:text-gray-300" > < strong > Name: < /strong> {selectedRequest.farmer_name}</p >
                                <
                                p className = "dark:text-gray-300" > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
                                <
                                p className = "dark:text-gray-300" > < strong > Location: < /strong> {selectedRequest.kebele_name}, {selectedRequest.woreda_name}, {selectedRequest.zone_name}, {selectedRequest.region_name}</p >
                                <
                                /div>

                                <
                                div className = "mb-4" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > Request Information < /h3> <
                                p className = "dark:text-gray-300" > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                                <
                                p className = "dark:text-gray-300" > < strong > Amount: < /strong> {selectedRequest.requested_amount}</p >
                                <
                                p className = "dark:text-gray-300" > < strong > Reason: < /strong> {selectedRequest.request_reason}</p >
                                <
                                p className = "dark:text-gray-300" > < strong > Date: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p >
                                <
                                /div>

                                <
                                div className = "mb-4" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > Status History < /h3> <
                                div className = "space-y-2 max-h-60 overflow-y-auto" > { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } <
                                /div> <
                                /div>

                                {
                                    canWoredaAdminAct(selectedRequest) && ( <
                                        div className = "mb-4" >
                                        <
                                        h3 className = "text-lg font-semibold dark:text-white" > Update Status < /h3> <
                                        div className = "flex space-x-2 mb-2" >
                                        <
                                        button onClick = {
                                            () => setRequestStatus('Approved') }
                                        className = { `px-4 py-2 rounded ${requestStatus === 'Approved' ? 'bg-green-600 text-white' : 'bg-gray-200 dark:bg-gray-700 dark:text-white'}` } >
                                        Approve <
                                        /button> <
                                        button onClick = {
                                            () => setRequestStatus('Rejected') }
                                        className = { `px-4 py-2 rounded ${requestStatus === 'Rejected' ? 'bg-red-600 text-white' : 'bg-gray-200 dark:bg-gray-700 dark:text-white'}` } >
                                        Reject <
                                        /button> <
                                        /div>

                                        {
                                            requestStatus && ( <
                                                div >
                                                <
                                                label className = "block text-gray-700 dark:text-gray-300 mb-2" > Reason(Optional) < /label> <
                                                textarea value = { decisionReason }
                                                onChange = {
                                                    (e) => setDecisionReason(e.target.value) }
                                                className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                                rows = "3"
                                                placeholder = "Enter reason for your decision" >
                                                < /textarea> <
                                                /div>
                                            )
                                        } <
                                        /div>
                                    )
                                }

                                <
                                div className = "flex justify-end space-x-2" >
                                <
                                button onClick = {
                                    () => {
                                        setShowRequestModal(false);
                                        setSelectedRequest(null);
                                        setRequestStatus('');
                                        setDecisionReason('');
                                    }
                                }
                                className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
                                Close <
                                /button>

                                {
                                    requestStatus && canWoredaAdminAct(selectedRequest) && ( <
                                        button onClick = { confirmStatusUpdate }
                                        className = { `px-4 py-2 rounded text-white ${requestStatus === 'Approved' ? 'bg-green-600 hover:bg-green-700' : 'bg-red-600 hover:bg-red-700'}` } >
                                        Confirm { requestStatus } <
                                        /button>
                                    )
                                } <
                                /div> <
                                /div> <
                                /div>
                            )
                        } <
                        /div>
                    );
                };

                export default WoredaDashboard;
