// File: KebeleDashboard.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const KebeleDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const [darkMode, setDarkMode] = useState(false);
        const navigate = useNavigate();

        // Farmer Management State
        const [farmers, setFarmers] = useState([]);
        const [showFarmerModal, setShowFarmerModal] = useState(false);
        const [editingFarmer, setEditingFarmer] = useState(null);
        const [farmerForm, setFarmerForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: ''
        });

        // Product Management State
        const [products, setProducts] = useState([]);
        const [myProducts, setMyProducts] = useState([]);
        const [otherProducts, setOtherProducts] = useState([]);
        const [productFilter, setProductFilter] = useState('all'); // 'all', 'own', 'others'
        const [showProductModal, setShowProductModal] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            amount: '',
            description: '',
            price: ''
        });

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [filteredRequests, setFilteredRequests] = useState([]);
        const [selectedRequests, setSelectedRequests] = useState([]);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [requestStatus, setRequestStatus] = useState('');
        const [decisionReason, setDecisionReason] = useState('');
        const [bulkAction, setBulkAction] = useState('');
        const [statusFilter, setStatusFilter] = useState('all');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            acceptedRequests: 0,
            totalFarmers: 0,
            totalProducts: 0
        });

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Kebele' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        useEffect(() => {
            if (requests.length > 0) {
                filterRequestsByStatus();
            }
        }, [statusFilter, requests]);

        useEffect(() => {
            // Apply dark mode to the entire document
            if (darkMode) {
                document.documentElement.classList.add('dark');
            } else {
                document.documentElement.classList.remove('dark');
            }
        }, [darkMode]);

        const filterRequestsByStatus = () => {
            if (statusFilter === 'all') {
                setFilteredRequests(requests);
                return;
            }

            let filtered = [];
            switch (statusFilter) {
                case 'pending':
                    filtered = requests.filter(req => req.status === 'Pending');
                    break;
                case 'approved':
                    filtered = requests.filter(req => req.status === 'Approved');
                    break;
                case 'accepted':
                    filtered = requests.filter(req => req.status === 'Accepted');
                    break;
                case 'rejected':
                    filtered = requests.filter(req => req.status === 'Rejected');
                    break;
                default:
                    filtered = requests;
            }
            setFilteredRequests(filtered);
        };

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Fetch farmers in scope
                const farmersResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (farmersResponse.ok) {
                    const data = await farmersResponse.json();
                    const farmersData = data.filter(item => item.type === 'farmer');
                    setFarmers(farmersData);
                    setStats(prev => ({...prev, totalFarmers: farmersData.length }));
                }

                // Fetch all products
                const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Fetch my products
                const myProductsResponse = await fetch('http://localhost:5000/api/admins/myproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (myProductsResponse.ok) {
                    const myProductsData = await myProductsResponse.json();
                    setMyProducts(myProductsData);
                }

                // Fetch other admins' products
                const otherProductsResponse = await fetch('http://localhost:5000/api/admins/otherproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (otherProductsResponse.ok) {
                    const otherProductsData = await otherProductsResponse.json();
                    setOtherProducts(otherProductsData);
                }

                // Fetch requests with detailed status
                const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setRequests(requestsData);
                    setFilteredRequests(requestsData);

                    // Calculate stats from requests data
                    const totalRequests = requestsData.length;
                    const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                    const approvedRequests = requestsData.filter(req => req.status === 'Approved').length;
                    const acceptedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                    const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                    setStats(prev => ({
                        ...prev,
                        totalRequests,
                        pendingRequests,
                        approvedRequests,
                        acceptedRequests,
                        rejectedRequests
                    }));
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Farmer Management Functions
        const handleFarmerSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');

                if (farmerForm.password !== farmerForm.confirmPassword) {
                    alert('Passwords do not match');
                    return;
                }

                const url = editingFarmer ?
                    `http://localhost:5000/api/admins/edit/${editingFarmer.id}` :
                    'http://localhost:5000/api/admins/createfarmer';

                const method = editingFarmer ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        fullName: farmerForm.fullName,
                        phoneNumber: farmerForm.phoneNumber,
                        password: farmerForm.password,
                        role: 'Farmer',
                        region_name: user.region_name,
                        zone_name: user.zone_name,
                        woreda_name: user.woreda_name,
                        kebele_name: user.kebele_name
                    })
                });

                if (response.ok) {
                    setShowFarmerModal(false);
                    setEditingFarmer(null);
                    setFarmerForm({ fullName: '', phoneNumber: '', password: '', confirmPassword: '' });
                    fetchDashboardData();
                    alert(editingFarmer ? 'Farmer updated successfully' : 'Farmer registered successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error processing farmer');
                }
            } catch (error) {
                console.error('Error processing farmer:', error);
                alert('Error processing farmer');
            }
        };

        const handleEditFarmer = (farmer) => {
            setEditingFarmer(farmer);
            setFarmerForm({
                fullName: farmer.full_name,
                phoneNumber: farmer.phone_number,
                password: '',
                confirmPassword: ''
            });
            setShowFarmerModal(true);
        };

        const handleDeleteFarmer = async(farmerId) => {
            if (window.confirm('Are you sure you want to delete this farmer?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/admins/delete/${farmerId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Farmer deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting farmer');
                    }
                } catch (error) {
                    console.error('Error deleting farmer:', error);
                    alert('Error deleting farmer');
                }
            }
        };

        // Product Management Functions
        const handleProductSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');

                const url = editingProduct ?
                    `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                    'http://localhost:5000/api/admins/addproduct';

                const method = editingProduct ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        name: productForm.name,
                        category: productForm.category,
                        amount: productForm.amount,
                        description: productForm.description,
                        price: productForm.price
                    })
                });

                if (response.ok) {
                    setShowProductModal(false);
                    setEditingProduct(null);
                    setProductForm({
                        name: '',
                        category: '',
                        amount: '',
                        description: '',
                        price: ''
                    });
                    fetchDashboardData();
                    alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error processing product');
                }
            } catch (error) {
                console.error('Error processing product:', error);
                alert('Error processing product');
            }
        };

        const handleEditProduct = (product) => {
            setEditingProduct(product);
            setProductForm({
                name: product.name,
                category: product.category,
                amount: product.amount,
                description: product.description,
                price: product.price
            });
            setShowProductModal(true);
        };

        const handleDeleteProduct = async(productId) => {
            if (window.confirm('Are you sure you want to delete this product?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Product deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting product');
                    }
                } catch (error) {
                    console.error('Error deleting product:', error);
                    alert('Error deleting product');
                }
            }
        };

        // Request Management Functions
        const updateRequestStatus = async(requestId, status, reason = '') => {
            try {
                const token = localStorage.getItem('token');

                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        status: status,
                        feedback: reason
                    })
                });

                const responseData = await response.json();

                if (response.ok) {
                    setShowRequestModal(false);
                    setSelectedRequest(null);
                    setRequestStatus('');
                    setDecisionReason('');
                    fetchDashboardData();
                    alert(`Request ${status} successfully`);
                } else {
                    alert(responseData.message || 'Error updating request');
                }
            } catch (error) {
                console.error('Error updating request:', error);
                alert('Error updating request');
            }
        };

        const handleStatusUpdate = (request, status) => {
            setSelectedRequest(request);
            setRequestStatus(status);
            setShowRequestModal(true);
        };

        const confirmStatusUpdate = () => {
            if (selectedRequest && requestStatus) {
                updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
            }
        };

        const toggleRequestSelection = (requestId) => {
            if (selectedRequests.includes(requestId)) {
                setSelectedRequests(selectedRequests.filter(id => id !== requestId));
            } else {
                setSelectedRequests([...selectedRequests, requestId]);
            }
        };

        const selectAllRequests = () => {
            if (selectedRequests.length === filteredRequests.filter(r => r.status === 'Pending').length) {
                setSelectedRequests([]);
            } else {
                setSelectedRequests(filteredRequests.filter(r => r.status === 'Pending').map(r => r.id));
            }
        };

        const handleBulkUpdate = async() => {
            if (!bulkAction || selectedRequests.length === 0) {
                alert('Please select an action and at least one request');
                return;
            }

            try {
                const token = localStorage.getItem('token');

                for (const requestId of selectedRequests) {
                    await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                        method: 'PUT',
                        headers: {
                            'Content-Type': 'application/json',
                            'Authorization': `Bearer ${token}`
                        },
                        body: JSON.stringify({
                            status: bulkAction === 'approve' ? 'Approved' : bulkAction === 'accept' ? 'Accepted' : 'Rejected'
                        })
                    });
                }

                setBulkAction('');
                setSelectedRequests([]);
                fetchDashboardData();
                alert('Bulk update completed successfully');
            } catch (error) {
                console.error('Error performing bulk update:', error);
                alert('Error performing bulk update');
            }
        };

        const getStatusBadge = (status) => {
            const statusClasses = {
                'Pending': 'bg-yellow-100 text-yellow-800',
                'Approved': 'bg-blue-100 text-blue-800',
                'Accepted': 'bg-green-100 text-green-800',
                'Rejected': 'bg-red-100 text-red-800'
            };

            return ( <
                span className = { `px-2 py-1 rounded-full text-xs font-medium ${statusClasses[status] || ''}` } > { status } <
                /span>
            );
        };

        const getLevelStatus = (level, request) => {
            const status = request[`${level}_status`];
            const admin = request[`${level}_admin_name`];
            const feedback = request[`${level}_feedback`];
            const date = request[`${level}_approved_at`];

            const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

            return ( <
                div className = "mb-4 p-3 bg-gray-50 rounded" >
                <
                h4 className = "font-semibold" > { formatLevel }
                Level < /h4> <
                p > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p > < strong > Admin: < /strong> {admin}</p > } { feedback && < p > < strong > Feedback: < /strong> {feedback}</p > } { date && < p > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
                /div>
            );
        };

        const viewRequestDetails = async(requestId) => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const requestDetails = await response.json();
                    setSelectedRequest(requestDetails);
                    setShowRequestModal(true);
                } else {
                    alert('Error fetching request details');
                }
            } catch (error) {
                console.error('Error fetching request details:', error);
                alert('Error fetching request details');
            }
        };

        // Filter products based on selected filter
        const getFilteredProducts = () => {
            switch (productFilter) {
                case 'own':
                    return myProducts;
                case 'others':
                    return otherProducts;
                case 'all':
                default:
                    return products;
            }
        };

        if (!user) {
            return <div className = "flex justify-center items-center h-screen" > Loading... < /div>;
        }

        return ( <
            div className = { `flex min-h-screen ${darkMode ? 'dark bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}` } > { /* Sidebar */ } <
            div className = { `w-64 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg p-4 flex flex-col` } >
            <
            h2 className = "text-xl font-bold mb-6" > Kebele Dashboard < /h2> <
            nav className = "flex-1" >
            <
            button className = { `w-full text-left py-2 px-4 rounded mb-2 ${activeTab === 'overview' ? 'bg-blue-500 text-white' : 'hover:bg-gray-200 dark:hover:bg-gray-700'}` }
            onClick = {
                () => setActiveTab('overview') } >
            📊Overview <
            /button> <
            button className = { `w-full text-left py-2 px-4 rounded mb-2 ${activeTab === 'farmers' ? 'bg-blue-500 text-white' : 'hover:bg-gray-200 dark:hover:bg-gray-700'}` }
            onClick = {
                () => setActiveTab('farmers') } >
            👨‍🌾Farmers <
            /button> <
            button className = { `w-full text-left py-2 px-4 rounded mb-2 ${activeTab === 'products' ? 'bg-blue-500 text-white' : 'hover:bg-gray-200 dark:hover:bg-gray-700'}` }
            onClick = {
                () => setActiveTab('products') } >
            🛒Products <
            /button> <
            button className = { `w-full text-left py-2 px-4 rounded mb-2 ${activeTab === 'requests' ? 'bg-blue-500 text-white' : 'hover:bg-gray-200 dark:hover:bg-gray-700'}` }
            onClick = {
                () => setActiveTab('requests') } >
            📋Requests <
            /button> <
            /nav> <
            div className = "mt-auto" >
            <
            button className = "w-full py-2 px-4 rounded mb-2 hover:bg-gray-200 dark:hover:bg-gray-700"
            onClick = {
                () => setDarkMode(!darkMode) } >
            { darkMode ? '☀️ Light Mode' : '🌙 Dark Mode' } <
            /button> <
            /div> <
            /div>

            { /* Main Content */ } <
            div className = "flex-1 flex flex-col" >
            <
            header className = { `${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-sm p-4 flex justify-between items-center` } >
            <
            h1 className = "text-2xl font-semibold" > Welcome, { user.fullName }(Kebele Admin) < /h1> <
            button onClick = { handleLogout }
            className = "bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded" >
            Logout <
            /button> <
            /header>

            <
            main className = "flex-1 p-6 overflow-auto" > { /* Overview Tab */ } {
                activeTab === 'overview' && ( <
                    div >
                    <
                    h2 className = "text-2xl font-bold mb-6" > System Overview < /h2> <
                    div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8" >
                    <
                    div className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h3 className = "text-2xl font-bold" > { stats.totalRequests } < /h3> <
                    p > Total Requests < /p> <
                    /div> <
                    div className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h3 className = "text-2xl font-bold" > { stats.pendingRequests } < /h3> <
                    p > Pending Requests < /p> <
                    /div> <
                    div className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h3 className = "text-2xl font-bold" > { stats.approvedRequests } < /h3> <
                    p > Approved Requests < /p> <
                    /div> <
                    div className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h3 className = "text-2xl font-bold" > { stats.acceptedRequests } < /h3> <
                    p > Accepted Requests < /p> <
                    /div> <
                    div className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h3 className = "text-2xl font-bold" > { stats.rejectedRequests } < /h3> <
                    p > Rejected Requests < /p> <
                    /div> <
                    div className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h3 className = "text-2xl font-bold" > { stats.totalFarmers } < /h3> <
                    p > Farmers < /p> <
                    /div> <
                    div className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h3 className = "text-2xl font-bold" > { stats.totalProducts } < /h3> <
                    p > Products < /p> <
                    /div> <
                    /div>

                    <
                    div className = { `p-6 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                    <
                    h2 className = "text-xl font-bold mb-4" > Quick Actions < /h2> <
                    div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" >
                    <
                    button onClick = {
                        () => setActiveTab('requests') }
                    className = "py-3 px-4 bg-blue-500 hover:bg-blue-600 text-white rounded" >
                    📋Manage Requests <
                    /button> <
                    button onClick = {
                        () => setActiveTab('farmers') }
                    className = "py-3 px-4 bg-green-500 hover:bg-green-600 text-white rounded" >
                    👨‍🌾Manage Farmers <
                    /button> <
                    button onClick = {
                        () => setActiveTab('products') }
                    className = "py-3 px-4 bg-purple-500 hover:bg-purple-600 text-white rounded" >
                    🛒Manage Products <
                    /button> <
                    button onClick = {
                        () => setShowFarmerModal(true) }
                    className = "py-3 px-4 bg-teal-500 hover:bg-teal-600 text-white rounded" >
                    ➕Register Farmer <
                    /button> <
                    button onClick = {
                        () => setShowProductModal(true) }
                    className = "py-3 px-4 bg-orange-500 hover:bg-orange-600 text-white rounded" >
                    ➕Add Product <
                    /button> <
                    /div> <
                    /div> <
                    /div>
                )
            }

            { /* Farmers Tab */ } {
                activeTab === 'farmers' && ( <
                    div >
                    <
                    div className = "flex justify-between items-center mb-6" >
                    <
                    div >
                    <
                    h2 className = "text-2xl font-bold" > Farmer Management < /h2> <
                    p > Managing farmers in your kebele: { user.kebele_name } < /p> <
                    /div> <
                    button onClick = {
                        () => setShowFarmerModal(true) }
                    className = "bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded" >
                    ➕Register New Farmer <
                    /button> <
                    /div>

                    <
                    div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                        farmers.map((farmer) => ( <
                            div key = { farmer.id }
                            className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                            <
                            div className = "mb-4" >
                            <
                            h3 className = "text-lg font-semibold" > { farmer.full_name } < /h3> <
                            p > 📞Phone: { farmer.phone_number } < /p> <
                            p > 📍Kebele: { farmer.kebele_name } < /p> <
                            p > 📅Registered: { new Date(farmer.created_at).toLocaleDateString() } < /p> <
                            /div> <
                            div className = "flex space-x-2 mb-4" >
                            <
                            button onClick = {
                                () => handleEditFarmer(farmer) }
                            className = "bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded text-sm" >
                            ✏️Edit <
                            /button> <
                            button onClick = {
                                () => handleDeleteFarmer(farmer.id) }
                            className = "bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded text-sm" >
                            🗑️Delete <
                            /button> <
                            /div> <
                            div >
                            <
                            h4 className = "font-semibold" > Recent Requests: < /h4> {
                                requests
                                    .filter(req => req.farmer_id === farmer.id)
                                    .slice(0, 3)
                                    .map(req => ( <
                                        div key = { req.id }
                                        className = "flex justify-between items-center mt-2" >
                                        <
                                        span className = "text-sm" > { req.product_name } < /span> { getStatusBadge(req.status) } <
                                        /div>
                                    ))
                            } {
                                requests.filter(req => req.farmer_id === farmer.id).length === 0 && ( <
                                    p className = "text-sm mt-2" > No requests from this farmer < /p>
                                )
                            } <
                            /div> <
                            /div>
                        ))
                    } {
                        farmers.length === 0 && ( <
                            p className = "col-span-full text-center py-8" > No farmers found in your kebele. < /p>
                        )
                    } <
                    /div> <
                    /div>
                )
            }

            { /* Products Tab */ } {
                activeTab === 'products' && ( <
                    div >
                    <
                    div className = "flex justify-between items-center mb-6" >
                    <
                    div >
                    <
                    h2 className = "text-2xl font-bold" > Product Management < /h2> <
                    p > Managing products in your kebele < /p> <
                    /div> <
                    button onClick = {
                        () => setShowProductModal(true) }
                    className = "bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded" >
                    ➕Add New Product <
                    /button> <
                    /div>

                    <
                    div className = "mb-6" >
                    <
                    div className = "flex space-x-4 mb-4" >
                    <
                    button className = { `py-2 px-4 rounded ${productFilter === 'all' ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'}` }
                    onClick = {
                        () => setProductFilter('all') } >
                    All Products <
                    /button> <
                    button className = { `py-2 px-4 rounded ${productFilter === 'own' ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'}` }
                    onClick = {
                        () => setProductFilter('own') } >
                    My Products <
                    /button> <
                    button className = { `py-2 px-4 rounded ${productFilter === 'others' ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'}` }
                    onClick = {
                        () => setProductFilter('others') } >
                    Other Admins ' Products <
                    /button> <
                    /div> <
                    /div>

                    <
                    div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                        getFilteredProducts().map((product) => ( <
                            div key = { product.id }
                            className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                            <
                            div className = "mb-4" >
                            <
                            h3 className = "text-lg font-semibold" > { product.name } < /h3> <
                            p className = "text-sm" > { product.description } < /p> <
                            p > 📦Category: { product.category } < /p> <
                            p > 💰Price: Birr { product.price } < /p> <
                            p > 📦Amount: { product.amount } < /p> {
                                productFilter !== 'own' && ( <
                                    >
                                    <
                                    p > 👤Added by: { product.created_by_name }({ product.admin_role }) < /p> <
                                    p > 📍Location: { product.region_name }
                                    / {product.zone_name} / { product.woreda_name }
                                    / {product.kebele_name}</p >
                                    <
                                    />
                                )
                            } <
                            p > 📅Created: { new Date(product.created_at).toLocaleDateString() } < /p> <
                            /div> {
                                productFilter === 'own' && ( <
                                    div className = "flex space-x-2" >
                                    <
                                    button onClick = {
                                        () => handleEditProduct(product) }
                                    className = "bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded text-sm" >
                                    ✏️Edit <
                                    /button> <
                                    button onClick = {
                                        () => handleDeleteProduct(product.id) }
                                    className = "bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded text-sm" >
                                    🗑️Delete <
                                    /button> <
                                    /div>
                                )
                            } <
                            /div>
                        ))
                    } {
                        getFilteredProducts().length === 0 && ( <
                            p className = "col-span-full text-center py-8" > {
                                productFilter === 'own' ?
                                "You haven't added any products yet." :
                                    productFilter === 'others' ?
                                    "No products from other admins." :
                                    "No products found."
                            } <
                            /p>
                        )
                    } <
                    /div> <
                    /div>
                )
            }

            { /* Requests Tab */ } {
                activeTab === 'requests' && ( <
                        div >
                        <
                        div className = "mb-6" >
                        <
                        h2 className = "text-2xl font-bold" > Request Management < /h2> <
                        p > Manage farmer requests in your kebele: { user.kebele_name } < /p> <
                        /div>

                        <
                        div className = { `p-4 rounded shadow mb-6 ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                        <
                        div className = "flex flex-wrap items-center gap-4 mb-4" >
                        <
                        div >
                        <
                        label className = "mr-2" > Filter by Status: < /label> <
                        select value = { statusFilter }
                        onChange = {
                            (e) => setStatusFilter(e.target.value) }
                        className = { `p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` } >
                        <
                        option value = "all" > All Requests < /option> <
                        option value = "pending" > Pending < /option> <
                        option value = "approved" > Approved < /option> <
                        option value = "accepted" > Accepted < /option> <
                        option value = "rejected" > Rejected < /option> <
                        /select> <
                        /div>

                        {
                            filteredRequests.filter(r => r.status === 'Pending').length > 0 && ( <
                                div className = "flex flex-wrap items-center gap-2" >
                                <
                                select value = { bulkAction }
                                onChange = {
                                    (e) => setBulkAction(e.target.value) }
                                className = { `p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` } >
                                <
                                option value = "" > Select Bulk Action < /option> <
                                option value = "approve" > Approve < /option> <
                                option value = "accept" > Accept < /option> <
                                option value = "reject" > Reject < /option> <
                                /select> <
                                button onClick = { handleBulkUpdate }
                                className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                                Apply to Selected({ selectedRequests.length }) <
                                /button> <
                                button onClick = { selectAllRequests }
                                className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                                {
                                    selectedRequests.length === filteredRequests.filter(r => r.status === 'Pending').length ?
                                    'Deselect All' : 'Select All Pending'
                                } <
                                /button> <
                                /div>
                            )
                        } <
                        /div> <
                        /div>

                        <
                        div className = "grid grid-cols-1 gap-4" > {
                            filteredRequests.map((request) => ( <
                                    div key = { request.id }
                                    className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                                    <
                                    div className = "flex flex-col md:flex-row justify-between" >
                                    <
                                    div className = "flex-1" >
                                    <
                                    h3 className = "text-lg font-semibold" > { request.product_name } < /h3> <
                                    p > 👨‍🌾Farmer: { request.farmer_name }({ request.farmer_phone }) < /p> <
                                    p > 📦Quantity: { request.quantity } < /p> {
                                        request.note && < p > 📝Note: { request.note } < /p>} <
                                            p > 📦Category: { request.category } < /p> <
                                            p > 💵Amount: { request.product_amount } < /p> <
                                            p > 💰Price: Birr { request.price } < /p> <
                                            p > 📅Date: { new Date(request.created_at).toLocaleDateString() } < /p> <
                                            div className = "mt-2" > { getStatusBadge(request.status) } <
                                            /div> {
                                                request.handled_by_admin && ( <
                                                    p className = "mt-2" > 👤Handled by: { request.handled_by_admin } < /p>
                                                )
                                            } <
                                            button
                                        onClick = {
                                            () => viewRequestDetails(request.id) }
                                        className = "mt-3 bg-gray-500 hover:bg-gray-600 text-white py-1 px-3 rounded text-sm" >
                                            View Details <
                                            /button> <
                                            /div> <
                                            div className = "mt-4 md:mt-0 md:ml-4 flex flex-col space-y-2" > {
                                                request.status === 'Pending' && ( <
                                                    >
                                                    <
                                                    div className = "flex items-center" >
                                                    <
                                                    input type = "checkbox"
                                                    checked = { selectedRequests.includes(request.id) }
                                                    onChange = {
                                                        () => toggleRequestSelection(request.id) }
                                                    className = "mr-2" /
                                                    >
                                                    <
                                                    label > Select < /label> <
                                                    /div> <
                                                    div className = "flex flex-col space-y-2" >
                                                    <
                                                    button onClick = {
                                                        () => handleStatusUpdate(request, 'Approved') }
                                                    className = "bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded text-sm" >
                                                    Approve <
                                                    /button> <
                                                    button onClick = {
                                                        () => handleStatusUpdate(request, 'Accepted') }
                                                    className = "bg-green-500 hover:bg-green-600 text-white py-1 px-3 rounded text-sm" >
                                                    Accept <
                                                    /button> <
                                                    button onClick = {
                                                        () => handleStatusUpdate(request, 'Rejected') }
                                                    className = "bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded text-sm" >
                                                    Reject <
                                                    /button> <
                                                    /div> <
                                                    />
                                                )
                                            } {
                                                (request.status === 'Accepted' || request.status === 'Approved' || request.status === 'Rejected') && ( <
                                                    p className = "text-sm italic" > Processed < /p>
                                                )
                                            } <
                                            /div> <
                                            /div> <
                                            /div>
                                    ))
                            } {
                                filteredRequests.length === 0 && ( <
                                    p className = "text-center py-8" > No requests found in your kebele. < /p>
                                )
                            } <
                            /div>

                            { /* Status Update Modal */ } {
                                showRequestModal && selectedRequest && ( <
                                    div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                                    <
                                    div className = { `w-full max-w-md p-6 rounded shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                                    <
                                    h3 className = "text-xl font-bold mb-4" > Update Request Status < /h3> <
                                    div className = "mb-4" >
                                    <
                                    h4 className = "font-semibold" > Request Details < /h4> <
                                    p > Product: { selectedRequest.product_name } < /p> <
                                    p > Farmer: { selectedRequest.farmer_name } < /p> <
                                    p > Quantity: { selectedRequest.quantity } < /p> <
                                    p > Note: { selectedRequest.note || 'None' } < /p> <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block mb-2" > Status: < /label> <
                                    select value = { requestStatus }
                                    onChange = {
                                        (e) => setRequestStatus(e.target.value) }
                                    className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` } >
                                    <
                                    option value = "" > Select Status < /option> <
                                    option value = "Approved" > Approve < /option> <
                                    option value = "Accepted" > Accept < /option> <
                                    option value = "Rejected" > Reject < /option> <
                                    /select> <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block mb-2" > Reason / Feedback: < /label> <
                                    textarea value = { decisionReason }
                                    onChange = {
                                        (e) => setDecisionReason(e.target.value) }
                                    className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                                    rows = "3"
                                    placeholder = "Enter reason for your decision" >
                                    < /textarea> <
                                    /div> <
                                    div className = "flex justify-end space-x-2" >
                                    <
                                    button onClick = {
                                        () => {
                                            setShowRequestModal(false);
                                            setSelectedRequest(null);
                                            setRequestStatus('');
                                            setDecisionReason('');
                                        }
                                    }
                                    className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                                    Cancel <
                                    /button> <
                                    button onClick = { confirmStatusUpdate }
                                    className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
                                    disabled = {!requestStatus } >
                                    Update Status <
                                    /button> <
                                    /div> <
                                    /div> <
                                    /div>
                                )
                            } <
                            /div>
                        )
                    } <
                    /main> <
                    /div>

                { /* Farmer Modal */ } {
                    showFarmerModal && ( <
                        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                        <
                        div className = { `w-full max-w-md p-6 rounded shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                        <
                        h3 className = "text-xl font-bold mb-4" > { editingFarmer ? 'Edit Farmer' : 'Register New Farmer' } < /h3> <
                        form onSubmit = { handleFarmerSubmit } >
                        <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Full Name: < /label> <
                        input type = "text"
                        value = { farmerForm.fullName }
                        onChange = {
                            (e) => setFarmerForm({...farmerForm, fullName: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required /
                        >
                        <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Phone Number: < /label> <
                        input type = "tel"
                        value = { farmerForm.phoneNumber }
                        onChange = {
                            (e) => setFarmerForm({...farmerForm, phoneNumber: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required /
                        >
                        <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Password: < /label> <
                        input type = "password"
                        value = { farmerForm.password }
                        onChange = {
                            (e) => setFarmerForm({...farmerForm, password: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required = {!editingFarmer }
                        /> <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Confirm Password: < /label> <
                        input type = "password"
                        value = { farmerForm.confirmPassword }
                        onChange = {
                            (e) => setFarmerForm({...farmerForm, confirmPassword: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required = {!editingFarmer }
                        /> <
                        /div> <
                        div className = "flex justify-end space-x-2" >
                        <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowFarmerModal(false);
                                setEditingFarmer(null);
                                setFarmerForm({ fullName: '', phoneNumber: '', password: '', confirmPassword: '' });
                            }
                        }
                        className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                        Cancel <
                        /button> <
                        button type = "submit"
                        className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                        { editingFarmer ? 'Update Farmer' : 'Register Farmer' } <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                }

                { /* Product Modal */ } {
                    showProductModal && ( <
                        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                        <
                        div className = { `w-full max-w-md p-6 rounded shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                        <
                        h3 className = "text-xl font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add New Product' } < /h3> <
                        form onSubmit = { handleProductSubmit } >
                        <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Product Name: < /label> <
                        input type = "text"
                        value = { productForm.name }
                        onChange = {
                            (e) => setProductForm({...productForm, name: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required /
                        >
                        <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Category: < /label> <
                        input type = "text"
                        value = { productForm.category }
                        onChange = {
                            (e) => setProductForm({...productForm, category: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required /
                        >
                        <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Amount: < /label> <
                        input type = "number"
                        value = { productForm.amount }
                        onChange = {
                            (e) => setProductForm({...productForm, amount: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required /
                        >
                        <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Price: < /label> <
                        input type = "number"
                        step = "0.01"
                        value = { productForm.price }
                        onChange = {
                            (e) => setProductForm({...productForm, price: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        required /
                        >
                        <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block mb-2" > Description: < /label> <
                        textarea value = { productForm.description }
                        onChange = {
                            (e) => setProductForm({...productForm, description: e.target.value }) }
                        className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white' : 'bg-white border'}` }
                        rows = "3"
                        required >
                        < /textarea> <
                        /div> <
                        div className = "flex justify-end space-x-2" >
                        <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowProductModal(false);
                                setEditingProduct(null);
                                setProductForm({ name: '', category: '', amount: '', description: '', price: '' });
                            }
                        }
                        className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                        Cancel <
                        /button> <
                        button type = "submit"
                        className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                        { editingProduct ? 'Update Product' : 'Add Product' } <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                } <
                /div>
            );
        };

        export default KebeleDashboard;
