import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, createUserWithEmailAndPassword, signInAnonymously } from 'firebase/auth';
import { getFirestore, collection, doc, setDoc, getDocs, getDoc } from 'firebase/firestore';

// Define global variables provided by the environment, with fallbacks for local development
const appId = typeof window !== 'undefined' && typeof window.__app_id !== 'undefined' ? window.__app_id : 'default-app-id';
const firebaseConfig = typeof window !== 'undefined' && typeof window.__firebase_config !== 'undefined' ? JSON.parse(window.__firebase_config) : {};
const initialAuthToken = typeof window !== 'undefined' && typeof window.__initial_auth_token !== 'undefined' ? window.__initial_auth_token : undefined;

// --- Components for different views ---

// Updated Dashboard component with the new button
const Dashboard = ({ onAddAdminClick, userRole }) => ( <
    div className = "p-8 flex-1" >
    <
    div className = "max-w-4xl mx-auto" >
    <
    h2 className = "text-3xl font-bold mb-4" > Federal Admin Dashboard < /h2> <
    p className = "text-lg text-gray-700 dark:text-gray-300 mb-6" >
    Welcome to the dashboard.You can see a summary of all requests here. <
    /p>

    { /* The new button for adding a regional admin */ } {
        userRole === 'Federal Admin' && ( <
            button onClick = { onAddAdminClick }
            className = "mt-4 px-6 py-3 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition duration-300 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" >
            Add Regional Admin <
            /button>
        )
    }

    { /* Placeholder for other dashboard content */ } <
    div className = "mt-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" >
    <
    div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
    <
    h3 className = "text-xl font-semibold mb-2" > Total Requests < /h3> <
    p className = "text-4xl font-bold text-gray-900 dark:text-white" > 1, 250 < /p> <
    /div> <
    div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
    <
    h3 className = "text-xl font-semibold mb-2" > Pending Approvals < /h3> <
    p className = "text-4xl font-bold text-yellow-500" > 25 < /p> <
    /div> <
    div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
    <
    h3 className = "text-xl font-semibold mb-2" > Approved Requests < /h3> <
    p className = "text-4xl font-bold text-green-500" > 1, 012 < /p> <
    /div> <
    /div> <
    /div> <
    /div>
);

// Placeholder for other admin views
const ProductRequests = () => ( <
    div className = "p-8" >
    <
    h2 className = "text-2xl font-bold mb-4" > All Product Requests < /h2> <
    p > This is where you would view all product requests across all levels. < /p> <
    /div>
);

const Approvals = () => ( <
    div className = "p-8" >
    <
    h2 className = "text-2xl font-bold mb-4" > Approvals < /h2> <
    p > This is where you would manage pending approvals. < /p> <
    /div>
);

// Component to create a new Regional Admin
const AddRegionalAdminForm = ({ db, auth, appId, regions }) => {
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [selectedRegion, setSelectedRegion] = useState(regions[0] || '');
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);

    // Handle form submission to create the new admin
    const handleCreateAdmin = async(e) => {
        e.preventDefault();
        setLoading(true);
        setMessage('');

        try {
            // Step 1: Create user in Firebase Authentication
            // This automatically handles the password hashing and security
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const newUserId = userCredential.user.uid;

            // Step 2: Store user's profile and role in Firestore
            // Using a public collection to be accessible by other parts of the system
            const userDocRef = doc(db, `artifacts/${appId}/public/data/admins`, newUserId);
            await setDoc(userDocRef, {
                user_id: newUserId,
                full_name: fullName,
                role: 'Region Admin',
                location_id: selectedRegion,
            });

            setMessage(`Success! A new Regional Admin '${fullName}' has been created for region '${selectedRegion}'. Their user ID is ${newUserId}.`);
            // Clear form fields
            setFullName('');
            setEmail('');
            setPassword('');
            setSelectedRegion(regions[0] || '');

        } catch (error) {
            console.error("Error creating admin:", error);
            let errorMessage = "An unknown error occurred. Please try again.";
            if (error.code === 'auth/email-already-in-use') {
                errorMessage = 'This email is already in use. Please use a different one.';
            } else if (error.code === 'auth/weak-password') {
                errorMessage = 'The password is too weak. Please use a stronger password.';
            }
            setMessage(`Error: ${errorMessage}`);
        } finally {
            setLoading(false);
        }
    };

    return ( <
        div className = "p-8 bg-gray-50 dark:bg-gray-800 flex-1" >
        <
        div className = "max-w-2xl mx-auto bg-white dark:bg-gray-700 p-8 rounded-2xl shadow-lg" >
        <
        h2 className = "text-3xl font-bold mb-6 text-center text-gray-800 dark:text-gray-100" > Create Regional Admin < /h2> <
        p className = "mb-6 text-sm text-center text-gray-600 dark:text-gray-300" >
        As a Federal Admin, you can create new Regional Admin accounts. <
        /p>

        <
        form onSubmit = { handleCreateAdmin }
        className = "space-y-6" >
        <
        div >
        <
        label htmlFor = "fullName"
        className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
        Full Name <
        /label> <
        input type = "text"
        id = "fullName"
        value = { fullName }
        onChange = {
            (e) => setFullName(e.target.value) }
        className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
        required /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "email"
        className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
        Email Address(User ID) <
        /label> <
        input type = "email"
        id = "email"
        value = { email }
        onChange = {
            (e) => setEmail(e.target.value) }
        className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
        required /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "password"
        className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
        Password <
        /label> <
        input type = "password"
        id = "password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value) }
        className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
        required /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "region"
        className = "block text-sm font-medium text-gray-700 dark:text-gray-200" >
        Assign Region <
        /label> <
        select id = "region"
        value = { selectedRegion }
        onChange = {
            (e) => setSelectedRegion(e.target.value) }
        className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-600 dark:border-gray-500 dark:text-white p-3"
        required >
        {
            regions.map((region) => ( <
                option key = { region }
                value = { region } > { region } <
                /option>
            ))
        } <
        /select> <
        /div>

        <
        button type = "submit"
        disabled = { loading || !db || !auth }
        className = "w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 transition duration-150 ease-in-out" >
        { loading ? 'Creating...' : 'Create Regional Admin' } <
        /button> <
        /form>

        {
            message && ( <
                div className = { `mt-6 text-center text-sm font-medium p-4 rounded-md ${
            message.startsWith('Error') ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'
          }` } > { message } <
                /div>
            )
        } <
        /div> <
        /div>
    );
};

// --- Main App Component ---
const App = () => {
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);
    const [userId, setUserId] = useState(null);
    const [userRole, setUserRole] = useState(null);
    const [regions, setRegions] = useState([]);
    const [currentView, setCurrentView] = useState('dashboard');
    const [loading, setLoading] = useState(true);

    // Initialize Firebase and set up auth listener
    useEffect(() => {
        const initFirebase = async() => {
            try {
                const app = initializeApp(firebaseConfig);
                const firestoreDb = getFirestore(app);
                const firebaseAuth = getAuth(app);
                setDb(firestoreDb);
                setAuth(firebaseAuth);

                onAuthStateChanged(firebaseAuth, async(user) => {
                    if (user) {
                        setUserId(user.uid);
                        await fetchAndSetUserRole(firestoreDb, user.uid);
                        await fetchRegions(firestoreDb);
                    } else {
                        // Sign in with the custom token or anonymously
                        if (initialAuthToken) {
                            await signInWithCustomToken(firebaseAuth, initialAuthToken);
                        } else {
                            // Sign in anonymously if no token is available
                            await signInAnonymously(firebaseAuth);
                        }
                    }
                    setLoading(false);
                });

            } catch (error) {
                console.error("Firebase initialization or auth error:", error);
                setLoading(false);
            }
        };
        initFirebase();
    }, []);

    // Fetch and set the user's role from Firestore
    const fetchAndSetUserRole = async(firestoreDb, uid) => {
        try {
            const userDocRef = doc(firestoreDb, `artifacts/${appId}/public/data/admins`, uid);
            const docSnap = await getDoc(userDocRef);
            if (docSnap.exists()) {
                const userData = docSnap.data();
                setUserRole(userData.role);
            } else {
                // Assume Federal Admin for this demo if not found in admins table
                // In a real app, you would have a more robust way to determine this
                setUserRole('Federal Admin');
            }
        } catch (error) {
            console.error("Error fetching user role:", error);
        }
    };

    // Fetch regions from Firestore
    const fetchRegions = async(firestoreDb) => {
        try {
            const collectionRef = collection(firestoreDb, `artifacts/${appId}/public/data/locations`);
            const querySnapshot = await getDocs(collectionRef);
            const fetchedRegions = querySnapshot.docs.map(doc => doc.data().region);
            setRegions(fetchedRegions);
        } catch (error) {
            console.error("Error fetching regions:", error);
        }
    };

    const renderContent = () => {
        if (loading) {
            return <div className = "p-8 text-center text-gray-500" > Loading... < /div>;
        }
        if (!userId || userRole !== 'Federal Admin') {
            return ( <
                div className = "p-8 text-center text-red-500" >
                Access Denied.You must be logged in as a Federal Admin to use this application. <
                /div>
            );
        }

        switch (currentView) {
            case 'dashboard':
                return <Dashboard onAddAdminClick = {
                    () => setCurrentView('masterData') }
                userRole = { userRole }
                />;
            case 'productRequests':
                return <ProductRequests / > ;
            case 'approvals':
                return <Approvals / > ;
            case 'masterData':
                return ( <
                    div className = "p-8" >
                    <
                    h2 className = "text-2xl font-bold mb-4" > Master Data < /h2> <
                    p className = "mb-4" > Here you can manage products, locations, and users. < /p> <
                    AddRegionalAdminForm db = { db }
                    auth = { auth }
                    appId = { appId }
                    regions = { regions }
                    /> <
                    /div>
                );
            default:
                return <Dashboard onAddAdminClick = {
                    () => setCurrentView('masterData') }
                userRole = { userRole }
                />;
        }
    };

    return ( <
        div className = "min-h-screen flex bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white" > { /* Sidebar Navigation */ } <
        aside className = "w-64 bg-white dark:bg-gray-800 shadow-lg p-6 flex flex-col justify-between" >
        <
        nav className = "space-y-2" >
        <
        div className = "p-4 text-center" >
        <
        h1 className = "text-xl font-bold" > Admin Panel < /h1> <
        /div> <
        button onClick = {
            () => setCurrentView('dashboard') }
        className = { `w-full text-left p-3 rounded-xl transition-all duration-200 ${currentView === 'dashboard' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'}` } >
        Dashboard <
        /button> <
        button onClick = {
            () => setCurrentView('productRequests') }
        className = { `w-full text-left p-3 rounded-xl transition-all duration-200 ${currentView === 'productRequests' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'}` } >
        Product Requests <
        /button> <
        button onClick = {
            () => setCurrentView('approvals') }
        className = { `w-full text-left p-3 rounded-xl transition-all duration-200 ${currentView === 'approvals' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'}` } >
        Approvals <
        /button> <
        button onClick = {
            () => setCurrentView('masterData') }
        className = { `w-full text-left p-3 rounded-xl transition-all duration-200 ${currentView === 'masterData' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'}` } >
        Master Data <
        /button> <
        /nav> <
        /aside>

        { /* Main Content Area */ } <
        main className = "flex-1 p-8 overflow-y-auto" > { renderContent() } <
        /main> <
        /div>
    );
};

export default App;
