import React, { useEffect, useState } from 'react';
import api from '../api'; // ✅ for dashboards

export default function FarmerDashboard() {
    const [my, setMy] = useState([]);
    const [products, setProducts] = useState([]);
    const [form, setForm] = useState({ product_id: '', quantity: 1, note: '' });
    const [loading, setLoading] = useState(true);

    const load = async() => {
        setLoading(true);
        try {
            const [requests, prods] = await Promise.all([
                api('/api/requests/my'),
                api('/api/products') // you likely have GET /api/products; if not, list from your existing endpoint
            ]);
            setMy(requests);
            setProducts(prods);
        } catch (e) { console.error(e); }
        setLoading(false);
    };

    useEffect(() => { load(); }, []);

    const submit = async(e) => {
        e.preventDefault();
        await api('/api/requests', { method: 'POST', body: form });
        setForm({ product_id: '', quantity: 1, note: '' });
        load();
    };

    if (loading) return <div > Loading... < /div>;
    return ( <
        div >
        <
        h2 > Farmer Dashboard < /h2>

        <
        form onSubmit = { submit }
        style = {
            { marginBottom: 16 }
        } >
        <
        select value = { form.product_id }
        onChange = { e => setForm({...form, product_id: Number(e.target.value) }) }
        required >
        <
        option value = "" > Select product < /option> {
        products.map(p => < option key = { p.id }
            value = { p.id } > { p.name } < /option>)} < /
            select > <
            input type = "number"
            min = "1"
            value = { form.quantity }
            onChange = { e => setForm({...form, quantity: Number(e.target.value) }) }
            /> <
            input type = "text"
            placeholder = "Note (optional)"
            value = { form.note }
            onChange = { e => setForm({...form, note: e.target.value }) }
            /> <
            button type = "submit" > Request < /button> < /
            form >

            <
            table >
            <
            thead > < tr >
            <
            th > # < /th><th>Product</th > < th > Qty < /th><th>Status</th > < th > Decided By < /th><th>At</th >
            <
            /tr></thead >
            <
            tbody > {
                my.map(r => ( <
                    tr key = { r.id } >
                    <
                    td > { r.id } < /td> <
                    td > { r.product_name } < /td> <
                    td > { r.quantity } < /td> <
                    td > { r.status } < /td> <
                    td > { r.decided_by_admin_role || '-' } < /td> <
                    td > { r.decided_at ? new Date(r.decided_at).toLocaleString() : '-' } < /td> < /
                    tr >
                ))
            } <
            /tbody> < /
            table > <
            /div>
        );
    }
