import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const RegionDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const [regionInfo, setRegionInfo] = useState(null);
        const navigate = useNavigate();

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [filteredRequests, setFilteredRequests] = useState([]);
        const [statusFilter, setStatusFilter] = useState('all');
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [requestStatus, setRequestStatus] = useState('');
        const [decisionReason, setDecisionReason] = useState('');

        // Product Management State
        const [products, setProducts] = useState([]);
        const [showProductForm, setShowProductForm] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            amount: '',
            price: '',
            description: ''
        });
        const [productError, setProductError] = useState('');

        // Zone Management State
        const [zones, setZones] = useState([]);
        const [showZoneForm, setShowZoneForm] = useState(false);
        const [editingZone, setEditingZone] = useState(null);
        const [zoneForm, setZoneForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            zoneName: ''
        });
        const [zoneError, setZoneError] = useState('');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            acceptedRequests: 0,
            totalProducts: 0,
            totalZones: 0
        });

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Region' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            setRegionInfo({
                name: userData.region_name,
                code: userData.region_name.substring(0, 3).toUpperCase()
            });
            fetchDashboardData();
        }, [navigate]);

        useEffect(() => {
            if (requests.length > 0) {
                filterRequests();
            }
        }, [statusFilter, requests]);

        const filterRequests = () => {
            let filtered = [...requests];

            // Filter by status
            if (statusFilter !== 'all') {
                filtered = filtered.filter(req => req.status === statusFilter);
            }

            setFilteredRequests(filtered);
        };

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Fetch requests with detailed status
                const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setRequests(requestsData);
                    filterRequests();

                    // Calculate stats from requests data
                    const totalRequests = requestsData.length;
                    const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                    const approvedRequests = requestsData.filter(req => req.status === 'Approved').length;
                    const acceptedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                    const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                    setStats(prev => ({
                        ...prev,
                        totalRequests,
                        pendingRequests,
                        approvedRequests,
                        acceptedRequests,
                        rejectedRequests
                    }));
                }

                // Fetch products
                const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Fetch zones in this region
                const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (adminsResponse.ok) {
                    const adminsData = await adminsResponse.json();
                    // Filter to only show zones in this region
                    const regionZones = adminsData.filter(admin =>
                        admin.type === 'admin' && admin.role === 'Zone' && admin.region_name === user.region_name
                    );
                    setZones(regionZones);
                    setStats(prev => ({...prev, totalZones: regionZones.length }));
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Check if Region admin can act on this request
        const canRegionAdminAct = (request) => {
            // Region admin can only act on requests that have been approved by Zone admin
            return request.zone_status === 'Approved' && request.region_status === 'Pending';
        };

        // Check if admin can delete this request (only rejected ones)
        const canDeleteRequest = (request) => {
            return request.status === 'Rejected' && request.region_status === 'Rejected';
        };

        // Update request status at region level
        const updateRequestStatus = async(requestId, status, reason = '') => {
            try {
                const token = localStorage.getItem('token');

                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        status: status,
                        feedback: reason
                    })
                });

                const responseData = await response.json();

                if (response.ok) {
                    setShowRequestModal(false);
                    setSelectedRequest(null);
                    setRequestStatus('');
                    setDecisionReason('');
                    fetchDashboardData();
                    alert(`Request ${status} successfully`);
                } else {
                    alert(responseData.message || 'Error updating request');
                }
            } catch (error) {
                console.error('Error updating request:', error);
                alert('Error updating request');
            }
        };

        const handleStatusUpdate = (request, status) => {
            setSelectedRequest(request);
            setRequestStatus(status);
            setShowRequestModal(true);
        };

        const confirmStatusUpdate = () => {
            if (selectedRequest && requestStatus) {
                updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
            }
        };

        const handleDeleteRequest = async(requestId) => {
            if (window.confirm('Are you sure you want to delete this request?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/admins/deleterequest/${requestId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Request deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting request');
                    }
                } catch (error) {
                    console.error('Error deleting request:', error);
                    alert('Error deleting request');
                }
            }
        };

        const viewRequestDetails = async(requestId) => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const requestDetails = await response.json();
                    setSelectedRequest(requestDetails);
                    setShowRequestModal(true);
                } else {
                    alert('Error fetching request details');
                }
            } catch (error) {
                console.error('Error fetching request details:', error);
                alert('Error fetching request details');
            }
        };

        const getStatusBadge = (status) => {
            const statusClasses = {
                'Pending': 'bg-yellow-100 text-yellow-800',
                'Approved': 'bg-blue-100 text-blue-800',
                'Accepted': 'bg-green-100 text-green-800',
                'Rejected': 'bg-red-100 text-red-800'
            };

            return ( <
                span className = { `px-2 py-1 rounded-full text-xs font-medium ${statusClasses[status] || ''}` } > { status } <
                /span>
            );
        };

        const getLevelStatus = (level, request) => {
            const status = request[`${level}_status`];
            const admin = request[`${level}_admin_name`];
            const feedback = request[`${level}_feedback`];
            const date = request[`${level}_approved_at`];

            const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

            return ( <
                div className = "mb-4 p-3 bg-gray-50 rounded" >
                <
                h4 className = "font-semibold" > { formatLevel }
                Level < /h4> <
                p > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p > < strong > Admin: < /strong> {admin}</p > } { feedback && < p > < strong > Feedback: < /strong> {feedback}</p > } { date && < p > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
                /div>
            );
        };

        // Product Management Functions
        const handleProductSubmit = async(e) => {
            e.preventDefault();
            setProductError('');

            // Validate product amount and price
            if (parseInt(productForm.amount) < 1) {
                setProductError('Product amount must be at least 1');
                return;
            }

            if (parseFloat(productForm.price) <= 0) {
                setProductError('Product price must be greater than 0');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const url = editingProduct ?
                    `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                    'http://localhost:5000/api/admins/addproduct';

                const method = editingProduct ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(productForm)
                });

                if (response.ok) {
                    setShowProductForm(false);
                    setEditingProduct(null);
                    setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
                    fetchDashboardData();
                    alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
                } else {
                    const errorData = await response.json();
                    setProductError(errorData.message || 'Error saving product');
                }
            } catch (error) {
                console.error('Error saving product:', error);
                setProductError('Error saving product');
            }
        };

        const deleteProduct = async(productId) => {
            if (!window.confirm('Are you sure you want to delete this product?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Product deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting product');
                }
            } catch (error) {
                console.error('Error deleting product:', error);
                alert('Error deleting product');
            }
        };

        const editProduct = (product) => {
            // Check if product belongs to current admin
            if (product.created_by_admin_id !== user.id) {
                alert('You can only edit your own products');
                return;
            }

            setEditingProduct(product);
            setProductForm({
                name: product.name || '',
                category: product.category || '',
                amount: product.amount || '',
                price: product.price || '',
                description: product.description || ''
            });
            setShowProductForm(true);
        };

        // Zone Management Functions
        const handleZoneSubmit = async(e) => {
            e.preventDefault();
            setZoneError('');

            if (zoneForm.password !== zoneForm.confirmPassword && !editingZone) {
                setZoneError('Passwords do not match');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                let url, method;

                if (editingZone) {
                    url = `http://localhost:5000/api/admins/edit/${editingZone.id}`;
                    method = 'PUT';
                } else {
                    url = 'http://localhost:5000/api/admins/register';
                    method = 'POST';
                }

                // Prepare data for submission
                const submitData = {
                    fullName: zoneForm.fullName,
                    phoneNumber: zoneForm.phoneNumber,
                    password: zoneForm.password,
                    role: 'Zone',
                    region_name: user.region_name,
                    zone_name: zoneForm.zoneName
                };

                // Don't send password if editing and password is empty
                if (editingZone && !submitData.password) {
                    delete submitData.password;
                }

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(submitData)
                });

                const data = await response.json();

                if (response.ok) {
                    setShowZoneForm(false);
                    setEditingZone(null);
                    setZoneForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        zoneName: ''
                    });
                    fetchDashboardData();
                    alert(editingZone ? 'Zone admin updated successfully' : 'Zone admin registered successfully');
                } else {
                    setZoneError(data.message || 'Error processing zone');
                }
            } catch (error) {
                console.error('Error processing zone:', error);
                setZoneError('Server error occurred');
            }
        };

        const deleteZone = async(zoneId) => {
            if (!window.confirm('Are you sure you want to delete this zone admin?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/delete/${zoneId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Zone admin deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting zone admin');
                }
            } catch (error) {
                console.error('Error deleting zone:', error);
                alert('Error deleting zone admin');
            }
        };

        const editZone = (zone) => {
            setEditingZone(zone);
            setZoneForm({
                fullName: zone.full_name || '',
                phoneNumber: zone.phone_number || '',
                password: '',
                confirmPassword: '',
                zoneName: zone.zone_name || ''
            });
            setShowZoneForm(true);
        };

        if (!user || !regionInfo) {
            return <div className = "flex justify-center items-center h-screen" > Loading... < /div>;
        }

        return ( <
                div className = "flex h-screen bg-gray-100" > { /* Sidebar */ } <
                div className = "w-64 bg-blue-800 text-white" >
                <
                div className = "p-4" >
                <
                h2 className = "text-xl font-bold" > Region Dashboard < /h2> <
                div className = "region-info mt-4" >
                <
                h3 className = "text-lg font-semibold" > { regionInfo.name } < /h3> <
                p className = "text-sm" > Region Code: { regionInfo.code } < /p> <
                /div> <
                /div> <
                nav className = "mt-6" >
                <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'overview' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('overview') } >
                Overview <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'requests' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('requests') } >
                Requests <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'products' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('products') } >
                Products <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'zones' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('zones') } >
                Zones <
                /button> <
                /nav> <
                /div>

                { /* Main Content */ } <
                div className = "flex-1 overflow-auto" >
                <
                header className = "bg-white shadow p-4 flex justify-between items-center" >
                <
                div >
                <
                h1 className = "text-xl font-bold" > Welcome, { user.fullName }({ regionInfo.name }
                    Admin) < /h1> <
                p className = "text-gray-600" > Region: { user.region_name } < /p> <
                /div> <
                button onClick = { handleLogout }
                className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded" >
                Logout <
                /button> <
                /header>

                <
                main className = "p-4" > { /* Overview Tab */ } {
                    activeTab === 'overview' && ( <
                        div className = "overview-tab" >
                        <
                        h2 className = "text-2xl font-bold mb-4" > Region Overview < /h2> <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6" >
                        <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.totalRequests } < /h3> <
                        p > Total Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.pendingRequests } < /h3> <
                        p > Pending Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.approvedRequests } < /h3> <
                        p > Approved Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.acceptedRequests } < /h3> <
                        p > Accepted Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.rejectedRequests } < /h3> <
                        p > Rejected Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.totalProducts } < /h3> <
                        p > Available Products < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.totalZones } < /h3> <
                        p > Registered Zone Admins < /p> <
                        /div> <
                        /div>

                        <
                        div className = "recent-activity bg-white p-4 rounded shadow" >
                        <
                        h2 className = "text-xl font-bold mb-4" > Recent Activity < /h2> <
                        div className = "activity-list" > {
                            requests.slice(0, 5).map(request => ( <
                                div key = { request.id }
                                className = "flex justify-between items-center py-2 border-b" >
                                <
                                div >
                                <
                                span className = "font-medium" > Request# { request.id } < /span> <
                                span className = "text-gray-600 ml-2" > { request.product_name } - { request.quantity }
                                units < /span> <
                                /div> { getStatusBadge(request.status) } <
                                /div>
                            ))
                        } <
                        /div> <
                        /div> <
                        /div>
                    )
                }

                { /* Requests Tab */ } {
                    activeTab === 'requests' && ( <
                        div className = "requests-tab" >
                        <
                        div className = "mb-4" >
                        <
                        h2 className = "text-2xl font-bold mb-2" > Request Management < /h2> <
                        p className = "text-gray-600" > Manage farmer requests in your region: { user.region_name } < /p>

                        <
                        div className = "flex items-center space-x-4 mt-4" >
                        <
                        div className = "flex items-center space-x-2" >
                        <
                        label className = "font-medium" > Filter by Status: < /label> <
                        select value = { statusFilter }
                        onChange = {
                            (e) => setStatusFilter(e.target.value) }
                        className = "border rounded p-1" >
                        <
                        option value = "all" > All Requests < /option> <
                        option value = "Pending" > Pending < /option> <
                        option value = "Approved" > Approved < /option> <
                        option value = "Accepted" > Accepted < /option> <
                        option value = "Rejected" > Rejected < /option> <
                        /select> <
                        /div> <
                        /div> <
                        /div>

                        <
                        div className = "grid grid-cols-1 gap-4" > {
                            filteredRequests.map((request) => ( <
                                div key = { request.id }
                                className = "bg-white p-4 rounded shadow" >
                                <
                                div className = "flex justify-between items-start" >
                                <
                                div className = "flex-1" >
                                <
                                h3 className = "text-lg font-semibold" > { request.product_name } < /h3> <
                                p className = "text-gray-600" > Farmer: { request.farmer_name } < /p> <
                                p className = "text-gray-600" > Location: { request.region_name }
                                / {request.zone_name} / { request.woreda_name }
                                / {request.kebele_name}</p >
                                <
                                p className = "text-gray-600" > Requested: { new Date(request.created_at).toLocaleDateString() } < /p> <
                                p className = "text-gray-600" > Amount: { request.amount } < /p> <
                                p className = "text-gray-600" > Reason: { request.reason } < /p> <
                                /div> <
                                div className = "flex flex-col items-end" >
                                <
                                div className = "mb-2" > { getStatusBadge(request.status) } <
                                /div> <
                                div className = "flex space-x-2" >
                                <
                                button onClick = {
                                    () => viewRequestDetails(request.id) }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm" >
                                View Details <
                                /button> {
                                    canRegionAdminAct(request) && ( <
                                        >
                                        <
                                        button onClick = {
                                            () => handleStatusUpdate(request, 'Approved') }
                                        className = "bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded text-sm" >
                                        Approve <
                                        /button> <
                                        button onClick = {
                                            () => handleStatusUpdate(request, 'Rejected') }
                                        className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm" >
                                        Reject <
                                        /button> <
                                        />
                                    )
                                } {
                                    canDeleteRequest(request) && ( <
                                        button onClick = {
                                            () => handleDeleteRequest(request.id) }
                                        className = "bg-gray-500 hover:bg-gray-600 text-white px-3 py-1 rounded text-sm" >
                                        Delete <
                                        /button>
                                    )
                                } <
                                /div> <
                                /div> <
                                /div> <
                                /div>
                            ))
                        } {
                            filteredRequests.length === 0 && ( <
                                p className = "text-gray-600" > No requests found with the selected filters. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Products Tab */ } {
                    activeTab === 'products' && ( <
                        div className = "products-tab" >
                        <
                        div className = "flex justify-between items-center mb-4" >
                        <
                        h2 className = "text-2xl font-bold" > Product Management < /h2> <
                        button onClick = {
                            () => setShowProductForm(true) }
                        className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
                        Add Product <
                        /button> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            products.map((product) => ( <
                                div key = { product.id }
                                className = "bg-white p-4 rounded shadow" >
                                <
                                h3 className = "text-lg font-semibold" > { product.name } < /h3> <
                                p className = "text-gray-600" > { product.description } < /p> <
                                p className = "text-gray-600" > Category: { product.category } < /p> <
                                p className = "text-gray-600" > Amount: { product.amount } < /p> <
                                p className = "text-gray-600" > Price: { product.price } < /p> <
                                p className = "text-gray-600" > Created: { new Date(product.created_at).toLocaleDateString() } < /p> <
                                div className = "mt-4 flex space-x-2" >
                                <
                                button onClick = {
                                    () => editProduct(product) }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded" >
                                Edit <
                                /button> <
                                button onClick = {
                                    () => deleteProduct(product.id) }
                                className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded" >
                                Delete <
                                /button> <
                                /div> <
                                /div>
                            ))
                        } {
                            products.length === 0 && ( <
                                p className = "text-gray-600" > No products found. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Zones Tab */ } {
                    activeTab === 'zones' && ( <
                        div className = "zones-tab" >
                        <
                        div className = "flex justify-between items-center mb-4" >
                        <
                        h2 className = "text-2xl font-bold" > Zone Management < /h2> <
                        button onClick = {
                            () => {
                                setEditingZone(null);
                                setZoneForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    zoneName: ''
                                });
                                setShowZoneForm(true);
                            }
                        }
                        className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
                        Register Zone Admin <
                        /button> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            zones.map((zone) => ( <
                                div key = { zone.id }
                                className = "bg-white p-4 rounded shadow" >
                                <
                                h3 className = "text-lg font-semibold" > { zone.full_name } < /h3> <
                                p className = "text-gray-600" > Phone: { zone.phone_number } < /p> <
                                p className = "text-gray-600" > Zone: { zone.zone_name } < /p> <
                                p className = "text-gray-600" > Registered: { new Date(zone.created_at).toLocaleDateString() } < /p> <
                                div className = "mt-4 flex space-x-2" >
                                <
                                button onClick = {
                                    () => editZone(zone) }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded" >
                                Edit <
                                /button> <
                                button onClick = {
                                    () => deleteZone(zone.id) }
                                className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded" >
                                Delete <
                                /button> <
                                /div> <
                                /div>
                            ))
                        } {
                            zones.length === 0 && ( <
                                p className = "text-gray-600" > No zone admins registered yet. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                } <
                /main> <
                /div>

                { /* Product Form Modal */ } {
                    showProductForm && ( <
                        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                        <
                        div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
                        <
                        h2 className = "text-xl font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add Product' } < /h2> {
                            productError && < div className = "text-red-500 mb-4" > { productError } < /div>} <
                                form onSubmit = { handleProductSubmit } >
                                <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700" > Product Name < /label> <
                                input
                            type = "text"
                            value = { productForm.name }
                            onChange = {
                                (e) => setProductForm({...productForm, name: e.target.value }) }
                            className = "w-full border rounded p-2"
                            required
                                /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700" > Category < /label> <
                                input
                            type = "text"
                            value = { productForm.category }
                            onChange = {
                                (e) => setProductForm({...productForm, category: e.target.value }) }
                            className = "w-full border rounded p-2"
                            required
                                /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700" > Amount < /label> <
                                input
                            type = "number"
                            min = "1"
                            value = { productForm.amount }
                            onChange = {
                                (e) => setProductForm({...productForm, amount: e.target.value }) }
                            className = "w-full border rounded p-2"
                            required
                                /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700" > Price < /label> <
                                input
                            type = "number"
                            step = "0.01"
                            min = "0.01"
                            value = { productForm.price }
                            onChange = {
                                (e) => setProductForm({...productForm, price: e.target.value }) }
                            className = "w-full border rounded p-2"
                            required
                                /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700" > Description < /label> <
                                textarea
                            value = { productForm.description }
                            onChange = {
                                (e) => setProductForm({...productForm, description: e.target.value }) }
                            className = "w-full border rounded p-2"
                            rows = "3"
                            required
                                /
                                >
                                <
                                /div> <
                                div className = "flex justify-end space-x-2" >
                                <
                                button
                            type = "button"
                            onClick = {
                                () => {
                                    setShowProductForm(false);
                                    setEditingProduct(null);
                                    setProductForm({ name: '', category: '', amount: '', price: '', description: '' });
                                    setProductError('');
                                }
                            }
                            className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                                Cancel <
                                /button> <
                                button
                            type = "submit"
                            className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                                { editingProduct ? 'Update' : 'Add' } <
                                /button> <
                                /div> <
                                /form> <
                                /div> <
                                /div>
                        )
                    }

                    { /* Zone Form Modal */ } {
                        showZoneForm && ( <
                            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                            <
                            div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
                            <
                            h2 className = "text-xl font-bold mb-4" > { editingZone ? 'Edit Zone Admin' : 'Register Zone Admin' } < /h2> {
                                zoneError && < div className = "text-red-500 mb-4" > { zoneError } < /div>} <
                                    form onSubmit = { handleZoneSubmit } >
                                    <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700" > Full Name < /label> <
                                    input
                                type = "text"
                                value = { zoneForm.fullName }
                                onChange = {
                                    (e) => setZoneForm({...zoneForm, fullName: e.target.value }) }
                                className = "w-full border rounded p-2"
                                required
                                    /
                                    >
                                    <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700" > Phone Number < /label> <
                                    input
                                type = "tel"
                                value = { zoneForm.phoneNumber }
                                onChange = {
                                    (e) => setZoneForm({...zoneForm, phoneNumber: e.target.value }) }
                                className = "w-full border rounded p-2"
                                required
                                    /
                                    >
                                    <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700" > Zone Name < /label> <
                                    input
                                type = "text"
                                value = { zoneForm.zoneName }
                                onChange = {
                                    (e) => setZoneForm({...zoneForm, zoneName: e.target.value }) }
                                className = "w-full border rounded p-2"
                                required
                                    /
                                    >
                                    <
                                    /div> {
                                        !editingZone && ( <
                                            >
                                            <
                                            div className = "mb-4" >
                                            <
                                            label className = "block text-gray-700" > Password < /label> <
                                            input type = "password"
                                            value = { zoneForm.password }
                                            onChange = {
                                                (e) => setZoneForm({...zoneForm, password: e.target.value }) }
                                            className = "w-full border rounded p-2"
                                            required = {!editingZone }
                                            /> <
                                            /div> <
                                            div className = "mb-4" >
                                            <
                                            label className = "block text-gray-700" > Confirm Password < /label> <
                                            input type = "password"
                                            value = { zoneForm.confirmPassword }
                                            onChange = {
                                                (e) => setZoneForm({...zoneForm, confirmPassword: e.target.value }) }
                                            className = "w-full border rounded p-2"
                                            required = {!editingZone }
                                            /> <
                                            /div> <
                                            />
                                        )
                                    } <
                                    div className = "flex justify-end space-x-2" >
                                    <
                                    button
                                type = "button"
                                onClick = {
                                    () => {
                                        setShowZoneForm(false);
                                        setEditingZone(null);
                                        setZoneError('');
                                        setZoneForm({
                                            fullName: '',
                                            phoneNumber: '',
                                            password: '',
                                            confirmPassword: '',
                                            zoneName: ''
                                        });
                                    }
                                }
                                className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                                    Cancel <
                                    /button> <
                                    button
                                type = "submit"
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                                    { editingZone ? 'Update' : 'Register' } <
                                    /button> <
                                    /div> <
                                    /form> <
                                    /div> <
                                    /div>
                            )
                        }

                        { /* Request Status Modal - SCROLLABLE VERSION */ } {
                            showRequestModal && ( <
                                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                                <
                                div className = "bg-white rounded shadow-lg w-full max-w-2xl max-h-[90vh] overflow-hidden flex flex-col" >
                                <
                                div className = "p-6 border-b" >
                                <
                                h2 className = "text-xl font-bold" > Request Details < /h2> <
                                /div>

                                <
                                div className = "overflow-y-auto flex-1 p-6" > {
                                    selectedRequest && ( <
                                        div className = "mb-6" >
                                        <
                                        div className = "grid grid-cols-1 md:grid-cols-2 gap-4 mb-4" >
                                        <
                                        div >
                                        <
                                        p > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                                        <
                                        p > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                                        <
                                        p > < strong > Amount: < /strong> {selectedRequest.amount}</p >
                                        <
                                        /div> <
                                        div >
                                        <
                                        p > < strong > Location: < /strong> {selectedRequest.region_name} / { selectedRequest.zone_name }
                                        / {selectedRequest.woreda_name} / { selectedRequest.kebele_name } < /p> <
                                        p > < strong > Request Date: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p >
                                        <
                                        p > < strong > Status: < /strong> {getStatusBadge(selectedRequest.status)}</p >
                                        <
                                        /div> <
                                        /div>

                                        <
                                        div className = "mb-4" >
                                        <
                                        h3 className = "font-semibold mb-2" > Reason
                                        for Request: < /h3> <
                                        p className = "bg-gray-50 p-3 rounded" > { selectedRequest.reason } < /p> <
                                        /div>

                                        <
                                        div className = "approval-flow" >
                                        <
                                        h3 className = "font-semibold mb-2" > Approval Flow: < /h3> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } <
                                        /div>

                                        {
                                            canRegionAdminAct(selectedRequest) && ( <
                                                div className = "mt-4" >
                                                <
                                                h3 className = "font-semibold mb-2" > Decision Reason: < /h3> <
                                                textarea value = { decisionReason }
                                                onChange = {
                                                    (e) => setDecisionReason(e.target.value) }
                                                className = "w-full border rounded p-2"
                                                rows = "3"
                                                placeholder = "Enter reason for your decision" /
                                                >
                                                <
                                                /div>
                                            )
                                        } <
                                        /div>
                                    )
                                } <
                                /div>

                                <
                                div className = "p-4 border-t bg-gray-50" > {
                                    canRegionAdminAct(selectedRequest) && ( <
                                        div className = "flex justify-end space-x-2" >
                                        <
                                        button onClick = {
                                            () => {
                                                setShowRequestModal(false);
                                                setSelectedRequest(null);
                                                setRequestStatus('');
                                                setDecisionReason('');
                                            }
                                        }
                                        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                                        Cancel <
                                        /button> <
                                        button onClick = { confirmStatusUpdate }
                                        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                                        Confirm { requestStatus } <
                                        /button> <
                                        /div>
                                    )
                                }

                                {
                                    !canRegionAdminAct(selectedRequest) && ( <
                                        div className = "flex justify-end" >
                                        <
                                        button onClick = {
                                            () => {
                                                setShowRequestModal(false);
                                                setSelectedRequest(null);
                                                setRequestStatus('');
                                                setDecisionReason('');
                                            }
                                        }
                                        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
                                        Close <
                                        /button> <
                                        /div>
                                    )
                                } <
                                /div> <
                                /div> <
                                /div>
                            )
                        } <
                        /div>
                    );
                };

                export default RegionDashboard;
