// src/dashboards/FederalDashboard.js
import React, { useEffect, useState } from 'react';
import axios from '../api'; // make sure your api.js has export default axios instance

export default function FederalDashboard({ currentUser }) {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch all requests
    const fetchRequests = async() => {
        try {
            const res = await axios.get('/requests'); // your backend endpoint
            setRequests(res.data);
        } catch (err) {
            console.error('Failed to fetch requests:', err);
            alert('Failed to fetch requests');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchRequests();
    }, []);

    // Filter requests for consistency region → kebele
    const consistentRequests = requests.filter(r =>
        r.region_name === r.zone_name &&
        r.zone_name === r.woreda_name &&
        r.woreda_name === r.kebele_name
    );

    // Approve or reject a request
    const handleApproval = async(requestId, status) => {
        try {
            await axios.put(`/requests/${requestId}/approve`, { status });
            // Update frontend after approval
            setRequests(prev =>
                prev.map(r =>
                    r.id === requestId ? {...r, status } : r
                )
            );
        } catch (err) {
            console.error('Failed to update request:', err);
            alert('Failed to update request');
        }
    };

    if (loading) return <p > Loading requests... < /p>;

    return ( <
        div className = "p-4" >
        <
        h1 className = "text-2xl font-bold mb-4" > Federal Dashboard < /h1> {
            consistentRequests.length === 0 ? ( <
                p > No consistent requests found < /p>
            ) : ( <
                table className = "min-w-full bg-white border" >
                <
                thead >
                <
                tr >
                <
                th className = "border px-4 py-2" > Request ID < /th> <
                th className = "border px-4 py-2" > Farmer Name < /th> <
                th className = "border px-4 py-2" > Product < /th> <
                th className = "border px-4 py-2" > Region < /th> <
                th className = "border px-4 py-2" > Zone < /th> <
                th className = "border px-4 py-2" > Woreda < /th> <
                th className = "border px-4 py-2" > Kebele < /th> <
                th className = "border px-4 py-2" > Status < /th> <
                th className = "border px-4 py-2" > Actions < /th> <
                /tr> <
                /thead> <
                tbody > {
                    consistentRequests.map(r => ( <
                        tr key = { r.id } >
                        <
                        td className = "border px-4 py-2" > { r.id } < /td> <
                        td className = "border px-4 py-2" > { r.farmer_name } < /td> <
                        td className = "border px-4 py-2" > { r.product_name } < /td> <
                        td className = "border px-4 py-2" > { r.region_name } < /td> <
                        td className = "border px-4 py-2" > { r.zone_name } < /td> <
                        td className = "border px-4 py-2" > { r.woreda_name } < /td> <
                        td className = "border px-4 py-2" > { r.kebele_name } < /td> <
                        td className = "border px-4 py-2" > { r.status } < /td> <
                        td className = "border px-4 py-2 space-x-2" >
                        <
                        button className = "bg-green-500 text-white px-2 py-1 rounded"
                        onClick = {
                            () => handleApproval(r.id, 'approved') } >
                        Approve <
                        /button> <
                        button className = "bg-red-500 text-white px-2 py-1 rounded"
                        onClick = {
                            () => handleApproval(r.id, 'rejected') } >
                        Reject <
                        /button> <
                        /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table>
            )
        } <
        /div>
    );
}
