import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const FarmerDashboard = () => {
  const [activeTab, setActiveTab] = useState('requests');
  const [user, setUser] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const navigate = useNavigate();

  // Data states
  const [requests, setRequests] = useState([]);
  const [deliveries, setDeliveries] = useState([]);
  const [products, setProducts] = useState([]);

  // Modals
  const [showRequestModal, setShowRequestModal] = useState(false);
  const [showStatusModal, setShowStatusModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [editingRequest, setEditingRequest] = useState(null);

  // Form
  const [requestForm, setRequestForm] = useState({
    product_id: '',
    quantity: '',
    note: ''
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = JSON.parse(localStorage.getItem('user') || 'null');
    const userType = localStorage.getItem('userType');

    if (!token || userType !== 'Farmer' || !userData) {
      navigate('/login');
      return;
    }

    setUser(userData);
    fetchDashboardData();
  }, [navigate]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Products
      const prodRes = await fetch('http://localhost:5000/api/farmers/products', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (prodRes.ok) setProducts(await prodRes.json());

      // Requests
      const reqRes = await fetch('http://localhost:5000/api/farmers/requests', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (reqRes.ok) setRequests(await reqRes.json());

      // Deliveries
      const delRes = await fetch('http://localhost:5000/api/farmers/deliveries', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (delRes.ok) setDeliveries(await delRes.json());
    } catch (err) {
      console.error('Error loading dashboard:', err);
      setError('Error loading dashboard');
    } finally {
      setLoading(false);
    }
  };

  // === AUTH ===
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // === REQUEST MANAGEMENT ===
  const handleRequestSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!requestForm.product_id || !requestForm.quantity) {
        alert('Product and quantity are required');
        return;
      }

      const url = editingRequest
        ? `http://localhost:5000/api/farmers/request/${editingRequest.id}`
        : 'http://localhost:5000/api/farmers/request';
      const method = editingRequest ? 'PUT' : 'POST';

      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({
          product_id: parseInt(requestForm.product_id),
          quantity: parseInt(requestForm.quantity),
          note: requestForm.note
        })
      });

      const data = await res.json();
      if (res.ok) {
        setShowRequestModal(false);
        setEditingRequest(null);
        setRequestForm({ product_id: '', quantity: '', note: '' });
        fetchDashboardData();
        alert(editingRequest ? 'Request updated' : 'Request created');
      } else {
        alert(data.message || 'Error processing request');
      }
    } catch (err) {
      console.error('Request submit error:', err);
    }
  };

  const handleEditRequest = (request) => {
    // Restriction: only editable if kebele and above still pending
    if (
      request.kebele_status !== 'Pending' ||
      request.woreda_status !== 'Pending' ||
      request.zone_status !== 'Pending' ||
      request.region_status !== 'Pending' ||
      request.federal_status !== 'Pending'
    ) {
      alert('Cannot edit: request already processed at kebele or higher level');
      return;
    }

    setEditingRequest(request);
    setRequestForm({
      product_id: request.product_id.toString(),
      quantity: request.quantity.toString(),
      note: request.note || ''
    });
    setShowRequestModal(true);
  };

  const handleDeleteRequest = async (id) => {
    if (!window.confirm('Delete this request?')) return;
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/farmers/request/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      if (res.ok) {
        fetchDashboardData();
        alert('Request deleted');
      } else {
        alert(data.message || 'Error deleting');
      }
    } catch (err) {
      console.error('Delete error:', err);
    }
  };

  // === DELIVERY CONFIRMATION ===
  const handleConfirmDelivery = async (requestId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/farmers/requests/${requestId}/confirm-delivery`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' },
        body: JSON.stringify({})
      });
      const data = await res.json();
      if (res.ok) {
        alert('Delivery confirmed!');
        fetchDashboardData();
      } else {
        alert(data.message || 'Error confirming delivery');
      }
    } catch (err) {
      console.error('Confirm delivery error:', err);
    }
  };

  // === RENDER HELPERS ===
  const getStatusBadge = (status) => {
    let color = 'bg-gray-500';
    if (status === 'Pending') color = 'bg-yellow-500';
    if (status === 'Accepted') color = 'bg-blue-500';
    if (status === 'Rejected') color = 'bg-red-500';
    if (status === 'Approved') color = 'bg-green-600';
    return (
      <span className={`px-2 py-1 rounded text-white text-xs ${color}`}>
        {status}
      </span>
    );
  };

  if (loading) return <div className="p-8">Loading...</div>;
  if (error) return <div className="p-8 text-red-500">{error}</div>;

  return (
    <div className={`flex h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
      {/* Sidebar */}
      <div className={`w-64 p-4 flex flex-col ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg`}>
        <h2 className="text-2xl font-bold mb-6">Farmer Dashboard</h2>
        <nav className="flex flex-col space-y-2">
          <button className={`py-2 px-3 rounded ${activeTab === 'requests' ? 'bg-blue-600 text-white' : ''}`} onClick={() => setActiveTab('requests')}>üìã My Requests</button>
          <button className={`py-2 px-3 rounded ${activeTab === 'products' ? 'bg-blue-600 text-white' : ''}`} onClick={() => setActiveTab('products')}>üõí Products</button>
          <button className={`py-2 px-3 rounded ${activeTab === 'deliveries' ? 'bg-blue-600 text-white' : ''}`} onClick={() => setActiveTab('deliveries')}>üì¶ Deliveries</button>
        </nav>
        <div className="mt-auto">
          <button className="w-full py-2 px-3 rounded bg-gray-600 text-white mb-2" onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
          </button>
          <button className="w-full py-2 px-3 rounded bg-red-600 text-white" onClick={handleLogout}>Logout</button>
        </div>
      </div>

      {/* Main content */}
      <div className="flex-1 p-6 overflow-y-auto">
        <h1 className="text-2xl font-bold mb-4">Welcome, {user?.fullName} (Farmer)</h1>

        {/* Requests Tab */}
        {activeTab === 'requests' && (
          <div>
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold">My Requests</h2>
              <button className="bg-green-600 text-white px-3 py-1 rounded" onClick={() => setShowRequestModal(true)}>‚ûï New Request</button>
            </div>
            <div className="grid gap-4">
              {requests.map(req => (
                <div key={req.id} className={`p-4 rounded shadow ${darkMode ? 'bg-gray-700' : 'bg-white'}`}>
                  <h3 className="font-semibold">{req.product_name}</h3>
                  <p>Qty: {req.quantity}</p>
                  <p>{getStatusBadge(req.status)}</p>
                  <div className="flex space-x-2 mt-2">
                    {/* Restriction: Edit/Delete only if kebele+above Pending */}
                    {(req.kebele_status === 'Pending' && req.woreda_status === 'Pending' && req.zone_status === 'Pending' && req.region_status === 'Pending' && req.federal_status === 'Pending') && (
                      <>
                        <button className="px-2 py-1 bg-blue-600 text-white rounded" onClick={() => handleEditRequest(req)}>‚úèÔ∏è Edit</button>
                        <button className="px-2 py-1 bg-red-600 text-white rounded" onClick={() => handleDeleteRequest(req.id)}>üóëÔ∏è Delete</button>
                      </>
                    )}
                    {/* Confirm Delivery visible only if accepted */}
                    {(req.status === 'Accepted') && (
                      <button className="px-2 py-1 bg-green-600 text-white rounded" onClick={() => handleConfirmDelivery(req.id)}>‚úÖ Confirm Delivery</button>
                    )}
                  </div>
                </div>
              ))}
              {requests.length === 0 && <p>No requests yet</p>}
            </div>
          </div>
        )}

        {/* Products Tab */}
        {activeTab === 'products' && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Products</h2>
            <div className="grid gap-4 md:grid-cols-2">
              {products.map(p => (
                <div key={p.id} className={`p-4 rounded shadow ${darkMode ? 'bg-gray-700' : 'bg-white'}`}>
                  <h3 className="font-semibold">{p.name}</h3>
                  <p>{p.description}</p>
                  <p>Category: {p.category}</p>
                  <p>Price: {p.price}</p>
                  <button className="mt-2 px-2 py-1 bg-green-600 text-white rounded"
                    onClick={() => {
                      setEditingRequest(null);
                      setRequestForm({ product_id: p.id.toString(), quantity: '', note: '' });
                      setShowRequestModal(true);
                    }}
                  >üìã Request</button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Deliveries Tab */}
        {activeTab === 'deliveries' && (
          <div>
            <h2 className="text-xl font-semibold mb-4">My Deliveries</h2>
            <div className="grid gap-4">
              {deliveries.map(d => (
                <div key={d.id} className={`p-4 rounded shadow ${darkMode ? 'bg-gray-700' : 'bg-white'}`}>
                  <h3 className="font-semibold">{d.product_name}</h3>
                  <p>Quantity: {d.quantity}</p>
                  <p>Accepted at: {d.accepted_role} by {d.accepted_admin_name}</p>
                  <p>Confirmed: {new Date(d.confirmation_date).toLocaleString()}</p>
                </div>
              ))}
              {deliveries.length === 0 && <p>No confirmed deliveries</p>}
            </div>
          </div>
        )}

        {/* Request Modal */}
        {showRequestModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className={`p-6 rounded shadow w-96 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
              <h3 className="text-lg font-bold mb-4">{editingRequest ? 'Edit Request' : 'New Request'}</h3>
              <form onSubmit={handleRequestSubmit} className="space-y-4">
                <div>
                  <label className="block">Product</label>
                  <select
                    value={requestForm.product_id}
                    onChange={e => setRequestForm({ ...requestForm, product_id: e.target.value })}
                    className="w-full border px-2 py-1 rounded"
                    required
                    disabled={!!editingRequest}
                  >
                    <option value="">Select</option>
                    {products.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                  </select>
                </div>
                <div>
                  <label className="block">Quantity</label>
                  <input type="number" min="1" value={requestForm.quantity}
                    onChange={e => setRequestForm({ ...requestForm, quantity: e.target.value })}
                    className="w-full border px-2 py-1 rounded"
                    required />
                </div>
                <div>
                  <label className="block">Note</label>
                  <textarea value={requestForm.note}
                    onChange={e => setRequestForm({ ...requestForm, note: e.target.value })}
                    className="w-full border px-2 py-1 rounded" />
                </div>
                <div className="flex justify-end space-x-2">
                  <button type="submit" className="bg-blue-600 text-white px-3 py-1 rounded">
                    {editingRequest ? 'Update' : 'Create'}
                  </button>
                  <button type="button" className="bg-gray-400 px-3 py-1 rounded"
                    onClick={() => { setShowRequestModal(false); setEditingRequest(null); }}>Cancel</button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Status Modal (optional, same as before) */}
        {showStatusModal && selectedRequest && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className={`p-6 rounded shadow w-96 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
              <h3 className="text-lg font-bold mb-4">Request Status</h3>
              <p>Product: {selectedRequest.product_name}</p>
              <p>Qty: {selectedRequest.quantity}</p>
              <p>Status: {getStatusBadge(selectedRequest.status)}</p>
              <div className="mt-4 flex justify-end">
                <button className="bg-gray-500 text-white px-3 py-1 rounded"
                  onClick={() => { setShowStatusModal(false); setSelectedRequest(null); }}>Close</button>
              </div>
            </div>
          </div>
        )}

      </div>
    </div>
  );
};

export default FarmerDashboard;
