import React from 'react';
import { Home, ClipboardList, Package, CheckCircle, XCircle, FileText } from 'lucide-react';
import { getAuth, signOut } from 'firebase/auth';

const Sidebar = ({ currentPage, setCurrentPage, isSidebarOpen, userRole }) => {

    const navigation = [
        { name: 'Dashboard', href: 'dashboard', icon: Home, roles: ['farmer', 'woreda', 'zone', 'region'] },
        { name: 'New Request', href: 'new-request', icon: Package, roles: ['farmer'] },
        { name: 'Pending Requests', href: 'pending-requests', icon: ClipboardList, roles: ['woreda', 'zone', 'region'] },
        { name: 'Approved Requests', href: 'approved-requests', icon: CheckCircle, roles: ['woreda', 'zone', 'region'] },
        { name: 'Rejected Requests', href: 'rejected-requests', icon: XCircle, roles: ['woreda', 'zone', 'region'] },
        { name: 'Reports', href: 'reports', icon: FileText, roles: ['woreda', 'zone', 'region'] },
    ];

    const filteredNavigation = navigation.filter(item => item.roles.includes(userRole));

    return ( <
        div className = { `transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out fixed inset-y-0 left-0 w-64 bg-white dark:bg-gray-800 shadow-lg z-40 lg:relative lg:translate-x-0` } >
        <
        div className = "flex flex-col h-full p-4" >
        <
        div className = "flex-shrink-0" >
        <
        h2 className = "text-xl font-bold text-gray-800 dark:text-gray-100" > Menu < /h2> < /
        div > <
        nav className = "flex-1 mt-6" >
        <
        ul className = "space-y-2" > {
            filteredNavigation.map((item) => ( <
                li key = { item.name } >
                <
                button onClick = {
                    () => setCurrentPage(item.href)
                }
                className = { `w-full flex items-center p-3 rounded-lg text-left transition-colors duration-200 ${
                                        currentPage === item.href
                                            ? 'bg-indigo-500 text-white shadow-md'
                                            : 'text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
                                    }` } >
                <
                item.icon className = "h-5 w-5 mr-3" / >
                <
                span className = "font-medium" > { item.name } < /span> < /
                button > <
                /li>
            ))
        } <
        /ul> < /
        nav > <
        /div> < /
        div >
    );
};

export default Sidebar;
