import React, { useState } from 'react';
import { useForm } from 'react-hook-form';

const LoginForm = ({ onLogin, locations, showModal }) => {
    const { register, handleSubmit, watch } = useForm();
    const [isSubmitting, setIsSubmitting] = useState(false);
    const selectedRole = watch('role');
    const selectedRegion = watch('region');
    const selectedZone = watch('zone');
    const selectedWoreda = watch('woreda');
    const filteredZones = locations.zones.filter(zone => zone.parent_id === selectedRegion);
    const filteredWoredas = locations.woredas.filter(woreda => woreda.parent_id === selectedZone);

    const onSubmit = async(data) => {
        setIsSubmitting(true);
        let locationId = '';
        if (data.locationLevel === 'region') {
            locationId = data.region;
        } else if (data.locationLevel === 'zone') {
            locationId = data.zone;
        } else if (data.locationLevel === 'woreda') {
            locationId = data.woreda;
        }
        await onLogin({...data, locationId });
        setIsSubmitting(false);
    };

    return ( <
        div className = "flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300" >
        <
        div className = "w-full max-w-md p-8 space-y-6 bg-white dark:bg-gray-800 rounded-xl shadow-lg" >
        <
        h2 className = "text-3xl font-bold text-center text-gray-900 dark:text-white" > Login to PRMS < /h2> <
        p className = "text-center text-gray-600 dark:text-gray-400" > Please select your role and location to
        continue. < /p> <
        form onSubmit = { handleSubmit(onSubmit) }
        className = "space-y-6" >
        <
        div >
        <
        label htmlFor = "name"
        className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
        Your Name <
        /label> <
        input id = "name"
        type = "text" {...register('name', { required: true }) }
        className = "mt-1 block w-full px-3 py-2 bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm text-gray-900 dark:text-gray-100" /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "role"
        className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
        Role <
        /label> <
        select id = "role" {...register('role', { required: true }) }
        className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-gray-100" >
        <
        option value = "" > Select a role < /option> <
        option value = "farmer" > Farmer < /option> <
        option value = "woreda" > Woreda Administrator < /option> <
        option value = "zone" > Zone Administrator < /option> <
        option value = "region" > Region Administrator < /option> <
        /select> <
        /div>

        {
            selectedRole && selectedRole !== 'farmer' && ( <
                div >
                <
                label htmlFor = "locationLevel"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
                Location Level <
                /label> <
                select id = "locationLevel" {...register('locationLevel', { required: true }) }
                className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-gray-100" >
                <
                option value = "" > Select Location Level < /option> <
                option value = "region" > Region < /option> <
                option value = "zone" > Zone < /option> <
                option value = "woreda" > Woreda < /option> <
                /select> <
                /div>
            )
        }

        {
            watch('locationLevel') === 'region' && ( <
                div >
                <
                label htmlFor = "region"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
                Region <
                /label> <
                select id = "region" {...register('region', { required: true }) }
                className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-gray-100" >
                <
                option value = "" > Select a Region < /option> {
                    locations.regions.map(region => ( <
                        option key = { region.id }
                        value = { region.id } > { region.name } < /option>
                    ))
                } <
                /select> <
                /div>
            )
        }

        {
            watch('locationLevel') === 'zone' && ( <
                >
                <
                div >
                <
                label htmlFor = "region"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
                Region <
                /label> <
                select id = "region" {...register('region', { required: true }) }
                className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-gray-100" >
                <
                option value = "" > Select a Region < /option> {
                    locations.regions.map(region => ( <
                        option key = { region.id }
                        value = { region.id } > { region.name } < /option>
                    ))
                } <
                /select> <
                /div> {
                    selectedRegion && ( <
                        div >
                        <
                        label htmlFor = "zone"
                        className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
                        Zone <
                        /label> <
                        select id = "zone" {...register('zone', { required: true }) }
                        className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-text-gray-100" >
                        <
                        option value = "" > Select a Zone < /option> {
                            filteredZones.map(zone => ( <
                                option key = { zone.id }
                                value = { zone.id } > { zone.name } < /option>
                            ))
                        } <
                        /select> <
                        /div>
                    )
                } <
                />
            )
        }

        {
            watch('locationLevel') === 'woreda' && ( <
                >
                <
                div >
                <
                label htmlFor = "region"
                className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
                Region <
                /label> <
                select id = "region" {...register('region', { required: true }) }
                className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-gray-100" >
                <
                option value = "" > Select a Region < /option> {
                    locations.regions.map(region => ( <
                        option key = { region.id }
                        value = { region.id } > { region.name } < /option>
                    ))
                } <
                /select> <
                /div> {
                    selectedRegion && ( <
                        div >
                        <
                        label htmlFor = "zone"
                        className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
                        Zone <
                        /label> <
                        select id = "zone" {...register('zone', { required: true }) }
                        className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-gray-100" >
                        <
                        option value = "" > Select a Zone < /option> {
                            filteredZones.map(zone => ( <
                                option key = { zone.id }
                                value = { zone.id } > { zone.name } < /option>
                            ))
                        } <
                        /select> <
                        /div>
                    )
                } {
                    selectedZone && ( <
                        div >
                        <
                        label htmlFor = "woreda"
                        className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
                        Woreda <
                        /label> <
                        select id = "woreda" {...register('woreda', { required: true }) }
                        className = "mt-1 block w-full pl-3 pr-10 py-2 text-base bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md text-gray-900 dark:text-gray-100" >
                        <
                        option value = "" > Select a Woreda < /option> {
                            filteredWoredas.map(woreda => ( <
                                option key = { woreda.id }
                                value = { woreda.id } > { woreda.name } < /option>
                            ))
                        } <
                        /select> <
                        /div>
                    )
                } <
                />
            )
        }

        <
        button type = "submit"
        disabled = { isSubmitting }
        className = "w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50" >
        { isSubmitting ? 'Logging in...' : 'Login' } <
        /button> <
        /form> <
        /div> <
        /div>
    );
};

export default LoginForm;
