import React, { useState, useEffect } from 'react';
import {
    Box,
    Typography,
    Grid,
    Card,
    CardContent,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    CircularProgress,
    Alert
} from '@mui/material';
import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
    PieChart,
    Pie,
    Cell
} from 'recharts';
import {
    getZones,
    getZoneStats,
    getZonePerformanceTrend
} from '../services/api';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D'];

const ZoneDashboard = () => {
    const [zones, setZones] = useState([]);
    const [selectedZone, setSelectedZone] = useState('');
    const [zoneStats, setZoneStats] = useState(null);
    const [performanceData, setPerformanceData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchZones();
    }, []);

    useEffect(() => {
        if (selectedZone) {
            fetchZoneData(selectedZone);
        }
    }, [selectedZone]);

    const fetchZones = async() => {
        try {
            setLoading(true);
            const response = await getZones();
            setZones(response.data);
            if (response.data.length > 0) {
                setSelectedZone(response.data[0].id);
            }
        } catch (err) {
            setError('Failed to fetch zones');
            console.error('Error fetching zones:', err);
        } finally {
            setLoading(false);
        }
    };

    const fetchZoneData = async(zoneId) => {
        try {
            setLoading(true);
            setError('');

            const [statsResponse, trendResponse] = await Promise.all([
                getZoneStats(zoneId),
                getZonePerformanceTrend(zoneId)
            ]);

            setZoneStats(statsResponse.data);
            setPerformanceData(trendResponse.data);
        } catch (err) {
            setError('Failed to fetch zone data');
            console.error('Error fetching zone data:', err);
        } finally {
            setLoading(false);
        }
    };

    const formatChartData = () => {
        if (!zoneStats) return [];

        return [
            { name: 'Total Sales', value: zoneStats.totalSales || 0 },
            { name: 'Active Customers', value: zoneStats.activeCustomers || 0 },
            { name: 'Revenue', value: zoneStats.revenue || 0 },
            { name: 'Orders', value: zoneStats.totalOrders || 0 }
        ];
    };

    const handleZoneChange = (event) => {
        setSelectedZone(event.target.value);
    };

    if (loading && !zoneStats) {
        return ( <
            Box display = "flex"
            justifyContent = "center"
            alignItems = "center"
            minHeight = "400px" >
            <
            CircularProgress / >
            <
            /Box>
        );
    }

    return ( <
        Box >
        <
        Typography variant = "h4"
        gutterBottom >
        Zone Performance Dashboard <
        /Typography>

        {
            error && ( <
                Alert severity = "error"
                sx = {
                    { mb: 2 } } > { error } <
                /Alert>
            )
        }

        <
        Grid container spacing = { 3 } > { /* Zone Selection */ } <
        Grid item xs = { 12 } >
        <
        FormControl fullWidth >
        <
        InputLabel > Select Zone < /InputLabel> <
        Select value = { selectedZone }
        label = "Select Zone"
        onChange = { handleZoneChange }
        disabled = { loading } >
        {
            zones.map((zone) => ( <
                MenuItem key = { zone.id }
                value = { zone.id } > { zone.name } <
                /MenuItem>
            ))
        } <
        /Select> <
        /FormControl> <
        /Grid>

        { /* Performance Metrics */ } {
            zoneStats && ( <
                >
                <
                Grid item xs = { 12 }
                md = { 6 } >
                <
                Card >
                <
                CardContent >
                <
                Typography variant = "h6"
                gutterBottom >
                Performance Overview <
                /Typography> <
                Grid container spacing = { 2 } >
                <
                Grid item xs = { 6 } >
                <
                Typography variant = "body2"
                color = "textSecondary" >
                Total Sales <
                /Typography> <
                Typography variant = "h6" >
                $ {
                    (zoneStats.totalSales || 0).toLocaleString() } <
                /Typography> <
                /Grid> <
                Grid item xs = { 6 } >
                <
                Typography variant = "body2"
                color = "textSecondary" >
                Active Customers <
                /Typography> <
                Typography variant = "h6" > {
                    (zoneStats.activeCustomers || 0).toLocaleString() } <
                /Typography> <
                /Grid> <
                Grid item xs = { 6 } >
                <
                Typography variant = "body2"
                color = "textSecondary" >
                Revenue <
                /Typography> <
                Typography variant = "h6" >
                $ {
                    (zoneStats.revenue || 0).toLocaleString() } <
                /Typography> <
                /Grid> <
                Grid item xs = { 6 } >
                <
                Typography variant = "body2"
                color = "textSecondary" >
                Total Orders <
                /Typography> <
                Typography variant = "h6" > {
                    (zoneStats.totalOrders || 0).toLocaleString() } <
                /Typography> <
                /Grid> <
                /Grid> <
                /CardContent> <
                /Card> <
                /Grid>

                { /* Performance Chart */ } <
                Grid item xs = { 12 }
                md = { 6 } >
                <
                Card >
                <
                CardContent >
                <
                Typography variant = "h6"
                gutterBottom >
                Performance Distribution <
                /Typography> <
                ResponsiveContainer width = "100%"
                height = { 300 } >
                <
                PieChart >
                <
                Pie data = { formatChartData() }
                cx = "50%"
                cy = "50%"
                labelLine = { false }
                label = {
                    ({ name, percent }) =>
                    `${name}: ${(percent * 100).toFixed(0)}%`
                }
                outerRadius = { 80 }
                fill = "#8884d8"
                dataKey = "value" >
                {
                    formatChartData().map((entry, index) => ( <
                        Cell key = { `cell-${index}` }
                        fill = { COLORS[index % COLORS.length] }
                        />
                    ))
                } <
                /Pie> <
                Tooltip / >
                <
                /PieChart> <
                /ResponsiveContainer> <
                /CardContent> <
                /Card> <
                /Grid>

                { /* Trend Chart */ } <
                Grid item xs = { 12 } >
                <
                Card >
                <
                CardContent >
                <
                Typography variant = "h6"
                gutterBottom >
                Performance Trend <
                /Typography> <
                ResponsiveContainer width = "100%"
                height = { 400 } >
                <
                BarChart data = { performanceData } >
                <
                CartesianGrid strokeDasharray = "3 3" / >
                <
                XAxis dataKey = "period" / >
                <
                YAxis / >
                <
                Tooltip / >
                <
                Legend / >
                <
                Bar dataKey = "sales"
                fill = "#0088FE"
                name = "Sales" / >
                <
                Bar dataKey = "revenue"
                fill = "#00C49F"
                name = "Revenue" / >
                <
                Bar dataKey = "customers"
                fill = "#FFBB28"
                name = "Customers" / >
                <
                /BarChart> <
                /ResponsiveContainer> <
                /CardContent> <
                /Card> <
                /Grid> <
                />
            )
        } <
        /Grid> <
        /Box>
    );
};

export default ZoneDashboard;
